INSERT INTO `products` VALUES ('1', '♠', '1', '10900260', '<p><strong>Điểm nổi bật của sản phẩm</strong></p>\r\n\r\n<p>- Khe cắm thẻ nhớ SD / SDHC / SDXC</p>\r\n\r\n<p>- Cảm biến 18MP APS-C CMOS&nbsp;</p>\r\n\r\n<p>- 3.0 &quot;Clear View Vari-Angle LCD</p>\r\n\r\n<p>- Dải ISO 100-6400</p>\r\n\r\n<p>- Chế độ phim Full HD w / Manual Exposure</p>\r\n\r\n<p>- Tương th&iacute;ch với ống k&iacute;nh Canon EF v&agrave; EF-S</p>\r\n', '<h2 style=\"text-align:center\"><strong><span style=\"color:#0000cd\"><span style=\"font-size:14px\">Canon EOS 600D</span></span></strong></h2>\r\n\r\n<p><span style=\"color:#0000cd\"><strong><span style=\"font-size:14px\">GIỚI THIỆU</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif\"><strong>M&aacute;y ảnh Canon 600D</strong> được trang bị cảm biến CMOS chất lượng cao18 megapixel v&agrave; bộ xử l&yacute; h&igrave;nh ảnh DIGIC 4 cực kỳ mạnh mẽ, cho h&igrave;nh ảnh chất lượng cao. C&aacute;c t&iacute;nh năng như Tự động lựa chọn cảnh th&ocirc;ng minh, Basic+ v&agrave; chức năng Hướng dẫn sẽ gi&uacute;p bạn sử dụng c&aacute;c chức năng s&aacute;ng tạo của m&aacute;y một c&aacute;ch đơn giản. T&iacute;nh năng S&aacute;ng tạo cải tiến cũng như ghi video Full HD v&agrave; chụp h&igrave;nh từ Video đều được t&iacute;ch hợp trong&nbsp;Canon 600D<strong>.</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif\">Với ti&ecirc;u ch&iacute; tối ưu ho&aacute; thao t&aacute;c chụp h&igrave;nh của </span>Canon 600D<span style=\"font-family:arial,helvetica,sans-serif\"> đ&atilde; được trang bị cảm biến CMOS 18megapixel (cỡ APS-C) nhằm tăng hiệu quả chụp h&igrave;nh của bạn, gi&uacute;p bạn chụp được mọi cảnh từ những phong cảnh ngoạn mục cho tới chụp cận cảnh động vật hoang d&atilde;, r&otilde; đến từng chi tiết. Với độ ph&acirc;n giải lớn 18megapixel, bạn cũng c&oacute; thể chuyển ảnh tới ph&ograve;ng trưng b&agrave;y thậm ch&iacute; ngay cả sau khi crop h&igrave;nh.</span></span></p>\r\n\r\n<p>&nbsp;\r\n<p>&nbsp;</p>\r\n</p>\r\n\r\n<p><span style=\"font-size:12px\"><strong>M&agrave;n h&igrave;nh ngắm LCD đa g&oacute;c ngắm cỡ rộng 3-inch</strong></span></p>\r\n\r\n<p><span style=\"font-family:arial,helvetica,sans-serif; font-size:12px\">Để ghi v&agrave; xem lại ảnh, </span><strong>Canon 600D</strong><span style=\"font-family:arial,helvetica,sans-serif; font-size:12px\"> sử dụng một m&agrave;n h&igrave;nh ngắm LCD đa g&oacute;c ngắm cỡ rộng 3 inch rất r&otilde; n&eacute;t bao gồm hơn một triệu điểm ảnh. M&agrave;n h&igrave;nh đa g&oacute;c ngắm hiện đại cũng được bổ xung th&ecirc;m để những tay m&aacute;y ham t&igrave;m t&ograve;i c&oacute; thể ph&aacute;t hiện được c&aacute;c khu&ocirc;n h&igrave;nh đẹp bởi thiết kế đặc biệt kiểu khớp nối cho ph&eacute;p bạn xoay m&agrave;n h&igrave;nh ở hầu hết c&aacute;c g&oacute;c.</span></p>\r\n\r\n<h2><span style=\"font-size:12px\"><strong>T&iacute;nh năng tự động tr&ecirc;n Canon 600D lựa chọn cảnh th&ocirc;ng minh</strong></span></h2>\r\n\r\n<p><span style=\"font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif\">Cuộc c&aacute;ch mạng nổi bật của Canon ch&iacute;nh l&agrave; t&iacute;nh năng Tự động lựa chọn cảnh th&ocirc;ng minh, một chế độ chụp ho&agrave;n to&agrave;n tự động, sử dụng tối ưu Hệ thống d&ograve; t&igrave;m cảnh EOS, một hệ thống t&iacute;ch hợp nhằm ph&acirc;n t&iacute;ch mọi yếu tố của cảnh chụp bao gồm mặt người v&agrave;/hoặc độ s&aacute;ng của ảnh, chuyển động, độ tương phản, m&agrave;u sắc, khoảng c&aacute;ch v&agrave; c&ograve;n nhiều hơn thế nữa. Th&ocirc;ng tin n&agrave;y sau đ&oacute; sẽ được T&iacute;nh năng Tự động chọn cảnh th&ocirc;ng minh đ&aacute;nh gi&aacute; để tạo ra những tấm h&igrave;nh đẹp, thu được n&eacute;t tinh tu&yacute; của những vật chụp xung quanh.</span></span></p>\r\n\r\n<p><span style=\"color:#0000cd\"><span style=\"font-size:14px\"><strong>T&Iacute;NH NĂNG NỔI BẬT</strong></span></span></p>\r\n\r\n<p><strong>Bộ cảm biến CMOS k&iacute;ch thước APS-C độ ph&acirc;n giải 18MP</strong></p>\r\n\r\n<p>Được trang bị bộ cảm biến CMOS APS-cỡ C 18 megapixel cao cấp trong d&ograve;ng DSLR, <a href=\"http://zshop.vn/canon-600d-18-55.html\"><em>Canon EOS 600D </em></a>c&oacute; thể chụp được định dạng ảnh lớn nhất (RAW v&agrave; JPEG+RAW) với chi tiết v&agrave; độ n&eacute;t cao ngay cả trong điều kiện &aacute;nh s&aacute;ng yếu. Cảm biến 18MP CMOS kh&ocirc;ng chỉ cho ph&eacute;p crop ảnh vẫn giữ được độ sắc n&eacute;t của chi tiết m&agrave; c&ograve;n đem đến tốc độ truyền dữ liệu si&ecirc;u tốc nhờ đường t&iacute;n hiệu đa k&ecirc;nh, tăng cường khả năng phản ứng của m&aacute;y cũng như giảm nhiễu v&agrave; c&ocirc; lập c&aacute;c m&agrave;u giả được ph&aacute;t hiện.</p>\r\n\r\n<h2><span style=\"font-size:12px\"><strong>Canon 600D sở hữu vi xử l&yacute; h&igrave;nh ảnh DIGIC 4</strong></span></h2>\r\n\r\n<p>DIGIC 4 l&agrave; thế hệ thứ s&aacute;u trong bộ xử l&yacute; h&igrave;nh ảnh được nghi&ecirc;n cứu v&agrave; sản xuất của Canon. So với DIGIC 3, DIGIC 4 tăng cường c&aacute;c thuật to&aacute;n khử nhiễu nhằm hạn chế tối đa c&aacute;c chi tiết giả tr&ecirc;n ảnh, <strong>Canon EOS 600D</strong> cải tiến vượt trội chất lượng h&igrave;nh ảnh v&agrave; tốc độ xử l&iacute;. Ngo&agrave;i ra, DIGIC 4 c&ograve;n hỗ trợ chụp ảnh định dạng file RAW với độ s&acirc;u m&agrave;u 14 bit v&agrave; chức năng Face Detection cho ph&eacute;p nhận diện khu&ocirc;n mặt nhanh gấp 3 lần v&agrave; tốc độ xử l&yacute; t&iacute;n hiệu gấp 1.3 lần.</p>\r\n\r\n<p><strong>Dải ISO chuẩn 100 - 6400 mở rộng đến 12800</strong></p>\r\n\r\n<p>Dải ISO rộng đặc biệt quan trọng khi cho ph&eacute;p bạn chụp ảnh ở nhiều điều kiện s&aacute;ng kh&aacute;c nhau, d&ugrave; l&agrave; khung cảnh ngo&agrave;i trời ban ng&agrave;y hay cảnh đ&ecirc;m thiếu s&aacute;ng.&nbsp;<strong>Canon 600D</strong>&nbsp;c&oacute; dải ISO chuẩn 100 - 6400 v&agrave; c&oacute; thể mở rộng đến 12800 gi&uacute;p tăng vượt trội tốc độ đ&oacute;ng mở m&agrave;n tr&acirc;̣p, nắm bắt c&aacute;c khoảnh khắc để c&oacute; tốc độ chụp chớp nho&aacute;ng 3.7fps, hạn ch&ecirc;́ t&ocirc;́i đa hi&ecirc;̣n tượng nh&ograve;e do rung lắc của th&acirc;n m&aacute;y v&agrave; chụp được cả những đối tượng di chuyển.</p>\r\n\r\n<p><strong>Nhật k&iacute; video với Video Snapshot v&agrave; Advanced movie crop</strong></p>\r\n\r\n<p>B&ecirc;n cạnh khả năng quay phim Full HD,&nbsp;<span style=\"color:#0000ff\"><strong>Canon 600D</strong></span>&nbsp;thể hiện sự n&acirc;ng cấp vượt trội với chức năng Advanced movie crop cho ph&eacute;p bạn zoom từ 3x -10x c&aacute;c đối tượng đang quay phim v&agrave; chức năng Video Snapshot để gh&eacute;p những đoạn clip ngắn với nhau k&egrave;m nhạc nền. Với chức năng Video Snapshot đầy th&uacute; vị n&agrave;y, những kỉ niệm như ng&agrave;y đầu b&eacute; con nh&agrave; bạn đi nh&agrave; trẻ, nhổ chiếc răng đầu ti&ecirc;n hoặc tập đi xe đạp đều sẽ trở th&agrave;nh đoạn video nhật k&iacute; đầy cảm x&uacute;c m&agrave; bạn kh&ocirc;ng thể bỏ qua.</p>\r\n\r\n<p><strong>Hỗ trợ tối đa chụp ảnh đẹp</strong></p>\r\n\r\n<p>Với hai chức năng Basic+ v&agrave; Scene Intelligent Auto, bạn chỉ cần n&acirc;ng&nbsp;<strong>Canon 600D</strong>&nbsp;l&ecirc;n l&agrave; m&aacute;y ảnh sẽ tự động ph&acirc;n t&iacute;ch v&agrave; c&agrave;i đặt c&aacute;c th&ocirc;ng số như Độ phơi s&aacute;ng, Lấy n&eacute;t, C&acirc;n bằng trắng, Điều chỉnh s&aacute;ng để mang đến t&ocirc;ng m&agrave;u đẹp nhất cho bức ảnh của bạn. Ngo&agrave;i ra, chức năng Feature Guide c&ograve;n hiển thị m&ocirc; tả chức năng của c&aacute;c n&uacute;t bấm để bạn c&oacute; được c&aacute;i nh&igrave;n tổng quan nhất về chiếc m&aacute;y ảnh của m&igrave;nh ngay những lần đầu sử dụng.</p>\r\n\r\n<p><strong>M&agrave;n h&igrave;nh lật v&agrave; chức năng LiveView</strong></p>\r\n\r\n<p>Chức năng ngắm trực tiếp LiveView giờ đ&atilde; được tăng th&ecirc;m sức mạnh v&agrave; sự tiện dụng nhờ m&agrave;n h&igrave;nh 3&rdquo; c&oacute; thể lật xoay để bạn dễ d&agrave;ng ngắm v&agrave; chụp từ những g&oacute;c độ kh&oacute;, khi bạn sử dụng tripod hoặc chụp từ hướng tr&ecirc;n đầu m&igrave;nh xuống. M&agrave;n h&igrave;nh n&agrave;y sử dụng c&ocirc;ng nghệ hiển thị TFT 1.040.000 điểm m&agrave;u được phủ 2 lớp chống l&oacute;a v&agrave; 7 mức s&aacute;ng để bạn được xem lại những khoảng khắc tuyệt vời nhất m&agrave;&nbsp;<strong>Canon 600D</strong>&nbsp;nắm bắt.</p>\r\n', '1', '0', '41', '2014-12-01');
INSERT INTO `products` VALUES ('2', 'Canon 7D Mark II (Body)', '1', '38448300', '<p><strong>Điểm nổi bật của sản phẩm</strong></p>\r\n\r\n<p>- Sử dụng chip xử l&yacute; h&igrave;nh ảnh k&eacute;p Digic V (7D hiện tại l&agrave; chip k&eacute;p Digic IV), tốc độ chụp l&ecirc;n tới 10,2 khung h&igrave;nh mỗi gi&acirc;y (7D hiện tại l&agrave; 8 khung h&igrave;nh mỗi gi&acirc;y).</p>\r\n\r\n<p>- Khe cắm thẻ nhớ k&eacute;p nhưng chưa c&oacute; th&ocirc;ng tin về loại thẻ hỗ trợ.&nbsp;</p>\r\n\r\n<p>- Canon 7D Mark II&nbsp;cũng được cho l&agrave; được thiết kế lại với nhiều điểm kh&aacute;c biệt so với &quot;đời đầu&quot; cũng như bộ pin mới.</p>\r\n\r\n<p>- Thời điểm ra mắt của sản phẩm l&agrave; v&agrave;o th&aacute;ng 2 năm sau.</p>\r\n<p><strong>Điểm nổi bật của sản phẩm</strong></p>\r\n\r\n<p>- Sử dụng chip xử l&yacute; h&igrave;nh ảnh k&eacute;p Digic V (7D hiện tại l&agrave; chip k&eacute;p Digic IV), tốc độ chụp l&ecirc;n tới 10,2 khung h&igrave;nh mỗi gi&acirc;y (7D hiện tại l&agrave; 8 khung h&igrave;nh mỗi gi&acirc;y).</p>\r\n\r\n<p>- Khe cắm thẻ nhớ k&eacute;p nhưng chưa c&oacute; th&ocirc;ng tin về loại thẻ hỗ trợ.&nbsp;</p>\r\n\r\n<p>- Canon 7D Mark II&nbsp;cũng được cho l&agrave; được thiết kế lại với nhiều điểm kh&aacute;c biệt so với &quot;đời đầu&quot; cũng như bộ pin mới.</p>\r\n\r\n<p>- Thời điểm ra mắt của sản phẩm l&agrave; v&agrave;o th&aacute;ng 2 năm sau.</p>\r\n<p><strong>Điểm nổi bật của sản phẩm</strong></p>\r\n\r\n<p>- Sử dụng chip xử l&yacute; h&igrave;nh ảnh k&eacute;p Digic V (7D hiện tại l&agrave; chip k&eacute;p Digic IV), tốc độ chụp l&ecirc;n tới 10,2 khung h&igrave;nh mỗi gi&acirc;y (7D hiện tại l&agrave; 8 khung h&igrave;nh mỗi gi&acirc;y).</p>\r\n\r\n<p>- Khe cắm thẻ nhớ k&eacute;p nhưng chưa c&oacute; th&ocirc;ng tin về loại thẻ hỗ trợ.&nbsp;</p>\r\n\r\n<p>- Canon 7D Mark II&nbsp;cũng được cho l&agrave; được thiết kế lại với nhiều điểm kh&aacute;c biệt so với &quot;đời đầu&quot; cũng như bộ pin mới.</p>\r\n\r\n<p>- Thời điểm ra mắt của sản phẩm l&agrave; v&agrave;o th&aacute;ng 2 năm sau.</p>\r\n', '<h2 style=\"text-align:center\"><strong><span style=\"color:#0000cd\"><span style=\"font-size:14px\">Canon 7D Mark II (Body)</span></span></strong></h2>\r\n\r\n<p><span style=\"color:#0000cd\"><span style=\"font-size:14px\"><strong>GIỚI THIỆU</strong></span></span></p>\r\n\r\n<p>Được thiết kế với thi&ecirc;n hướng th&ecirc;m tốc độ, <strong>M&aacute;y ảnh Canon DSLR EOS 7D Mark II</strong>&nbsp;c&oacute; đặc trưng bởi cảm biến APS-C với k&iacute;ch thước 20.2MP CMOS, bộ xử l&yacute; h&igrave;nh ảnh k&eacute;p DIGIC 6 v&agrave; tốc độ chụp li&ecirc;n tục tr&ecirc;n 10 fps. Ph&aacute;t hiện được 65 điểm cross-type, hệ thống AF v&agrave; Dual Pixel CMOS AF c&oacute; lợi &iacute;ch c&ocirc;ng nghệ nhanh ch&oacute;ng, hiệu quả, tập trung ch&iacute;nh x&aacute;c cho cả k&iacute;nh ngắm quang học v&agrave; xem bắn trực tiếp. Ngo&agrave;i ra, sự kết hợp của bộ vi xử l&yacute; h&igrave;nh ảnh k&eacute;p c&ugrave;ng với bộ cảm biến CMOS cũng g&oacute;p phần l&agrave;m tăng cường độ nhạy &aacute;nh s&aacute;ng yếu với ISO 16000 v&agrave; tiếp tục mở rộng đến ISO 51200. M&aacute;y ảnh Canon 7D Mark II dễ d&agrave;ng thực hiện trong một m&ocirc;i trường kh&oacute; khăn v&agrave; l&agrave; một c&ocirc;ng cụ l&yacute; tưởng giống như một c&ocirc;ng cụ cho nhiếp ảnh gia động vật hoang d&atilde;. M&aacute;y ảnh DSLR EOS 7D Mark II đều đ&atilde; nằm trong tay của c&aacute;c nhiếp ảnh gia hiện đại.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Việc bổ sung th&ecirc;m c&aacute;c t&iacute;nh năng cho <strong>7D Mark II</strong> để hỗ trợ việc ghi lại đầy đủ c&aacute;c video HD 1080p c&oacute; sẵn v&agrave; tốc độ khung h&igrave;nh l&ecirc;n đến 60 fps. Ngo&agrave;i mục đ&iacute;ch tiết kiệm thẻ nhớ trong, video kh&ocirc;ng n&eacute;n cũng c&oacute; thể được ghi lại để ghi &acirc;m t&ugrave;y chọn b&ecirc;n ngo&agrave;i th&ocirc;ng qua kết nối HDMI. Tuỳ chỉnh Movie Servo AF, được hỗ trợ bởi hệ thống k&eacute;p Pixel CMOS AF để l&agrave;m nổi bật khả năng quay phim bằng c&aacute;ch cho ph&eacute;p thiết lập tốc độ v&agrave; độ nhạy điều khiển li&ecirc;n tục tập trung với c&aacute;c đối tượng di chuyển.</p>\r\n\r\n<p>Điều đặc biệt của khả năng chụp ảnh l&agrave; một cơ chế m&agrave;n trập bền thử nghiệm cho đến 200.000 actuations. M&agrave;n h&igrave;nh LCD 1.04m-dot Clear View II 3,0&quot; cố định được đưa v&agrave;o thiết kế, Canon 7D Mark II c&ugrave;ng với ống k&iacute;nh ngắm th&ocirc;ng minh, c&ocirc;ng cụ quang học cho khả năng gi&aacute;m s&aacute;t v&agrave; xem lại linh hoạt. N&uacute;t t&ugrave;y chỉnh được đặt tr&ecirc;n th&acirc;n m&aacute;y để điều khiển c&aacute;c thiết lập c&aacute; nh&acirc;n v&agrave; t&iacute;ch hợp module GPS kh&aacute;c nhau v&agrave; tự động nh&uacute;ng v&agrave;o c&aacute;c tập tin dữ liệu h&igrave;nh ảnh v&agrave;o thẻ nhớ khi chụp.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"color:#0000cd\"><span style=\"font-size:14px\"><strong>T&Iacute;NH NĂNG NỔI BẬT</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:12px\"><strong>7D Mark II với cảm biến APS-C 20.2MP CMOS v&agrave; bộ xử l&yacute; h&igrave;nh ảnh k&eacute;p DIGIC 6</strong></span></p>\r\n\r\n<p>- Với một cảm biến APS-C CMOS 20,2 megapixel v&agrave; bộ vi xử l&yacute; h&igrave;nh ảnh k&eacute;p DIGIC 6, Canon 7D Mark II c&oacute; khả năng tạo độ ph&acirc;n giải cao, chi tiết h&igrave;nh ảnh v&agrave; video 1080p full HD với độ nhạy đ&aacute;ng ch&uacute; &yacute; v&agrave; tiếng ồn tối thiểu. Một dải ISO 100-16.000 v&agrave; hơn nữa được mở rộng theo ti&ecirc;u chuẩn ISO 51200. <em>7D Mark II</em> rất ph&ugrave; hợp để l&agrave;m việc trong điều kiện &aacute;nh s&aacute;ng kh&oacute; khăn v&agrave; sức mạnh xử l&yacute; của bộ vi xử l&yacute; DIGIC 6 cũng g&oacute;p phần n&acirc;ng cao chất lượng &aacute;nh s&aacute;ng yếu với m&agrave;u sắc mịn m&agrave;ng, sửa chữa tỷ lệ hao hụt &acirc;m v&agrave; quang sai.</p>\r\n\r\n<p>- Việc thiết kế bộ vi xử l&yacute; k&eacute;p c&oacute; lợi cho hệ thống h&igrave;nh ảnh th&ocirc;ng qua việc bổ sung c&aacute;c tốc độ, &aacute;p dụng biện 10 fps chụp li&ecirc;n tục cho hơn 1000 h&igrave;nh ảnh JPEG, RAW 31 hoặc 19 file RAW + JPEG, c&ugrave;ng với tốc độ m&agrave;n trập 1/8000 gi&acirc;y. V&agrave; tốc độ đồng bộ flash tr&ecirc;n 1/250 gi&acirc;y. Ngo&agrave;i ra, thời gian đứng chỉ 0.055 gi&acirc;y. Thời gian khởi động chỉ 0,1 gi&acirc;y v&agrave; m&aacute;y ảnh đ&atilde; sẵn s&agrave;ng để thực hiện như mong muốn.</p>\r\n\r\n<div><strong>Hệ thống AF - 65 điểm Cross-Type</strong></div>\r\n\r\n<p>Khi chụp với k&iacute;nh ngắm quang học, tất cả hệ thống 65 điểm cross-type lấy n&eacute;t tự động được sử dụng để đảm bảo độ rộng khung cho việc tập trung ch&iacute;nh x&aacute;c v&agrave; nhanh ch&oacute;ng. Điểm trung t&acirc;m của cụm n&agrave;y l&agrave; một cảm biến cross-type k&eacute;p với độ ch&iacute;nh x&aacute;c cao mang lại lợi &iacute;ch l&agrave;m việc với f / 2,8 hoặc nhanh hơn, ống k&iacute;nh v&agrave; hỗ trợ tập trung xuống -3 EV để cho ph&eacute;p l&agrave;m việc trong điều kiện &aacute;nh s&aacute;ng yếu. Những điểm trọng t&acirc;m được h&igrave;nh th&agrave;nh v&agrave; cho ph&eacute;p dẫn đường ở c&aacute;c yếu tố cụ thể trong khung h&igrave;nh để tập trung v&agrave;o, c&aacute;ch kh&aacute;c tất cả c&oacute; thể được sử dụng để đảm bảo tr&ecirc;n to&agrave;n khung.</p>\r\n\r\n<div><strong>Pixel CMOS AF K&eacute;p</strong></div>\r\n\r\n<p>- Pixel CMOS AF K&eacute;p l&agrave; một c&aacute;i nh&igrave;n tập trung c&ocirc;ng nghệ sống v&agrave;o l&agrave;m việc để đảm bảo khả năng lấy n&eacute;t tự động cực kỳ nhanh ch&oacute;ng v&agrave; ch&iacute;nh x&aacute;c m&agrave; hoạt động tương tự như một m&aacute;y quay phim. Hệ thống n&agrave;y t&iacute;ch hợp hai diode t&aacute;ch s&oacute;ng quang ri&ecirc;ng biệt trong mỗi điểm ảnh để cung cấp một mạng lưới rộng khắp v&agrave; d&agrave;y đặc của giai đoạn ph&aacute;t hiện c&aacute;c yếu tố thu thập tr&ecirc;n gần 80% của khung h&igrave;nh để gi&uacute;p tập trung nhanh hơn, kiểm so&aacute;t trực tiếp vị tr&iacute; hơn. Pixel CMOS AF k&eacute;p được sử dụng trong c&aacute;c ứng dụng xem trực tiếp v&agrave; quay video.</p>\r\n\r\n<p>- Khi l&agrave;m việc với h&igrave;nh ảnh, hệ thống n&agrave;y ch&uacute; trọng v&agrave;o hoạt động để c&oacute; được tập trung một c&aacute;ch nhanh ch&oacute;ng v&agrave; ch&iacute;nh x&aacute;c, l&agrave;m cho n&oacute; ph&ugrave; hợp nhất để chụp v&agrave; theo d&otilde;i đối tượng chuyển động v&agrave; quan trọng l&agrave; đạt được độ chuẩn với mỗi cảnh quay. Khi quay video, h&igrave;nh ảnh vẫn mịn m&agrave;ng v&agrave; tự nhi&ecirc;n khi thay đổi c&aacute;c đối tượng kh&aacute;c nhau hoặc c&aacute;c khoảng c&aacute;ch kh&aacute;c nhau trong bối cảnh. Theo d&otilde;i chủ đề trong phim cũng được n&acirc;ng cao do khả năng của hệ thống k&eacute;p Pixel CMOS AF để nhận ra đối tượng v&agrave; duy tr&igrave; tập trung khi l&agrave;m việc trong bối cảnh thay đổi hoặc lộn xộn.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<div><strong>EOS HD Video</strong></div>\r\n\r\n<p>- Full HD quay video 1080p được hỗ trợ trong nhiều tốc độ, l&ecirc;n đến 60 fps, trong cả hai định dạng MOV v&agrave; MP4. Ngo&agrave;i ra, video kh&ocirc;ng n&eacute;n c&oacute; thể được ghi lại từ một t&ugrave;y chọn b&ecirc;n ngo&agrave;i th&ocirc;ng qua đầu ra HDMI. Trong m&aacute;y ảnh, video được ghi lại bằng c&aacute;ch sử dụng chất lượng cao H.264 / MPEG-4 AVC codec v&agrave; điều khiển bằng tay được hỗ trợ tinh tế hơn trong việc nh&igrave;n v&agrave; cảm nhận của bản ghi &acirc;m. Video clip sẽ được tự động ph&acirc;n v&ugrave;ng sau khi k&iacute;ch thước tập tin đ&atilde; đạt đến 4GB v&agrave; cả IPB v&agrave; tất cả n&eacute;n i-khung c&oacute; sẵn để sử dụng t&ugrave;y thuộc v&agrave;o bi&ecirc;n tập.</p>\r\n\r\n<p>- Khi quay video, t&ugrave;y biến Movie Servo AF c&oacute; thể được sử dụng để điều chỉnh tập trung vị tr&iacute;, tốc độ, v&agrave; khoảng thời gian theo d&otilde;i về chất lượng AF trơn mượt v&agrave; mịn. Hỗ trợ cũng c&oacute; sẵn cho c&aacute;c ống k&iacute;nh STM, trong đ&oacute; cung cấp đặc biệt ch&uacute; trọng v&agrave;o tĩnh v&agrave; mịn cho chất lượng tổng thể của bộ phim.</p>\r\n\r\n<p>- Trong m&aacute;y ảnh, &acirc;m thanh được ghi lại bằng c&aacute;ch sử dụng định dạng Linear PCM cho c&aacute;c tập tin MOV, hoặc c&aacute;c định dạng AAC cho c&aacute;c tập tin MP4, hỗ trợ c&oacute; sẵn cho việc sử dụng một micro b&ecirc;n ngo&agrave;i t&ugrave;y chọn trong qu&aacute; tr&igrave;nh ghi. Ngo&agrave;i ra, một đầu v&agrave;o tai nghe cũng c&oacute; sẵn cho thời gian theo d&otilde;i &acirc;m thanh.</p>\r\n\r\n<h2><span style=\"font-size:12px\"><strong>Thiết kế th&acirc;n m&aacute;y Canon 7D Mark II</strong></span></h2>\r\n\r\n<p>Với một cơ thể hợp kim magi&ecirc; bền với tr&agrave;n ngập&nbsp; thử nghiệm đến 200.000 chu kỳ, c&ugrave;ng với tăng cường gương v&agrave; m&agrave;n trập cơ chế ổ đĩa, m&aacute;y ảnh <em>7D Mark II</em> gồ ghề n&agrave;y cũng được bịt k&iacute;n để chống bụi v&agrave; độ ẩm để sử dụng trong điều kiện khắc nghiệt. Ở ph&iacute;a sau của m&aacute;y ảnh l&agrave; m&agrave;n h&igrave;nh LCD 3,0&quot; 1.04m, chấm s&aacute;ng c&oacute; thể được sử dụng để theo d&otilde;i trực tiếp xem, xem x&eacute;t h&igrave;nh ảnh, v&agrave; điều hướng menu. N&oacute; c&oacute; t&iacute;nh năng chống vết bẩn v&agrave; lớp phủ Clear View II chống phản chiếu để cho ph&eacute;p xem tốt hơn trong điều kiện &aacute;nh s&aacute;ng ch&oacute;i. Ngo&agrave;i ra, để ph&aacute;t hiện tầm mắt, một k&iacute;nh ngắm quang học th&ocirc;ng minh II được t&iacute;ch hợp v&agrave;o thiết kế v&agrave; cung cấp đảm bảo 100% khung h&igrave;nh, độ ch&iacute;nh x&aacute;c th&agrave;nh phần lớn hơn v&agrave; một m&agrave;n h&igrave;nh hiển thị lớp phủ để l&agrave;m nổi bật c&aacute;c thiết lập chụp th&iacute;ch hợp, chẳng hạn như một mức độ kỹ thuật số, th&ocirc;ng tin tiếp x&uacute;c, v&agrave; c&aacute;c chức năng m&aacute;y ảnh kh&aacute;c. Thiết kế cơ thể cũng kết hợp một khe cắm thẻ nhớ k&eacute;p chấp nhận một thẻ CompactFlash v&agrave; SD / SDHC / SDXC để mở rộng khả năng lưu tập tin bằng c&aacute;ch cho ph&eacute;p ghi &acirc;m tr&agrave;n hoặc trong m&aacute;y nh&acirc;n bản. M&aacute;y ảnh n&agrave;y cũng c&oacute; một loạt c&aacute;c điều khiển t&ugrave;y biến c&oacute; thể điều chỉnh một loạt c&aacute;c thiết lập dựa tr&ecirc;n sở th&iacute;ch c&aacute; nh&acirc;n của bạn.</p>\r\n\r\n<h2><span style=\"font-size:12px\"><strong>Hệ thống d&ograve; t&igrave;m cảnh v&agrave; phơi s&aacute;ng tr&ecirc;n Canon 7D Mark II</strong></span></h2>\r\n\r\n<p>Để tăng cường độ ch&iacute;nh x&aacute;c li&ecirc;n quan đến độ tiếp x&uacute;c, một hệ thống ISA (Intelligent Scene Analysis) sử dụng cảm biến đo độc lập 150.000-pixel RGBc&oacute; lợi cho c&aacute;c hệ thống ITR AF (theo d&otilde;i th&ocirc;ng minh v&agrave; c&ocirc;ng nhận), cho ph&ugrave; hợp, theo d&otilde;i đối tượng ch&iacute;nh x&aacute;c v&agrave; tiếp x&uacute;c với khả năng đ&oacute;. C&aacute;c hệ thống n&agrave;y cũng được t&iacute;ch hợp c&aacute;c thuật to&aacute;n theo d&otilde;i chuy&ecirc;n dụng được thiết kế để nhận diện khu&ocirc;n mặt v&agrave; m&agrave;u sắc, để hỗ trợ cả chức năng tự động lấy n&eacute;t v&agrave; đo s&aacute;ng tiếp x&uacute;c.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<div><strong>C&aacute;c t&iacute;nh năng kh&aacute;c của m&aacute;y ảnh Canon 7D Mark II</strong></div>\r\n\r\n<p>- Khoảng c&aacute;ch built-in cho ph&eacute;p tạo ra c&aacute;c h&igrave;nh ảnh thời gian tr&ocirc;i đi v&agrave; hỗ trợ ghi &acirc;m 1-99 khung h&igrave;nh li&ecirc;n tiếp trong khoảng thời gian được lựa chọn trước.</p>\r\n\r\n<p>- Thiết lập tốc độ m&agrave;n trập b&oacute;ng cho ph&eacute;p bạn giữ cho m&agrave;n trập mở miễn l&agrave; mong muốn cho một phạm vi mở rộng khả năng s&aacute;ng tạo.</p>\r\n\r\n<p>- Một m&aacute;y thu GPS t&iacute;ch hợp tự động ghi lại dữ liệu vị tr&iacute; tiếp x&uacute;c với nhau, bao gồm kinh độ v&agrave; vĩ độ, độ cao, v&agrave; thời gian UTC như dữ liệu EXIF​​, v&agrave; cũng cung cấp một la b&agrave;n kỹ thuật số. Cũng c&oacute; thể được theo d&otilde;i trong khoảng thời gian thiết lập để giới thiệu c&aacute;c tuyến đường của bạn thực hiện trong qu&aacute; tr&igrave;nh chụp.</p>\r\n\r\n<p>- C&ocirc;ng nghệ kiểm so&aacute;t độ rung gương ti&ecirc;n tiến hỗ trợ đặc biệt tốc độ chụp li&ecirc;n tiếp 7D Mark II v&agrave; gi&uacute;p duy tr&igrave; kết quả ph&ugrave; hợp.</p>\r\n\r\n<p>- Một hệ thống nhấp nh&aacute;y c&oacute; thể tự động cảnh b&aacute;o bạn, trong k&iacute;nh ngắm, khi một nguồn s&aacute;ng nhấp nh&aacute;y đ&atilde; được ph&aacute;t hiện. Những ảnh hưởng của c&aacute;c nguồn &aacute;nh s&aacute;ng c&oacute; thể được bồi thường bằng c&aacute;ch sử dụng chức năng chụp chống rung h&igrave;nh, v&agrave; sẽ tự động ghi lại h&igrave;nh ảnh chỉ trong trường hợp khối lượng &aacute;nh s&aacute;ng cao điểm. Hệ thống n&agrave;y đặc biệt hữu &iacute;ch cho việc giảm tiếp x&uacute;c hoặc m&agrave;u sắc kh&ocirc;ng ph&ugrave; hợp kết quả - 1 việc phổ biến khi li&ecirc;n tục chụp trong điều kiện &aacute;nh s&aacute;ng kh&oacute; khăn.</p>\r\n\r\n<p>- Trong m&aacute;y ảnh <a href=\"http://zshop.vn/may-anh-canon-dslr/\"><strong>Canon 7D Mark II</strong></a> c&oacute; thể chỉnh sửa cho một loạt c&aacute;c quang sai ống k&iacute;nh, bao gồm cả khả năng kiểm so&aacute;t &aacute;nh s&aacute;ng ngoại vi, quang sai m&agrave;u v&agrave; biến dạng kh&aacute;c. Khi l&agrave;m việc trong chế độ xem trực tiếp, c&aacute;c chức năng m&ocirc; phỏng h&igrave;nh ảnh c&oacute; thể được sử dụng để theo d&otilde;i những t&aacute;c động của điều chỉnh biến dạng cho tinh chỉnh kết quả.</p>\r\n\r\n<p>- Được trang bị cổng USB 3.0 c&oacute; tốc độ truyền nhanh giữa m&aacute;y ảnh 7D Mark II v&agrave; một m&aacute;y t&iacute;nh hoặc t&ugrave;y chọn WFT-E7 (Version 2) cho kết nối Wi-Fi.</p>\r\n\r\n<p>- Chế độ Picture Style cho ph&eacute;p bạn thiết lập được x&aacute;c định trước để nh&igrave;n h&igrave;nh ảnh trong m&aacute;y ảnh v&agrave; bao gồm tự động, ti&ecirc;u chuẩn, ch&acirc;n dung, phong cảnh, trung t&iacute;nh, Trung th&agrave;nh, đơn sắc, v&agrave; l&ecirc;n đến ba thiết lập người d&ugrave;ng định nghĩa.</p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>-&nbsp;</strong></span>Hỗ trợ ng&ocirc;n ngữ: Anh, Đức, Ph&aacute;p, H&agrave; Lan, Đan Mạch, Bồ Đ&agrave;o Nha, Phần Lan, &Yacute;, Na Uy, Thụy Điển, T&acirc;y Ban Nha, Hy Lạp, Nga, Ba Lan, Cộng h&ograve;a S&eacute;c, Hungary, Romania, Ukraine, Thổ Nhĩ Kỳ, Ả Rập, Th&aacute;i, Tiếng Trung Quốc, truyền thống Trung Quốc , H&agrave;n Quốc, v&agrave; Nhật Bản.</p>\r\n', '1', '9', '26', '2014-12-02');
INSERT INTO `products` VALUES ('3', 'Canon 5D Mark III (Body)', '1', '59898680', '<p><strong>Điểm nổi bật của sản phẩm</strong></p>\r\n\r\n<p>- Bộ cảm biến 22.3MP CMOS Full-Frame</p>\r\n\r\n<p>- ISO mở rộng(50-102400)</p>\r\n\r\n<p>- Bộ xử l&yacute; DiGiC 5</p>\r\n\r\n<p>- Chụp li&ecirc;n tiếp 6 h&igrave;nh/gi&acirc;y</p>\r\n\r\n<p>- M&agrave;n h&igrave;nh 3,2 inch, 1,04 triệu điểm ảnh</p>\r\n\r\n<p>- Khe cắm thẻ CF, SDXC</p>\r\n', '<h2 style=\"text-align:center\"><strong><span style=\"color:rgb(0, 0, 205); font-size:14px\">Canon 5D Mark III</span></strong></h2>\r\n\r\n<p><strong><span style=\"font-size:14px\">GIỚI THIỆU</span></strong></p>\r\n\r\n<p>Đạt giải thưởng danh gi&aacute; &ldquo;Best Video DSLR&rdquo; của Hiệp hội nhiếp ảnh b&aacute;o ch&iacute; TIPA 2012 v&agrave; giải &ldquo;Best Product 2012-2013&rdquo; cho Advanced SLR Camera của Hiệp hội &acirc;m thanh v&agrave; h&igrave;nh ảnh ch&acirc;u &Acirc;u EISA,&nbsp;<a href=\"http://zshop.vn/may-anh-canon-dslr/\"><strong>Canon 5D Mark III</strong></a><span style=\"color:rgb(44, 43, 43)\">&nbsp;đ&atilde; trở th&agrave;nh một trong những chiếc m&aacute;y ảnh c&oacute; t&iacute;nh s&aacute;ng tạo cao, kế nhiệm đ&agrave;n anh Canon EOS 5D Mark II v&agrave; l&agrave;m thỏa l&ograve;ng mong đợi của những người y&ecirc;u mến thương hiệu Canon. Được trang bị cảm biến Full Frame 35mm 22.3MP mạnh mẽ c&ugrave;ng vi xử l&yacute; ảnh DIGIC 5+ v&agrave; hệ thống lấy n&eacute;t 61 điểm tự động, chiếc m&aacute;y ảnh cho khả năng nhạy s&aacute;ng cao, tối ưu h&oacute;a tốc độ xử l&yacute;, khử nhiễu tốt v&agrave; tăng cường tốc độ xử l&yacute; l&ecirc;n gấp nhiều lần so với những d&ograve;ng th&ocirc;ng thường để ảnh lu&ocirc;n đẹp nhất mỗi lần bạn chụp trong bất kể t&igrave;nh huống n&agrave;o. Với việc được trang bị t&iacute;nh năng quay phim Full HD, đ&acirc;y ch&iacute;nh l&agrave; chiếc m&aacute;y ảnh chuy&ecirc;n nghiệp tiếp tục mang đến định nghĩa mới về nhiếp ảnh c&ugrave;ng cụm từ kh&aacute; th&uacute; vị: video-graphy, gi&uacute;p người thợ chụp sẵn s&agrave;ng ghi lại những khoảnh khắc qu&yacute; gi&aacute; của cuộc sống. C&ugrave;ng đ&oacute; l&agrave; những hỗ trợ cao cấp kh&aacute;c như khả kỹ thuật chụp HDR , cảm biến tự rũ bụi, ISO hỗ trợ l&ecirc;n đến 102.400, thiết kế mạnh mẽ, đ&uacute;ng chuẩn chuy&ecirc;n nghiệp khiến&nbsp;</span><a href=\"http://zshop.vn/may-anh-canon-dslr/\"><strong>Canon 5D Mark III</strong></a><span style=\"color:rgb(44, 43, 43)\">&nbsp;trở th&agrave;nh si&ecirc;u phẩm đ&aacute;ng mơ ước của bạn, những nhiếp ảnh gia t&agrave;i năng.</span></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p><span style=\"color:#0000cd\"><span style=\"font-size:14px\"><strong>T&Iacute;NH NĂNG NỔI BẬT</strong></span></span></p>\r\n\r\n<p><a href=\"http://zshop.vn/may-anh-canon-dslr/\"><strong>Canon 5D Mark III</strong></a><span style=\"color:rgb(44, 43, 43)\">&nbsp;&nbsp;</span><span style=\"color:rgb(44, 43, 43)\">trang bị&nbsp;</span><span style=\"background-color:initial; color:rgb(45, 45, 45); font-family:tahoma,arial,helvetica,sans-serif; font-size:13px\">cảm biến CMOS full-frame</span><span style=\"color:rgb(44, 43, 43)\">&nbsp;</span><strong>&nbsp;</strong><span style=\"color:rgb(44, 43, 43)\">k&iacute;ch thước 36x24mm c&oacute; độ ph&acirc;n giải nhỉnh hơn Mark II một ch&uacute;t, 22.3 Megapixel. Tuy vậy, chất lượng h&igrave;nh ảnh được đ&aacute;nh gi&aacute; r&otilde; n&eacute;t hơn, tuyệt vời hơn người tiền nhiệm Mark II do cảm biến mới c&oacute; nhi&ecirc;u cải tiến về cấu tr&uacute;c transistor, cũng như khả năng giảm nhiễu ngay tr&ecirc;n chip m&agrave; Canon trang bị cho con cưng mới.</span></p>\r\n\r\n<p><span style=\"color:rgb(44, 43, 43)\">Điểm cộng s&aacute;ng nhất của Mark III ch&iacute;nh l&agrave;&nbsp;</span><span style=\"background-color:initial; color:rgb(45, 45, 45); font-family:tahoma,arial,helvetica,sans-serif; font-size:13px\">chip xử l&yacute; ảnh DIGIC 5</span><span style=\"color:rgb(44, 43, 43)\">&nbsp;với tốc độ xử l&yacute; m&agrave; Canon tiết lộ l&agrave; mạnh gấp 17 lần so với DIGIC 4 của Mark II. Hơn cả, n&oacute; hứa hẹn cho h&igrave;nh ảnh r&otilde; n&eacute;t hơn,&nbsp; m&agrave;u sắc trung thực hơn v&agrave; sự chuyển m&agrave;u dịu hơn. Đồng thời, chất lượng h&igrave;nh ảnh cao hơn nhờ khả năng điều chỉnh ngoại vi, quang sai m&agrave;u v&agrave; khử nhiễu tốt hơn ở ISO cao (khi sử dụng với ống k&igrave;nh ngo&agrave;i) m&agrave; kh&ocirc;ng ảnh hưởng đến hiệu suất hoạt động của m&aacute;y. T&iacute;nh năng tr&ecirc;n cũng l&agrave; nhờ DIGIC 5 mang lại.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Độ nhạy s&aacute;ng th&ocirc;ng thường từ&nbsp;, c&oacute; thể mở rộng ISO 100 - 25.600&nbsp;<span style=\"color:rgb(44, 43, 43)\">th&agrave;nh&nbsp;</span><span style=\"background-color:initial; color:rgb(45, 45, 45); font-family:tahoma,arial,helvetica,sans-serif; font-size:13px\">ISO 50 - 102.400</span><span style=\"color:rgb(44, 43, 43)\">, cho h&igrave;nh ảnh chất lượng cao. So với người tiền nhiệm Mark II, chất lượng ảnh của Mark III cải tiến đ&ocirc;i ch&uacute;t&nbsp; trong nhiều mặt: độ tương phản, độ ph&acirc;n giải, r&otilde; n&eacute;t chi tiết, v&agrave; đặc biệt l&agrave; ảnh chụp trong b&oacute;ng tối. Ở chế độ video, độ nhạy s&aacute;ng cao nhất l&agrave; ISO 25.600.</span></p>\r\n\r\n<div>Kh&ocirc;ng những thế, Canon 5D Mark III được người d&ugrave;ng đ&aacute;nh gi&aacute; cao nhờ kế thừa v&agrave; cải tiến c&aacute;c cảm biến cho m&aacute;y ảnh. Cụ thể l&agrave; Canon 5d Mark III kế thừa cảm biến lấy n&eacute;t tự động 61 điểm với 41 điểm crosstype từ EOS 1 D v&agrave; chế độ AI Servo III được cải tiến từ Mark II. Th&ecirc;m v&agrave;o đ&oacute;, Mark III c&ograve;n được trang bị cảm biến đo s&aacute;ng iFCL 2 lớp với 63 v&ugrave;ng đo. Đặc biệt, cảm biến n&agrave;y c&ograve;n hỗ trợ một chế độ đo s&aacute;ng gần với phạm chi 1,5%.</div>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<div>Canon 5D Mark III c&oacute; thể quay phim chuẩn HD 720p ở tốc độ 60 v&agrave; 50 &nbsp;khung h&igrave;nh mỗi gi&acirc;y v&agrave; quay video 1080p ở tốc độ 24, 25 v&agrave; 30 khung h&igrave;nh mỗi gi&acirc;y. V&agrave; như đ&atilde; n&ecirc;u ở tr&ecirc;n, ISO của m&aacute;y đạt mức 100 đến 12.800 v&agrave; mở rộng l&ecirc;n tới mức 25.600 khi quay video chuẩn Full HD v&agrave; chất lượng &acirc;m thanh stereo. R&otilde; r&agrave;ng l&agrave; sản phẩm mới n&agrave;y của Canon lu&ocirc;n hướng đến sự thuận tiện cho người d&ugrave;ng, người d&ugrave;ng c&oacute; thể điều chỉnh c&aacute;c th&ocirc;ng số về độ nhạy s&aacute;ng ISO, tốc độ m&agrave;n trập, khẩu độ khi đang quay video để c&oacute; được những thước phim h&agrave;i l&ograve;ng. Ngo&agrave;i ra, Canon 5D Mark III được trang bị th&ecirc;m 1 jack cắm headphone để kiểm so&aacute;t &acirc;m thanh khi quay video. Độ d&agrave;i tối đa của clip l&agrave; 29 ph&uacute;t, 59 gi&acirc;y ở mọi độ ph&acirc;n giải.</div>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<p><span style=\"background-color:rgb(255, 255, 255); color:rgb(44, 43, 43); font-family:arial,helvetica,sans-serif; font-size:12px\">Một số t&iacute;nh năng nhỏ nhưng cũng v&ocirc; c&ugrave;ng quan trọng g&oacute;p phần tạo n&ecirc;n th&agrave;nh c&ocirc;ng của&nbsp;</span><span style=\"color:rgb(45, 45, 45)\">Canon 5D Mark III:</span></p>\r\n\r\n<p><span style=\"background-color:rgb(255, 255, 255); color:rgb(44, 43, 43); font-family:arial,helvetica,sans-serif; font-size:12px\">- C&oacute; lớp vỏ l&agrave;m từ hợp kim ma-gi&ecirc; được Canon th&ocirc;ng b&aacute;o l&agrave; cho khả năng chống chọi thời tiết, độ ẩm v&agrave; nhiệt độ tốt hơn so với phi&ecirc;n bản trước</span></p>\r\n\r\n<p><span style=\"color:rgb(44, 43, 43)\">- Bộ phận rung để rũ bỏ bụi tr&ecirc;n cảm biến</span></p>\r\n\r\n<p>- Chế độ chụp y&ecirc;n lặng gi&uacute;p giảm ồn khi chụp</p>\r\n\r\n<p>- K&iacute;nh ngắm đạt v&ugrave;ng phủ 100%</p>\r\n\r\n<p>- Hai khe cắm thẻ nhớ</p>\r\n\r\n<p>- bộ pin phụ BG-E11 c&oacute; thể tăng gấp đ&ocirc;i thời lượng chụp</p>\r\n\r\n<p><span style=\"color:rgb(44, 43, 43)\">Một số di chuyển trong vị tr&iacute; c&aacute;c n&uacute;t bấm cũng tạo n&ecirc;n sự hợp l&yacute; v&agrave; h&agrave;i h&ograve;a hơn. C&aacute;c n&uacute;t Menu v&agrave; Info được đặt ở ph&iacute;a tr&ecirc;n b&ecirc;n tr&aacute;i. D&atilde;y 5 n&uacute;t Creative Style, n&uacute;t Rate mới, n&uacute;t Zoom, v&agrave; cuối c&ugrave;ng l&agrave; n&uacute;t xem lại (Playback) v&agrave; n&uacute;t x&oacute;a (Delete) nằm b&ecirc;n tr&aacute;i m&agrave;n h&igrave;nh LCD. Ph&iacute;a dưới m&agrave;n h&igrave;nh LCD c&oacute; ba lỗ nhỏ cho loa, v&agrave; một n&uacute;m tr&ograve;n nhỏ l&agrave; cảm biến &aacute;nh s&aacute;ng xung quanh, c&oacute; t&aacute;c dụng tự động điều chỉnh đ&egrave;n nền của m&agrave;n h&igrave;nh LCD.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Cảm biến Full Frame 35mm 22.3MP</strong></p>\r\n\r\n<p>Tương tự như tr&ecirc;n d&ograve;ng m&aacute;y cao cấp hơn flag-ship EOS-1Ds Mark III, Canon 5D Mark III được trang bị cảm biến Full Frame 35mm, độ ph&acirc;n giải cao 22.3MP, cho chất lượng h&igrave;nh ảnh vượt trội nhờ khả năng nhạy s&aacute;ng cao, tối ưu h&oacute;a tốc độ xử l&yacute; v&agrave; khử nhiễu khi chụp ở ISO cao, cho ảnh đẹp sắc n&eacute;t, r&otilde; r&agrave;ng đến từng chi tiết c&ugrave;ng m&agrave;u sắc sống động v&agrave; trung thực đến kh&ocirc;ng ngờ.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Vi xử l&yacute; ảnh ti&ecirc;n tiến DIGIC 5+</strong></p>\r\n\r\n<p>Tiếp tục những cải tiến mới so với thế hệ trước đ&oacute;, vi xử l&yacute; DIGIC 5+ cho khả năng truyền dữ liệu si&ecirc;u tốc nhờ đường truyền t&iacute;n hiệu đa k&ecirc;nh (nhanh hơn 30% so với DIGIC 5) cũng như c&acirc;n bằng trắng đa v&ugrave;ng, v&agrave; n&acirc;ng cao khả năng phản ứng chụp của m&aacute;y, để ảnh lu&ocirc;n đẹp bất kể t&igrave;nh huống n&agrave;o.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Hệ thống lấy n&eacute;t 61 điểm c&ugrave;ng cảm biến đo s&aacute;ng 63 v&ugrave;ng</strong></p>\r\n\r\n<p>Thừa hưởng hệ thống lấy n&eacute;t 61 điểm tự động với 41 điểm lấy n&eacute;t cross-type từ si&ecirc;u phẩm Canon EOS-1D X, Canon 5D Mark III cho khả năng lấy n&eacute;t tự động nhanh v&agrave; ch&iacute;nh x&aacute;c hơn, đạt tốc độ chụp 6fps để bạn kịp thời nắm bắt mọi khoảnh khắc tho&aacute;ng qua đồng thời để ảnh lu&ocirc;n đẹp nhất mỗi lần bạn bấm m&aacute;y. Cảm biến đo s&aacute;ng đa lớp 63 v&ugrave;ng iFCL được thiết kế mỗi lớp sẽ nhạy cảm với một bước s&oacute;ng kh&aacute;c nhau của &aacute;nh s&aacute;ng sau đ&oacute; sẽ d&ugrave;ng thuật to&aacute;n kết hợp lại cho ra kết quả đo s&aacute;ng đạt độ ch&iacute;nh x&aacute;c cao, bất kể t&igrave;nh huống n&agrave;o v&agrave; bất chấp m&agrave;u sắc chủ đạo l&agrave; g&igrave;.</p>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<p><strong>Trang bị quay phim Full HD tr&ecirc;n một m&aacute;y chụp ảnh chuy&ecirc;n nghiệp</strong></p>\r\n\r\n<p>L&agrave; d&ograve;ng m&aacute;y chuy&ecirc;n nghiệp đầu ti&ecirc;n được trang bị khả năng quay phim chuẩn Full HD, tốc độ 30fps, m&atilde; h&oacute;a H.264 x&oacute;a mờ khoảng c&aacute;ch giữa một chiếc m&aacute;y ảnh chuy&ecirc;n nghiệp v&agrave; một chiếc m&aacute;y quay phim, tạo một định nghĩa mới về kh&aacute;i niệm nhiếp ảnh: video-graphy, đồng thời gi&uacute;p bạn lưu lại những khoảng khắc qu&yacute; gi&aacute; của cuộc sống.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Dải nhạy s&aacute;ng rộng 100-25.600, mở rộng đến 102.400</strong></p>\r\n\r\n<div>Dải ISO rộng đặc biệt quan trọng khi cho ph&eacute;p bạn đ&oacute;ng băng để chụp được những h&igrave;nh ảnh chuyển động nhanh v&agrave; chụp ảnh trong điều kiện &aacute;nh s&aacute;ng yếu. Với tốc độ chụp li&ecirc;n tục 6fps ở độ ph&acirc;n giải cao, Canon 5D Mark III cho bạn dễ d&agrave;ng nắm bắt những h&igrave;nh ảnh th&uacute; vị, độc đ&aacute;o của những đối tượng quanh m&igrave;nh.</div>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<p><strong>Hỗ trợ chụp ảnh gốc RAW v&agrave; kỹ thuật chụp HDR&nbsp;</strong></p>\r\n\r\n<p>Kỹ thuật chụp ảnh HDR (High Dynamic Range) của &hellip;Canon 5D Mark III cho ph&eacute;p mở rộng dải tần nhạy s&aacute;ng nhằm m&ocirc; tả chi tiết v&agrave; trung thực nhất những g&igrave; m&agrave; mắt thường nh&igrave;n thấy được trong khi chụp. Ngo&agrave;i ra, nhắm đến đối tượng l&agrave; c&aacute;c nhiếp ảnh gia chuy&ecirc;n chụp h&igrave;nh thời trang, ảnh cưới, studio, phong cảnh&hellip;Canon 5D Mark III hỗ trợ chế độ chụp ảnh RAW 14-bit để người cầm m&aacute;y lu&ocirc;n c&oacute; những dữ liệu ảnh gốc qu&yacute; gi&aacute; phụ vụ cho việc chỉnh sửa theo mục đ&iacute;ch v&agrave; &yacute; muốn.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Thiết kế mạnh mẽ</strong></p>\r\n\r\n<p>Với thiết kế mạnh mẽ c&ugrave;ng h&igrave;nh d&aacute;ng vu&ocirc;ng vắn, khỏe khoắn, Canon 5D Mark III đem đến cho người d&ugrave;ng cảm gi&aacute;c thật sự chuy&ecirc;n nghiệp khi cầm tr&ecirc;n tay. B&aacute;ng cầm rộng bọc cao su mềm cho cảm gi&aacute;c cầm nắm chắc chắn v&agrave; giảm thiểu tối đa hiện tượng rung lắc khi ngắm chụp, để ảnh lu&ocirc;n đạt chất lượng tối ưu bất kể t&igrave;nh huống n&agrave;o. M&agrave;n h&igrave;nh rộng 3.2&rdquo; gi&uacute;p bạn thoải m&aacute;i xem lại ảnh đ&atilde; chụp, đồng thời được chống ch&oacute;i v&agrave; chống trầy xước, hỗ trợ tối đa người d&ugrave;ng ở những g&oacute;c chụp kh&oacute;.</p>\r\n', '1', '12', '5', '2014-12-02');
INSERT INTO `products` VALUES ('4', ' Canon 700D + Kit 18-55 IS STM (Chính hãng) ', '1', '13898570', '<p><strong>Điểm nổi bật của sản phẩm</strong></p>\r\n\r\n<p><span style=\"color:rgb(45, 45, 45)\">- Cảm biến: 18 megapixel APS-C Hybrid CMOS</span></p>\r\n\r\n<p>- Bộ xử l&iacute; h&igrave;nh ảnh: DIGIC 5, chụp được ảnh RAW 14 bit</p>\r\n\r\n<p>- ISO: 100 đến 12.800, mở rộng đến 25.600</p>\r\n\r\n<p>-&nbsp;<span style=\"color:rgb(0, 0, 0)\">Hệ thống lấy n&eacute;t: 9 điểm, to&agrave;n bộ l&agrave; điểm cross type</span></p>\r\n\r\n<p>- Chụp ảnh li&ecirc;n tục: 5 khung h&igrave;nh/gi&acirc;y</p>\r\n', '<h2 style=\"text-align:center\"><strong>Canon 700D</strong></h2>\r\n\r\n<div>\r\n<h2>&nbsp;</h2>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"color:rgb(0, 0, 205)\"><strong>GIỚI THIỆU</strong></span></span></p>\r\n\r\n<p><strong><a href=\"http://zshop.vn/may-anh-canon-dslr/\">M&aacute;y ảnh</a> Canon 700D</strong>&nbsp;sở hữu cảm biến Hybrid CMOS độ ph&acirc;n giải 18 megapixel, chip xử l&yacute; h&igrave;nh ảnh DIGIC 5 v&agrave; m&agrave;n h&igrave;nh cảm ứng điện dung k&iacute;ch thước 3.2 inch độ ph&acirc;n giải hơn 1 triệu điểm ảnh.&nbsp;Canon 700D&nbsp;c&oacute; tốc độ chụp 5fps với 9 điểm AF. Dải ISO dao động từ 100-12.800, mở rộng l&ecirc;n đến 25.600.</p>\r\n</div>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;\r\n<p><br />\r\nB&ecirc;n cạnh chiếc&nbsp;<span style=\"color:#000000\">Canon 100D</span>, h&ocirc;m nay Canon cũng đ&atilde; ra mắt&nbsp;<a href=\"http://zshop.vn/may-anh-canon-dslr/\"><strong>Canon 700D</strong></a><span style=\"color:rgb(0, 0, 0)\">&nbsp;để tiếp nối cho chiếc&nbsp;</span><span style=\"color:#000000\">Canon 650D</span><span style=\"color:rgb(0, 0, 0)\">. Cấu h&igrave;nh của&nbsp;</span>Canon 700D<span style=\"color:rgb(0, 0, 0)\">&nbsp;tương tự như người tiền nhiệm, bao gồm&nbsp;</span><span style=\"color:rgb(0, 0, 0)\">cảm biến APS-C Hybrid CMOS 18 megapixel hỗ trợ t&iacute;nh năng lấy n&eacute;t tương phản</span><span style=\"color:rgb(0, 0, 0)\">&nbsp;b&ecirc;n cạnh lấy n&eacute;t pha, bộ xử l&iacute; DIGIC 5, hệ thống lấy n&eacute;t với 9 điểm chữ thập, t&iacute;nh năng quay phim Full-HD. Điểm kh&aacute;c biệt của Canon 700D so với 650D đ&oacute; l&agrave; m&aacute;y đi k&egrave;m theo ống kit EF-S 18-55mm f/3.5-5.6 IS STM. Lens n&agrave;y sử dụng m&ocirc;-tơ bước (Stepper Motor) giống với&nbsp;</span><span style=\"color:rgb(0, 0, 0)\">ống 18-135mm STM v&agrave; pancake 40mm STM</span><span style=\"color:rgb(0, 0, 0)\">, do đ&oacute; mang lại khả năng lấy n&eacute;t nhanh v&agrave; &ecirc;m &aacute;i, đặc biệt l&agrave; khi kết hợp với hệ thống lấy n&eacute;t lai của&nbsp;Canon 700D.</span></p>\r\n</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Canon cho biết th&ecirc;m rằng phần b&aacute;ng cầm tay bọc cao su v&agrave; b&aacute;nh xe chọn chế độ chụp tr&ecirc;n&nbsp;Canon 700D&nbsp;đ&atilde; được cải tiến để người d&ugrave;ng cảm thấy tiện v&agrave; thoải m&aacute;i hơn. B&aacute;nh xe n&agrave;y c&oacute; thể xoay 360 độ, gi&uacute;p người d&ugrave;ng nhanh ch&oacute;ng truy cập chế độ chụp đ&ecirc;m, chụp HDR v&agrave; những chế độ cảnh kh&aacute;c m&agrave; kh&ocirc;ng phải vất vả đ&agrave;o s&acirc;u v&agrave;o nhiều menu như 650D.</p>\r\n\r\n<p><br />\r\nVới&nbsp;Canon 700D&nbsp;khi chụp ở chế độ Live View, người d&ugrave;ng c&oacute; thể xem trước một trong 7 bộ lọc hiệu ứng theo thời gian thực.&nbsp;Movie Servo AF&nbsp;l&agrave; một chế độ đ&atilde; từng c&oacute; mặt tr&ecirc;n&nbsp;<span style=\"color:#000000\">Canon 650D</span>&nbsp;v&agrave; b&acirc;y giờ n&oacute; tiếp tục xuất hiện tr&ecirc;n&nbsp;<a href=\"http://zshop.vn/may-anh-canon-dslr/\"><strong>Canon 700D</strong></a><span style=\"color:rgb(0, 0, 0)\">. Chế độ n&agrave;y sử dụng khả năng lấy n&eacute;t lai để lấy n&eacute;t li&ecirc;n tục khi quay phim, tuy nhi&ecirc;n n&oacute; chỉ c&oacute; thể d&ugrave;ng với&nbsp;</span><span style=\"color:rgb(0, 0, 0)\">c&aacute;c ống k&iacute;nh STM</span><span style=\"color:rgb(0, 0, 0)\">&nbsp;m&agrave; th&ocirc;i.</span></p>\r\n\r\n<p><br />\r\nTheo dự kiến,&nbsp;<a href=\"http://zshop.vn/may-anh-canon-dslr/\"><strong>Canon 700D</strong></a>&nbsp;sẽ được b&aacute;n ra trong th&aacute;ng sau với gi&aacute; 749,99$ cho th&acirc;n m&aacute;y; 899,99$ đi k&egrave;m ống&nbsp;EF-S 18-55mm STM; v&agrave; 1099,99$ nếu đi k&egrave;m ống EF-S 18-135mm STM. Lens EF-S 18-55mm f/3.5-5.6 IS STM cũng sẽ l&ecirc;n kệ với c&ugrave;ng thời điểm như tr&ecirc;n v&agrave; gi&aacute; b&aacute;n l&agrave; 249,99$.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"color:#0000cd\"><span style=\"font-size:14px\"><strong>T&Iacute;NH NĂNG NỔI BẬT</strong></span></span></p>\r\n\r\n<p><strong>Hệ thống tự động lấy n&eacute;t lai Hybrid AF</strong></p>\r\n\r\n<p>L&agrave; mẫu EOS đầu ti&ecirc;n sử dụng hệ thống lấy n&eacute;t tự động lai Hybrid AF,&nbsp;Canon 700D&nbsp;cho ph&eacute;p kết hợp giữa lấy n&eacute;t theo pha&nbsp;9 điểm (Phase Detection)&nbsp;v&agrave; lấy n&eacute;t theo độ tương phản (Contract AF), gi&uacute;p cho việc lấy n&eacute;t cực nhanh với độ ch&iacute;nh x&aacute;c cao, ngay cả trong trường hợp thiếu s&aacute;ng hoặc chủ thể ảnh đang di chuyển. Hệ thống lấy n&eacute;t lai n&agrave;y đặc biệt hữu dụng trong chế độ Live View khi gương lật gập xuống v&agrave; lấy n&eacute;t theo pha kh&ocirc;ng hoạt động được.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Sức mạnh vi xử l&yacute; DIGIC 5 c&ugrave;ng cảm biến lai Hybrid CMOS 18.0MP</strong></p>\r\n\r\n<p>Với những cải tiến mới so với đời trước,&nbsp;DIGIC 5&nbsp;được tăng cường khả năng khử nhiễu khi chụp ở ISO cao v&agrave; c&acirc;n bằng trắng đa v&ugrave;ng cho h&igrave;nh ảnh đẹp mịn m&agrave;ng với m&agrave;u sắc tự nhi&ecirc;n, ch&acirc;n thực ngay trong khung cảnh c&oacute; nhiều nguồn chiếu s&aacute;ng phức tạp. Cảm biến lai&nbsp;Hybrid CMOS&nbsp;k&iacute;ch thước 22.3 x 14.9mm cho tốc độ truyền dữ liệu si&ecirc;u tốc, tối ưu h&oacute;a khả năng xử l&yacute; ảnh nhờ đường truyền t&iacute;n hiệu đa k&ecirc;nh, đồng thời tăng khả năng phản ứng của m&aacute;y cho ảnh đẹp bất kể t&igrave;nh huống n&agrave;o.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Dải nhạy s&aacute;ng rộng 100-12800, mở rộng đến 25600</strong></p>\r\n\r\n<p>Dải ISO rộng đặc biệt quan trọng khi cho ph&eacute;p bạn đ&oacute;ng băng để chụp được những h&igrave;nh ảnh chuyển động nhanh v&agrave; chụp ảnh trong điều kiện &aacute;nh s&aacute;ng yếu. Với tốc độ chụp li&ecirc;n tục 5fps ở độ ph&acirc;n giải cao v&agrave; chụp li&ecirc;n tục l&ecirc;n đến 22 tấm,&nbsp;<a href=\"http://zshop.vn/may-anh-canon-dslr/\"><strong>Canon 700D</strong></a>&nbsp;cho bạn dễ d&agrave;ng nắm bắt những h&igrave;nh ảnh th&uacute; vị, độc đ&aacute;o của những đối tượng quanh m&igrave;nh.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Quay phim Full HD chế độ Movie Servo AF</strong></p>\r\n\r\n<p>Ngo&agrave;i khả năng quay phim Full HD chuẩn &acirc;m thanh stereo tốc độ 30fps,&nbsp;Canon 700D&nbsp;c&ograve;n thu h&uacute;t người d&ugrave;ng bởi chế độ Movie Servo AF cho khả năng lấy n&eacute;t tự động to&agrave;n thời gian quay thay v&igrave; lấy n&eacute;t bằng tay như c&aacute;c d&ograve;ng EOS trước. B&ecirc;n cạnh đ&oacute;, m&aacute;y c&ograve;n hỗ trợ th&ecirc;m chế độ Video Snapshot, cho ph&eacute;p người d&ugrave;ng quay những đoạn phim ngắn từ 2-8 gi&acirc;y, rồi bi&ecirc;n tập lại với nhau ngay tr&ecirc;n m&aacute;y.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Cảm biến đo s&aacute;ng iFCL 63 v&ugrave;ng</strong></p>\r\n\r\n<p>Cảm biến đo s&aacute;ng đa lớp 63 v&ugrave;ng iFCL được thiết kế mỗi lớp sẽ nhạy cảm với một bước s&oacute;ng kh&aacute;c nhau của &aacute;nh s&aacute;ng sau đ&oacute; sẽ d&ugrave;ng thuật to&aacute;n kết hợp lại cho ra kết quả đo s&aacute;ng đạt độ ch&iacute;nh x&aacute;c cao, bất kể t&igrave;nh huống n&agrave;o v&agrave; bất chấp m&agrave;u sắc chủ đạo l&agrave; g&igrave;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>T&iacute;ch cực cải tiến so với đời trước</strong></p>\r\n\r\n<p>So với&nbsp;<span style=\"color:#000000\">Canon 650D</span>,&nbsp;Canon 700D&nbsp;đ&atilde; c&oacute; những cải tiến đ&aacute;ng kể v&agrave; ho&agrave;n thiện như:</p>\r\n\r\n<p>- M&aacute;y k&egrave;m ống&nbsp;STM&nbsp;lấy n&eacute;t &ecirc;m v&agrave; ch&iacute;nh x&aacute;c khi quay phim.</p>\r\n\r\n<p>- Lớp phủ nh&aacute;m tr&ecirc;n th&acirc;n m&aacute;y gi&uacute;p cầm nắm v&agrave; thao t&aacute;c chắc tay.</p>\r\n\r\n<p>- V&ograve;ng chỉnh chế độ chụp c&oacute; thể xoay tr&ograve;n 360 độ.</p>\r\n\r\n<p>- Creative filter c&oacute; thể được xem trước khi chụp, để quyết định cảnh chụp nao d&ugrave;ng hiệu ứng n&agrave;o sẽ tuyệt vời hơn.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>M&agrave;n h&igrave;nh cảm ứng lật xoay 3&rdquo;</strong></p>\r\n\r\n<p>Tương tự như tr&ecirc;n 650D,&nbsp;<a href=\"http://zshop.vn/may-anh-canon-dslr/\"><strong>Canon 700D</strong></a>&nbsp;được trang bị m&agrave;n h&igrave;nh LCD cảm ứng điện dung đa điểm, c&oacute; khả năng xoay lật linh hoạt, gi&uacute;p bạn thoải m&aacute;i ngắm, chụp ở mọi g&oacute;c độ kh&oacute;, khi bạn sử dụng tripod hoặc chụp từ hướng tr&ecirc;n đỉnh đầu m&igrave;nh xuống. M&agrave;n h&igrave;nh cảm ứng c&ograve;n đặc biệt hữu &iacute;ch khi người d&ugrave;ng c&oacute; thể chỉnh n&eacute;t v&agrave; nhấn chụp ngay tr&ecirc;n m&agrave;n h&igrave;nh hoặc d&ugrave;ng ng&oacute;n tay để zoom h&igrave;nh to l&ecirc;n hoặc thu nhỏ h&igrave;nh nhỏ lại.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Hỗ trợ tối đa người sử dụng</strong></p>\r\n\r\n<p>Giao diện hướng dẫn của&nbsp;Canon 700D&nbsp;được tr&igrave;nh b&agrave;y hết sức trực quan v&agrave; dễ hiểu, hỗ trợ tối đa những người mới bắt đầu sử dụng DSLR. Ngo&agrave;i ra với mục đ&iacute;ch phục vụ những nhiếp ảnh gia mới v&agrave;o nghề, m&aacute;y c&ograve;n được t&iacute;ch hợp hai chế độ chụp mới l&agrave; chế độ chụp cảnh đ&ecirc;m Handheld Night Scene v&agrave; chế độ ngược s&aacute;ng HDR Backlight Control gi&uacute;p cho việc chụp ảnh của bạn dễ d&agrave;ng hơn bao giờ hết.</p>\r\n\r\n<p><br />\r\n<span style=\"font-size:12px\"><strong>Cấu h&igrave;nh của&nbsp;Canon 700D</strong></span></p>\r\n\r\n<p>- Cảm biến: 18 megapixel APS-C Hybrid CMOS</p>\r\n\r\n<p>- Bộ xử l&iacute; h&igrave;nh ảnh Canon 700D: DIGIC 5, chụp được ảnh RAW 14 bit</p>\r\n\r\n<p>- ISO: 100 đến 12.800, mở rộng đến 25.600</p>\r\n\r\n<p>- Chụp ảnh li&ecirc;n tục của Canon 700D: 5 khung h&igrave;nh/gi&acirc;y</p>\r\n\r\n<p>- M&agrave;n h&igrave;nh: LCD cảm ứng điện dung 3&quot; độ ph&acirc;n giải 1.040.000 pixel, lật xoay</p>\r\n\r\n<p>- Viewfinder: quang học, c&oacute; độ bao phủ 95%</p>\r\n\r\n<p>- Hệ thống lấy n&eacute;t: 9 điểm, to&agrave;n bộ l&agrave; điểm cross type</p>\r\n\r\n<p>- Canon 700D c&oacute; khả năng lấy n&eacute;t theo pha v&agrave; theo độ tương phản</p>\r\n\r\n<p>- Quay phim Full-HD, tốc độ 24/25/30 fps, micro stereo, lấy n&eacute;t li&ecirc;n tục khi quay phim (Movie Servo AF)</p>\r\n\r\n<p>- Canon 700D hỗ trợ 7 bộ lọc hiệu ứng (Creative Filters: sơn đậm, m&agrave;u nước, ảnh trắng đen, soft focus, camera đồ chơi, hiệu ứng mắt c&aacute;, hiệu ứng thu nhỏ), c&oacute; thể xem những bộ lọc n&agrave;y theo thời gian thực trong chế độ Live View m&agrave; kh&ocirc;ng phải chụp ngay</p>\r\n\r\n<p>- Đi k&egrave;m ống kit EF-S 18-55mm f/3.5- 5.6 IS STM</p>\r\n\r\n<p>- K&iacute;ch thước: 133 x 100 x 79 mm</p>\r\n\r\n<p>- Trọng lượng Canon 700D: 580g</p>\r\n', '1', '11', '3', '2014-12-03');
INSERT INTO `products` VALUES ('5', ' Canon 5D Mark III + 24-105mm F4 (Chính hãng) ', '1', '73786700', '<p><strong>Điểm nổi bật của sản phẩm</strong></p>\r\n\r\n<p>- Bộ cảm biến 22.3MP CMOS Full-Frame</p>\r\n\r\n<p>- ISO mở rộng(50-102400)</p>\r\n\r\n<p>- Bộ xử l&yacute; DiGiC 5</p>\r\n\r\n<p>- Chụp li&ecirc;n tiếp 6 h&igrave;nh/gi&acirc;y</p>\r\n\r\n<p>- M&agrave;n h&igrave;nh 3,2 inch, 1,04 triệu điểm ảnh</p>\r\n\r\n<p>- Khe cắm thẻ CF, SDXC</p>\r\n', '<p style=\"text-align:center\"><strong><span style=\"font-size:14px\">Canon 5D Mark III</span></strong></p>\r\n\r\n<p><span style=\"color:#0000cd\"><span style=\"font-size:14px\"><strong>GIỚI THIỆU</strong></span></span></p>\r\n\r\n<p>Đạt giải thưởng danh gi&aacute; &ldquo;Best Video DSLR&rdquo; của Hiệp hội nhiếp ảnh b&aacute;o ch&iacute; TIPA 2012 v&agrave; giải &ldquo;Best Product 2012-2013&rdquo; cho Advanced SLR Camera của Hiệp hội &acirc;m thanh v&agrave; h&igrave;nh ảnh ch&acirc;u &Acirc;u EISA, <strong><a href=\"http://zshop.vn/may-anh-canon-dslr/\">m&aacute;y ảnh</a>&nbsp;Canon 5D Mark III</strong> đ&atilde; trở th&agrave;nh một trong những chiếc m&aacute;y ảnh c&oacute; t&iacute;nh s&aacute;ng tạo cao, kế nhiệm đ&agrave;n anh Canon EOS 5D Mark II v&agrave; l&agrave;m thỏa l&ograve;ng mong đợi của những người y&ecirc;u mến thương hiệu Canon. Được trang bị cảm biến Full Frame 35mm 22.3MP mạnh mẽ c&ugrave;ng vi xử l&yacute; ảnh DIGIC 5+ v&agrave; hệ thống lấy n&eacute;t 61 điểm tự động, chiếc m&aacute;y ảnh cho khả năng nhạy s&aacute;ng cao, tối ưu h&oacute;a tốc độ xử l&yacute;, khử nhiễu tốt v&agrave; tăng cường tốc độ xử l&yacute; l&ecirc;n gấp nhiều lần so với những d&ograve;ng th&ocirc;ng thường để ảnh lu&ocirc;n đẹp nhất mỗi lần bạn chụp trong bất kể t&igrave;nh huống n&agrave;o. Với việc được trang bị t&iacute;nh năng quay phim Full HD, đ&acirc;y ch&iacute;nh l&agrave; chiếc m&aacute;y ảnh chuy&ecirc;n nghiệp tiếp tục mang đến định nghĩa mới về nhiếp ảnh c&ugrave;ng cụm từ kh&aacute; th&uacute; vị: video-graphy, gi&uacute;p người thợ chụp sẵn s&agrave;ng ghi lại những khoảnh khắc qu&yacute; gi&aacute; của cuộc sống. C&ugrave;ng đ&oacute; l&agrave; những hỗ trợ cao cấp kh&aacute;c như khả kỹ thuật chụp HDR , cảm biến tự rũ bụi, ISO hỗ trợ l&ecirc;n đến 102.400, thiết kế mạnh mẽ, đ&uacute;ng chuẩn chuy&ecirc;n nghiệp khiến Canon 5D Mark III trở th&agrave;nh si&ecirc;u phẩm đ&aacute;ng mơ ước của bạn, những nhiếp ảnh gia t&agrave;i năng.</p>\r\n\r\n<p>&nbsp;\r\n<p><strong><a href=\"http://zshop.vn/may-anh-canon-dslr/\">Canon 5D Mark III </a></strong>&nbsp;trang bị cảm biến CMOS full-frame k&iacute;ch thước 36x24mm c&oacute; độ ph&acirc;n giải nhỉnh hơn Mark II một ch&uacute;t, 22.3 Megapixel. Tuy vậy, chất lượng h&igrave;nh ảnh được đ&aacute;nh gi&aacute; r&otilde; n&eacute;t hơn, tuyệt vời hơn người tiền nhiệm Mark II do cảm biến mới c&oacute; nhi&ecirc;u cải tiến về cấu tr&uacute;c transistor, cũng như khả năng giảm nhiễu ngay tr&ecirc;n chip m&agrave; Canon trang bị cho con cưng mới.</p>\r\n</p>\r\n\r\n<p>Điểm cộng s&aacute;ng nhất của Mark III ch&iacute;nh l&agrave; chip xử l&yacute; ảnh DIGIC 5 với tốc độ xử l&yacute; m&agrave; Canon tiết lộ l&agrave; mạnh gấp 17 lần so với DIGIC 4 của Mark II. Hơn cả, n&oacute; hứa hẹn cho h&igrave;nh ảnh r&otilde; n&eacute;t hơn, &nbsp;m&agrave;u sắc trung thực hơn v&agrave; sự chuyển m&agrave;u dịu hơn. Đồng thời, chất lượng h&igrave;nh ảnh cao hơn nhờ khả năng điều chỉnh ngoại vi, quang sai m&agrave;u v&agrave; khử nhiễu tốt hơn ở ISO cao (khi sử dụng với ống k&igrave;nh ngo&agrave;i) m&agrave; kh&ocirc;ng ảnh hưởng đến hiệu suất hoạt động của m&aacute;y. T&iacute;nh năng tr&ecirc;n cũng l&agrave; nhờ DIGIC 5 mang lại.</p>\r\n\r\n<p>Độ nhạy s&aacute;ng th&ocirc;ng thường từ ISO 100 - 25.600, c&oacute; thể mở rộng th&agrave;nh ISO 50 - 102.400, cho h&igrave;nh ảnh chất lượng cao. So với người tiền nhiệm Mark II, chất lượng ảnh của Mark III cải tiến đ&ocirc;i ch&uacute;t &nbsp;trong nhiều mặt: độ tương phản, độ ph&acirc;n giải, r&otilde; n&eacute;t chi tiết, v&agrave; đặc biệt l&agrave; ảnh chụp trong b&oacute;ng tối. Ở chế độ video, độ nhạy s&aacute;ng cao nhất l&agrave; ISO 25.600.</p>\r\n\r\n<p>Kh&ocirc;ng những thế, Canon 5D Mark III được người d&ugrave;ng đ&aacute;nh gi&aacute; cao nhờ kế thừa v&agrave; cải tiến c&aacute;c cảm biến cho m&aacute;y ảnh. Cụ thể l&agrave; Canon 5d Mark III kế thừa cảm biến lấy n&eacute;t tự động 61 điểm với 41 điểm crosstype từ EOS 1 D v&agrave; chế độ AI Servo III được cải tiến từ Mark II. Th&ecirc;m v&agrave;o đ&oacute;, Mark III c&ograve;n được trang bị cảm biến đo s&aacute;ng iFCL 2 lớp với 63 v&ugrave;ng đo. Đặc biệt, cảm biến n&agrave;y c&ograve;n hỗ trợ một chế độ đo s&aacute;ng gần với phạm chi 1,5%.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><a href=\"http://zshop.vn/may-anh-canon-dslr/\">Canon 5D Mark III</a></strong> c&oacute; thể quay phim chuẩn HD 720p ở tốc độ 60 v&agrave; 50 &nbsp;khung h&igrave;nh mỗi gi&acirc;y v&agrave; quay video 1080p ở tốc độ 24, 25 v&agrave; 30 khung h&igrave;nh mỗi gi&acirc;y. V&agrave; như đ&atilde; n&ecirc;u ở tr&ecirc;n, ISO của m&aacute;y đạt mức 100 đến 12.800 v&agrave; mở rộng l&ecirc;n tới mức 25.600 khi quay video chuẩn Full HD v&agrave; chất lượng &acirc;m thanh stereo. R&otilde; r&agrave;ng l&agrave; sản phẩm mới n&agrave;y của Canon lu&ocirc;n hướng đến sự thuận tiện cho người d&ugrave;ng, người d&ugrave;ng c&oacute; thể điều chỉnh c&aacute;c th&ocirc;ng số về độ nhạy s&aacute;ng ISO, tốc độ m&agrave;n trập, khẩu độ khi đang quay video để c&oacute; được những thước phim h&agrave;i l&ograve;ng. Ngo&agrave;i ra, Canon 5D Mark III được trang bị th&ecirc;m 1 jack cắm headphone để kiểm so&aacute;t &acirc;m thanh khi quay video. Độ d&agrave;i tối đa của clip l&agrave; 29 ph&uacute;t, 59 gi&acirc;y ở mọi độ ph&acirc;n giải.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Một số t&iacute;nh năng nhỏ nhưng cũng v&ocirc; c&ugrave;ng quan trọng g&oacute;p phần tạo n&ecirc;n th&agrave;nh c&ocirc;ng của Canon 5D Mark III:</strong></p>\r\n\r\n<p>-&nbsp;C&oacute; lớp vỏ l&agrave;m từ hợp kim ma-gi&ecirc; được Canon th&ocirc;ng b&aacute;o l&agrave; cho khả năng chống chọi thời tiết, độ ẩm v&agrave; nhiệt độ tốt hơn so với phi&ecirc;n bản trước</p>\r\n\r\n<p>-&nbsp;Bộ phận rung để rũ bỏ bụi tr&ecirc;n cảm biến</p>\r\n\r\n<p>-&nbsp;Chế độ chụp y&ecirc;n lặng gi&uacute;p giảm ồn khi chụp</p>\r\n\r\n<p>-&nbsp;K&iacute;nh ngắm đạt v&ugrave;ng phủ 100%</p>\r\n\r\n<p>-&nbsp;Hai khe cắm thẻ nhớ</p>\r\n\r\n<p>-&nbsp;bộ pin phụ BG-E11 c&oacute; thể tăng gấp đ&ocirc;i thời lượng chụp</p>\r\n\r\n<p>Một số di chuyển trong vị tr&iacute; c&aacute;c n&uacute;t bấm cũng tạo n&ecirc;n sự hợp l&yacute; v&agrave; h&agrave;i h&ograve;a hơn. C&aacute;c n&uacute;t Menu v&agrave; Info được đặt ở ph&iacute;a tr&ecirc;n b&ecirc;n tr&aacute;i. D&atilde;y 5 n&uacute;t Creative Style, n&uacute;t Rate mới, n&uacute;t Zoom, v&agrave; cuối c&ugrave;ng l&agrave; n&uacute;t xem lại (Playback) v&agrave; n&uacute;t x&oacute;a (Delete) nằm b&ecirc;n tr&aacute;i m&agrave;n h&igrave;nh LCD. Ph&iacute;a dưới m&agrave;n h&igrave;nh LCD c&oacute; ba lỗ nhỏ cho loa, v&agrave; một n&uacute;m tr&ograve;n nhỏ l&agrave; cảm biến &aacute;nh s&aacute;ng xung quanh, c&oacute; t&aacute;c dụng tự động điều chỉnh đ&egrave;n nền của m&agrave;n h&igrave;nh LCD.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"color:#0000cd\"><span style=\"font-size:14px\"><strong>T&Iacute;NH NĂNG NỔI BẬT</strong></span></span></p>\r\n\r\n<p><strong>Cảm biến Full Frame 35mm 22.3MP</strong></p>\r\n\r\n<p>Tương tự như tr&ecirc;n d&ograve;ng m&aacute;y cao cấp hơn flag-ship EOS-1Ds Mark III, Canon 5D Mark III được trang bị cảm biến Full Frame 35mm, độ ph&acirc;n giải cao 22.3MP, cho chất lượng h&igrave;nh ảnh vượt trội nhờ khả năng nhạy s&aacute;ng cao, tối ưu h&oacute;a tốc độ xử l&yacute; v&agrave; khử nhiễu khi chụp ở ISO cao, cho ảnh đẹp sắc n&eacute;t, r&otilde; r&agrave;ng đến từng chi tiết c&ugrave;ng m&agrave;u sắc sống động v&agrave; trung thực đến kh&ocirc;ng ngờ.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Vi xử l&yacute; ảnh ti&ecirc;n tiến DIGIC 5+</strong></p>\r\n\r\n<p>Tiếp tục những cải tiến mới so với thế hệ trước đ&oacute;, vi xử l&yacute; DIGIC 5+ cho khả năng truyền dữ liệu si&ecirc;u tốc nhờ đường truyền t&iacute;n hiệu đa k&ecirc;nh (nhanh hơn 30% so với DIGIC 5) cũng như c&acirc;n bằng trắng đa v&ugrave;ng, v&agrave; n&acirc;ng cao khả năng phản ứng chụp của m&aacute;y, để ảnh lu&ocirc;n đẹp bất kể t&igrave;nh huống n&agrave;o.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Hệ thống lấy n&eacute;t 61 điểm c&ugrave;ng cảm biến đo s&aacute;ng 63 v&ugrave;ng</strong></p>\r\n\r\n<p>Thừa hưởng hệ thống lấy n&eacute;t 61 điểm tự động với 41 điểm lấy n&eacute;t cross-type từ si&ecirc;u phẩm Canon EOS-1D X, Canon 5D Mark III cho khả năng lấy n&eacute;t tự động nhanh v&agrave; ch&iacute;nh x&aacute;c hơn, đạt tốc độ chụp 6fps để bạn kịp thời nắm bắt mọi khoảnh khắc tho&aacute;ng qua đồng thời để ảnh lu&ocirc;n đẹp nhất mỗi lần bạn bấm m&aacute;y. Cảm biến đo s&aacute;ng đa lớp 63 v&ugrave;ng iFCL được thiết kế mỗi lớp sẽ nhạy cảm với một bước s&oacute;ng kh&aacute;c nhau của &aacute;nh s&aacute;ng sau đ&oacute; sẽ d&ugrave;ng thuật to&aacute;n kết hợp lại cho ra kết quả đo s&aacute;ng đạt độ ch&iacute;nh x&aacute;c cao, bất kể t&igrave;nh huống n&agrave;o v&agrave; bất chấp m&agrave;u sắc chủ đạo l&agrave; g&igrave;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Trang bị quay phim Full HD tr&ecirc;n một m&aacute;y chụp ảnh chuy&ecirc;n nghiệp</strong></p>\r\n\r\n<p>L&agrave; d&ograve;ng m&aacute;y chuy&ecirc;n nghiệp đầu ti&ecirc;n được trang bị khả năng quay phim chuẩn Full HD, tốc độ 30fps, m&atilde; h&oacute;a H.264 x&oacute;a mờ khoảng c&aacute;ch giữa một chiếc m&aacute;y ảnh chuy&ecirc;n nghiệp v&agrave; một chiếc m&aacute;y quay phim, tạo một định nghĩa mới về kh&aacute;i niệm nhiếp ảnh: video-graphy, đồng thời gi&uacute;p bạn lưu lại những khoảng khắc qu&yacute; gi&aacute; của cuộc sống.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Dải nhạy s&aacute;ng rộng 100-25.600, mở rộng đến 102.400</strong></p>\r\n\r\n<p>Dải ISO rộng đặc biệt quan trọng khi cho ph&eacute;p bạn đ&oacute;ng băng để chụp được những h&igrave;nh ảnh chuyển động nhanh v&agrave; chụp ảnh trong điều kiện &aacute;nh s&aacute;ng yếu. Với tốc độ chụp li&ecirc;n tục 6fps ở độ ph&acirc;n giải cao, Canon 5D Mark III cho bạn dễ d&agrave;ng nắm bắt những h&igrave;nh ảnh th&uacute; vị, độc đ&aacute;o của những đối tượng quanh m&igrave;nh.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Hỗ trợ chụp ảnh gốc RAW v&agrave; kỹ thuật chụp HDR&nbsp;</strong></p>\r\n\r\n<p>Kỹ thuật chụp ảnh HDR (High Dynamic Range) của &hellip;Canon 5D Mark III cho ph&eacute;p mở rộng dải tần nhạy s&aacute;ng nhằm m&ocirc; tả chi tiết v&agrave; trung thực nhất những g&igrave; m&agrave; mắt thường nh&igrave;n thấy được trong khi chụp. Ngo&agrave;i ra, nhắm đến đối tượng l&agrave; c&aacute;c nhiếp ảnh gia chuy&ecirc;n chụp h&igrave;nh thời trang, ảnh cưới, studio, phong cảnh&hellip;Canon 5D Mark III hỗ trợ chế độ chụp ảnh RAW 14-bit để người cầm m&aacute;y lu&ocirc;n c&oacute; những dữ liệu ảnh gốc qu&yacute; gi&aacute; phụ vụ cho việc chỉnh sửa theo mục đ&iacute;ch v&agrave; &yacute; muốn.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Thiết kế mạnh mẽ</strong></p>\r\n\r\n<p>Với thiết kế mạnh mẽ c&ugrave;ng h&igrave;nh d&aacute;ng vu&ocirc;ng vắn, khỏe khoắn, Canon 5D Mark III đem đến cho người d&ugrave;ng cảm gi&aacute;c thật sự chuy&ecirc;n nghiệp khi cầm tr&ecirc;n tay. B&aacute;ng cầm rộng bọc cao su mềm cho cảm gi&aacute;c cầm nắm chắc chắn v&agrave; giảm thiểu tối đa hiện tượng rung lắc khi ngắm chụp, để ảnh lu&ocirc;n đạt chất lượng tối ưu bất kể t&igrave;nh huống n&agrave;o. M&agrave;n h&igrave;nh rộng 3.2&rdquo; gi&uacute;p bạn thoải m&aacute;i xem lại ảnh đ&atilde; chụp, đồng thời được chống ch&oacute;i v&agrave; chống trầy xước, hỗ trợ tối đa người d&ugrave;ng ở những g&oacute;c chụp kh&oacute;.</p>\r\n', '1', '10', '10', '2014-12-05');
INSERT INTO `products` VALUES ('6', ' Canon 6D (Body) (Chính hãng) ', '1', '33886600', '<p><strong>Điểm nổi bật của sản phẩm</strong></p>\r\n\r\n<p>- K&iacute;ch thước cảm biến:&nbsp; Full frame (36 x 24 mm)</p>\r\n\r\n<p>- Chế độ quay Video:&nbsp; 1080p</p>\r\n\r\n<p>- Megapixel (Số điểm ảnh hiệu dụng):&nbsp; 20.2 Megapixels</p>\r\n\r\n<p>- Tốc độ chụp (Shutter Speed):&nbsp; 30 - 1/4000 sec</p>\r\n\r\n<p>- Độ nhạy s&aacute;ng (ISO) Auto: 100 - 25600 in 1/3 stops, plus 50, 51200, 102400 as option</p>\r\n', '<p style=\"text-align:center\"><strong>M&aacute;y ảnh&nbsp;Canon 6D</strong></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"color:#0000cd\"><strong>GIỚI THIỆU</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:12px\">Kh&ocirc;ng l&acirc;u sau tin đồn về một chiếc m&aacute;y ảnh DSLR với cảm biến 35mm (full-frame) gi&aacute; rẻ, Canon h&ocirc;m nay đ&atilde; ch&iacute;nh thức giới thiệu m&aacute;y ảnh&nbsp;Canon 6D&nbsp;với cảm biến k&iacute;ch cỡ&nbsp;tương tự như tr&ecirc;n <strong><a href=\"http://zshop.vn/may-anh-canon-dslr/\">m&aacute;y ảnh</a>&nbsp;Canon 5D Mark III</strong>&nbsp;nhưng gi&aacute; rẻ hơn nhiều. Kh&ocirc;ng chỉ l&agrave; một chiếc m&aacute;y ảnh full-frame với gi&aacute; hấp dẫn, 6D c&ograve;n l&agrave; m&aacute;y ảnh chuy&ecirc;n nghiệp đầu ti&ecirc;n được t&iacute;ch hợp GPS cũng như kết nối WiFi, cho ph&eacute;p người d&ugrave;ng sử dụng c&aacute;c ứng dụng tr&ecirc;n Android hay iOS để điều khiển m&aacute;y.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"color:#0000cd\"><span style=\"font-size:14px\"><strong>T&Iacute;NH NĂNG NỔI BẬT</strong></span></span></p>\r\n\r\n<p><strong>Thiết kế:</strong></p>\r\n\r\n<p>Nh&igrave;n từ ph&iacute;a trước,&nbsp;<strong><a href=\"http://zshop.vn/may-anh-canon-dslr/\">Canon 6D</a></strong>&nbsp;c&oacute; thiết kế kh&aacute; đơn giản, ở phần tr&ecirc;n nơi c&oacute; lăng k&iacute;nh k&iacute;nh ngắm hơi gồ ra c&ugrave;ng với logo Canon, tr&ocirc;ng như một c&aacute;i tr&aacute;n d&ocirc;. Kh&ocirc;ng hề c&oacute; flash t&iacute;ch hợp, điều kh&ocirc;ng b&igrave;nh thường đối với một m&aacute;y ảnh d&agrave;nh cho người d&ugrave;ng cao cấp, nhưng ph&ugrave; hợp với m&aacute;y ảnh full-frame Canon. Cổng hồng ngoại được đặt ngay tr&ecirc;n phần b&aacute;ng cầm tay, một đ&egrave;n hẹn giờ (kh&ocirc;ng phải đ&egrave;n hỗ trợ lấy n&eacute;t AF) nằm giữa phần b&aacute;ng cầm v&agrave; ống k&iacute;nh. Nằm hơi chếch b&ecirc;n dưới ph&iacute;a tr&aacute;i của ng&agrave;m ống k&iacute;nh l&agrave; n&uacute;t xem trước độ s&acirc;u trường ảnh, kh&aacute; nhỏ n&ecirc;n kh&oacute; nhận thấy. N&uacute;t nhả ống k&iacute;nh kh&aacute; lớn nằm ở vị tr&iacute; th&ocirc;ng thường, v&agrave; 3 lỗ microphone nằm ngay b&ecirc;n dưới logo.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"Đánh giá máy ảnh full-frame Canon EOS 6D\" src=\"http://zshop.vn/images/product_kts/canon-6d-000.jpg\" style=\"height:387px; width:500px\" title=\"Canon 6D (Body)\" /></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"color:#0000cd\"><span style=\"font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif\">Đ&aacute;nh gi&aacute; nhanh m&aacute;y ảnh full-frame&nbsp;Canon 6D</span></span></span></p>\r\n\r\n<p><span style=\"font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif\">Mode dial c&oacute; &iacute;t t&ugrave;y chọn hơn so với th&ocirc;ng thường, n&ecirc;n khoảng c&aacute;ch giữa c&aacute;c t&ugrave;y chọn kh&aacute; lớn, ở giữa v&ograve;ng xoay chọn chế độ chụp n&agrave;y l&agrave; n&uacute;t kh&oacute;a. N&uacute;t nguồn nằm ngay b&ecirc;n dưới Model dial. Giống như tr&ecirc;n model Canon 60D, chiếc m&aacute;y ảnh Canon 6D cũng c&oacute; một d&atilde;y c&aacute;c n&uacute;t nằm ph&iacute;a tr&ecirc;n m&agrave;n h&igrave;nh phụ để chỉnh nhanh c&aacute;c thiết lập th&ocirc;ng số chụp. Th&ocirc;ng tin về Wi-Fi v&agrave; GPS cũng như chế độ HDR cũng hiển thị tr&ecirc;n m&agrave;n h&igrave;nh n&agrave;y.</span></span></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif\"><img alt=\"Máy ảnh full-frame Canon EOS 6D có thiết kế khá đơn giản\" src=\"http://zshop.vn/images/product_kts/canon-6d-001.jpg\" style=\"color:rgb(44, 43, 43); height:278px; text-align:center; width:500px\" title=\"Canon 6D (Body)\" /></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"color:#0000cd\"><span style=\"font-family:arial,helvetica,sans-serif; font-size:12px\">M&aacute;y ảnh full-frame</span><span style=\"font-family:arial,helvetica,sans-serif; font-size:12px\">&nbsp;</span><span style=\"font-family:tahoma,arial,helvetica,sans-serif; font-size:13px\">&nbsp;Canon 6D&nbsp;</span><span style=\"font-family:arial,helvetica,sans-serif; font-size:12px\">c&oacute; thiết kế kh&aacute; đơn giản</span></span></p>\r\n\r\n<p><span style=\"color:rgb(44, 43, 43); font-family:arial,helvetica,sans-serif; font-size:12px\">Kh&ocirc;ng c&oacute; m&agrave;n h&igrave;nh xoay lật, cụm điều khiển của&nbsp;</span><a href=\"http://zshop.vn/canon-6d.html\" style=\"font-family: arial, helvetica, sans-serif; font-size: 12px;\"><strong>Canon 6D</strong></a><span style=\"color:rgb(44, 43, 43); font-family:arial,helvetica,sans-serif; font-size:12px\">&nbsp;c&oacute; nhiều điểm chung với model T4i hơn so với c&aacute;c m&aacute;y ảnh kh&aacute;c. Canon đ&atilde; loại bỏ 5 n&uacute;t b&ecirc;n tr&aacute;i m&agrave;n h&igrave;nh LCD, x&oacute;a n&uacute;t Picture Styles v&agrave; RAW/JPEG (từ EOS 7D), v&agrave; bố tr&iacute; c&aacute;c n&uacute;t c&ograve;n lại như ảnh tr&ecirc;n.</span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif\"><img alt=\"Canon 6D cũng bỏ đi cần gạt 8 chiều và thay bằng một đĩa điều hướng 8 chiều\" src=\"http://zshop.vn/images/product_kts/canon-6d-002.jpg\" style=\"border:0px none; height:382px; width:500px\" title=\"Canon 6D (Body)\" /></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"color:#0000cd\"><span style=\"font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif\">M&aacute;y ảnh</span></span>&nbsp;<span style=\"font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif\">Canon 6D</span></span><span style=\"font-family:arial,helvetica,sans-serif; font-size:12px\">&nbsp;cũng bỏ đi cần gạt 8 chiều v&agrave; thay bằng một đĩa điều hướng 8 chiều</span></span></p>\r\n\r\n<p><span style=\"color:rgb(44, 43, 43); font-family:arial,helvetica,sans-serif; font-size:12px\">Canon cũng bỏ đi cần gạt 8 chiều v&agrave; thay bằng một đĩa điều hướng 8 chiều. N&uacute;t Q sẽ cho ph&eacute;p người d&ugrave;ng truy cập tới menu nhanh để điều chỉnh c&aacute;c thiết lập. N&uacute;t Live View/Movie Record cho ph&eacute;p chuyển đổi nhanh giữa hai chế độ xem sống Live View v&agrave; quay phim.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif\"><strong>Cảm biến:</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif\">Canon đ&atilde; ph&aacute;t triển một cảm biến CMOS full-frame ho&agrave;n to&agrave;n mới cho Canon 6D. Với k&iacute;ch thước 35,8 x 23,9 mm, n&oacute; nhỏ hơn một ch&uacute;t so với c&aacute;c cảm biến được t&igrave;m thấy trong Canon 5D Mark II v&agrave; Canon 5D Mark III. Độ ph&acirc;n giải hiệu dụng l&agrave; 20.2 megapixel. Mặc d&ugrave; c&oacute; độ ph&acirc;n giải thua mức 24.3 megapixel của Nikon D600, nhưng k&iacute;ch thước điểm ảnh của Canon 6D lớn hơn: với k&iacute;ch thước điểm ảnh 6,55 micron, so với 5,9 micron của D600.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif\"><strong>Bộ vi xử l&yacute;:</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif\">Ảnh xuất ra từ bộ cảm biến mới được xử l&yacute; bởi một bộ xử l&yacute; h&igrave;nh ảnh DIGIC 5+, tương tự như bộ xử l&yacute; của d&ograve;ng m&aacute;y đắt hơn Canon 5D Mark III. Canon n&oacute;i rằng Canon EOS 6D sử dụng hệ chuyển đổi analog-sang-kỹ thuật số 14-bit.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif\"><strong>Hiệu suất:</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif\">Chiếc m&aacute;y ảnh Canon 6D thua d&ograve;ng Nikon D600 ở khả năng chụp ảnh li&ecirc;n tục. Trong khi D600 đạt tốc độ 5.5 fps th&igrave; 6D chỉ đạt 4.5 fps.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif\"><strong>Độ nhạy s&aacute;ng:</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif\">Mặc d&ugrave; kh&ocirc;ng sử dụng bộ cảm biến giống Canon 5D Mark III, Canon 6D cung cấp c&ugrave;ng một dải ISO từ ISO 100 đến 25.600, c&oacute; thể mở rộng từ 50 đến 102.400. Ngược lại, Nikon D600 c&oacute; một dải ISO ti&ecirc;u chuẩn l&agrave; ISO 100 &ndash; 6400, mở rộng từ ISO 50 - 25.600.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif\"><strong>Tự động lấy n&eacute;t:</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif\">Điểm kh&aacute;c biệt r&otilde; r&agrave;ng nhất giữa Canon 6D v&agrave; c&aacute;c model full-frame đắt tiền hơn ch&iacute;nh l&agrave; hệ thống tự động lấy n&eacute;t. Trong khi Canon 5D Mark III c&oacute; hệ thống tự động lấy n&eacute;t 61-điểm với 41 điểm cross-type, Canon 6D chỉ c&oacute; 11 điểm trong một mảng h&igrave;nh thoi, với điểm trung t&acirc;m l&agrave; điểm cross-type duy nhất. (Về mặt n&agrave;y, 6D giống với 5D Mark II hơn, với một hệ thống lấy n&eacute;t 15-điểm v&agrave; 1 điểm cross-type, nhưng 6 điểm trong số n&agrave;y chỉ l&agrave; điểm hỗ trợ lấy n&eacute;t).</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif\"><strong>K&iacute;nh ngắm:</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif\">K&iacute;nh ngắm quang của m&aacute;y ảnh Canon 6D c&oacute; v&ugrave;ng phủ 97%, xếp sau mức 98% của EOS 5D Mark II. Canon 5D Mark III v&agrave; Nikon D600 đều c&oacute; v&ugrave;ng phủ 100%.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif\"><strong>M&agrave;n h&igrave;nh hiển thị:</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif\">C&oacute; hai m&agrave;n h&igrave;nh tr&ecirc;n chiếc m&aacute;y ảnh Canon 6D, cũng thường thấy tr&ecirc;n c&aacute;c m&aacute;y ảnh c&oacute; c&ugrave;ng tầm gi&aacute;. M&agrave;n h&igrave;nh nhỏ nằm ph&iacute;a mặt tr&ecirc;n của m&aacute;y l&agrave; một m&agrave;n h&igrave;nh LCD đơn sắc, hiển thị trạng th&aacute;i của m&aacute;y, thực hiện c&aacute;c c&agrave;i đặt tr&ecirc;n m&aacute;y, gi&uacute;p bạn tiết kiệm pin v&agrave; thời gian hơn so với m&agrave;n h&igrave;nh ở mặt sau.</span></span></p>\r\n\r\n<p><span style=\"font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif\">Trong khi đ&oacute;, m&agrave;n h&igrave;nh mặt sau l&agrave; một m&agrave;n h&igrave;nh LCD 3.0-inch Clear View với độ ph&acirc;n giải 1.040.000 dot. M&agrave;n h&igrave;nh n&agrave;y c&oacute; độ tương phản k&eacute;m hơn m&agrave;n h&igrave;nh Clear View II tr&ecirc;n Canon 5D Mark III. N&oacute; c&oacute; g&oacute;c nh&igrave;n rộng 170 độ, l&agrave; m&agrave;n h&igrave;nh cố định, kh&ocirc;ng c&oacute; cảm ứng.</span></span></p>\r\n\r\n<p><span style=\"font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif\">So với Nikon D600, số điểm ảnh của m&agrave;n h&igrave;nh tr&ecirc;n Canon 6D cao hơn một ch&uacute;t. Khi mở ở chế độ xem sống Live View, m&agrave;n h&igrave;nh c&oacute; v&ugrave;ng phủ 100%.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif\"><strong>Flash:</strong></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif\">Kh&ocirc;ng c&oacute; đ&egrave;n flash t&iacute;ch hợp trong Canon 6D (trong khi Nikon D600 c&oacute; flash), v&igrave; vậy c&oacute; thể bạn phải mất th&ecirc;m chi ph&iacute; sắm đ&egrave;n flash rời.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif\"><img alt=\"Với Canon 6D bạn phải mất thêm chi phí sắm đèn flash rời cho máy\" src=\"http://zshop.vn/images/product_kts/canon-6d-006.jpg\" style=\"border:0px none; color:rgb(44, 43, 43); font-family:tahoma; font-size:14px; height:722px; text-align:center; width:450px\" title=\"Canon 6D (Body)\" /></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"color:#0000cd\"><span style=\"font-family:arial,helvetica,sans-serif; font-size:12px\">Với m&aacute;y ảnh Canon 6D bạn phải mất th&ecirc;m chi ph&iacute; sắm đ&egrave;n flash rời cho m&aacute;y</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif\"><strong>Chế độ phơi s&aacute;ng:</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif\">C&aacute;c t&ugrave;y chọn chế độ phơi s&aacute;ng của Canon 6D gần như giống hệt với đối thủ cạnh tranh Nikon. B&ecirc;n cạnh c&aacute;c chế độ P, Av, Tv, M th&ocirc;ng thường, c&ograve;n c&oacute; chế độ Scene Auto +. &nbsp;Ngo&agrave;i ra c&ograve;n c&oacute; hai chế độ t&ugrave;y chỉnh Custom, Bulb v&agrave; Creative Auto nhằm hỗ trợ th&ecirc;m cho những tay m&aacute;y nghiệp dư.Đo s&aacute;ng:</span></span></p>\r\n\r\n<p><span style=\"font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif\">M&aacute;y ảnh Canon 6D thực hiện đo s&aacute;ng với 63-zone, cảm biến đo s&aacute;ng iFCL dual-layer (2 lớp). Cảm biến n&agrave;y được sử dụng lần đầu tr&ecirc;n Canon 7D v&agrave; cũng được d&ugrave;ng tr&ecirc;n Canon 5D Mark III.</span></span></p>\r\n\r\n<p><span style=\"font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif\">Trong hai lớp đo s&aacute;ng, lớp tr&ecirc;n nhạy cảm với m&agrave;u đỏ v&agrave; m&agrave;u xanh l&aacute; c&acirc;y, trong khi lớp ph&iacute;a dưới ph&aacute;t hiện m&agrave;u xanh dương v&agrave; m&agrave;u xanh l&aacute; c&acirc;y. Hệ thống đo s&aacute;ng n&agrave;y cho ph&eacute;p ph&aacute;t hiện tốt hơn chủ thể, th&ocirc;ng tin được phản hồi trở lại hệ thống tự động lấy n&eacute;t để hỗ trợ theo d&otilde;i chủ thể. Nikon, ngược lại, hệ thống đo s&aacute;ng RGB chi tiết hơn với 2.016 pixel, được gọi l&agrave; 3D color matrix metering II.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif\"><strong>M&agrave;n trập:</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif\">Một điểm kh&aacute;c biệt nữa giữa Canon 6D v&agrave; Canon 5D Mark III l&agrave; cơ chế m&agrave;n trập. Trong khi Canon 5D Mark III được đ&aacute;nh gi&aacute; l&agrave; c&oacute; thể hoạt động tốt trong khoảng 150.000 chu kỳ, giống như D600 của Nikon, m&agrave;n trập 6D của Canon chỉ d&ugrave;ng nhiều nhất 100.000 chu kỳ, ngắn hơn nhiều. Điều đ&oacute; kh&ocirc;ng c&oacute; nghĩa l&agrave; n&oacute; chắc chắn kh&ocirc;ng thể chụp qu&aacute; 100.000 bức ảnh, nhưng n&oacute;i chung l&agrave; tuổi thọ của n&oacute; kh&ocirc;ng được bền như m&agrave;n trập trong m&aacute;y ảnh kh&aacute;c.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif\"><strong>Chế độ Creative:</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif\">C&oacute; một v&agrave;i t&ugrave;y chọn chụp s&aacute;ng tạo tr&ecirc;n Canon 6D kh&aacute; giống nhưng kh&ocirc;ng phải được b&ecirc; nguy&ecirc;n xi từ 5D Mark III.</span></span></p>\r\n\r\n<p><span style=\"font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif\">Đầu ti&ecirc;n l&agrave; chế độ chụp High Dynamic Range, cho ph&eacute;p chụp 3 ảnh ri&ecirc;ng biệt với c&aacute;c mức phơi s&aacute;ng kh&aacute;c nhau, sau đ&oacute; kết hợp ch&uacute;ng lại th&agrave;nh một ảnh duy nhất c&oacute; dải tần nhạy s&aacute;ng cao hơn. C&aacute;c chế độ HDR tương tự như EOS 5D Mark III, nhưng kh&ocirc;ng cho ph&eacute;p lưu c&aacute;c ảnh nguồn. Thay v&agrave;o đ&oacute;, việc kết hợp c&aacute;c ảnh được thực hiện chỉ trong m&aacute;y ảnh, v&agrave; nếu bạn kh&ocirc;ng h&agrave;i l&ograve;ng với kết quả, bạn sẽ cần phải chụp lại. (Với 5D Mark III, nếu bạn đổi &yacute; hoặc chưa h&agrave;i l&ograve;ng, bạn c&oacute; thể chỉnh HDR bằng tay tr&ecirc;n m&aacute;y t&iacute;nh của bạn, bạn c&oacute; t&ugrave;y chọn để lưu h&igrave;nh ảnh nguồn). Số hiệu ứng HDR tr&ecirc;n Canon 6D cũng &iacute;t hơn tr&ecirc;n c&aacute;c anh chị em đắt tiền hơn của n&oacute;.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif\">Một t&ugrave;y chọn s&aacute;ng tạo th&uacute; vị kh&aacute;c, đ&oacute; l&agrave; chế độ phơi s&aacute;ng Multiple Exposure, chồng nhiều bức ảnh l&ecirc;n nhau. Đ&oacute; l&agrave; một kỹ thuật hữu &iacute;ch để tạo ra một bức ảnh phơi s&aacute;ng l&acirc;u hơn, giảm nhiễu hoặc đạt một số hiệu ứng. Bạn c&oacute; thể xếp chồng 9 h&igrave;nh ảnh, v&agrave; bạn c&oacute; thể sử dụng một ảnh RAW để l&agrave;m ảnh đầu ti&ecirc;n cho chuỗi ảnh xếp chồng nhau n&agrave;y. C&oacute; 4 phương ph&aacute;p gh&eacute;p chồng ảnh kh&aacute;c nhau: Additive, Average, Bright, Dark để cho c&aacute;c sắc th&aacute;i ảnh kh&aacute;c nhau. Thậm ch&iacute; bạn c&oacute; thể xem trước kết quả tr&ecirc;n m&agrave;n h&igrave;nh LCD, v&agrave; c&oacute; thể chọn undo (hủy) thao t&aacute;c gần nhất nếu bạn kh&ocirc;ng muốn một hiệu ứng n&agrave;o đ&oacute;. Mark III c&oacute; nhiều chế độ s&aacute;ng t&aacute;c kiểu n&agrave;y hơn, nhưng d&ugrave; sao đ&acirc;y cũng l&agrave; điều tuyệt vời cho người d&ugrave;ng.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif\"><strong>Giảm bụi:</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif\">Canon 6D được t&iacute;ch hợp hệ thống l&agrave;m sạch EOS Integrated Cleaning System, sử dụng một th&agrave;nh phần &aacute;p điện để rũ c&aacute;c hạt bụi ra khỏi bộ lọc th&ocirc;ng thấp ở ph&iacute;a trước của bộ cảm biến.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif\"><img alt=\"Canon 6D được tích hợp hệ thống làm sạch EOS Integrated Cleaning System\" src=\"http://zshop.vn/images/product_kts/canon-6d-009.jpg\" style=\"border:0px none; height:281px; width:500px\" title=\"Canon 6D (Body)\" /></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"color:#0000cd\"><span style=\"font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif\">M&aacute;y ảnh Canon 6D được t&iacute;ch hợp hệ thống l&agrave;m sạch EOS Integrated Cleaning System</span></span></span></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p><strong>Quay phim:</strong></p>\r\n\r\n<p><span style=\"color:rgb(44, 43, 43); font-family:arial,helvetica,sans-serif; font-size:12px\">Canon&nbsp;đ&atilde; đưa v&agrave;o 6D kh&aacute; nhiều t&iacute;nh năng quay video nổi bật đ&atilde; c&oacute; tr&ecirc;n c&aacute;c model 5D, bao gồm quay&nbsp;</span><strong>video Full HD</strong><span style=\"color:rgb(44, 43, 43); font-family:arial,helvetica,sans-serif; font-size:12px\">&nbsp;(1080p, 1.920 x 1.080 pixel) ở 30, 25, hoặc 24 fps; 720p (1.280 x 720 pixel) ở 60 hoặc 50 fps, VGA (640 x 480 pixel) ở 30 hoặc 25 fps. Độ d&agrave;i mỗi clip được giới hạn l&agrave; 29 ph&uacute;t, 59 gi&acirc;y v&agrave; tự động chia đoạn sau mỗi 4GB dung lượng phim được lưu. Video định dạng ALL-I intraframe đ&ograve;i hỏi phải d&ugrave;ng thẻ nhớ chuẩn UHS-I, c&ograve;n định dạng&nbsp;</span><strong>IPB interframe-compressed</strong><span style=\"color:rgb(44, 43, 43); font-family:arial,helvetica,sans-serif; font-size:12px\">&nbsp;th&igrave;&nbsp; c&oacute; thể lưu tr&ecirc;n thẻ SD th&ocirc;ng thường.</span></p>\r\n\r\n<p><span style=\"font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif\">Người d&ugrave;ng c&oacute; thể t&ugrave;y chỉnh độ phơi s&aacute;ng v&agrave; &acirc;m thanh theo &yacute; muốn. M&aacute;y c&oacute; cả micro t&iacute;ch hợp v&agrave; jack cắm micro ngo&agrave;i, nhưng sẽ kh&ocirc;ng c&oacute; jack cắm tai nghe để theo d&otilde;i &acirc;m thanh trong l&uacute;c quay như tr&ecirc;n&nbsp;<a href=\"http://zshop.vn/may-anh-canon-dslr/\"><strong>Canon 5D Mark III</strong></a>.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif\"><img alt=\"Canon 6D khá nhiều tính năng quay video nổi bật đã có trên các model 5D\" src=\"http://zshop.vn/images/product_kts/canon-6d-010.jpg\" style=\"border:0px none; height:281px; width:500px\" title=\"Canon 6D (Body)\" /></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"color:#0000cd\"><span style=\"font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif\">Canon&nbsp;6D&nbsp;kh&aacute; nhiều t&iacute;nh năng quay video nổi bật đ&atilde; c&oacute; tr&ecirc;n c&aacute;c model 5D</span></span></span></p>\r\n\r\n<p><span style=\"font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif\"><strong>Kết nối mạng kh&ocirc;ng d&acirc;y:</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif\">Một trong những t&iacute;nh năng m&agrave;&nbsp;<a href=\"http://zshop.vn/may-anh-canon-dslr/\"><strong>Canon&nbsp;6D</strong></a>&nbsp;vượt l&ecirc;n so với c&aacute;c &quot;anh em&quot; đắt tiền hơn, đ&oacute; l&agrave; n&oacute; được t&iacute;ch hợp kết nối Wi-Fi. Tuy nhi&ecirc;n, khả năng bắt s&oacute;ng Wi-Fi IEEE 802.11 b / g / n của 6D kh&ocirc;ng mạnh, chỉ trong phạm vi 30m. Trong khi đ&oacute;, nếu chọn mua th&ecirc;m thiết bị WFT-E7A Wireless File Transmitter để d&ugrave;ng với&nbsp;Canon 5D Mark III&nbsp;th&igrave; bạn c&oacute; thể kết nối trong phạm vi 150m. Nếu bạn chụp ảnh với mục đ&iacute;ch chuyển h&igrave;nh ảnh l&ecirc;n internet th&ocirc;ng qua một điện thoại th&ocirc;ng minh trong t&uacute;i của bạn, th&igrave;&nbsp;Wi-Fi t&iacute;ch hợp của&nbsp;</span></span><span style=\"color:rgb(45, 45, 45)\">Canon 6D sẽ tiện lợi hơn.</span></p>\r\n\r\n<p><span style=\"font-family:arial,helvetica,sans-serif; font-size:12px\">M&aacute;y ảnh&nbsp;</span><a href=\"http://zshop.vn/may-anh-canon-dslr/\"><strong>Canon 6D</strong></a><span style=\"font-family:arial,helvetica,sans-serif; font-size:12px\">&nbsp;kh&ocirc;ng chỉ kết nối với điện thoại th&ocirc;ng minh m&agrave; c&ograve;n c&oacute; thể kết nối với một số m&aacute;y ảnh PowerShot-series, m&aacute;y t&iacute;nh bảng cũng như điện thoại th&ocirc;ng minh Android hay iOS. Bạn cũng c&oacute; thể chia sẻ h&igrave;nh ảnh tr&ecirc;n c&aacute;c trang web mạng x&atilde; hội. Ảnh sẽ được chuyển tới địa chỉ bạn muốn th&ocirc;ng qua dịch vụ đ&aacute;m m&acirc;y IGAGE GATEWAY của Canon, c&oacute; y&ecirc;u cầu đăng k&yacute;.</span></p>\r\n\r\n<p><span style=\"font-family:arial,helvetica,sans-serif; font-size:12px\">C&ograve;n nữa, bạn c&oacute; thể điều khiển từ xa chiếc</span><span style=\"font-family:arial,helvetica,sans-serif; font-size:12px\">&nbsp;</span><a href=\"http://zshop.vn/may-anh-canon-dslr/\" style=\"font-family: arial, helvetica, sans-serif; font-size: 12px;\"><strong>Canon 6D</strong>&nbsp;</a><span style=\"font-family:arial,helvetica,sans-serif; font-size:12px\">của bạn từ c&aacute;c thiết bị gắn k&egrave;m, bằng c&aacute;ch sử dụng ứng dụng miễn ph&iacute; Canon EOS Remote. (Bạn sẽ cần phải c&oacute; một thiết bị chạy iOS 5 hay Android 2.3 + / 4.0 + cho việc n&agrave;y.)</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif\"><strong>Định vị địa l&yacute;:</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif\">Canon 6D cũng l&agrave; m&aacute;y ảnh DSLR đầu ti&ecirc;n của Canon t&iacute;ch hợp GPS, cho ph&eacute;p m&aacute;y ảnh x&aacute;c định vị tr&iacute; của n&oacute;, sau đ&oacute; ghi lại th&ocirc;ng số vĩ độ, kinh độ, độ cao, v&agrave; đ&oacute;ng dấu thời gian UTC trong dữ liệu EXIF của mỗi h&igrave;nh ảnh. 5D Mark III đ&ograve;i hỏi một thiết bị ri&ecirc;ng biệt để đạt được điều n&agrave;y. Bạn c&oacute; thể xem c&aacute;c địa điểm chụp ảnh của bạn tr&ecirc;n bản đồ bằng c&aacute;ch sử dụng phần mềm Map Utility k&egrave;m theo.</span></span></p>\r\n\r\n<p><span style=\"font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif\">C&aacute;c lựa chọn kết nối kh&aacute;c bao gồm: một cổng kết hợp USB 2.0 v&agrave; đầu ra video ti&ecirc;u chuẩn, đầu ra video độ n&eacute;t cao HDMI, một jack cắm micro ngo&agrave;i chuẩn 3.5 mm, v&agrave; cổng điều khiển từ xa.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif\"><strong>Thời lượng pin:</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif\">M&aacute;y sử dụng c&ugrave;ng một chuẩn pin lithium-ion LP-E6 như tr&ecirc;n c&aacute;c model&nbsp;</span></span><span style=\"color:rgb(45, 45, 45)\">Canon 6D, Canon 7D, Canon 5D Mark II,Canon 5D Mark III</span><span style=\"font-family:arial,helvetica,sans-serif; font-size:12px\">, n&ecirc;n bạn c&oacute; thể d&ugrave;ng chung bộ pin giữa c&aacute;c d&ograve;ng m&aacute;y n&agrave;y. Bộ pin n&agrave;y c&oacute; thời lượng sử dụng theo ti&ecirc;u chuẩn thử nghiệm CIPA l&agrave; khoảng 1.090 ảnh chụp tại 23&deg;C v&agrave; 980 bức ảnh ở 0&deg;C. Tuổi thọ pin khi sử dụng GPS v&agrave; WiFi chưa được n&ecirc;u r&otilde;.</span></p>\r\n\r\n<p><strong>Canon 6D</strong><span style=\"font-family:arial,helvetica,sans-serif; font-size:12px\">&nbsp;tương th&iacute;ch với thẻ Secure Digital bao gồm cả SDHC v&agrave; SDXC dung lượng cao c&aacute;c loại. Kh&ocirc;ng giống như EOS 5D II v&agrave; III, kh&ocirc;ng c&ograve;n bất kỳ sự hỗ trợ n&agrave;o cho thẻ CompactFlash.</span></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif\"><img alt=\"Canon 6D sử dụng cùng một chuẩn pin lithium-ion LP-E6 \" src=\"http://zshop.vn/images/product_kts/canon-6d-011.jpg\" style=\"border:0px none; height:281px; width:500px\" title=\"Canon 6D (Body)\" /></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"color:#0000cd\"><span style=\"font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif\">Canon 6D&nbsp;sử dụng c&ugrave;ng một chuẩn pin&nbsp;lithium-ion LP-E6</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif\"><strong>Kết luận:</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif\">Với sự ra đời của&nbsp;</span></span><span style=\"color:rgb(45, 45, 45)\">Canon 6D</span><span style=\"font-family:arial,helvetica,sans-serif; font-size:12px\">&nbsp;việc sở hữu một m&aacute;y ảnh full-frame đ&atilde; trở n&ecirc;n dễ d&agrave;ng hơn. Đổi lại, bạn sẽ thiếu đi một số t&iacute;nh năng cao cấp đ&atilde; thấy tr&ecirc;n d&ograve;ng&nbsp;</span><em>5D series</em><span style=\"font-family:arial,helvetica,sans-serif; font-size:12px\">. So với&nbsp;</span><span style=\"color:rgb(45, 45, 45)\">Canon 5D Mark III, Canon 6D</span><span style=\"font-family:arial,helvetica,sans-serif; font-size:12px\">&nbsp;c&oacute; thể c&ograve;n thua k&eacute;m nhiều.</span></p>\r\n\r\n<p><span style=\"font-family:arial,helvetica,sans-serif; font-size:12px\">L&agrave;&nbsp;</span><strong><a href=\"http://zshop.vn/may-anh-canon-dslr/\">m&aacute;y ảnh</a> DSLR</strong><span style=\"font-family:arial,helvetica,sans-serif; font-size:12px\">&nbsp;đầu ti&ecirc;n của Canon t&iacute;ch hợp Wi-Fi v&agrave; GPS, chiếc&nbsp;</span><a href=\"http://zshop.vn/may-anh-canon-dslr/\"><strong>Canon 6D</strong></a><span style=\"font-family:arial,helvetica,sans-serif; font-size:12px\">&nbsp;sẽ l&agrave; một lựa chọn th&uacute; vị với c&aacute;c nhiếp ảnh gia cỡ trung. Độ ph&acirc;n giải 20.2MP của m&aacute;y c&oacute; thể kh&ocirc;ng ấn tượng so với c&aacute;c m&aacute;y DSLR định dạng APS-C, nhưng chắc chắn chất lượng h&igrave;nh ảnh sẽ tốt hơn đ&aacute;ng kể nhờ cảm biến full-frame.</span></p>\r\n', '1', '0', '7', '2014-12-27');
INSERT INTO `products` VALUES ('7', ' Canon 6D + 24-105mm F4 (Chính hãng) ', '1', '49300150', '<p>Điểm nổi bật của sản phẩm</p>\r\n\r\n<p>- K&iacute;ch thước cảm biến:&nbsp; Full frame (36 x 24 mm)</p>\r\n\r\n<p>- Chế độ quay Video:&nbsp; 1080p</p>\r\n\r\n<p>- Megapixel (Số điểm ảnh hiệu dụng):&nbsp; 20.2 Megapixels</p>\r\n\r\n<p>- Tốc độ chụp (Shutter Speed):&nbsp; 30 - 1/4000 sec</p>\r\n\r\n<p>- Độ nhạy s&aacute;ng (ISO) Auto: 100 - 25600 in 1/3 stops, plus 50, 51200, 102400 as option</p>\r\n', '<h2 style=\"text-align:center\"><span style=\"color:rgb(0, 0, 205); font-size:14px\">M&aacute;y ảnh&nbsp;Canon 6D</span></h2>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"color:#0000cd\"><span style=\"font-size:14px\"><strong>GIỚI THIỆU</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:12px\">Kh&ocirc;ng l&acirc;u sau tin đồn về một chiếc m&aacute;y ảnh DSLR với cảm biến 35mm (full-frame) gi&aacute; rẻ, Canon h&ocirc;m nay đ&atilde; ch&iacute;nh thức giới thiệu <strong><a href=\"http://zshop.vn/may-anh-canon-dslr/\">m&aacute;y ảnh</a>&nbsp;Canon 6D</strong>&nbsp;với cảm biến k&iacute;ch cỡ&nbsp;tương tự như tr&ecirc;n&nbsp;Canon 5D Mark III&nbsp;nhưng gi&aacute; rẻ hơn nhiều. Kh&ocirc;ng chỉ l&agrave; một chiếc m&aacute;y ảnh full-frame với gi&aacute; hấp dẫn, 6D c&ograve;n l&agrave; m&aacute;y ảnh chuy&ecirc;n nghiệp đầu ti&ecirc;n được t&iacute;ch hợp GPS cũng như kết nối WiFi, cho ph&eacute;p người d&ugrave;ng sử dụng c&aacute;c ứng dụng tr&ecirc;n Android hay iOS để điều khiển m&aacute;y.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"color:#0000cd\"><span style=\"font-size:14px\"><strong>T&Iacute;NH NĂNG NỔI BẬT</strong></span></span></p>\r\n\r\n<p><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"font-size:12px\"><strong>Thiết kế:</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:12px\">Nh&igrave;n từ ph&iacute;a trước,&nbsp;<a href=\"http://zshop.vn/may-anh-canon-dslr/\"><strong>Canon 6D</strong></a>&nbsp;c&oacute; thiết kế kh&aacute; đơn giản, ở phần tr&ecirc;n nơi c&oacute; lăng k&iacute;nh k&iacute;nh ngắm hơi gồ ra c&ugrave;ng với logo Canon, tr&ocirc;ng như một c&aacute;i tr&aacute;n d&ocirc;. Kh&ocirc;ng hề c&oacute; flash t&iacute;ch hợp, điều kh&ocirc;ng b&igrave;nh thường đối với một m&aacute;y ảnh d&agrave;nh cho người d&ugrave;ng cao cấp, nhưng ph&ugrave; hợp với m&aacute;y ảnh full-frame Canon. Cổng hồng ngoại được đặt ngay tr&ecirc;n phần b&aacute;ng cầm tay, một đ&egrave;n hẹn giờ (kh&ocirc;ng phải đ&egrave;n hỗ trợ lấy n&eacute;t AF) nằm giữa phần b&aacute;ng cầm v&agrave; ống k&iacute;nh. Nằm hơi chếch b&ecirc;n dưới ph&iacute;a tr&aacute;i của ng&agrave;m ống k&iacute;nh l&agrave; n&uacute;t xem trước độ s&acirc;u trường ảnh, kh&aacute; nhỏ n&ecirc;n kh&oacute; nhận thấy. N&uacute;t nhả ống k&iacute;nh kh&aacute; lớn nằm ở vị tr&iacute; th&ocirc;ng thường, v&agrave; 3 lỗ microphone nằm ngay b&ecirc;n dưới logo.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:12px\"><img alt=\"Đánh giá máy ảnh full-frame Canon EOS 6D\" src=\"http://zshop.vn/images/product_kts/canon-6d-000.jpg\" style=\"height:387px; width:500px\" title=\"Canon 6D + 24-105mm F4 \" /></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"color:#0000cd\"><span style=\"font-size:12px\">Đ&aacute;nh gi&aacute; nhanh m&aacute;y ảnh full-frame&nbsp;Canon 6D</span></span></p>\r\n\r\n<p><span style=\"font-size:12px\">Mode dial c&oacute; &iacute;t t&ugrave;y chọn hơn so với th&ocirc;ng thường, n&ecirc;n khoảng c&aacute;ch giữa c&aacute;c t&ugrave;y chọn kh&aacute; lớn, ở giữa v&ograve;ng xoay chọn chế độ chụp n&agrave;y l&agrave; n&uacute;t kh&oacute;a. N&uacute;t nguồn nằm ngay b&ecirc;n dưới Model dial. Giống như tr&ecirc;n model Canon 60D, chiếc m&aacute;y ảnh&nbsp;</span><a href=\"http://zshop.vn/may-anh-canon-dslr/\"><strong>Canon 6D</strong></a><span style=\"color:rgb(44, 43, 43); font-size:12px\">&nbsp;cũng c&oacute; một d&atilde;y c&aacute;c n&uacute;t nằm ph&iacute;a tr&ecirc;n m&agrave;n h&igrave;nh phụ để chỉnh nhanh c&aacute;c thiết lập th&ocirc;ng số chụp. Th&ocirc;ng tin về Wi-Fi v&agrave; GPS cũng như chế độ HDR cũng hiển thị tr&ecirc;n m&agrave;n h&igrave;nh n&agrave;y.</span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:12px\"><img alt=\"Máy ảnh full-frame Canon EOS 6D có thiết kế khá đơn giản\" src=\"http://zshop.vn/images/product_kts/canon-6d-001.jpg\" style=\"height:278px; width:500px\" title=\"Canon 6D + 24-105mm F4 \" /></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"color:#0000cd\"><span style=\"font-size:12px\">M&aacute;y ảnh full-frame&nbsp;Canon 6D&nbsp;c&oacute; thiết kế kh&aacute; đơn giản</span></span></p>\r\n\r\n<p><span style=\"font-size:12px\">Kh&ocirc;ng c&oacute; m&agrave;n h&igrave;nh xoay lật, cụm điều khiển của&nbsp;</span><a href=\"http://zshop.vn/may-anh-canon-dslr/\"><strong>Canon 6D</strong></a><span style=\"color:rgb(44, 43, 43); font-size:12px\">&nbsp;c&oacute; nhiều điểm chung với model T4i hơn so với c&aacute;c m&aacute;y ảnh kh&aacute;c. Canon đ&atilde; loại bỏ 5 n&uacute;t b&ecirc;n tr&aacute;i m&agrave;n h&igrave;nh LCD, x&oacute;a n&uacute;t Picture Styles v&agrave; RAW/JPEG (từ EOS 7D), v&agrave; bố tr&iacute; c&aacute;c n&uacute;t c&ograve;n lại như ảnh tr&ecirc;n.</span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:12px\"><img alt=\"Canon 6D cũng bỏ đi cần gạt 8 chiều và thay bằng một đĩa điều hướng 8 chiều\" src=\"http://zshop.vn/images/product_kts/canon-6d-002.jpg\" style=\"border:0px none; height:382px; width:500px\" title=\"Canon 6D + 24-105mm F4 \" /></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"color:#0000cd\"><span style=\"font-size:12px\">M&aacute;y ảnh&nbsp;Canon 6D&nbsp;cũng bỏ đi cần gạt 8 chiều v&agrave; thay bằng một đĩa điều hướng 8 chiều</span></span></p>\r\n\r\n<p><span style=\"font-size:12px\">Canon cũng bỏ đi cần gạt 8 chiều v&agrave; thay bằng một đĩa điều hướng 8 chiều. N&uacute;t Q sẽ cho ph&eacute;p người d&ugrave;ng truy cập tới menu nhanh để điều chỉnh c&aacute;c thiết lập. N&uacute;t&nbsp;Live View/Movie Record&nbsp;cho ph&eacute;p chuyển đổi nhanh giữa hai chế độ xem sống Live View v&agrave; quay phim.</span></p>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<div><strong>Cảm biến:</strong></div>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<div>Đ&aacute;nh gi&aacute; nhanh m&aacute;y ảnh full-frame Canon EOS 6DCanon đ&atilde; ph&aacute;t triển một cảm biến CMOS full-frame ho&agrave;n to&agrave;n mới cho Canon 6D. Với k&iacute;ch thước 35,8 x 23,9 mm, n&oacute; nhỏ hơn một ch&uacute;t so với c&aacute;c cảm biến được t&igrave;m thấy trong Canon 5D Mark II v&agrave; Canon 5D Mark III. Độ ph&acirc;n giải hiệu dụng l&agrave; 20.2 megapixel. Mặc d&ugrave; c&oacute; độ ph&acirc;n giải thua mức 24.3 megapixel của Nikon D600, nhưng k&iacute;ch thước điểm ảnh của Canon 6D lớn hơn: với k&iacute;ch thước điểm ảnh 6,55 micron, so với 5,9 micron của D600.</div>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<div><strong>Bộ vi xử l&yacute;:</strong></div>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<div>Ảnh xuất ra từ bộ cảm biến mới được xử l&yacute; bởi một bộ xử l&yacute; h&igrave;nh ảnh DIGIC 5+, tương tự như bộ xử l&yacute; của d&ograve;ng m&aacute;y đắt hơn Canon 5D Mark III. Canon n&oacute;i rằng Canon EOS 6D sử dụng hệ chuyển đổi analog-sang-kỹ thuật số 14-bit.Đ&aacute;nh gi&aacute; nhanh m&aacute;y ảnh full-frame Canon EOS 6D</div>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<div><strong>Hiệu suất:</strong></div>\r\n\r\n<div>Chiếc m&aacute;y ảnh Canon 6D thua d&ograve;ng Nikon D600 ở khả năng chụp ảnh li&ecirc;n tục. Trong khi D600 đạt tốc độ 5.5 fps th&igrave; 6D chỉ đạt 4.5 fps.</div>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<div><strong>Độ nhạy s&aacute;ng:</strong></div>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<div>Mặc d&ugrave; kh&ocirc;ng sử dụng bộ cảm biến giống Canon 5D Mark III, Canon 6D cung cấp c&ugrave;ng một dải ISO từ ISO 100 đến 25.600, c&oacute; thể mở rộng từ 50 đến 102.400. Ngược lại, Nikon D600 c&oacute; một dải ISO ti&ecirc;u chuẩn l&agrave; ISO 100 &ndash; 6400, mở rộng từ ISO 50 - 25.600.</div>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<div><strong>Tự động lấy n&eacute;t:</strong></div>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<div>Điểm kh&aacute;c biệt r&otilde; r&agrave;ng nhất giữa Canon 6D v&agrave; c&aacute;c model full-frame đắt tiền hơn ch&iacute;nh l&agrave; hệ thống tự động lấy n&eacute;t. Trong khi Canon 5D Mark III c&oacute; hệ thống tự động lấy n&eacute;t 61-điểm với 41 điểm cross-type, Canon 6D chỉ c&oacute; 11 điểm trong một mảng h&igrave;nh thoi, với điểm trung t&acirc;m l&agrave; điểm cross-type duy nhất. (Về mặt n&agrave;y, 6D giống với 5D Mark II hơn, với một hệ thống lấy n&eacute;t 15-điểm v&agrave; 1 điểm cross-type, nhưng 6 điểm trong số n&agrave;y chỉ l&agrave; điểm hỗ trợ lấy n&eacute;t).</div>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<div><strong>K&iacute;nh ngắm:</strong></div>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<div>K&iacute;nh ngắm quang của m&aacute;y ảnh Canon 6D c&oacute; v&ugrave;ng phủ 97%, xếp sau mức 98% của EOS 5D Mark II. Canon 5D Mark III v&agrave; Nikon D600 đều c&oacute; v&ugrave;ng phủ 100%.</div>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<p><strong>M&agrave;n h&igrave;nh hiển thị:</strong></p>\r\n\r\n<p>C&oacute; hai m&agrave;n h&igrave;nh tr&ecirc;n chiếc m&aacute;y ảnh Canon 6D, cũng thường thấy tr&ecirc;n c&aacute;c m&aacute;y ảnh c&oacute; c&ugrave;ng tầm gi&aacute;. M&agrave;n h&igrave;nh nhỏ nằm ph&iacute;a mặt tr&ecirc;n của m&aacute;y l&agrave; một m&agrave;n h&igrave;nh LCD đơn sắc, hiển thị trạng th&aacute;i của m&aacute;y, thực hiện c&aacute;c c&agrave;i đặt tr&ecirc;n m&aacute;y, gi&uacute;p bạn tiết kiệm pin v&agrave; thời gian hơn so với m&agrave;n h&igrave;nh ở mặt sau.</p>\r\n\r\n<p>Trong khi đ&oacute;, m&agrave;n h&igrave;nh mặt sau l&agrave; một m&agrave;n h&igrave;nh LCD 3.0-inch Clear View với độ ph&acirc;n giải 1.040.000 dot. M&agrave;n h&igrave;nh n&agrave;y c&oacute; độ tương phản k&eacute;m hơn m&agrave;n h&igrave;nh Clear View II tr&ecirc;n Canon 5D Mark III. N&oacute; c&oacute; g&oacute;c nh&igrave;n rộng 170 độ, l&agrave; m&agrave;n h&igrave;nh cố định, kh&ocirc;ng c&oacute; cảm ứng.</p>\r\n\r\n<div>So với Nikon D600, số điểm ảnh của m&agrave;n h&igrave;nh tr&ecirc;n Canon 6D cao hơn một ch&uacute;t. Khi mở ở chế độ xem sống Live View, m&agrave;n h&igrave;nh c&oacute; v&ugrave;ng phủ 100%.</div>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<div><strong>Flash:</strong></div>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<div>Kh&ocirc;ng c&oacute; đ&egrave;n flash t&iacute;ch hợp trong Canon 6D (trong khi Nikon D600 c&oacute; flash), v&igrave; vậy c&oacute; thể bạn phải mất th&ecirc;m chi ph&iacute; sắm đ&egrave;n flash rời.</div>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<div>Với Canon 6D bạn phải mất th&ecirc;m chi ph&iacute; sắm đ&egrave;n flash rời cho m&aacute;y</div>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<div>Với m&aacute;y ảnh Canon 6D bạn phải mất th&ecirc;m chi ph&iacute; sắm đ&egrave;n flash rời cho m&aacute;y</div>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<div><strong>Chế độ phơi s&aacute;ng:</strong></div>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<div>C&aacute;c t&ugrave;y chọn chế độ phơi s&aacute;ng của Canon 6D gần như giống hệt với đối thủ cạnh tranh Nikon. B&ecirc;n cạnh c&aacute;c chế độ P, Av, Tv, M th&ocirc;ng thường, c&ograve;n c&oacute; chế độ Scene Auto +. &nbsp;Ngo&agrave;i ra c&ograve;n c&oacute; hai chế độ t&ugrave;y chỉnh Custom, Bulb v&agrave; Creative Auto nhằm hỗ trợ th&ecirc;m cho những tay m&aacute;y nghiệp dư.</div>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<div><strong>Đo s&aacute;ng:</strong></div>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<div>M&aacute;y ảnh Canon 6D thực hiện đo s&aacute;ng với 63-zone, cảm biến đo s&aacute;ng iFCL dual-layer (2 lớp). Cảm biến n&agrave;y được sử dụng lần đầu tr&ecirc;n Canon 7D v&agrave; cũng được d&ugrave;ng tr&ecirc;n Canon 5D Mark III.</div>\r\n\r\n<div>Trong hai lớp đo s&aacute;ng, lớp tr&ecirc;n nhạy cảm với m&agrave;u đỏ v&agrave; m&agrave;u xanh l&aacute; c&acirc;y, trong khi lớp ph&iacute;a dưới ph&aacute;t hiện m&agrave;u xanh dương v&agrave; m&agrave;u xanh l&aacute; c&acirc;y. Hệ thống đo s&aacute;ng n&agrave;y cho ph&eacute;p ph&aacute;t hiện tốt hơn chủ thể, th&ocirc;ng tin được phản hồi trở lại hệ thống tự động lấy n&eacute;t để hỗ trợ theo d&otilde;i chủ thể. Nikon, ngược lại, hệ thống đo s&aacute;ng RGB chi tiết hơn với 2.016 pixel, được gọi l&agrave; 3D color matrix metering II.</div>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<div><strong>M&agrave;n trập:</strong></div>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<div>Một điểm kh&aacute;c biệt nữa giữa Canon 6D v&agrave; Canon 5D Mark III l&agrave; cơ chế m&agrave;n trập. Trong khi Canon 5D Mark III được đ&aacute;nh gi&aacute; l&agrave; c&oacute; thể hoạt động tốt trong khoảng 150.000 chu kỳ, giống như D600 của Nikon, m&agrave;n trập 6D của Canon chỉ d&ugrave;ng nhiều nhất 100.000 chu kỳ, ngắn hơn nhiều. Điều đ&oacute; kh&ocirc;ng c&oacute; nghĩa l&agrave; n&oacute; chắc chắn kh&ocirc;ng thể chụp qu&aacute; 100.000 bức ảnh, nhưng n&oacute;i chung l&agrave; tuổi thọ của n&oacute; kh&ocirc;ng được bền như m&agrave;n trập trong m&aacute;y ảnh kh&aacute;c.</div>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<div><strong>Chế độ Creative:</strong></div>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<div>C&oacute; một v&agrave;i t&ugrave;y chọn chụp s&aacute;ng tạo tr&ecirc;n Canon 6D kh&aacute; giống nhưng kh&ocirc;ng phải được b&ecirc; nguy&ecirc;n xi từ 5D Mark III.Đ&aacute;nh gi&aacute; nhanh m&aacute;y ảnh full-frame Canon EOS 6D</div>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<p>Đầu ti&ecirc;n l&agrave; chế độ chụp High Dynamic Range, cho ph&eacute;p chụp 3 ảnh ri&ecirc;ng biệt với c&aacute;c mức phơi s&aacute;ng kh&aacute;c nhau, sau đ&oacute; kết hợp ch&uacute;ng lại th&agrave;nh một ảnh duy nhất c&oacute; dải tần nhạy s&aacute;ng cao hơn. C&aacute;c chế độ HDR tương tự như EOS 5D Mark III, nhưng kh&ocirc;ng cho ph&eacute;p lưu c&aacute;c ảnh nguồn. Thay v&agrave;o đ&oacute;, việc kết hợp c&aacute;c ảnh được thực hiện chỉ trong m&aacute;y ảnh, v&agrave; nếu bạn kh&ocirc;ng h&agrave;i l&ograve;ng với kết quả, bạn sẽ cần phải chụp lại. (Với 5D Mark III, nếu bạn đổi &yacute; hoặc chưa h&agrave;i l&ograve;ng, bạn c&oacute; thể chỉnh HDR bằng tay tr&ecirc;n m&aacute;y t&iacute;nh của bạn, bạn c&oacute; t&ugrave;y chọn để lưu h&igrave;nh ảnh nguồn). Số hiệu ứng HDR tr&ecirc;n Canon 6D cũng &iacute;t hơn tr&ecirc;n c&aacute;c anh chị em đắt tiền hơn của n&oacute;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Một t&ugrave;y chọn s&aacute;ng tạo th&uacute; vị kh&aacute;c, đ&oacute; l&agrave; chế độ phơi s&aacute;ng Multiple Exposure, chồng nhiều bức ảnh l&ecirc;n nhau. Đ&oacute; l&agrave; một kỹ thuật hữu &iacute;ch để tạo ra một bức ảnh phơi s&aacute;ng l&acirc;u hơn, giảm nhiễu hoặc đạt một số hiệu ứng. Bạn c&oacute; thể xếp chồng 9 h&igrave;nh ảnh, v&agrave; bạn c&oacute; thể sử dụng một ảnh RAW để l&agrave;m ảnh đầu ti&ecirc;n cho chuỗi ảnh xếp chồng nhau n&agrave;y. C&oacute; 4 phương ph&aacute;p gh&eacute;p chồng ảnh kh&aacute;c nhau: Additive, Average, Bright, Dark để cho c&aacute;c sắc th&aacute;i ảnh kh&aacute;c nhau. Thậm ch&iacute; bạn c&oacute; thể xem trước kết quả tr&ecirc;n m&agrave;n h&igrave;nh LCD, v&agrave; c&oacute; thể chọn undo (hủy) thao t&aacute;c gần nhất nếu bạn kh&ocirc;ng muốn một hiệu ứng n&agrave;o đ&oacute;. Mark III c&oacute; nhiều chế độ s&aacute;ng t&aacute;c kiểu n&agrave;y hơn, nhưng d&ugrave; sao đ&acirc;y cũng l&agrave; điều tuyệt vời cho người d&ugrave;ng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Giảm bụi:</strong></p>\r\n\r\n<p>Canon 6D được t&iacute;ch hợp hệ thống l&agrave;m sạch EOS Integrated Cleaning System, sử dụng một th&agrave;nh phần &aacute;p điện để rũ c&aacute;c hạt bụi ra khỏi bộ lọc th&ocirc;ng thấp ở ph&iacute;a trước của bộ cảm biến.</p>\r\n\r\n<p>Canon 6D được t&iacute;ch hợp hệ thống l&agrave;m sạch EOS Integrated Cleaning System</p>\r\n\r\n<p>M&aacute;y ảnh Canon 6D được t&iacute;ch hợp hệ thống l&agrave;m sạch EOS Integrated Cleaning System</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Quay phim:</strong></p>\r\n\r\n<p>Canon đ&atilde; đưa v&agrave;o 6D kh&aacute; nhiều t&iacute;nh năng quay video nổi bật đ&atilde; c&oacute; tr&ecirc;n c&aacute;c model 5D, bao gồm quay video Full HD (1080p, 1.920 x 1.080 pixel) ở 30, 25, hoặc 24 fps; 720p (1.280 x 720 pixel) ở 60 hoặc 50 fps, VGA (640 x 480 pixel) ở 30 hoặc 25 fps. Độ d&agrave;i mỗi clip được giới hạn l&agrave; 29 ph&uacute;t, 59 gi&acirc;y v&agrave; tự động chia đoạn sau mỗi 4GB dung lượng phim được lưu. Video định dạng ALL-I intraframe đ&ograve;i hỏi phải d&ugrave;ng thẻ nhớ chuẩn UHS-I, c&ograve;n định dạng IPB interframe-compressed th&igrave; &nbsp;c&oacute; thể lưu tr&ecirc;n thẻ SD th&ocirc;ng thường.</p>\r\n\r\n<p>Người d&ugrave;ng c&oacute; thể t&ugrave;y chỉnh độ phơi s&aacute;ng v&agrave; &acirc;m thanh theo &yacute; muốn. M&aacute;y c&oacute; cả micro t&iacute;ch hợp v&agrave; jack cắm micro ngo&agrave;i, nhưng sẽ kh&ocirc;ng c&oacute; jack cắm tai nghe để theo d&otilde;i &acirc;m thanh trong l&uacute;c quay như tr&ecirc;n Canon 5D Mark III.</p>\r\n\r\n<p>Canon 6D kh&aacute; nhiều t&iacute;nh năng quay video nổi bật đ&atilde; c&oacute; tr&ecirc;n c&aacute;c model 5D</p>\r\n\r\n<div>Canon 6D kh&aacute; nhiều t&iacute;nh năng quay video nổi bật đ&atilde; c&oacute; tr&ecirc;n c&aacute;c model 5D</div>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<div><strong>Kết nối mạng kh&ocirc;ng d&acirc;y:</strong></div>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<p>Một trong những t&iacute;nh năng m&agrave; Canon 6D vượt l&ecirc;n so với c&aacute;c &quot;anh em&quot; đắt tiền hơn, đ&oacute; l&agrave; n&oacute; được t&iacute;ch hợp kết nối Wi-Fi. Tuy nhi&ecirc;n, khả năng bắt s&oacute;ng Wi-Fi IEEE 802.11 b / g / n của 6D kh&ocirc;ng mạnh, chỉ trong phạm vi 30m. Trong khi đ&oacute;, nếu chọn mua th&ecirc;m thiết bị WFT-E7A Wireless File Transmitter để d&ugrave;ng với Canon 5D Mark III th&igrave; bạn c&oacute; thể kết nối trong phạm vi 150m. Nếu bạn chụp ảnh với mục đ&iacute;ch chuyển h&igrave;nh ảnh l&ecirc;n internet th&ocirc;ng qua một điện thoại th&ocirc;ng minh trong t&uacute;i của bạn, th&igrave; Wi-Fi t&iacute;ch hợp của Canon 6D sẽ tiện lợi hơn.</p>\r\n\r\n<p>M&aacute;y ảnh Canon 6D kh&ocirc;ng chỉ kết nối với điện thoại th&ocirc;ng minh m&agrave; c&ograve;n c&oacute; thể kết nối với một số m&aacute;y ảnh PowerShot-series, m&aacute;y t&iacute;nh bảng cũng như điện thoại th&ocirc;ng minh Android hay iOS. Bạn cũng c&oacute; thể chia sẻ h&igrave;nh ảnh tr&ecirc;n c&aacute;c trang web mạng x&atilde; hội. Ảnh sẽ được chuyển tới địa chỉ bạn muốn th&ocirc;ng qua dịch vụ đ&aacute;m m&acirc;y IGAGE GATEWAY của Canon, c&oacute; y&ecirc;u cầu đăng k&yacute;.</p>\r\n\r\n<p>C&ograve;n nữa, bạn c&oacute; thể điều khiển từ xa chiếc Canon 6D của bạn từ c&aacute;c thiết bị gắn k&egrave;m, bằng c&aacute;ch sử dụng ứng dụng miễn ph&iacute; Canon EOS Remote. (Bạn sẽ cần phải c&oacute; một thiết bị chạy iOS 5 hay Android 2.3 + / 4.0 + cho việc n&agrave;y.)</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Định vị địa l&yacute;:</strong></p>\r\n\r\n<p>Canon 6D cũng l&agrave; m&aacute;y ảnh DSLR đầu ti&ecirc;n của Canon t&iacute;ch hợp GPS, cho ph&eacute;p m&aacute;y ảnh x&aacute;c định vị tr&iacute; của n&oacute;, sau đ&oacute; ghi lại th&ocirc;ng số vĩ độ, kinh độ, độ cao, v&agrave; đ&oacute;ng dấu thời gian UTC trong dữ liệu EXIF của mỗi h&igrave;nh ảnh. 5D Mark III đ&ograve;i hỏi một thiết bị ri&ecirc;ng biệt để đạt được điều n&agrave;y. Bạn c&oacute; thể xem c&aacute;c địa điểm chụp ảnh của bạn tr&ecirc;n bản đồ bằng c&aacute;ch sử dụng phần mềm Map Utility k&egrave;m theo.</p>\r\n\r\n<p>C&aacute;c lựa chọn kết nối kh&aacute;c bao gồm: một cổng kết hợp USB 2.0 v&agrave; đầu ra video ti&ecirc;u chuẩn, đầu ra video độ n&eacute;t cao HDMI, một jack cắm micro ngo&agrave;i chuẩn 3.5 mm, v&agrave; cổng điều khiển từ xa.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Thời lượng pin:</strong></p>\r\n\r\n<p>M&aacute;y sử dụng c&ugrave;ng một chuẩn pin lithium-ion LP-E6 như tr&ecirc;n c&aacute;c model Canon 6D, Canon 7D, Canon 5D Mark II, Canon 5D Mark III, n&ecirc;n bạn c&oacute; thể d&ugrave;ng chung bộ pin giữa c&aacute;c d&ograve;ng m&aacute;y n&agrave;y. Bộ pin n&agrave;y c&oacute; thời lượng sử dụng theo ti&ecirc;u chuẩn thử nghiệm CIPA l&agrave; khoảng 1.090 ảnh chụp tại 23&deg;C v&agrave; 980 bức ảnh ở 0&deg;C. Tuổi thọ pin khi sử dụng GPS v&agrave; WiFi chưa được n&ecirc;u r&otilde;.</p>\r\n\r\n<p>Canon 6D tương th&iacute;ch với thẻ Secure Digital bao gồm cả SDHC v&agrave; SDXC dung lượng cao c&aacute;c loại. Kh&ocirc;ng giống như EOS 5D II v&agrave; III, kh&ocirc;ng c&ograve;n bất kỳ sự hỗ trợ n&agrave;o cho thẻ CompactFlash.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kết luận:</strong></p>\r\n\r\n<p>Với sự ra đời của Canon 6D việc sở hữu một m&aacute;y ảnh full-frame đ&atilde; trở n&ecirc;n dễ d&agrave;ng hơn. Đổi lại, bạn sẽ thiếu đi một số t&iacute;nh năng cao cấp đ&atilde; thấy tr&ecirc;n d&ograve;ng 5D series. So với Canon 5D Mark III, Canon 6D c&oacute; thể c&ograve;n thua k&eacute;m nhiều.</p>\r\n\r\n<p>L&agrave; m&aacute;y ảnh DSLR đầu ti&ecirc;n của Canon t&iacute;ch hợp Wi-Fi v&agrave; GPS, chiếc Canon 6D sẽ l&agrave; một lựa chọn th&uacute; vị với c&aacute;c nhiếp ảnh gia cỡ trung. Độ ph&acirc;n giải 20.2MP của m&aacute;y c&oacute; thể kh&ocirc;ng ấn tượng so với c&aacute;c m&aacute;y DSLR định dạng APS-C, nhưng chắc chắn chất lượng h&igrave;nh ảnh sẽ tốt hơn đ&aacute;ng kể nhờ cảm biến full-frame.</p>\r\n', '1', '13', '3', '2014-12-08');
INSERT INTO `products` VALUES ('8', ' Canon 600D (Body) (Chính hãng) ', '1', '9104650', '<p>Điểm nổi bật của sản phẩm</p>\r\n\r\n<p>- Khe cắm thẻ nhớ SD / SDHC / SDXC</p>\r\n\r\n<p>- 18MP APS-C CMOS cảm biến</p>\r\n\r\n<p>- 3.0 &quot;Clear View Vari-Angle LCD</p>\r\n\r\n<p>- Dải ISO100-6400</p>\r\n\r\n<p>- Chế độ phim Full HD w / Manual Exposure</p>\r\n\r\n<p>- Tương th&iacute;ch với ống k&iacute;nh Canon EF v&agrave; EF-S</p>\r\n', '<h2 style=\"text-align:center\"><strong><span style=\"color:#0000cd\"><span style=\"font-size:14px\">Canon 600D Body</span></span></strong></h2>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"color:#0000cd\"><strong><span style=\"font-size:14px\">GIỚI THIỆU</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif\"><strong><a href=\"http://zshop.vn/may-anh-canon-dslr/\">M&aacute;y ảnh</a>&nbsp;&nbsp;</strong>được trang bị cảm biến CMOS chất lượng cao18 megapixel v&agrave; bộ xử l&yacute; h&igrave;nh ảnh DIGIC 4 cực kỳ mạnh mẽ, cho h&igrave;nh ảnh chất lượng cao. C&aacute;c t&iacute;nh năng như Tự động lựa chọn cảnh th&ocirc;ng minh, Basic+ v&agrave; chức năng Hướng dẫn sẽ gi&uacute;p bạn sử dụng c&aacute;c chức năng s&aacute;ng tạo của m&aacute;y một c&aacute;ch đơn giản. T&iacute;nh năng S&aacute;ng tạo cải tiến cũng như ghi video Full HD v&agrave; chụp h&igrave;nh từ Video đều được t&iacute;ch hợp trong&nbsp;Canon&nbsp;</span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:12px\"><img alt=\" Canon với Firmware 1.0.0\" src=\"http://zshop.vn/images/hinhbaiviet/front.jpg\" style=\"height:375px; width:500px\" title=\"Canon 600D (Body)\" /></span></p>\r\n\r\n<p style=\"text-align:center\"><br />\r\n<span style=\"color:#0000cd\"><span style=\"font-size:12px\">&nbsp;Canon 600D&nbsp;với Firmware 1.0.0</span></span></p>\r\n\r\n<p style=\"text-align:left\"><br />\r\n<span style=\"font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif\">Với ti&ecirc;u ch&iacute; tối ưu ho&aacute; thao t&aacute;c chụp h&igrave;nh của&nbsp;</span><span style=\"font-family:arial,helvetica,sans-serif\">đ&atilde; được trang bị cảm biến CMOS 18megapixel (cỡ APS-C) nhằm tăng hiệu quả chụp h&igrave;nh của bạn, gi&uacute;p bạn chụp được mọi cảnh từ những phong cảnh ngoạn mục cho tới chụp cận cảnh động vật hoang d&atilde;, r&otilde; đến từng chi tiết. Với độ ph&acirc;n giải lớn 18megapixel, bạn cũng c&oacute; thể chuyển ảnh tới ph&ograve;ng trưng b&agrave;y thậm ch&iacute; ngay cả sau khi crop h&igrave;nh.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"color:#0000cd\"><strong>T&Iacute;NH NĂNG NỔI BẬT</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:12px\"><strong>M&agrave;n h&igrave;nh ngắm LCD đa g&oacute;c ngắm cỡ rộng 3-inch</strong></span></p>\r\n\r\n<p><span style=\"font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif\">Để ghi v&agrave; xem lại ảnh,&nbsp;&nbsp;sử dụng một m&agrave;n h&igrave;nh ngắm LCD đa g&oacute;c ngắm cỡ rộng 3 inch rất r&otilde; n&eacute;t bao gồm hơn một triệu điểm ảnh. M&agrave;n h&igrave;nh đa g&oacute;c ngắm hiện đại cũng được bổ xung th&ecirc;m để những tay m&aacute;y ham t&igrave;m t&ograve;i c&oacute; thể ph&aacute;t hiện được c&aacute;c khu&ocirc;n h&igrave;nh đẹp bởi thiết kế đặc biệt kiểu khớp nối cho ph&eacute;p bạn xoay m&agrave;n h&igrave;nh ở hầu hết c&aacute;c g&oacute;c.</span></span><br />\r\n&nbsp;</p>\r\n\r\n<p><span style=\"font-size:12px\"><strong>T&iacute;nh năng tự động lựa chọn cảnh th&ocirc;ng minh</strong></span></p>\r\n\r\n<p><span style=\"font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif\">Cuộc c&aacute;ch mạng nổi bật của Canon ch&iacute;nh l&agrave; t&iacute;nh năng Tự động lựa chọn cảnh th&ocirc;ng minh, một chế độ chụp ho&agrave;n to&agrave;n tự động, sử dụng tối ưu Hệ thống d&ograve; t&igrave;m cảnh EOS, một hệ thống t&iacute;ch hợp nhằm ph&acirc;n t&iacute;ch mọi yếu tố của cảnh chụp bao gồm mặt người v&agrave;/hoặc độ s&aacute;ng của ảnh, chuyển động, độ tương phản, m&agrave;u sắc, khoảng c&aacute;ch v&agrave; c&ograve;n nhiều hơn thế nữa. Th&ocirc;ng tin n&agrave;y sau đ&oacute; sẽ được T&iacute;nh năng Tự động chọn cảnh th&ocirc;ng minh đ&aacute;nh gi&aacute; để tạo ra những tấm h&igrave;nh đẹp, thu được n&eacute;t tinh tu&yacute; của những vật chụp xung quanh.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Bộ cảm biến CMOS k&iacute;ch thước APS-C độ ph&acirc;n giải 18MP</strong></p>\r\n\r\n<p>Được trang bị bộ cảm biến CMOS APS-cỡ C 18 megapixel cao cấp trong d&ograve;ng DSLR, Canon c&oacute; thể chụp được định dạng ảnh lớn nhất (RAW v&agrave; JPEG+RAW) với chi tiết v&agrave; độ n&eacute;t cao ngay cả trong điều kiện &aacute;nh s&aacute;ng yếu. Cảm biến 18MP CMOS kh&ocirc;ng chỉ cho ph&eacute;p crop ảnh vẫn giữ được độ sắc n&eacute;t của chi tiết m&agrave; c&ograve;n đem đến tốc độ truyền dữ liệu si&ecirc;u tốc nhờ đường t&iacute;n hiệu đa k&ecirc;nh, tăng cường khả năng phản ứng của m&aacute;y cũng như giảm nhiễu v&agrave; c&ocirc; lập c&aacute;c m&agrave;u giả được ph&aacute;t hiện.</p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"Canon 600D với màn hình xoay linh hoạt\" src=\"http://zshop.vn/images/hinhbaiviet/alexchow_3_2fb07ec160d74e1d5923880f3dd6aa1f.jpg\" style=\"font-size:12px; height:375px; text-align:center; width:500px\" title=\"Canon 600D (Body)\" /></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"color:#0000cd\">Canon với m&agrave;n h&igrave;nh xoay linh hoạt</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Sở hữu vi xử l&yacute; h&igrave;nh ảnh DIGIC 4</strong></p>\r\n\r\n<p>DIGIC 4&nbsp;l&agrave; thế hệ thứ s&aacute;u trong bộ xử l&yacute; h&igrave;nh ảnh được nghi&ecirc;n cứu v&agrave; sản xuất của Canon. So với&nbsp;DIGIC 3, DIGIC 4&nbsp;tăng cường c&aacute;c thuật to&aacute;n khử nhiễu nhằm hạn chế tối đa c&aacute;c chi tiết giả tr&ecirc;n ảnh, cải tiến vượt trội chất lượng h&igrave;nh ảnh v&agrave; tốc độ xử l&iacute;. Ngo&agrave;i ra, DIGIC 4 c&ograve;n hỗ trợ chụp ảnh định dạng file RAW với độ s&acirc;u m&agrave;u 14 bit v&agrave; chức năng Face Detection cho ph&eacute;p nhận diện khu&ocirc;n mặt nhanh gấp 3 lần v&agrave; tốc độ xử l&yacute; t&iacute;n hiệu gấp 1.3 lần.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Dải ISO chuẩn 100 - 6400 mở rộng đến 12800</strong></p>\r\n\r\n<p>Dải ISO rộng đặc biệt quan trọng khi cho ph&eacute;p bạn chụp ảnh ở nhiều điều kiện s&aacute;ng kh&aacute;c nhau, d&ugrave; l&agrave; khung cảnh ngo&agrave;i trời ban ng&agrave;y hay cảnh đ&ecirc;m thiếu s&aacute;ng.&nbsp;c&oacute; dải ISO chuẩn&nbsp;100 - 6400&nbsp;v&agrave; c&oacute; thể mở rộng đến 12800 gi&uacute;p tăng vượt trội tốc độ đ&oacute;ng mở m&agrave;n tr&acirc;̣p, nắm bắt c&aacute;c khoảnh khắc để c&oacute; tốc độ chụp chớp nho&aacute;ng 3.7fps, hạn ch&ecirc;́ t&ocirc;́i đa hi&ecirc;̣n tượng nh&ograve;e do rung lắc của th&acirc;n m&aacute;y v&agrave; chụp được cả những đối tượng di chuyển.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Nhật k&iacute; video với Video Snapshot v&agrave; Advanced movie crop</strong></p>\r\n\r\n<p>B&ecirc;n cạnh khả năng quay phim Full HD,&nbsp;thể hiện sự n&acirc;ng cấp vượt trội với chức năng Advanced movie crop cho ph&eacute;p bạn zoom từ 3x -10x c&aacute;c đối tượng đang quay phim v&agrave; chức năng Video Snapshot để gh&eacute;p những đoạn clip ngắn với nhau k&egrave;m nhạc nền. Với chức năng Video Snapshot đầy th&uacute; vị n&agrave;y, những kỉ niệm như ng&agrave;y đầu b&eacute; con nh&agrave; bạn đi nh&agrave; trẻ, nhổ chiếc răng đầu ti&ecirc;n hoặc tập đi xe đạp đều sẽ trở th&agrave;nh đoạn video nhật k&iacute; đầy cảm x&uacute;c m&agrave; bạn kh&ocirc;ng thể bỏ qua.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Hỗ trợ tối đa chụp ảnh đẹp</strong></p>\r\n\r\n<p>Với hai chức năng Basic+ v&agrave; Scene Intelligent Auto, bạn chỉ cần n&acirc;ng l&ecirc;n l&agrave; m&aacute;y ảnh sẽ tự động ph&acirc;n t&iacute;ch v&agrave; c&agrave;i đặt c&aacute;c th&ocirc;ng số như Độ phơi s&aacute;ng, Lấy n&eacute;t, C&acirc;n bằng trắng, Điều chỉnh s&aacute;ng để mang đến t&ocirc;ng m&agrave;u đẹp nhất cho bức ảnh của bạn. Ngo&agrave;i ra, chức năng Feature Guide c&ograve;n hiển thị m&ocirc; tả chức năng của c&aacute;c n&uacute;t bấm để bạn c&oacute; được c&aacute;i nh&igrave;n tổng quan nhất về chiếc m&aacute;y ảnh của m&igrave;nh ngay những lần đầu sử dụng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>M&agrave;n h&igrave;nh lật v&agrave; chức năng LiveView</strong></p>\r\n\r\n<p>Chức năng ngắm trực tiếp LiveView giờ đ&atilde; được tăng th&ecirc;m sức mạnh v&agrave; sự tiện dụng nhờ m&agrave;n h&igrave;nh 3&rdquo; c&oacute; thể lật xoay để bạn dễ d&agrave;ng ngắm v&agrave; chụp từ những g&oacute;c độ kh&oacute;, khi bạn sử dụng tripod hoặc chụp từ hướng tr&ecirc;n đầu m&igrave;nh xuống. M&agrave;n h&igrave;nh n&agrave;y sử dụng c&ocirc;ng nghệ hiển thị TFT 1.040.000 điểm m&agrave;u được phủ 2 lớp chống l&oacute;a v&agrave; 7 mức s&aacute;ng để bạn được xem lại những khoảng khắc tuyệt vời nhất m&agrave;&nbsp;&nbsp;nắm bắt.</p>\r\n', '1', '2', '2', '2014-12-17');
INSERT INTO `products` VALUES ('9', ' Canon 100D + 40mm F/2.8 STM / Màu trắng ', '1', '10894000', '<p>Điểm nổi bật của sản phẩm</p>\r\n\r\n<p><span style=\"color:rgb(45, 45, 45)\">- Cảm biến CMOS APS-C 18 megapixel</span></p>\r\n\r\n<p>- T&iacute;ch hợp hệ thống lấy Hybrid CMOS AF II</p>\r\n\r\n<p>- Bộ xử l&iacute; h&igrave;nh ảnh: DIGIC 5</p>\r\n\r\n<p>- Chụp được ảnh RAW 14 bit</p>\r\n\r\n<p>- ISO: 100 đến 12.800, mở rộng đến 25.600</p>\r\n', '<h2><span style=\"color:#0000cd\"><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"font-size:14px\">Canon 100D + 40mm F/2.8 STM</span></span></span></h2>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>GIỚI THIỆU</strong></span></p>\r\n\r\n<p>Với k&iacute;ch thước nhỏ hơn 25% v&agrave; nhẹ hơn đến 28% so với người anh em EOS 650D, <a href=\"http://zshop.vn/may-anh-canon-dslr/\"><strong>m&aacute;y ảnh</strong></a>&nbsp;<strong>Canon 100D</strong>&nbsp;tự h&agrave;o l&agrave; chiếc m&aacute;y DSLR tại thời điểm ra mắt, hứa hẹn mang đến cho bạn nhiều trải nghiệm bất ngờ v&agrave; th&uacute; vị. Tr&aacute;i tim của m&aacute;y l&agrave; cảm biến lai Hybrid CMOS 18.0MP, k&iacute;ch thước APS-C, hệ số crop 1.6x c&ugrave;ng sức mạnh của hệ vi xử l&yacute; DIGIC 5 ti&ecirc;n tiến v&agrave; hệ thống lấy n&eacute;t tự động lai Hybrid AF cho khả năng chụp ảnh chất lượng cao, xử l&yacute; ảnh si&ecirc;u tốc trong mọi điều kiện s&aacute;ng, v&agrave; mọi trạng th&aacute;i chủ thể ảnh đem đến những bức ảnh đẹp tuyệt vời c&ugrave;ng khả năng chụp tốc độ 4fps v&agrave; quay phim Full HD chuẩn &acirc;m thanh nổi stereo. Dải nhạy s&aacute;ng rộng hỗ trợ tối đa người chụp trong mọi điều kiện s&aacute;ng, do đ&oacute;&nbsp;Canon 100D&nbsp;kh&ocirc;ng cần phải t&iacute;ch hợp sẵn đ&egrave;n flash. Ngo&agrave;i ra, khả năng quay phim Full HD c&ugrave;ng những t&iacute;nh năng ưu việt c&ugrave;ng giao diện hỗ trợ trực quan khiến&nbsp;Canon 100D&nbsp;c&agrave;ng trở n&ecirc;n ho&agrave;n hảo hơn v&agrave; xứng đ&aacute;ng được bạn cho v&agrave;o danh s&aacute;ch &ldquo;wish lists&rdquo; của m&igrave;nh ngay h&ocirc;m nay.<br />\r\n&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"Canon 100D + 40mm F/2.8 STM / Màu trắng\" src=\"http://zshop.vn/images/detailed/32/2219378_tinhte_Canon-EOS-Kiss-X7-5.jpg\" style=\"height:375px; width:500px\" title=\"Canon 100D + 40mm F/2.8 STM\" /></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"color:#0000cd\">Canon 100D + 40mm F/2.8 STM / M&agrave;u trắng</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:12px\">Đ&uacute;ng như c&aacute;c tin đồn trước đ&oacute;, h&atilde;ng&nbsp;m&aacute;y ảnh Canon&nbsp;h&ocirc;m nay giới thiệu mẫu m&aacute;y&nbsp;</span><a href=\"http://zshop.vn/may-anh-canon-dslr/\"><strong>Canon 100D</strong></a><span style=\"font-size:12px\">&nbsp;(t&ecirc;n gọi Rebel SL1 tại Mỹ)</span><strong>&nbsp;</strong><span style=\"color:rgb(45, 45, 45)\">nhỏ v&agrave; nhẹ nhất trong d&ograve;ng m&aacute;y ảnh số ống k&iacute;nh rời DSLR tr&ecirc;n thị trường. Sản phẩm c&oacute; k&iacute;ch thước 117 x 91 x 69 mm c&ugrave;ng c&acirc;n nặng chỉ 403 gram, đ&atilde; bao gồm pin. Với những số đo n&agrave;y, m&aacute;y nhỏ hơn 25% v&agrave; nhẹ hơn tới 28% so với EOS 650D.</span></p>\r\n\r\n<div><span style=\"color:rgb(44, 43, 43); font-size:12px\">Nhỏ b&eacute;, lại l&agrave; sản phẩm được Canon sản xuất cho thị trường tầm thấp song m&aacute;y ảnh&nbsp;</span><a href=\"http://zshop.vn/may-anh-canon-dslr/\"><strong>Canon 100D</strong></a><span style=\"color:rgb(44, 43, 43); font-size:12px\">&nbsp;lại được trang bị phần cứng kh&aacute; đ&aacute;ng nể. Canon đ&atilde; đưa v&agrave;o cảm biến CMOS APS-C k&iacute;ch thước 22,3 x 14,9 mm độ ph&acirc;n giải 18 megapixel, chip xử l&yacute; h&igrave;nh ảnh Digic 5, khả năng quay video chuẩn Full HD tốc độ 30 khung h&igrave;nh mỗi gi&acirc;y. Model n&agrave;y cũng sử dụng hệ thống lấy n&eacute;t lai AF Hybrid kết hợp giữa lấy n&eacute;t theo pha v&agrave; tương phản. Tốc độ chụp li&ecirc;n tiếp của m&aacute;y l&agrave; 4 khung h&igrave;nh mỗi gi&acirc;y.</span></div>\r\n\r\n<p><span style=\"font-size:12px\">M&aacute;y ảnh&nbsp;</span>Canon 100D<span style=\"font-size:12px\">&nbsp;hỗ trợ độ nhạy s&aacute;ng ISO từ 100 đến 12.800, mở rộng l&ecirc;n mức 25.600. Tốc độ m&agrave;n trập tối đa 1/4000 gi&acirc;y v&agrave; tối thiểu l&agrave; 30 gi&acirc;y. K&iacute;nh ngắm quang c&oacute; độ phủ 95% v&agrave; hệ số ph&oacute;ng đại 0,87x. Đ&egrave;n flash t&iacute;ch hợp c&oacute; khoảng đ&aacute;nh xa 9,7 m&eacute;t v&agrave; tốc độ đồng bộ 1/200 gi&acirc;y.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><br />\r\n<strong>T&Iacute;NH NĂNG NỔI BẬT</strong></p>\r\n\r\n<p><strong>Hệ thống tự động lấy n&eacute;t lai Hybrid AF</strong></p>\r\n\r\n<p>Tương tự như tr&ecirc;n Canon EOS 700D nh&agrave; sản xuất đ&atilde; trang bị cho&nbsp;<a href=\"http://zshop.vn/may-anh-canon-dslr/\"><strong>Canon 100D</strong></a>&nbsp;hệ thống lấy n&eacute;t tự động lai Hybrid AF, cho ph&eacute;p kết hợp giữa lấy n&eacute;t theo pha 9 điểm (Phase Detection) v&agrave; lấy n&eacute;t theo độ tương phản (Contrast AF), gi&uacute;p cho việc lấy n&eacute;t cực nhanh với độ ch&iacute;nh x&aacute;c cao, ngay cả trong trường hợp thiếu s&aacute;ng hoặc chủ thể ảnh đang di chuyển. Hệ thống lấy n&eacute;t lai n&agrave;y đặc biệt hữu dụng trong chế độ Live View v&agrave; khi quay video.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Sức mạnh vi xử l&yacute; DIGIC 5 c&ugrave;ng cảm biến lai Hybrid CMOS 18.1MP</strong></p>\r\n\r\n<p>Với những cải tiến mới so với đời trước, DIGIC 5 được tăng cường khả năng khử nhiễu khi chụp ở ISO cao v&agrave; c&acirc;n bằng trắng đa v&ugrave;ng cho h&igrave;nh ảnh đẹp mịn m&agrave;ng với m&agrave;u sắc tự nhi&ecirc;n, ch&acirc;n thực ngay trong khung cảnh c&oacute; nhiều nguồn chiếu s&aacute;ng phức tạp. Cảm biến lai Hybrid CMOS k&iacute;ch thước 22.3 x 14.9mm c&ugrave;ng hệ số crop 1.6x, đem đến độ sắc n&eacute;t ho&agrave;n hảo, tăng cường khả năng nhạy s&aacute;ng, tỏa nhiệt hiệu quả, đồng thời cho tốc độ truyền dữ liệu si&ecirc;u tốc, tối ưu h&oacute;a khả năng xử l&yacute; ảnh nhờ đường truyền t&iacute;n hiệu đa k&ecirc;nh v&agrave; tăng khả năng phản ứng của m&aacute;y cho ảnh đẹp bất kể t&igrave;nh huống n&agrave;o.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Dải nhạy s&aacute;ng rộng 100-12800, mở rộng đến 25600</strong></p>\r\n\r\n<p>Dải ISO rộng đặc biệt quan trọng khi cho ph&eacute;p bạn đ&oacute;ng băng để chụp được những h&igrave;nh ảnh chuyển động nhanh v&agrave; chụp ảnh trong điều kiện &aacute;nh s&aacute;ng yếu. Với tốc độ chụp li&ecirc;n tục 4fps ở độ ph&acirc;n giải cao,&nbsp;<a href=\"http://zshop.vn/may-anh-canon-dslr/\"><strong>Canon 100D</strong></a>&nbsp;cho bạn dễ d&agrave;ng nắm bắt những h&igrave;nh ảnh th&uacute; vị, độc đ&aacute;o của những đối tượng quanh m&igrave;nh.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Cảm biến đo s&aacute;ng iFCL 63 v&ugrave;ng</strong></p>\r\n\r\n<p>Cảm biến đo s&aacute;ng đa lớp 63 v&ugrave;ng iFCL được thiết kế mỗi lớp sẽ nhạy cảm với một bước s&oacute;ng kh&aacute;c nhau của &aacute;nh s&aacute;ng sau đ&oacute; sẽ d&ugrave;ng thuật to&aacute;n kết hợp lại cho ra kết quả đo s&aacute;ng đạt độ ch&iacute;nh x&aacute;c cao, bất kể t&igrave;nh huống n&agrave;o v&agrave; bất chấp m&agrave;u sắc chủ đạo l&agrave; g&igrave;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>M&agrave;n h&igrave;nh cảm ứng điện dung</strong></p>\r\n\r\n<p>Tương tự như Canon EOS M,&nbsp;Canon 100D&nbsp;sử dụng m&agrave;n h&igrave;nh LCD Clear View k&iacute;ch thước 3.0&rdquo; c&ocirc;ng nghệ cảm ứng điện dung đa điểm cực nhạy v&agrave; độ ph&acirc;n giải cao l&ecirc;n đến 1.04 triệu điểm ảnh. M&agrave;n h&igrave;nh cảm ứng đặc biệt hữu &iacute;ch khi người d&ugrave;ng c&oacute; thể truy cập v&agrave; thao t&aacute;c Menu dễ d&agrave;ng, chỉnh n&eacute;t v&agrave; nhấn chụp ngay tr&ecirc;n m&agrave;n h&igrave;nh hoặc d&ugrave;ng ng&oacute;n tay để zoom h&igrave;nh to l&ecirc;n hoặc thu nhỏ h&igrave;nh nhỏ lại.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Quay phim Full HD chuẩn &acirc;m thanh nổi</strong></p>\r\n\r\n<p>Tương tự như những d&ograve;ng m&aacute;y cao cấp kh&aacute;c,&nbsp;Canon 100D&nbsp;sở hữu khả năng quay phim Full HD tốc độ đạt 30fps chuẩn &acirc;m thanh nổi stereo. Nhờ v&agrave;o hệ thống lấy n&eacute;t tự động lai, khả năng lấy n&eacute;t tự động li&ecirc;n tục khi quay được thực hiện dễ d&agrave;ng, gi&uacute;p bạn lưu giữ trọn vẹn những khoảnh khắc đẹp v&agrave; kh&oacute; qu&ecirc;n.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Thiết kế gọn nhẹ</strong></p>\r\n\r\n<p>Sản phẩm c&oacute; k&iacute;ch thước 116.8x90.7x69.4 mm c&ugrave;ng c&acirc;n nặng chỉ 407 gram, đ&atilde; bao gồm pin v&agrave; thẻ nhớ. Với những số đo n&agrave;y, m&aacute;y nhỏ hơn 25% v&agrave; nhẹ hơn tới 28% so với EOS 650D.<br />\r\n&nbsp;</p>\r\n\r\n<p><strong>Chức năng s&aacute;ng tạo h&igrave;nh ảnh ti&ecirc;n tiến</strong></p>\r\n\r\n<p>- C&aacute;c chế độ chụp h&igrave;nh đ&atilde; được n&acirc;ng cấp v&agrave; bổ sung một số chế độ mới cho ph&aacute;p người d&ugrave;ng chụp được những bức h&igrave;nh ưng &yacute; một c&aacute;ch dễ d&agrave;ng v&agrave; nhanh ch&oacute;ng&nbsp;</p>\r\n\r\n<p>- Bổ sung chế độ chụp Cảnh đặc biệt (SCN)</p>\r\n\r\n<p>- Chế độ th&ecirc;m mới: Chụp trẻ em, chụp thực phẩm, chụp &aacute;nh s&aacute;ng đ&egrave;n nến</p>\r\n\r\n<p>- Hiệu ứng mở rộng (trong chế độ chụp tự động s&aacute;ng tạo (CA))</p>\r\n\r\n<p>- Khi sử dụng hiệu ứng h&igrave;nh ảnh, 2 bức sẽ được chụp đồng thời trong đ&oacute; c&oacute; một bức sử l&yacute; v&agrave; bức c&ograve;n lại giữ nguy&ecirc;n gốc. - Bức ảnh chụp th&ecirc;m nhằm mục đ&iacute;ch lưu trữ v&agrave; c&oacute; thể &aacute;p dụng chế độ lọc sau đ&oacute;</p>\r\n\r\n<p>- Hiệu ứng quay phim thu nhỏ</p>\r\n\r\n<p>- Người d&ugrave;ng c&oacute; thể c&agrave;i đặt &quot;tua nhanh&quot; (5x, 10x v&agrave; 20x) để tăng tốc chuyển động của vật mẫu nhỏ. Chế độ n&agrave;y kh&ocirc;ng thu &acirc;m.</p>\r\n\r\n<p>&nbsp;</p>\r\n', '1', '5', '20', '2014-12-31');
INSERT INTO `products` VALUES ('10', ' Canon EOS 1100D + 18-55mm IS II (Chính hãng) / Đỏ', '1', '7499995', '<p>Điểm nổi bật của sản phẩm</p>\r\n\r\n<p>- Bộ cảm biến 12.2MP CMOS APS-C</p>\r\n\r\n<p><span style=\"color:rgb(45, 45, 45)\">- Khe cắm thẻ nhớ SD / SDHC / SDXC</span></p>\r\n\r\n<p><span style=\"color:rgb(45, 45, 45)\">-</span><span style=\"color:rgb(45, 45, 45)\">&nbsp;</span><span style=\"color:rgb(45, 45, 45)\">Bộ xử l&yacute; ảnh&nbsp;</span><span style=\"color:rgb(45, 45, 45)\">DIGIC 4</span></p>\r\n\r\n<p><span style=\"color:rgb(45, 45, 45)\">- Chế độ phim HD 720p</span></p>\r\n\r\n<p><span style=\"color:rgb(45, 45, 45)\">- Dải ISO 100-6400</span></p>\r\n\r\n<p><span style=\"color:rgb(45, 45, 45)\">- Tương th&iacute;ch với ống k&iacute;nh Canon EF v&agrave; EF-S</span></p>\r\n', '<p style=\"text-align:center\"><span style=\"color:#0000cd\"><span style=\"font-size:14px\"><strong>Canon 1100D</strong></span></span></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"color:#0000cd\"><strong>GIỚI THIỆU</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif\">Đoạt giải thưởng Best Buy của tạp ch&iacute; PC User năm 2011, chiếc <a href=\"http://zshop.vn/may-anh-canon-dslr/\"><strong>m&aacute;y ảnh</strong></a>&nbsp;</span></span><strong>canon 1100D</strong><span style=\"font-family:arial,helvetica,sans-serif; font-size:12px\">&nbsp;(k&egrave;m ống k&iacute;nh 18-55mm IS) kh&ocirc;ng chỉ mang đến cho bạn khả năng chụp ảnh s&aacute;ng tạo m&agrave; c&ograve;n gi&uacute;p bạn bước đến gần hơn với nghệ thuật nhiếp ảnh chuy&ecirc;n nghiệp. B&ecirc;n cạnh bộ cảm biến CMOS APS-C 12.2MP tăng độ n&eacute;t cho c&aacute;c chi tiết, bộ xử l&iacute; h&igrave;nh ảnh DIGIC 4 đẩy mạnh tốc độ truyền dữ liệu, m&aacute;y ảnh&nbsp;</span><a href=\"http://zshop.vn/may-anh-canon-dslr/\"><strong>canon 1100D</strong></a><span style=\"font-family:arial,helvetica,sans-serif; font-size:12px\">&nbsp;c&ograve;n sử dụng t&iacute;nh năng Feature Guide m&ocirc; tả c&aacute;c n&uacute;t chức năng, Creative Auto hướng dẫn trực quan để c&oacute; được những tấm ảnh ưng &yacute; thật dễ d&agrave;ng v&agrave; hệ thống lấy n&eacute;t 9 điểm tăng cường sự sắc n&eacute;t của đối tượng chụp.</span></p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"Canon EOS 1100D\" src=\"http://zshop.vn/images/nikon/untitled%20folder/untitled%20folder/j4/1206772-EOS-1100D-RED-31.jpg\" style=\"height:500px; width:500px\" title=\"Canon EOS 1100D + 18-55mm IS II\" /></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"color:#0000cd\">Canon EOS 1100D</span></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p><span style=\"color:#0000cd\"><span style=\"font-size:14px\"><strong>T&Iacute;NH NĂNG NỔI BẬT</strong></span></span></p>\r\n\r\n<p><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"font-size:12px\"><strong>Tổng quan thiết kế</strong><br />\r\n<br />\r\n<a href=\"http://zshop.vn/may-anh-canon-dslr/\"><strong>Canon 1100D</strong></a>&nbsp;kh&ocirc;ng c&oacute; nhiều kh&aacute;c biệt về thiết kế với&nbsp;model tiền nhiệm 1000D, M&aacute;y c&oacute; k&iacute;ch thước 130 x 100 x 78 mm, vỏ m&aacute;y được l&agrave;m bằng chất liệu nhựa b&oacute;ng. Nhờ vậy, trọng lượng của m&aacute;y kh&aacute; nhẹ chỉ 495g khi kh&ocirc;ng c&oacute; thẻ nhớ v&agrave; pin.</span></span></p>\r\n\r\n<p>Mặc d&ugrave; kh&ocirc;ng c&oacute; lớp đệm cao su như c&aacute;c d&ograve;ng sản phẩm b&aacute;n chuy&ecirc;n kh&aacute;c của Canon, nhưng canon 1100D c&oacute; tay nắm s&acirc;u, gi&uacute;p người d&ugrave;ng cầm nắm chắc tay. Th&acirc;n m&aacute;y c&oacute; thiết kế thon, gọn với bốn m&agrave;u sắc: đỏ sẫm, m&agrave;u đồng, m&agrave;u x&aacute;m v&agrave; m&agrave;u đen. Ngo&agrave;i ra, d&acirc;y đeo đi k&egrave;m cũng c&oacute; m&agrave;u tương ứng với th&acirc;n m&aacute;y.</p>\r\n\r\n<p>M&agrave;n h&igrave;nh LCD tr&ecirc;n canon 1100D c&oacute; k&iacute;ch thước 2.7 inch, độ ph&acirc;n giải 230.000 điểm ảnh, rất thuận tiện cho việc xem lại ảnh hay ngắm chụp qua m&agrave;n h&igrave;nh Live View. Tuy nhi&ecirc;n, m&agrave;n h&igrave;nh n&agrave;y kh&ocirc;ng c&oacute; cơ chế xoay, lật đa chiều như Canon 600D.</p>\r\n\r\n<p>Vị tr&iacute; một số n&uacute;t điều khiển được bố tr&iacute; kh&aacute;c canon 1100D. Cụ thể, n&uacute;t bật đ&egrave;n flash được t&iacute;ch hợp gần b&aacute;nh xe điều khiển v&agrave; n&uacute;t chụp chứ kh&ocirc;ng nằn b&ecirc;n cạnh tr&aacute;i đ&egrave;n như c&aacute;c sản phẩm DSLR kh&aacute;c của Canon. C&aacute;c ph&iacute;m điều khiển được trải rộng v&agrave; bằng phẳng hơn, gi&uacute;p cho người d&ugrave;ng dễ d&agrave;ng t&ugrave;y chỉnh c&aacute;c chế độ chụp. C&aacute;c ph&iacute;m Menu, DISP được đưa về phần b&ecirc;n phải của m&agrave;n h&igrave;nh, gi&uacute;p điều chỉnh nhanh c&aacute;c thiết lập như ISO, c&acirc;n bằng trắng v&agrave; c&aacute;c điều chỉnh kh&aacute;c trong Menu.</p>\r\n\r\n<p>Cạnh tr&aacute;i m&aacute;y l&agrave; cổng xuất dữ liệu HDMI v&agrave; USB 2.0. canon 1100D sử dụng thẻ SD, SDHC, SDXC để lưu trữ dữ liệu v&agrave; sử dụng pin Lithium-Ion LP-E10, Vị tr&iacute; thẻ nhớ v&agrave; pin thiết kế dưới đ&aacute;y m&aacute;y, tương tự như c&aacute;c do&agrave;ng DSLR kh&aacute;c của Canon.</p>\r\n\r\n<p>Ngo&agrave;i ra, canon 1100D được trang bị k&iacute;nh ngắm pentamirror, c&oacute; độ phủ 95% diện t&iacute;ch khung ảnh với độ ph&oacute;ng đại 0,80x.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><br />\r\n<span style=\"color:#0000cd\"><img alt=\"\" src=\"http://zshop.vn/images/nikon/untitled%20folder/untitled%20folder/j4/271.jpg\" style=\"height:364px; width:500px\" title=\"Canon EOS 1100D + 18-55mm IS II\" /></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"color:#0000cd\">Canon 1100D</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>T&iacute;nh năng quan trọng</strong></p>\r\n\r\n<p><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"font-size:12px\">Canon 1100D&nbsp;trang bị cảm biến CMOS, k&iacute;ch thước APS-C (22,2 x 14,7 mm), cho độ ph&acirc;n giải 12.2 megapixel. M&aacute;y sử dụng bộ xử l&yacute; DIGIC IV, c&oacute; khả năng quay video độ ph&acirc;n giải HD 720p, khả năng khử nhiễu tốt v&agrave; dải ISO từ 100 đến 6.400.&nbsp;</span></span></p>\r\n\r\n<p><span style=\"font-size:12px\">Dải ISO rộng đặc biệt quan trọng khi cho ph&eacute;p bạn chụp ảnh ở nhiều điều kiện s&aacute;ng kh&aacute;c nhau, d&ugrave; l&agrave; khung cảnh ngo&agrave;i trời ban ng&agrave;y hay cảnh đ&ecirc;m thiếu s&aacute;ng. Canon EOS 1100D c&oacute; dải ISO rộng 100 - 3200 gi&uacute;p tăng vượt trội tốc độ đ&oacute;ng mở m&agrave;n tr&acirc;̣p, nắm bắt c&aacute;c khoảnh khắc để c&oacute; tốc độ chụp li&ecirc;n tuc l&ecirc;n đến 3fps, hạn ch&ecirc;́ t&ocirc;́i đa hi&ecirc;̣n tượng nh&ograve;e, mờ do rung lắc của th&acirc;n m&aacute;y v&agrave; chụp được cả những đối tượng di chuyển.</span><br />\r\n<br />\r\n<span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"font-size:12px\">Hệ thống đo s&aacute;ng mới iFCL color-sensitive 63-v&ugrave;ng được &aacute;p dụng cho d&ograve;ng m&aacute;y cao cấp EOS 7D giờ đ&acirc;y cũng được t&iacute;ch hợp trong Canon 1100D. Ngo&agrave;i ra, model n&agrave;y được trang bị hệ thống lấy n&eacute;t tự động (AF) tại 9 điểm (căng n&eacute;t ch&eacute;o trung t&acirc;m f/5.6) v&agrave; c&oacute; t&iacute;nh năng d&ograve; t&igrave;m nguồn s&aacute;ng để lấy ti&ecirc;u cự c&oacute; độ ch&iacute;nh x&aacute;c cao, tốc độ nhanh.<br />\r\n<br />\r\nBộ lọc s&aacute;ng tạo (Creative Filter) của&nbsp;canon 1100D&nbsp;trong chế độ Basic, gi&uacute;p người d&ugrave;ng lựa chọn những t&ocirc;ng m&agrave;u đặc trưng m&agrave; kh&ocirc;ng cần đến c&aacute;c phần mềm xử l&yacute; ảnh. B&ecirc;n cạnh đ&oacute; l&agrave; chế độ Picture Style, người d&ugrave;ng c&oacute; thể chọn gam m&agrave;u theo sở th&iacute;ch của từng phong c&aacute;ch chụp ảnh như: Vivid (rực rỡ), Warm (ấm &aacute;p), Cool (t&ocirc;ng m&agrave;u lạnh) hay ảnh đen trắng (Monochrome).<br />\r\n<br />\r\nTuy c&oacute; khả năng quay video HD 720p nhưng&nbsp;Canon 1100D&nbsp;ghi &acirc;m cho video quay chỉ được hỗ trợ &acirc;m thanh mono v&agrave; kh&ocirc;ng c&oacute; kết nối cho micro gắn ngo&agrave;i.<br />\r\n<br />\r\nNgo&agrave;i cổng xuất HDMI,&nbsp;</span></span><a href=\"http://zshop.vn/may-anh-canon-dslr/\"><strong>canon 1100D</strong></a><span style=\"font-family:arial,helvetica,sans-serif; font-size:12px\">&nbsp;c&oacute; th&ecirc;m chức năng kết nối internet th&ocirc;ng qua thẻ Eye-Fi. Nhờ đ&oacute;, người d&ugrave;ng c&oacute; thể xuất ảnh về m&aacute;y t&iacute;nh c&aacute; nh&acirc;n v&agrave; tải trực tiếp h&igrave;nh ảnh cũng như c&aacute;c đoạn quay video tới c&aacute;c trang c&aacute; nh&acirc;n như Canon iMage Gateway hay Flickr.</span></p>\r\n\r\n<p><br />\r\n<span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"font-size:12px\">Thuộc DSLR phổ th&ocirc;ng n&ecirc;n&nbsp;canon 1100D&nbsp;c&oacute; thể sử dụng dễ d&agrave;ng tất cả c&aacute;c ống k&iacute;nh EF v&agrave; EF-S cũng như c&aacute;c phụ kiện kh&aacute;c của Canon. Đi k&egrave;m&nbsp;Canon 1100D&nbsp;l&agrave; ống k&iacute;nh kit 18-55mm f/3.5-5.6 IS II, cho ph&eacute;p chụp nhiều thể loại ảnh từ g&oacute;c rộng 18mm cho phong cảnh hay tele chụp ch&acirc;n dung.&nbsp;</span></span></p>\r\n', '1', '13', '12', '2014-12-26');
INSERT INTO `products` VALUES ('11', ' Canon 60D (Body) / Hàng nhập khẩu / BH 24 tháng ', '1', '14442950', '<p>Điểm nổi bật của sản phẩm</p>\r\n\r\n<p>- Bộ cảm biến CMOS k&iacute;ch thước APS-C 18.0 megapixel</p>\r\n\r\n<p>- T&iacute;nh năng xử l&yacute; hậu kỳ tr&ecirc;n m&aacute;y</p>\r\n\r\n<p>- Loại Thiết bị cảm biến CMOS</p>\r\n\r\n<p>-&nbsp;M&agrave;n h&igrave;nh M&agrave;n h&igrave;nh m&agrave;u tinh thể lỏng TFT</p>\r\n\r\n<p>-Tốc độ chụp li&ecirc;n tiếp Tối đa xấp xỉ 5,3 ảnh / gi&acirc;y</p>\r\n', '<h2 style=\"text-align:center\"><span style=\"color:#0000cd\"><span style=\"font-size:14px\">M&aacute;y ảnh Canon 60D</span></span></h2>\r\n\r\n<p><span style=\"color:#0000cd\"><span style=\"font-size:14px\"><strong>GIỚI THIỆU&nbsp;</strong></span></span></p>\r\n\r\n<div><strong><a href=\"http://zshop.vn/may-anh-canon-dslr/\">M&aacute;y ảnh</a> Canon 60D</strong> kh&ocirc;ng c&oacute; qu&aacute; nhiều c&ocirc;ng nghệ mang t&iacute;nh đột ph&aacute; nhưng l&agrave; lựa chọn ho&agrave;n hảo ở tầm 25 đến 30 triệu đồng cho c&aacute;c t&iacute;nh năng m&agrave; m&aacute;y sở hữu</div>\r\n\r\n<div>D&ograve;ng hai số của Canon (xxD) từ trước đến nay lu&ocirc;n đạt được những th&agrave;nh c&ocirc;ng đ&aacute;ng kể nếu so với đối thủ &quot;truyền kiếp&quot; Nikon. Tuy nhi&ecirc;n, những n&acirc;ng cấp từ model Canon 50D l&ecirc;n Canon 60D &nbsp;sẽ chẳng kh&oacute; để những người d&ugrave;ng l&acirc;u năm đo&aacute;n biết nếu như kh&ocirc;ng c&oacute; xuất hiện của Canon 7D.</div>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:12px\"><img alt=\"\" src=\"http://zshop.vn/images/mayanh/canon/1000027783_IMG_0146.jpg\" title=\"Canon 60D (Body)\" /></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"color:#0000cd\">Canon 60D (Body)</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif\">Model&nbsp;Canon 60D<span style=\"background-color:rgb(255, 255, 255); color:rgb(0, 0, 0)\">&nbsp; n&agrave;y ra mắt đ&atilde; l&agrave;m rất nhiều người t&ograve; m&ograve; về định nghĩa ph&acirc;n chia mới giữa c&aacute;c d&ograve;ng của Canon. EOS 7D được coi như l&agrave; một chiếc &quot;1D mini&quot; với hệ thống lấy n&eacute;t tự động 19 điểm v&agrave; tốc độ chụp li&ecirc;n tiếp l&ecirc;n tới 8 khung h&igrave;nh/gi&acirc;y. D&ograve;ng xxD nếu tiếp tục đi theo xu hướng cũ l&uacute;c n&agrave;y trở th&agrave;nh qu&aacute; thừa th&atilde;i v&agrave; đắt đỏ nếu so với 550D v&agrave; cũng kh&ocirc;ng đủ mạnh so với 7D.</span></span></span><br />\r\n&nbsp;</p>\r\n\r\n<p><span style=\"font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif\">Ch&iacute;nh v&igrave; vậy,&nbsp;Canon&nbsp;60D&nbsp;ra mắt với sự ra đi của lớp vỏ magi&ecirc; (thay bằng vỏ nhựa), k&iacute;ch thước nhỏ hơn một ch&uacute;t v&agrave; c&aacute;c t&iacute;nh năng chủ yếu theo hướng n&acirc;ng cấp so với d&ograve;ng xxxD (Rebel) như th&ecirc;m m&agrave;n h&igrave;nh LCD hiển thị th&ocirc;ng số ph&iacute;a tr&ecirc;n, b&aacute;nh xe điều chỉnh ở mặt sau so với chỉ c&oacute; c&aacute;c ph&iacute;m điều hướng.</span></span></p>\r\n\r\n<p><span style=\"font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif\">Ngo&agrave;i ra, c&aacute;c t&iacute;nh năng mới của&nbsp;Canon 60D<span style=\"background-color:rgb(255, 255, 255)\">&nbsp;như quay video Full HD, m&agrave;n h&igrave;nh lật xoay độ ph&acirc;n giải cao, độ ph&acirc;n giải cảm biến cũng được n&acirc;ng l&ecirc;n tới 18 Megapixel đ&atilde; khiến cho&nbsp;</span>Canon 60D<span style=\"background-color:rgb(255, 255, 255)\">&nbsp;đ&atilde; gần như nằm ch&iacute;nh x&aacute;c ở giữa khoảng 550D v&agrave; 7D.</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"color:#0000cd\"><span style=\"font-size:14px\"><strong>T&Iacute;NH NĂNG NỔI BẬT</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif\"><strong><a href=\"http://zshop.vn/may-anh-canon-dslr/\">Canon 60D</a></strong>&nbsp;c&oacute; k&iacute;ch thước nhỏ hơn so với d&ograve;ng 50D một ch&uacute;t v&agrave; vỏ cũng l&agrave;m bằng nhựa, chất liệu giống như của 550D chứ kh&ocirc;ng phải magi&ecirc; như truyền thống của xxD.</span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:12px\"><img alt=\"\" src=\"http://zshop.vn/images/mayanh/canon/1000027783_IMG_0215.jpg\" title=\"Canon 60D (Body)\" /></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"color:#0000cd\">Canon 60D</span></p>\r\n\r\n<p><strong><a href=\"http://zshop.vn/may-anh-canon-dslr/\"><span style=\"font-size:12px\">Canon 60D</span></a></strong><span style=\"color:rgb(44, 43, 43); font-family:arial,helvetica,sans-serif; font-size:12px\">&nbsp;c&oacute; th&acirc;n h&igrave;nh bằng nhựa v&agrave;o nhỏ hơn so với 50D</span></p>\r\n\r\n<p><span style=\"font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif\">Một số thay đổi kh&aacute;c về thiết kế của&nbsp;Canon 60D&nbsp;như ph&iacute;m nguồn chuyển l&ecirc;n ngay dưới b&aacute;nh xe chế độ. B&aacute;nh xe chế độ ở 60D cũng mặc định ở chế độ kh&oacute;a (kh&ocirc;ng thể xoay), để chuyển chế độ chụp người d&ugrave;ng phải ấn v&agrave;o n&uacute;t ph&iacute;a tr&ecirc;n mới c&oacute; thể thao t&aacute;c.</span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:12px\"><img alt=\"\" src=\"http://zshop.vn/images/mayanh/canon/1000027783_60d-vs-50d_480x0.jpg\" title=\"Canon 60D (Body)\" /></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"color:#0000cd\">Canon 60D</span></p>\r\n\r\n<p><span style=\"font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif\">Canon&nbsp;Canon 60D&nbsp;nhỏ hơn một ch&uacute;t so với 50D</span></span></p>\r\n\r\n<p><span style=\"font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif\">B&aacute;nh xe điều chỉnh ở mặt sau cũng c&oacute; th&ecirc;m c&aacute;c ph&iacute;m điều hướng thứ cấp ở b&ecirc;n trong. C&aacute;c ph&iacute;m điều chỉnh ở dưới m&agrave;n h&igrave;nh như tr&ecirc;n 50D được chuyển qua bố tr&iacute; xung quanh b&aacute;nh xe điều khiển.&nbsp;<strong><a href=\"http://zshop.vn/may-anh-canon-dslr/\">Canon 60D</a></strong>&nbsp;cũng lưu trữ th&ocirc;ng qua thẻ nhớ SD thay v&igrave; thẻ CF truyền thống.</span></span></p>\r\n\r\n<p><span style=\"font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif\">Canon 60D&nbsp;sử dụng pin LP-E6 Lithium-Ion tương tự như tr&ecirc;n 5D Mark II v&agrave; 7D.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><span style=\"font-size:12px\">M&agrave;n h&igrave;nh hiển thị</span></strong></p>\r\n\r\n<p><span style=\"font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif\">Canon 60D&nbsp;l&agrave; chiếc camera đầu ti&ecirc;n của d&ograve;ng EOS c&oacute; m&agrave;n h&igrave;nh lật xoay rất tiện dụng. Vẫn c&oacute; k&iacute;ch thước 3 inch như tr&ecirc;n 50D nhưng m&agrave;n h&igrave;nh n&agrave;y c&oacute; độ ph&acirc;n giải l&ecirc;n tới 1 Megapixel cho g&oacute;c nh&igrave;n tốt. Khi sử dụng ngo&agrave;i trời th&igrave; m&agrave;n h&igrave;nh n&agrave;y vẫn hiển thị rất sắc n&eacute;t v&agrave; hầu như kh&ocirc;ng bị b&oacute;ng hoặc l&oacute;a. Thay đổi n&agrave;y được coi l&agrave; bước đột ph&aacute; gi&uacute;p chức năng quay video c&oacute; thể sử dụng&nbsp;tiện lợi&nbsp;hơn rất nhiều.</span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:12px\"><img alt=\"\" src=\"http://zshop.vn/images/mayanh/canon/1000027783_IMG_0133.jpg\" title=\"Canon 60D (Body)\" /></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"color:#0000cd\">Canon 60D</span></p>\r\n\r\n<p><span style=\"font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif\">M&agrave;n h&igrave;nh lật xoay tiện dụng.</span></span></p>\r\n\r\n<p><span style=\"font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif\">Tuy nhi&ecirc;n, những &yacute; kiến đầu ti&ecirc;n về m&agrave;n h&igrave;nh n&agrave;y kh&aacute; tr&aacute;i chiều như độ ph&acirc;n giải cao đ&atilde; l&agrave;m ảnh xem tr&ecirc;n m&aacute;y lu&ocirc;n &quot;long lanh&quot; hơn kh&aacute; nhiều so với chất ảnh thực tế. Th&ecirc;m nữa, m&agrave;n h&igrave;nh c&oacute; xu hướng hiển thị thi&ecirc;n nhiều về gam m&agrave;u n&oacute;ng khi thể hiện tr&ecirc;n ảnh c&aacute;c sắc xanh lu&ocirc;n c&oacute; vẻ nhạt v&agrave; kh&oacute; nhận thấy hơn tr&ecirc;n m&aacute;y t&iacute;nh.</span></span></p>\r\n\r\n<p><span style=\"font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif\">&Iacute;ch lợi của m&agrave;n h&igrave;nh n&agrave;y khi chụp ảnh tĩnh lại chưa ph&aacute;t huy được hết. Nếu chụp bằng t&iacute;nh năng liveview, mỗi lần cần lấy n&eacute;t m&aacute;y sẽ thực hiện việc lật gương (tắt liveview tạm thời), sau đ&oacute; mới tiếp tục hiển thị h&igrave;nh ảnh l&ecirc;n m&agrave;n h&igrave;nh.</span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:12px\"><img alt=\"\" src=\"http://zshop.vn/images/mayanh/canon/1000027783_IMG_0167.jpg\" title=\"Canon 60D (Body)\" /></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"color:#0000cd\">Canon 60D</span></p>\r\n\r\n<p><span style=\"font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif\">M&agrave;n h&igrave;nh phụ ph&iacute;a tr&ecirc;n lược bỏ th&ocirc;ng số c&acirc;n bằng trắng v&agrave; k&iacute;ch thước ảnh</span></span></p>\r\n\r\n<p><span style=\"font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif\">M&agrave;n h&igrave;nh ph&iacute;a tr&ecirc;n của&nbsp;<strong><a href=\"http://zshop.vn/may-anh-canon-dslr/\">Canon 60D</a></strong>&nbsp;c&oacute; k&iacute;ch thước nhỏ hơn 50D một ch&uacute;t v&agrave; được thiết kế c&aacute;ch điệu với đường v&aacute;t. Điểm đ&aacute;ng tiếc l&agrave; c&aacute;c th&ocirc;ng số hiển thị tr&ecirc;n m&agrave;n h&igrave;nh n&agrave;y đ&atilde; bị lược bỏ phần c&acirc;n bằng trắng v&agrave; k&iacute;ch thước ảnh so với trước. Bốn ph&iacute;m chức năng b&ecirc;n tr&ecirc;n cũng chỉ c&oacute; t&aacute;c dụng điều chỉnh một th&ocirc;ng số duy nhất chứ kh&ocirc;ng phải hai như trước đ&acirc;y.</span></span></p>\r\n\r\n<p><span style=\"font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif\">C&aacute;c th&ocirc;ng số hiển thị b&ecirc;n trong khung ngắm cũng c&oacute; ch&uacute;t thay đổi khi vạch chỉ thời lượng pin xuất hiện, dải b&ugrave; trừ s&aacute;ng cũng tăng l&ecirc;n l&agrave; +/- 3EV thay v&igrave; +/-2EV như trước. Khung ngắm của&nbsp;Canon 60D&nbsp;hiển thị độ bao phủ 96%.</span></span></p>\r\n\r\n<p><span style=\"font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif\">Mặc d&ugrave; giữ lại kh&aacute; nhiều c&aacute;ch bố tr&iacute; v&agrave; c&aacute;c ph&iacute;m chức năng so với 50D nhưng&nbsp;Canon 60D&nbsp;vẫn &quot;bắt buộc&quot; phải c&oacute; một số thay đổi do sự xuất hiện của m&agrave;n h&igrave;nh lật xoay chiếm diện t&iacute;ch lớn hơn trước (c&ugrave;ng k&iacute;ch thước m&agrave;n h&igrave;nh nhưng th&ecirc;m khớp lật xoay).</span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:12px\"><img alt=\"\" src=\"http://zshop.vn/images/mayanh/canon/1000027783_IMG_0119.jpg\" title=\"Canon 60D (Body)\" /></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"color:#0000cd\">Canon 60D</span></p>\r\n\r\n<p><span style=\"font-size:12px\">Bố tr&iacute; c&aacute;c ph&iacute;m ph&iacute;a sau m&aacute;y thay đổi kh&aacute; nhiều.</span></p>\r\n\r\n<p><span style=\"font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif\">Đầu ti&ecirc;n l&agrave; ph&iacute;m nguồn được bố tr&iacute; ngay dưới b&aacute;nh xe chế độ, kh&aacute; giống so với 7D v&agrave; c&aacute;c d&ograve;ng m&aacute;y xxxD, thay đổi được cho l&agrave; tiện dụng hơn trước đ&acirc;y. Đ&aacute;ng kể nhất l&agrave; sự &quot;ra đi&quot; của joystick để lựa chọn điểm lấy n&eacute;t v&agrave; di chuyển c&aacute;c menu điều khiển kh&aacute;c. Thay v&agrave;o đ&oacute; người d&ugrave;ng sẽ phải thực hiện c&aacute;c thao t&aacute;c tr&ecirc;n qua c&aacute;c ph&iacute;m điều hướng b&ecirc;n dưới, điều quen thuộc với d&ograve;ng entry-level nhưng lại g&acirc;y bất ngờ v&agrave; lạ lẫm đối với người d&ugrave;ng quen thuộc của xxD. Theo l&yacute; giải của Canon, thay đổi n&agrave;y gi&uacute;p người d&ugrave;ng chủ động hơn trong c&aacute;c t&igrave;nh huống chụp dọc.</span></span></p>\r\n\r\n<p><span style=\"font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif\">Ở mặt sau m&aacute;y, d&atilde;y ph&iacute;m bấm chức năng cũng được thay đổi so với&nbsp;<strong><a href=\"http://zshop.vn/may-anh-canon-dslr/\">Canon 60D</a></strong>&nbsp;khi d&agrave;n trải ra xung quanh phần b&aacute;nh xe v&agrave; ph&iacute;m điều hướng. Hầu hết c&aacute;c n&uacute;t đều được lược bỏ việc ki&ecirc;m nhiệm nhiều chức năng tinh chỉnh, điểm hướng tới những người d&ugrave;ng &iacute;t kinh nghiệm. Hai ph&iacute;m chức năng Picture Style v&agrave; Jump cũng được lược bỏ. Để b&ugrave; đắp điều n&agrave;y, n&uacute;t &quot;Set&quot; sẽ c&oacute; th&ecirc;m nhiều chức năng mới như c&acirc;n bằng trắng v&agrave; c&aacute;c th&ocirc;ng số kỹ thuật kh&aacute;c.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:12px\"><img alt=\"\" src=\"http://zshop.vn/images/mayanh/canon/1000027783_IMG_0163.jpg\" title=\"Canon 60D (Body)\" /></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"color:#0000cd\">Canon 60D</span></p>\r\n\r\n<p><strong><span style=\"font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif\">Menu c&agrave;i đặt kh&aacute; đầy đủ v&agrave; dễ hiểu.</span></span></strong></p>\r\n\r\n<p><span style=\"font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif\">Sự xuất hiện của n&uacute;t &quot;Q&quot; cũng được cho l&agrave; giải quyết c&aacute;c vấn đề về chỉnh th&ocirc;ng số chụp như c&acirc;n bằng trắng, Picture Style v&agrave; k&iacute;ch thước ảnh chụp. L&uacute;c n&agrave;y, m&agrave;n h&igrave;nh hiển thị ph&iacute;a sau sẽ đ&oacute;ng vai tr&ograve; quan trọng hơn trước kh&aacute; nhiều nếu so với 40D hay 50D với chức năng ch&iacute;nh chỉ l&agrave; xem ảnh v&agrave; c&aacute;c th&ocirc;ng số menu kỹ thuật kh&aacute;c.</span></span></p>\r\n\r\n<p><span style=\"font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif\">Một điểm mới kh&aacute; đ&aacute;ng ch&uacute; &yacute; nữa ở 60D l&agrave; th&ecirc;m t&ugrave;y chọn cho người d&ugrave;ng ở chế độ Scene. Ở chế độ chụp cảnh vật, người d&ugrave;ng c&oacute; thể lựa chọn c&aacute;c thiết lập Ambience (m&ocirc;i trường) để tạo ra c&aacute;c hiệu ứng cho ảnh chụp được kh&aacute;c nhau như Vivid, Soft, Warm, Intense, Cool, Brighter... ở trong c&aacute;c thời điểm kh&aacute;c nhau như Daylight, Shade, Cloudy v&agrave; Sunset.</span></span></p>\r\n\r\n<p><span style=\"font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif\">Canon 60D&nbsp;cũng l&agrave; một trong những chiếc camera đầu ti&ecirc;n được trang bị thước ngắm c&acirc;n bằng điện tử tiện dụng.</span></span></p>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<p><strong>Chất lượng h&igrave;nh ảnh, khả năng lấy n&eacute;t, đo s&aacute;ng, c&acirc;n bằng trắng</strong></p>\r\n\r\n<p>Canon 60D<span style=\"color:rgb(47, 47, 47); font-family:arial,helvetica,sans-serif; font-size:12px\">&nbsp;sử dụng cảm biến CMOS 18 Megapixel theo chuẩn APS-C, hệ số crop 1,6x. Dải ISO của m&aacute;y trải từ 100 tới 6.400, mở rộng l&ecirc;n mức H (High) tương đương ISO 12.800.</span><span style=\"color:rgb(44, 43, 43); font-family:arial,helvetica,sans-serif; font-size:12px\">Do vẫn sử dụng một vi xử l&yacute; DIGIC 4 trong khi số &quot;chấm&quot; lại cao hơn tiền nhiệm 50D n&ecirc;n tốc độ chụp li&ecirc;n tiếp tr&ecirc;n m&aacute;y giảm xuống c&ograve;n 5,3 h&igrave;nh mỗi gi&acirc;y, li&ecirc;n tục 58 ảnh JPEG hoặc 16 ảnh RAW (so với EOS 50D l&agrave; 6,3 h&igrave;nh/gi&acirc;y, 90 ảnh JPEG hoặc 16 ảnh RAW v&agrave; EOS 7D l&agrave; 8 h&igrave;nh/gi&acirc;y, 126 ảnh JPEG hoặc 15 ảnh RAW).</span></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:12px\"><img alt=\"\" src=\"http://zshop.vn/images/mayanh/canon/1000027783_laynet_480x0.jpg\" title=\"Canon 60D (Body)\" /></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"color:#0000cd\">Canon 60D</span></p>\r\n\r\n<p><strong><span style=\"font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif\">Hệ thống lấy n&eacute;t 9 điểm v&agrave; th&ocirc;ng số hiển thị trong viewfinder.</span></span></strong></p>\r\n\r\n<p><span style=\"font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"color:rgb(47, 47, 47)\">Hệ thống lấy n&eacute;t của&nbsp;</span><a href=\"http://zshop.vn/may-anh-canon-dslr/\"><strong>Canon 60D</strong></a><span style=\"color:rgb(47, 47, 47)\">&nbsp;bao gồm 9 điểm xếp th&agrave;nh h&igrave;nh thoi, tất cả c&aacute;c điểm đều l&agrave; loại cross-type.</span>Qua thử nghiệm, model n&agrave;y cho tốc độ lấy n&eacute;t rất nhanh v&agrave; ch&iacute;nh x&aacute;c trong điều kiện đủ s&aacute;ng. Mặc d&ugrave; vậy, ở chế độ liveview, tốc độ n&agrave;y bị chậm đi kh&aacute; nhiều từ khi bấm n&uacute;t đến khi m&aacute;y chụp được ảnh (thời gian trễ l&agrave; do m&aacute;y phải lật gương lại để lấy n&eacute;t khi chụp).</span></span></p>\r\n\r\n<p><span style=\"font-size:12px\"><span style=\"font-family:arial,helvetica,sans-serif\">Do sử dụng cảm biến đo s&aacute;ng tốt giống như 7D l&agrave; iFCL 63 n&ecirc;n&nbsp;Canon 60D&nbsp;c&oacute; khả năng đo s&aacute;ng kh&aacute; ch&iacute;nh x&aacute;c v&agrave; được đ&aacute;nh gi&aacute; rất tốt. Thỉnh thoảng vẫn xuất hiện c&aacute;c trường hợp đo s&aacute;ng sai do bị phụ thuộc v&agrave;o c&aacute;c điểm lấy n&eacute;t AF nhưng đ&acirc;y l&agrave; một vấn đề kh&ocirc;ng lớn đối với những người đ&atilde; c&oacute; kinh nghiệm chụp ảnh một thời gian d&agrave;i.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Sức mạnh từ bộ đ&ocirc;i CMOS 18.0MP v&agrave; DIGIC 4</strong></p>\r\n\r\n<p>Canon 60D l&agrave; sự kết hợp ho&agrave;n hảo của cảm biến CCD 18.0MP k&iacute;ch thước lớn APS-C v&agrave; vi xử l&yacute; ảnh DIGIC 4 cho độ nhạy s&aacute;ng cao hơn, tối ưu h&oacute;a tốc độ xử l&yacute; v&agrave; chống nh&ograve;e mờ, cho bạn những bức h&igrave;nh rực rỡ, sống động ngay khi chụp trong điều kiện c&oacute; nhiều nguồn chiếu s&aacute;ng phức tạp. Với những cải tiến mới so với DIGIC 3, DIGIC 4 được tăng cường c&aacute;c thuật to&aacute;n khử nhiễu, hạn chế tối đa c&aacute;c chi tiết giả tr&ecirc;n ảnh cho những bức h&igrave;nh mịn m&agrave;ng v&agrave; r&otilde; n&eacute;t đến từng chi tiết.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Lấy n&eacute;t theo pha 9 điểm kết hợp cảm biến đo s&aacute;ng đa lớp 63 v&ugrave;ng</strong></p>\r\n\r\n<p>Canon 60D được Canon ưu &aacute;i t&iacute;ch hợp hệ thống lấy n&eacute;t tự động theo pha 9 điểm cross-type thay v&igrave; 1 điểm như c&aacute;c d&ograve;ng kh&aacute;c cho khả năng lấy n&eacute;t cực nhanh, ch&iacute;nh x&aacute;c v&agrave; dễ d&agrave;ng hơn trong mọi điều kiện. Cảm biến đo s&aacute;ng đa lớp 63 v&ugrave;ng iFCL được thiết kế mỗi lớp sẽ nhạy cảm với một bước s&oacute;ng kh&aacute;c nhau của &aacute;nh s&aacute;ng sau đ&oacute; sẽ d&ugrave;ng thuật to&aacute;n kết hợp lại cho ra kết quả đo s&aacute;ng đạt độ ch&iacute;nh x&aacute;c cao, bất kể t&igrave;nh huống n&agrave;o v&agrave; bất chấp m&agrave;u sắc chủ đạo l&agrave; g&igrave;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>M&agrave;n h&igrave;nh LCD s&aacute;ng r&otilde;, xoay gập linh hoạt</strong></p>\r\n\r\n<p><span style=\"color:rgb(45, 45, 45)\">Canon 60D</span>&nbsp;sở hữu m&agrave;n h&igrave;nh LCD 3&rdquo; độ ph&acirc;n giải 1.04 triệu điểm ảnh, c&oacute; khả năng lật, xoay đa g&oacute;c kết hợp c&ugrave;ng c&ocirc;ng nghệ chống l&oacute;a cho bạn khả năng chụp ảnh ở mọi g&oacute;c độ kh&oacute;, nhất l&agrave; khi đưa m&aacute;y l&ecirc;n qu&aacute; đầu để chụp. T&iacute;nh năng PictBridge cho ph&eacute;p bạn dễ d&agrave;ng kết nối với c&aacute;c m&aacute;y in tương th&iacute;ch, in ảnh trực tiếp m&agrave; kh&ocirc;ng cần th&ocirc;ng qua bất kỳ m&aacute;y t&iacute;nh n&agrave;o.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Dải nhạy s&aacute;ng rộng 100-6400, mở rộng l&ecirc;n đến 12800</strong></p>\r\n\r\n<p>Dải ISO rộng đặc biệt quan trọng khi cho ph&eacute;p bạn đ&oacute;ng băng để chụp được những h&igrave;nh ảnh chuyển động nhanh v&agrave; chụp ảnh trong điều kiện &aacute;nh s&aacute;ng yếu. Với tốc độ chụp li&ecirc;n tục 5.3fps ở độ ph&acirc;n giải cao v&agrave; chụp tối đa l&ecirc;n đến 58 ảnh JPEG hoặc 16 ảnh RAW,&nbsp;<strong><a href=\"http://zshop.vn/canon-60d-body-only.html\" style=\"text-decoration: none; font-family: Arial, Tahoma, sans-serif;\">Canon 60D</a></strong>&nbsp;gi&uacute;p bạn dễ d&agrave;ng nắm bắt những h&igrave;nh ảnh th&uacute; vị, độc đ&aacute;o của những đối tượng quanh m&igrave;nh.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Quay phim Full HD 1080p, chuẩn &acirc;m thanh stereo</strong></p>\r\n\r\n<p>Khả năng quay phim Full HD 1080p của&nbsp;Canon 60D khiến người d&ugrave;ng ngạc nhi&ecirc;n phải ngạc nhi&ecirc;n khi đạt tốc độ 60 khung h&igrave;nh/gi&acirc;y ở chế độ HD v&agrave; 30 khung h&igrave;nh/gi&acirc;y ở chế độ Full HD. Ngo&agrave;i ra chức năng Movie Crop c&ograve;n cho ph&eacute;p người d&ugrave;ng tạo hiệu ứng ph&oacute;ng đại gấp 7 lần. Được t&iacute;ch hợp cổng HDMI, Canon EOS 60D cho ph&eacute;p bạn chia sẻ những ph&uacute;t gi&acirc;y đ&aacute;ng nhớ, những khoảnh khắc trọng đại l&ecirc;n thiết bị chuẩn HD một c&aacute;ch dễ d&agrave;ng nhất.</p>\r\n', '1', '6', '4', '2014-12-17');
INSERT INTO `products` VALUES ('12', ' Canon 6D (Body)\r\n', '1', '29799530', '<p>Điểm nổi bật của sản phẩm</p>\r\n\r\n<p>- K&iacute;ch thước cảm biến:&nbsp; Full frame (36 x 24 mm)</p>\r\n\r\n<p>- Chế độ quay Video:&nbsp; 1080p</p>\r\n\r\n<p>- Megapixel (Số điểm ảnh hiệu dụng):&nbsp; 20.2 Megapixels</p>\r\n\r\n<p>- Tốc độ chụp (Shutter Speed):&nbsp; 30 - 1/4000 sec</p>\r\n\r\n<p>- Độ nhạy s&aacute;ng (ISO) Auto: 100 - 25600 in 1/3 stops, plus 50, 51200, 102400 as option</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<div class=\"clear\">&nbsp;</div>\r\n', '<h2 style=\"text-align:center\"><span style=\"color:rgb(0, 0, 205)\"><span style=\"font-size:18px\"><span style=\"font-family:arial,helvetica,sans-serif\">M&aacute;y ảnh&nbsp;</span></span></span><span style=\"font-size:18px\"><span style=\"font-family:arial,helvetica,sans-serif\"><a href=\"http://zshop.vn/canon-6d.html\"><span style=\"color:rgb(0, 0, 205)\">Canon 6D</span></a></span></span></h2>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<div>Kh&ocirc;ng l&acirc;u sau tin đồn về một chiếc m&aacute;y ảnh DSLR với cảm biến 35mm (full-frame) gi&aacute; rẻ, Canon h&ocirc;m nay đ&atilde; ch&iacute;nh thức giới thiệu m&aacute;y ảnh Canon 6D với cảm biến k&iacute;ch cỡ tương tự như tr&ecirc;n Canon 5D Mark III nhưng gi&aacute; rẻ hơn nhiều. Kh&ocirc;ng chỉ l&agrave; một chiếc m&aacute;y ảnh full-frame với gi&aacute; hấp dẫn, 6D c&ograve;n l&agrave; m&aacute;y ảnh chuy&ecirc;n nghiệp đầu ti&ecirc;n được t&iacute;ch hợp GPS cũng như kết nối WiFi, cho ph&eacute;p người d&ugrave;ng sử dụng c&aacute;c ứng dụng tr&ecirc;n Android hay iOS để điều khiển m&aacute;y.</div>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<div>\r\n<div>Thiết kế:</div>\r\n\r\n<div>Nh&igrave;n từ ph&iacute;a trước, Canon 6D c&oacute; thiết kế kh&aacute; đơn giản, ở phần tr&ecirc;n nơi c&oacute; lăng k&iacute;nh k&iacute;nh ngắm hơi gồ ra c&ugrave;ng với logo Canon, tr&ocirc;ng như một c&aacute;i tr&aacute;n d&ocirc;. Kh&ocirc;ng hề c&oacute; flash t&iacute;ch hợp, điều kh&ocirc;ng b&igrave;nh thường đối với một m&aacute;y ảnh d&agrave;nh cho người d&ugrave;ng cao cấp, nhưng ph&ugrave; hợp với m&aacute;y ảnh full-frame Canon. Cổng hồng ngoại được đặt ngay tr&ecirc;n phần b&aacute;ng cầm tay, một đ&egrave;n hẹn giờ (kh&ocirc;ng phải đ&egrave;n hỗ trợ lấy n&eacute;t AF) nằm giữa phần b&aacute;ng cầm v&agrave; ống k&iacute;nh. Nằm hơi chếch b&ecirc;n dưới ph&iacute;a tr&aacute;i của ng&agrave;m ống k&iacute;nh l&agrave; n&uacute;t xem trước độ s&acirc;u trường ảnh, kh&aacute; nhỏ n&ecirc;n kh&oacute; nhận thấy. N&uacute;t nhả ống k&iacute;nh kh&aacute; lớn nằm ở vị tr&iacute; th&ocirc;ng thường, v&agrave; 3 lỗ microphone nằm ngay b&ecirc;n dưới logo.</div>\r\n\r\n<div>&nbsp;</div>\r\n</div>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:12px\"><img alt=\"Đánh giá máy ảnh full-frame Canon EOS 6D\" src=\"http://zshop.vn/images/product_kts/canon-6d-000.jpg\" style=\"height:464px; width:600px\" title=\"Canon 6D (Body)\" /></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"color:rgb(0, 0, 205)\"><span style=\"font-size:12px\">Đ&aacute;nh gi&aacute; nhanh m&aacute;y ảnh full-frame Canon 6D</span></span></p>\r\n\r\n<p><span style=\"font-size:12px\">Mode dial c&oacute; &iacute;t t&ugrave;y chọn hơn so với th&ocirc;ng thường, n&ecirc;n khoảng c&aacute;ch giữa c&aacute;c t&ugrave;y chọn kh&aacute; lớn, ở giữa v&ograve;ng xoay chọn chế độ chụp n&agrave;y l&agrave; n&uacute;t kh&oacute;a. N&uacute;t nguồn nằm ngay b&ecirc;n dưới Model dial. Giống như tr&ecirc;n model Canon 60D, chiếc m&aacute;y ảnh&nbsp;</span><a href=\"http://zshop.vn/canon-6d.html\" style=\"font-size: 12px;\"><strong>Canon 6D</strong></a><span style=\"color:rgb(44, 43, 43); font-size:12px\">&nbsp;cũng c&oacute; một d&atilde;y c&aacute;c n&uacute;t nằm ph&iacute;a tr&ecirc;n m&agrave;n h&igrave;nh phụ để chỉnh nhanh c&aacute;c thiết lập th&ocirc;ng số chụp. Th&ocirc;ng tin về Wi-Fi v&agrave; GPS cũng như chế độ HDR cũng hiển thị tr&ecirc;n m&agrave;n h&igrave;nh n&agrave;y.</span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:12px\"><img alt=\"Máy ảnh full-frame Canon EOS 6D có thiết kế khá đơn giản\" src=\"http://zshop.vn/images/product_kts/canon-6d-001.jpg\" style=\"height:334px; width:600px\" title=\"Canon 6D (Body)\" /></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"color:rgb(0, 0, 205)\"><span style=\"font-size:12px\">M&aacute;y ảnh full-frame Canon 6D c&oacute; thiết kế kh&aacute; đơn giản</span></span></p>\r\n\r\n<p><span style=\"font-size:12px\">Kh&ocirc;ng c&oacute; m&agrave;n h&igrave;nh xoay lật, cụm điều khiển của&nbsp;</span><a href=\"http://zshop.vn/canon-6d.html\" style=\"font-size: 12px;\"><strong>Canon 6D</strong></a><span style=\"color:rgb(44, 43, 43); font-size:12px\">&nbsp;c&oacute; nhiều điểm chung với model T4i hơn so với c&aacute;c m&aacute;y ảnh kh&aacute;c. Canon đ&atilde; loại bỏ 5 n&uacute;t b&ecirc;n tr&aacute;i m&agrave;n h&igrave;nh LCD, x&oacute;a n&uacute;t Picture Styles v&agrave; RAW/JPEG (từ EOS 7D), v&agrave; bố tr&iacute; c&aacute;c n&uacute;t c&ograve;n lại như ảnh tr&ecirc;n.</span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:12px\"><img alt=\"Canon 6D cũng bỏ đi cần gạt 8 chiều và thay bằng một đĩa điều hướng 8 chiều\" src=\"http://zshop.vn/images/product_kts/canon-6d-002.jpg\" style=\"border:0px none; height:458px; width:600px\" title=\"Canon 6D (Body)\" /></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"color:rgb(0, 0, 205)\"><span style=\"font-size:12px\">M&aacute;y ảnh Canon 6D cũng bỏ đi cần gạt 8 chiều v&agrave; thay bằng một đĩa điều hướng 8 chiều</span></span></p>\r\n\r\n<p><span style=\"font-size:12px\">Canon cũng bỏ đi cần gạt 8 chiều v&agrave; thay bằng một đĩa điều hướng 8 chiều. N&uacute;t Q sẽ cho ph&eacute;p người d&ugrave;ng truy cập tới menu nhanh để điều chỉnh c&aacute;c thiết lập. N&uacute;t&nbsp;<strong>Live View/Movie Record</strong>&nbsp;cho ph&eacute;p chuyển đổi nhanh giữa hai chế độ xem sống Live View v&agrave; quay phim.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Cảm biến:</strong></span></p>\r\n\r\n<p><span style=\"font-size:12px\"><img alt=\"Đánh giá nhanh máy ảnh full-frame Canon EOS 6D\" src=\"http://zshop.vn/images/product_kts/image_gallery%20%283%29.jpg\" style=\"border:0px; float:left; height:180px; margin-left:10px; margin-right:10px; width:250px\" title=\"Canon 6D (Body)\" />Canon đ&atilde; ph&aacute;t triển một&nbsp;<strong>cảm biến CMOS full-frame ho&agrave;n to&agrave;n mới cho&nbsp;</strong></span><a href=\"http://zshop.vn/canon-6d.html\" style=\"font-size: 12px;\"><strong>Canon 6D</strong></a><span style=\"color:rgb(44, 43, 43); font-size:12px\">. Với k&iacute;ch thước 35,8 x 23,9 mm, n&oacute; nhỏ hơn một ch&uacute;t so với c&aacute;c cảm biến được t&igrave;m thấy trong</span></p>\r\n\r\n<p><span style=\"font-size:12px\"><strong>Bộ vi xử l&yacute;:</strong></span></p>\r\n\r\n<p><span style=\"font-size:12px\">Ảnh xuất ra từ bộ cảm biến mới được xử l&yacute; bởi một bộ xử l&yacute; h&igrave;nh ảnh DIGIC 5+, tương tự như bộ xử l&yacute; của d&ograve;ng m&aacute;y đắt hơn&nbsp;<strong><a href=\"http://zshop.vn/canon-5d-mark-iii.html\">Canon 5D Mark III</a></strong>. Canon n&oacute;i rằng&nbsp;<em>Canon&nbsp;EOS 6D&nbsp;</em>sử dụng hệ chuyển đổi analog-sang-kỹ thuật số 14-bit.<img alt=\"Đánh giá nhanh máy ảnh full-frame Canon EOS 6D\" src=\"http://zshop.vn/images/product_kts/image_gallery%20%284%29.jpg\" style=\"border:0px; float:right; height:170px; margin-left:10px; margin-right:10px; width:250px\" title=\"Canon 6D (Body)\" /></span></p>\r\n\r\n<p><span style=\"font-size:12px\"><strong>Hiệu suất:</strong></span></p>\r\n\r\n<p><span style=\"font-size:12px\">Chiếc m&aacute;y ảnh&nbsp;</span><a href=\"http://zshop.vn/canon-6d.html\" style=\"font-size: 12px;\"><strong>Canon 6D</strong></a><span style=\"font-size:12px\">&nbsp;thua d&ograve;ng&nbsp;</span><a href=\"http://zshop.vn/nikon-d600.html\" style=\"font-size: 12px;\" title=\"Máy ảnh Nikon D600\">Nikon D600</a><span style=\"font-size:12px\">&nbsp;ở khả năng chụp ảnh li&ecirc;n tục. Trong khi D600 đạt tốc độ 5.5 fps th&igrave; 6D chỉ đạt 4.5 fps.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:12px\"><strong>Độ nhạy s&aacute;ng:</strong></span></p>\r\n\r\n<p><span style=\"font-size:12px\">Mặc d&ugrave; kh&ocirc;ng sử dụng bộ cảm biến giống&nbsp;</span><strong><a href=\"http://zshop.vn/canon-5d-mark-iii.html\">Canon 5D Mark III</a></strong><span style=\"color:rgb(44, 43, 43); font-size:12px\">, Canon 6D cung cấp c&ugrave;ng một dải ISO từ ISO 100 đến 25.600, c&oacute; thể mở rộng từ 50 đến 102.400. Ngược lại, Nikon D600 c&oacute; một dải ISO ti&ecirc;u chuẩn l&agrave; ISO 100 &ndash; 6400, mở rộng từ ISO 50 - 25.600.</span></p>\r\n\r\n<p><span style=\"font-size:12px\"><img alt=\"Đánh giá nhanh máy ảnh full-frame Canon EOS 6D\" src=\"http://zshop.vn/images/product_kts/image_gallery%20%285%29.jpg\" style=\"border:0px; float:right; height:167px; margin-left:10px; margin-right:10px; width:250px\" title=\"Canon 6D (Body)\" /></span></p>\r\n\r\n<p><span style=\"font-size:12px\"><strong>Tự động lấy n&eacute;t:</strong></span></p>\r\n\r\n<p><span style=\"font-size:12px\">Điểm kh&aacute;c biệt r&otilde; r&agrave;ng nhất giữa&nbsp;</span><a href=\"http://zshop.vn/canon-6d.html\" style=\"font-size: 12px;\"><strong>Canon 6D</strong></a><span style=\"color:rgb(44, 43, 43); font-size:12px\">&nbsp;v&agrave; c&aacute;c model full-frame đắt tiền hơn ch&iacute;nh l&agrave; hệ thống tự động lấy n&eacute;t. Trong khi&nbsp;</span><strong><a href=\"http://zshop.vn/canon-5d-mark-iii.html\">Canon 5D Mark III</a></strong><span style=\"color:rgb(44, 43, 43); font-size:12px\">&nbsp;c&oacute; hệ thống tự động lấy n&eacute;t 61-điểm với 41 điểm cross-type,&nbsp;</span><a href=\"http://zshop.vn/canon-6d.html\" style=\"font-size: 12px;\"><strong>Canon 6D</strong></a><span style=\"color:rgb(44, 43, 43); font-size:12px\">&nbsp;chỉ c&oacute; 11 điểm trong một mảng h&igrave;nh thoi, với điểm trung t&acirc;m l&agrave; điểm cross-type duy nhất. (Về mặt n&agrave;y, 6D giống với&nbsp;<strong>5D Mark II</strong>&nbsp;hơn, với một hệ thống lấy n&eacute;t 15-điểm v&agrave; 1 điểm cross-type, nhưng 6 điểm trong số n&agrave;y chỉ l&agrave; điểm hỗ trợ lấy n&eacute;t).</span></p>\r\n\r\n<p><span style=\"font-size:12px\"><img alt=\"\" src=\"http://zshop.vn/images/product_kts/image_gallery.gif\" style=\"border:0px; float:right; height:250px; margin-left:10px; margin-right:10px; width:250px\" title=\"Canon 6D (Body)\" /></span></p>\r\n\r\n<p><span style=\"font-size:12px\"><strong>K&iacute;nh ngắm:</strong></span></p>\r\n\r\n<p><span style=\"font-size:12px\">K&iacute;nh ngắm quang của m&aacute;y ảnh&nbsp;<strong>Canon 6D</strong>&nbsp;c&oacute; v&ugrave;ng phủ 97%, xếp sau mức 98% của&nbsp;<strong>EOS 5D Mark II</strong>.&nbsp;</span><strong><a href=\"http://zshop.vn/canon-5d-mark-iii.html\">Canon 5D Mark III</a></strong><span style=\"color:rgb(44, 43, 43); font-size:12px\">&nbsp;v&agrave;&nbsp;</span><strong>Nikon D600</strong><span style=\"color:rgb(44, 43, 43); font-size:12px\">&nbsp;đều c&oacute; v&ugrave;ng phủ 100%.</span></p>\r\n\r\n<p><span style=\"font-size:12px\"><strong>M&agrave;n h&igrave;nh hiển thị:</strong></span></p>\r\n\r\n<p><span style=\"font-size:12px\">C&oacute; hai m&agrave;n h&igrave;nh tr&ecirc;n chiếc m&aacute;y ảnh&nbsp;</span><a href=\"http://zshop.vn/canon-6d.html\" style=\"font-size: 12px;\"><strong>Canon 6D</strong></a><span style=\"font-size:12px\">, cũng thường thấy tr&ecirc;n c&aacute;c m&aacute;y ảnh c&oacute; c&ugrave;ng tầm gi&aacute;. M&agrave;n h&igrave;nh nhỏ nằm ph&iacute;a mặt tr&ecirc;n của m&aacute;y l&agrave; một m&agrave;n h&igrave;nh LCD đơn sắc, hiển thị trạng th&aacute;i của m&aacute;y, thực hiện c&aacute;c c&agrave;i đặt tr&ecirc;n m&aacute;y, gi&uacute;p bạn tiết kiệm pin v&agrave; thời gian hơn so với m&agrave;n h&igrave;nh ở mặt sau.</span></p>\r\n\r\n<p><span style=\"font-size:12px\">Trong khi đ&oacute;, m&agrave;n h&igrave;nh mặt sau l&agrave; một m&agrave;n h&igrave;nh LCD 3.0-inch Clear View với độ ph&acirc;n giải 1.040.000 dot. M&agrave;n h&igrave;nh n&agrave;y c&oacute; độ tương phản k&eacute;m hơn m&agrave;n h&igrave;nh Clear View II tr&ecirc;n&nbsp;</span><strong><a href=\"http://zshop.vn/canon-5d-mark-iii.html\">Canon 5D Mark III</a></strong><span style=\"font-size:12px\">. N&oacute; c&oacute; g&oacute;c nh&igrave;n rộng 170 độ, l&agrave; m&agrave;n h&igrave;nh cố định, kh&ocirc;ng c&oacute; cảm ứng.</span></p>\r\n\r\n<p><span style=\"font-size:12px\">So với&nbsp;<strong>Nikon D600</strong>, số điểm ảnh của m&agrave;n h&igrave;nh tr&ecirc;n&nbsp;</span><a href=\"http://zshop.vn/canon-6d.html\" style=\"font-size: 12px;\"><strong>Canon 6D</strong></a><span style=\"font-size:12px\">&nbsp;cao hơn một ch&uacute;t. Khi mở ở chế độ xem sống Live View, m&agrave;n h&igrave;nh c&oacute; v&ugrave;ng phủ 100%.</span></p>\r\n\r\n<p><br />\r\n<span style=\"font-size:14px\"><strong>Flash:</strong></span></p>\r\n\r\n<p><span style=\"font-size:12px\">Kh&ocirc;ng c&oacute; đ&egrave;n flash t&iacute;ch hợp trong&nbsp;</span><a href=\"http://zshop.vn/canon-6d.html\" style=\"font-size: 12px;\"><strong>Canon 6D</strong></a><span style=\"color:rgb(44, 43, 43); font-size:12px\">&nbsp;(trong khi&nbsp;</span><strong><a href=\"http://zshop.vn/nikon-d600.html\" title=\"máy ảnh Nikon D600\">Nikon D600</a></strong><span style=\"color:rgb(44, 43, 43); font-size:12px\">&nbsp;c&oacute; flash), v&igrave; vậy c&oacute; thể bạn phải mất th&ecirc;m chi ph&iacute; sắm đ&egrave;n flash rời.</span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:12px\"><img alt=\"Với Canon 6D bạn phải mất thêm chi phí sắm đèn flash rời cho máy\" src=\"http://zshop.vn/images/product_kts/canon-6d-006.jpg\" style=\"border:0px none; height:722px; width:450px\" title=\"Canon 6D (Body)\" /></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"color:rgb(0, 0, 205)\"><span style=\"font-size:12px\">Với m&aacute;y ảnh Canon 6D bạn phải mất th&ecirc;m chi ph&iacute; sắm đ&egrave;n flash rời cho m&aacute;y</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Chế độ phơi s&aacute;ng:</strong></span></p>\r\n\r\n<p><span style=\"font-size:12px\">C&aacute;c t&ugrave;y chọn chế độ phơi s&aacute;ng của&nbsp;</span><a href=\"http://zshop.vn/canon-6d.html\" style=\"font-size: 12px;\"><strong>Canon 6D</strong></a><strong>&nbsp;gần như giống hệt với đối thủ cạnh tranh Nikon</strong><span style=\"color:rgb(44, 43, 43); font-size:12px\">. B&ecirc;n cạnh c&aacute;c chế độ P, Av, Tv, M th&ocirc;ng thường, c&ograve;n c&oacute; chế độ Scene Auto +.&nbsp; Ngo&agrave;i ra c&ograve;n c&oacute; hai chế độ t&ugrave;y chỉnh Custom, Bulb v&agrave; Creative Auto nhằm hỗ trợ th&ecirc;m cho những tay m&aacute;y nghiệp dư.</span></p>\r\n\r\n<p><span style=\"font-size:12px\"><img alt=\"Đánh giá nhanh máy ảnh full-frame Canon EOS 6D\" src=\"http://zshop.vn/images/product_kts/image_gallery%20%287%29.jpg\" style=\"border:0px; float:right; height:168px; margin-left:10px; margin-right:10px; width:250px\" title=\"Canon 6D (Body)\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Đo s&aacute;ng:</strong></span></p>\r\n\r\n<p><span style=\"font-size:12px\">M&aacute;y ảnh&nbsp;</span><a href=\"http://zshop.vn/canon-6d.html\" style=\"font-size: 12px;\"><strong>Canon 6D</strong></a><span style=\"font-size:12px\">&nbsp;thực hiện đo s&aacute;ng với 63-zone, cảm biến đo s&aacute;ng iFCL dual-layer (2 lớp). Cảm biến n&agrave;y được sử dụng lần đầu tr&ecirc;n&nbsp;</span><strong><a href=\"http://zshop.vn/canon-7d-body-only.html\" style=\"font-size: 12px;\" title=\"Máy ảnh Canon 7D\">Canon 7D</a></strong><span style=\"font-size:12px\">&nbsp;v&agrave; cũng được d&ugrave;ng tr&ecirc;n&nbsp;<strong>Canon 5D Mark III</strong>.</span></p>\r\n\r\n<p><span style=\"font-size:12px\">Trong hai lớp đo s&aacute;ng, lớp tr&ecirc;n nhạy cảm với m&agrave;u đỏ v&agrave; m&agrave;u xanh l&aacute; c&acirc;y, trong khi lớp ph&iacute;a dưới ph&aacute;t hiện m&agrave;u xanh dương v&agrave; m&agrave;u xanh l&aacute; c&acirc;y. Hệ thống đo s&aacute;ng n&agrave;y cho ph&eacute;p ph&aacute;t hiện tốt hơn chủ thể, th&ocirc;ng tin được phản hồi trở lại hệ thống tự động lấy n&eacute;t để hỗ trợ theo d&otilde;i chủ thể. Nikon, ngược lại, hệ thống đo s&aacute;ng RGB chi tiết hơn với 2.016 pixel, được gọi l&agrave; 3D color matrix metering II.</span><br />\r\n&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>M&agrave;n trập:</strong></span></p>\r\n\r\n<p><span style=\"font-size:12px\">Một điểm kh&aacute;c biệt nữa giữa&nbsp;</span><a href=\"http://zshop.vn/canon-6d.html\" style=\"font-size: 12px;\"><strong>Canon 6D</strong></a><span style=\"color:rgb(44, 43, 43); font-size:12px\">&nbsp;v&agrave;&nbsp;</span><strong>Canon 5D Mark III</strong><span style=\"color:rgb(44, 43, 43); font-size:12px\">&nbsp;l&agrave; cơ chế m&agrave;n trập. Trong khi&nbsp;<strong>Canon 5D Mark III</strong>&nbsp;được đ&aacute;nh gi&aacute; l&agrave; c&oacute; thể hoạt động tốt trong khoảng 150.000 chu kỳ, giống như D600 của Nikon, m&agrave;n trập 6D của Canon chỉ d&ugrave;ng nhiều nhất 100.000 chu kỳ, ngắn hơn nhiều. Điều đ&oacute; kh&ocirc;ng c&oacute; nghĩa l&agrave; n&oacute; chắc chắn kh&ocirc;ng thể chụp qu&aacute; 100.000 bức ảnh, nhưng n&oacute;i chung l&agrave; tuổi thọ của n&oacute; kh&ocirc;ng được bền như m&agrave;n trập trong m&aacute;y ảnh kh&aacute;c.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Chế độ Creative:</strong></span></p>\r\n\r\n<p><span style=\"font-size:12px\">C&oacute; một v&agrave;i t&ugrave;y chọn chụp s&aacute;ng tạo tr&ecirc;n&nbsp;<strong>Canon 6D</strong>&nbsp;kh&aacute; giống nhưng kh&ocirc;ng phải được b&ecirc; nguy&ecirc;n xi từ 5D Mark III.<img alt=\"Đánh giá nhanh máy ảnh full-frame Canon EOS 6D\" src=\"http://zshop.vn/images/product_kts/image_gallery%20%288%29.jpg\" style=\"border:0px; float:right; height:169px; margin-left:10px; margin-right:10px; width:300px\" title=\"Canon 6D (Body)\" /></span></p>\r\n\r\n<p><span style=\"font-size:12px\">Đầu ti&ecirc;n l&agrave; chế độ chụp High Dynamic Range, cho ph&eacute;p chụp 3 ảnh ri&ecirc;ng biệt với c&aacute;c mức phơi s&aacute;ng kh&aacute;c nhau, sau đ&oacute; kết hợp ch&uacute;ng lại th&agrave;nh một ảnh duy nhất c&oacute; dải tần nhạy s&aacute;ng cao hơn. C&aacute;c chế độ HDR tương tự như EOS 5D Mark III, nhưng kh&ocirc;ng cho ph&eacute;p lưu c&aacute;c ảnh nguồn. Thay v&agrave;o đ&oacute;, việc kết hợp c&aacute;c ảnh được thực hiện chỉ trong m&aacute;y ảnh, v&agrave; nếu bạn kh&ocirc;ng h&agrave;i l&ograve;ng với kết quả, bạn sẽ cần phải chụp lại. (Với 5D Mark III, nếu bạn đổi &yacute; hoặc chưa h&agrave;i l&ograve;ng, bạn c&oacute; thể chỉnh HDR bằng tay tr&ecirc;n m&aacute;y t&iacute;nh của bạn, bạn c&oacute; t&ugrave;y chọn để lưu h&igrave;nh ảnh nguồn). Số hiệu ứng HDR tr&ecirc;n Canon 6D cũng &iacute;t hơn tr&ecirc;n c&aacute;c anh chị em đắt tiền hơn của n&oacute;.</span></p>\r\n\r\n<p><span style=\"font-size:12px\">Một t&ugrave;y chọn s&aacute;ng tạo th&uacute; vị kh&aacute;c, đ&oacute; l&agrave; chế độ phơi s&aacute;ng Multiple Exposure, chồng nhiều bức ảnh l&ecirc;n nhau. Đ&oacute; l&agrave; một kỹ thuật hữu &iacute;ch để tạo ra một bức ảnh phơi s&aacute;ng l&acirc;u hơn, giảm nhiễu hoặc đạt một số hiệu ứng. Bạn c&oacute; thể xếp chồng 9 h&igrave;nh ảnh, v&agrave; bạn c&oacute; thể sử dụng một ảnh RAW để l&agrave;m ảnh đầu ti&ecirc;n cho chuỗi ảnh xếp chồng nhau n&agrave;y. C&oacute; 4 phương ph&aacute;p gh&eacute;p chồng ảnh kh&aacute;c nhau: Additive, Average, Bright, Dark để cho c&aacute;c sắc th&aacute;i ảnh kh&aacute;c nhau. Thậm ch&iacute; bạn c&oacute; thể xem trước kết quả tr&ecirc;n m&agrave;n h&igrave;nh LCD, v&agrave; c&oacute; thể chọn undo (hủy) thao t&aacute;c gần nhất nếu bạn kh&ocirc;ng muốn một hiệu ứng n&agrave;o đ&oacute;. Mark III c&oacute; nhiều chế độ s&aacute;ng t&aacute;c kiểu n&agrave;y hơn, nhưng d&ugrave; sao đ&acirc;y cũng l&agrave; điều tuyệt vời cho người d&ugrave;ng.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Giảm bụi:</strong></span></p>\r\n\r\n<p><a href=\"http://zshop.vn/canon-6d.html\" style=\"font-size: 12px;\"><strong>Canon 6D</strong></a><span style=\"font-size:12px\">&nbsp;</span><span style=\"font-size:12px\">được t&iacute;ch hợp hệ thống l&agrave;m sạch&nbsp;<strong>EOS Integrated Cleaning System</strong>, sử dụng một th&agrave;nh phần &aacute;p điện để rũ c&aacute;c hạt bụi ra khỏi bộ lọc th&ocirc;ng thấp ở ph&iacute;a trước của bộ cảm biến.</span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:12px\"><img alt=\"Canon 6D được tích hợp hệ thống làm sạch EOS Integrated Cleaning System\" src=\"http://zshop.vn/images/product_kts/canon-6d-009.jpg\" style=\"border:0px none; height:326px; width:580px\" title=\"Canon 6D (Body)\" /></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"color:rgb(0, 0, 205)\"><span style=\"font-size:12px\">M&aacute;y ảnh Canon&nbsp;6D được t&iacute;ch hợp hệ thống l&agrave;m sạch EOS Integrated Cleaning System</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Quay phim:</strong></span></p>\r\n\r\n<p><span style=\"font-size:12px\"><a href=\"http://zshop.vn/may-anh-canon-dslr/\" title=\"Máy ảnh Canon\">Canon</a>&nbsp;đ&atilde; đưa v&agrave;o 6D kh&aacute; nhiều t&iacute;nh năng quay video nổi bật đ&atilde; c&oacute; tr&ecirc;n c&aacute;c model 5D, bao gồm quay&nbsp;<strong>video Full HD</strong>&nbsp;(1080p, 1.920 x 1.080 pixel) ở 30, 25, hoặc 24 fps; 720p (1.280 x 720 pixel) ở 60 hoặc 50 fps, VGA (640 x 480 pixel) ở 30 hoặc 25 fps. Độ d&agrave;i mỗi clip được giới hạn l&agrave; 29 ph&uacute;t, 59 gi&acirc;y v&agrave; tự động chia đoạn sau mỗi 4GB dung lượng phim được lưu. Video định dạng ALL-I intraframe đ&ograve;i hỏi phải d&ugrave;ng thẻ nhớ chuẩn UHS-I, c&ograve;n định dạng&nbsp;<strong>IPB interframe-compressed</strong>&nbsp;th&igrave;&nbsp; c&oacute; thể lưu tr&ecirc;n thẻ SD th&ocirc;ng thường.</span></p>\r\n\r\n<p><span style=\"font-size:12px\">Người d&ugrave;ng c&oacute; thể t&ugrave;y chỉnh độ phơi s&aacute;ng v&agrave; &acirc;m thanh theo &yacute; muốn. M&aacute;y c&oacute; cả micro t&iacute;ch hợp v&agrave; jack cắm micro ngo&agrave;i, nhưng sẽ kh&ocirc;ng c&oacute; jack cắm tai nghe để theo d&otilde;i &acirc;m thanh trong l&uacute;c quay như tr&ecirc;n&nbsp;</span><strong><a href=\"http://zshop.vn/canon-5d-mark-iii.html\">Canon 5D Mark III</a></strong><span style=\"font-size:12px\">.</span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:12px\"><img alt=\"Canon 6D khá nhiều tính năng quay video nổi bật đã có trên các model 5D\" src=\"http://zshop.vn/images/product_kts/canon-6d-010.jpg\" style=\"border:0px none; height:326px; width:580px\" title=\"Canon 6D (Body)\" /></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"color:rgb(0, 0, 205)\"><span style=\"font-size:12px\">Canon&nbsp;6D kh&aacute; nhiều t&iacute;nh năng quay video nổi bật đ&atilde; c&oacute; tr&ecirc;n c&aacute;c model 5D</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Kết nối mạng kh&ocirc;ng d&acirc;y:</strong></span></p>\r\n\r\n<p><span style=\"font-size:12px\">Một trong những t&iacute;nh năng m&agrave;&nbsp;<strong>Canon&nbsp;6D</strong>&nbsp;vượt l&ecirc;n so với c&aacute;c &quot;anh em&quot; đắt tiền hơn, đ&oacute; l&agrave; n&oacute; được t&iacute;ch hợp kết nối Wi-Fi. Tuy nhi&ecirc;n, khả năng bắt s&oacute;ng Wi-Fi IEEE 802.11 b / g / n của 6D kh&ocirc;ng mạnh, chỉ trong phạm vi 30m. Trong khi đ&oacute;, nếu chọn mua th&ecirc;m thiết bị WFT-E7A Wireless File Transmitter để d&ugrave;ng với&nbsp;<strong><a href=\"http://zshop.vn/canon-5d-mark-iii.html\" title=\"Máy ảnh Canon 5D Mark III\">Canon 5D Mark III</a></strong>&nbsp;th&igrave; bạn c&oacute; thể kết nối trong phạm vi 150m. Nếu bạn chụp ảnh với mục đ&iacute;ch chuyển h&igrave;nh ảnh l&ecirc;n internet th&ocirc;ng qua một điện thoại th&ocirc;ng minh trong t&uacute;i của bạn, th&igrave;&nbsp;<strong>Wi-Fi t&iacute;ch hợp của&nbsp;</strong></span><a href=\"http://zshop.vn/canon-6d.html\" style=\"font-size: 12px;\"><strong>Canon 6D</strong></a><strong>&nbsp;sẽ tiện lợi hơn</strong><span style=\"font-size:12px\">.</span></p>\r\n\r\n<p><span style=\"font-size:12px\">M&aacute;y ảnh&nbsp;</span><a href=\"http://zshop.vn/canon-6d.html\" style=\"font-size: 12px;\"><strong>Canon 6D</strong></a><span style=\"font-size:12px\">&nbsp;kh&ocirc;ng chỉ kết nối với điện thoại th&ocirc;ng minh m&agrave; c&ograve;n c&oacute; thể kết nối với một số m&aacute;y ảnh PowerShot-series, m&aacute;y t&iacute;nh bảng cũng như điện thoại th&ocirc;ng minh Android hay iOS. Bạn cũng c&oacute; thể chia sẻ h&igrave;nh ảnh tr&ecirc;n c&aacute;c trang web mạng x&atilde; hội. Ảnh sẽ được chuyển tới địa chỉ bạn muốn th&ocirc;ng qua dịch vụ đ&aacute;m m&acirc;y IGAGE GATEWAY của Canon, c&oacute; y&ecirc;u cầu đăng k&yacute;.</span></p>\r\n\r\n<p><span style=\"font-size:12px\">C&ograve;n nữa, bạn c&oacute; thể điều khiển từ xa chiếc&nbsp;</span><a href=\"http://zshop.vn/canon-6d.html\" style=\"font-size: 12px;\"><strong>Canon 6D</strong></a><span style=\"font-size:12px\">&nbsp;của bạn từ c&aacute;c thiết bị gắn k&egrave;m, bằng c&aacute;ch sử dụng ứng dụng miễn ph&iacute; Canon EOS Remote. (Bạn sẽ cần phải c&oacute; một thiết bị chạy iOS 5 hay Android 2.3 + / 4.0 + cho việc n&agrave;y.)</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Định vị địa l&yacute;:</strong></span></p>\r\n\r\n<p><span style=\"font-size:12px\">Canon&nbsp;6D cũng l&agrave;&nbsp;<a href=\"http://zshop.vn/may-anh-dslr/\" title=\"Các dòng máy ảnh DSLR\"><u>m&aacute;y ảnh DSLR</u></a>&nbsp;đầu ti&ecirc;n của Canon t&iacute;ch hợp GPS, cho ph&eacute;p m&aacute;y ảnh x&aacute;c định vị tr&iacute; của n&oacute;, sau đ&oacute; ghi lại th&ocirc;ng số vĩ độ, kinh độ, độ cao, v&agrave; đ&oacute;ng dấu thời gian UTC trong dữ liệu EXIF của mỗi h&igrave;nh ảnh. 5D Mark III đ&ograve;i hỏi một thiết bị ri&ecirc;ng biệt để đạt được điều n&agrave;y. Bạn c&oacute; thể xem c&aacute;c địa điểm chụp ảnh của bạn tr&ecirc;n bản đồ bằng c&aacute;ch sử dụng phần mềm Map Utility k&egrave;m theo.</span></p>\r\n\r\n<p><span style=\"font-size:12px\">C&aacute;c lựa chọn kết nối kh&aacute;c bao gồm: một cổng kết hợp USB 2.0 v&agrave; đầu ra video ti&ecirc;u chuẩn, đầu ra video độ n&eacute;t cao HDMI, một jack cắm micro ngo&agrave;i chuẩn 3.5 mm, v&agrave; cổng điều khiển từ xa.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Thời lượng pin:</strong></span></p>\r\n\r\n<p><span style=\"font-size:12px\">M&aacute;y sử dụng c&ugrave;ng một chuẩn pin lithium-ion LP-E6 như tr&ecirc;n c&aacute;c model&nbsp;</span><a href=\"http://zshop.vn/canon-6d.html\" style=\"font-size: 12px;\"><strong>Canon 6D</strong></a><span style=\"font-size:12px\">,&nbsp;<strong><a href=\"http://zshop.vn/canon-7d-body-only.html\">Canon 7D</a>,&nbsp;<a href=\"http://zshop.vn/canon-eos-5d-mark-ii-new.html\">Canon 5D Mark II</a>,&nbsp;</strong></span><strong><a href=\"http://zshop.vn/canon-5d-mark-iii.html\">Canon 5D Mark III</a></strong><span style=\"font-size:12px\">, n&ecirc;n bạn c&oacute; thể d&ugrave;ng chung bộ pin giữa c&aacute;c d&ograve;ng m&aacute;y n&agrave;y. Bộ pin n&agrave;y c&oacute; thời lượng sử dụng theo ti&ecirc;u chuẩn thử nghiệm CIPA l&agrave; khoảng 1.090 ảnh chụp tại 23&deg;C v&agrave; 980 bức ảnh ở 0&deg;C. Tuổi thọ pin khi sử dụng GPS v&agrave; WiFi chưa được n&ecirc;u r&otilde;.</span></p>\r\n\r\n<p><a href=\"http://zshop.vn/canon-6d.html\" style=\"font-size: 12px;\"><strong>Canon 6D</strong></a><span style=\"font-size:12px\">&nbsp;tương th&iacute;ch với thẻ Secure Digital bao gồm cả SDHC v&agrave; SDXC dung lượng cao c&aacute;c loại. Kh&ocirc;ng giống như EOS 5D II v&agrave; III, kh&ocirc;ng c&ograve;n bất kỳ sự hỗ trợ n&agrave;o cho thẻ CompactFlash.</span></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><strong><span style=\"color:rgb(0, 0, 205)\"><span style=\"font-size:12px\">Canon 6D sử dụng c&ugrave;ng một chuẩn pin Lithium-Ion LP-E6</span></span></strong><br />\r\n<span style=\"font-size:12px\"><img alt=\"Canon 6D sử dụng cùng một chuẩn pin lithium-ion LP-E6 \" src=\"http://zshop.vn/images/product_kts/canon-6d-011.jpg\" style=\"border:0px none; height:326px; width:580px\" title=\"Canon 6D (Body)\" /></span></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<div>Kết luận:</div>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<div>Với sự ra đời của Canon 6D việc sở hữu một m&aacute;y ảnh full-frame đ&atilde; trở n&ecirc;n dễ d&agrave;ng hơn. Đổi lại, bạn sẽ thiếu đi một số t&iacute;nh năng cao cấp đ&atilde; thấy tr&ecirc;n d&ograve;ng 5D series. So với Canon 5D Mark III, Canon 6D c&oacute; thể c&ograve;n thua k&eacute;m nhiều.</div>\r\n\r\n<div>L&agrave; m&aacute;y ảnh DSLR đầu ti&ecirc;n của Canon t&iacute;ch hợp Wi-Fi v&agrave; GPS, chiếc Canon 6D sẽ l&agrave; một lựa chọn th&uacute; vị với c&aacute;c nhiếp ảnh gia cỡ trung. Độ ph&acirc;n giải 20.2MP của m&aacute;y c&oacute; thể kh&ocirc;ng ấn tượng so với c&aacute;c m&aacute;y DSLR định dạng APS-C, nhưng chắc chắn chất lượng h&igrave;nh ảnh sẽ tốt hơn đ&aacute;ng kể nhờ cảm biến full-frame.</div>\r\n', '1', '5', '5', '2014-12-13');
INSERT INTO `products` VALUES ('13', ' Canon EOS 1200D + 18-55mm IS II (Chính hãng) ', '1', '11816000', '<p>Điểm nổi bật của sản phẩm</p>\r\n\r\n<p>- Cảm biến APS-C CMOS 18MP&nbsp;</p>\r\n\r\n<p>- Video Full HD</p>\r\n\r\n<p>- Chế độ chụp tự động</p>\r\n\r\n<p>- Chụp li&ecirc;n tục 3,0 fps</p>\r\n', '<h2 style=\"text-align:center\"><strong>Canon 1200D</strong></h2>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"color:rgb(0, 0, 205)\"><span style=\"font-size:14px\"><strong>GIỚI THIỆU</strong></span></span></p>\r\n\r\n<p>Canon vừa giới thiệu chiếc <strong><a href=\"http://zshop.vn/may-anh-canon-dslr/\">m&aacute;y ảnh</a> EOS 1200D</strong> (hay Rebel EOS 1200D tại Mỹ) với Lens EF- S 18-55mm IS II l&agrave; một chiếc m&aacute;y ảnh DSLR 18MP APS-C với một bộ xử l&yacute; h&igrave;nh ảnh DIGIC 4 . N&oacute; được kết hợp với ống k&iacute;nh EF- S 18-55mm f/3.5-5.6 IS II tạo cho bạn một loạt c&aacute;c g&oacute;c rộng đến tele ti&ecirc;u cự ngắn .</p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"Canon EOS 1200D + 18-55mm IS II\" src=\"http://zshop.vn/images/detailed/33/Canon-EOS-1200-D.jpg\" style=\"height:444px; width:400px\" title=\"Canon EOS 1200D + 18-55mm IS II\" /></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"color:#0000cd\">Canon EOS 1200D + 18-55mm IS II</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Sự kết hợp của bộ cảm biến CMOS của EOS 1200D v&agrave; DIGIC 4 xử l&yacute; h&igrave;nh ảnh cung cấp r&otilde; r&agrave;ng cao , một dải &acirc;m rộng , v&agrave; t&aacute;i tạo m&agrave;u sắc tự nhi&ecirc;n. Với một loạt c&aacute;c ti&ecirc;u chuẩn ISO của 100-6400 ( mở rộng tới 12.800 ) , bạn c&oacute; thể chụp trong điều kiện thiếu &aacute;nh s&aacute;ng , l&agrave;m giảm nhu cầu của việc phải mua th&ecirc;m một ch&acirc;n m&aacute;y hay đ&egrave;n flash. Hệ thống tự động lấy n&eacute;t ch&iacute;n điểm bao gồm một trung t&acirc;m cross-type point AF để cung cấp sự tập trung ch&iacute;nh x&aacute;c trong phong quan v&agrave; ch&acirc;n dung .</p>\r\n\r\n<p>Bạn c&oacute; thể chụp video Full HD 1080p bằng c&aacute;ch nhấn n&uacute;t Live ghi &acirc;m Xem / Phim d&agrave;nh ri&ecirc;ng của EOS 1200D , 30 , 25 , hoặc 24 fps . Ngo&agrave;i ra, m&aacute;y ảnh n&agrave;y cũng c&oacute; thể ghi video HD 720p ở 60 v&agrave; 50 fps , v&agrave; 480p ở 30 v&agrave; 35 fps .</p>\r\n\r\n<p>3 &quot; m&agrave;n h&igrave;nh LCD ph&iacute;a sau c&oacute; 460.000 điểm ảnh , v&agrave; một g&oacute;c nh&igrave;n 170 &deg; , l&agrave;m cho n&oacute; &nbsp;tăng sự điều hướng menu 1 c&aacute;ch nhanh ch&oacute;ng khi xem hoặc chia sẻ h&igrave;nh ảnh v&agrave; video của bạn .</p>\r\n\r\n<p>EOS 1200D tương th&iacute;ch với đầy đủ c&aacute;c d&ograve;ng Canon EF v&agrave; EF- S ống k&iacute;nh, v&agrave; được gh&eacute;p nối ở đ&acirc;y với EF- S 18-55mm f/3.5-5.6 IS ống k&iacute;nh II. Ống k&iacute;nh n&agrave;y cung cấp g&oacute;c rộng đến độ d&agrave;i ti&ecirc;u cự ti&ecirc;u chuẩn tương đương 35mm 29 - 88mm . N&oacute; l&agrave; một ống k&iacute;nh đa năng c&oacute; thể tập trung gần như 9,8 &quot; .</p>\r\n\r\n<p>Ống k&iacute;nh c&oacute; t&iacute;nh năng ổn định về h&igrave;nh ảnh quang học, cung cấp l&ecirc;n đến bốn điểm dừng cho h&igrave;nh ảnh mờ do rung m&aacute;y v&agrave; tốc độ m&agrave;n h&igrave;nh chậm. Điều n&agrave;y gi&uacute;p để c&oacute; được h&igrave;nh ảnh r&otilde; n&eacute;t , ngay cả trong điều kiện &aacute;nh s&aacute;ng thấp.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"color:rgb(0, 0, 205)\"><strong>T&Iacute;NH NĂNG NỔI BẬT</strong></span></span></p>\r\n\r\n<p><strong>M&aacute;y ảnh 18.0 Megapixel CMOS v&agrave; DIGIC 4 xử l&yacute; h&igrave;nh ảnh</strong></p>\r\n\r\n<p>Độ ph&acirc;n giải cao , h&igrave;nh ảnh với tiếng ồn thấp , tốc độ v&agrave; hiệu suất nhanh ch&oacute;ng , trong đ&oacute; c&oacute; 3 fps chụp li&ecirc;n tục , được cung cấp bởi sự kết hợp của Canon DIGIC 4 xử l&yacute; h&igrave;nh ảnh v&agrave; cảm biến 18MP CMOS .</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Video Full HD</strong></p>\r\n\r\n<p>Lựa chọn giữa 1080p , 720p , 480p v&agrave; video HD , với c&aacute;c t&ugrave;y chọn kh&aacute;c nhau fps . EOS 1200D cũng c&oacute; một t&iacute;nh năng Video Snapshot để chụp đoạn clip ngắn của 2, 4, hoặc 8 gi&acirc;y , v&agrave; kết hợp ch&uacute;ng th&agrave;nh một đoạn video nổi bật clip.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Chụp Cảnh th&ocirc;ng minh với Chế độ tự động</strong></p>\r\n\r\n<p>Cảnh chế độ Intelligent Auto ph&acirc;n t&iacute;ch h&igrave;nh ảnh, v&agrave; chọn tiếp x&uacute;c ch&iacute;nh x&aacute;c v&agrave; cải tiến để tối ưu h&oacute;a c&aacute;c cảnh trong bức ảnh của bạn.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Hướng dẫn t&iacute;nh năng</strong></p>\r\n\r\n<p>Hướng dẫn t&iacute;nh năng hiển thị giới thiệu đơn giản c&aacute;c chế độ kh&aacute;c nhau với c&aacute;c chức năng V&agrave; N&oacute; kh&aacute; hữu hiệu khi bạn cố gắng t&igrave;m để x&aacute;c định chế độ hoặc những chức năng m&agrave; bạn mong muốn</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>3,0 fps chụp li&ecirc;n tục&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</strong></p>\r\n\r\n<p>Chụp 3,0 khung h&igrave;nh mỗi gi&acirc;y cho khoảng 69 h&igrave;nh ảnh JPEG hoặc 6 Bức ảnh RAW .</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>9 điểm hệ thống AF v&agrave; AI Servo AF</strong></p>\r\n\r\n<p>9 - point AF hệ thống sử dụng cả hai b&igrave;nh thường v&agrave; tập trung cao độ ch&iacute;nh x&aacute;c , t&ugrave;y thuộc v&agrave;o độ mở ống k&iacute;nh v&agrave; &aacute;nh s&aacute;ng c&oacute; sẵn trong bất kỳ t&igrave;nh huống n&agrave;o. Một hệ thống AI Servo AF duy tr&igrave; tập trung ph&ugrave; hợp khi chụp đối tượng chuyển động.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Bộ lọc tuyệt vời</strong></p>\r\n\r\n<p>Chọn từ 5 bộ lọc để thay đổi h&igrave;nh ảnh của bạn : Grainy B / W , Soft Focus , Fisheye Effect, Hiệu ứng thu nhỏ , v&agrave; chế độ đồ chơi</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>C&aacute;c chế độ chụp</strong></p>\r\n\r\n<p>C&oacute; 8 Khu cơ bản chế độ để lựa chọn khi chụp :</p>\r\n\r\n<p>- Cảnh tự động th&ocirc;ng minh ( chương tr&igrave;nh AE , kh&ocirc;ng shiftable )</p>\r\n\r\n<p>- Flash Tắt</p>\r\n\r\n<p>- Creative Auto</p>\r\n\r\n<p>- Ch&acirc;n dung</p>\r\n\r\n<p>- Cảnh quan</p>\r\n\r\n<p>- Cản đường</p>\r\n\r\n<p>- Thể thao</p>\r\n\r\n<p>- Ch&acirc;n dung Ban đ&ecirc;m</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Tạo chế độ tự động&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</strong></p>\r\n\r\n<p>Cho ph&eacute;p bạn thay đổi tuyệt đối trong cảnh , trong khi m&agrave;n h&igrave;nh tự động chụp v&agrave; hướng dẫn tr&ecirc;n m&agrave;n h&igrave;nh LCD v&agrave; giải th&iacute;ch l&agrave;m thế n&agrave;o để thay đổi tiếp x&uacute;c , giai điệu, độ sắc n&eacute;t, v&agrave; nhiều hơn nữa .</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>GPS tương th&iacute;ch</strong></p>\r\n\r\n<p>Với việc sử dụng một t&ugrave;y chọn GP -E2 GPS gắn v&agrave;o đế hoặc thiết bị đầu cuối kỹ thuật số,&nbsp;<a href=\"http://zshop.vn/may-anh-canon-dslr/\"><strong>EOS 1200D</strong></a>&nbsp;&nbsp;c&oacute; thể ghi lại vị tr&iacute; của bạn , vĩ độ, kinh độ, v&agrave; độ cao. Một la b&agrave;n điện tử sẽ ghi lại định hướng m&aacute;y ảnh của bạn trong mỗi cảnh quay , v&agrave; th&ocirc;ng tin thời gian thế giới cũng được ghi nhận th&ocirc;ng qua GPS đồng bộ .</p>\r\n', '1', '13', '4', '2014-12-26');
INSERT INTO `products` VALUES ('14', 'Nikon D5300 + Lens 18-55mm f/3.5-5.6 VR II (Chính ', '2', '14900820', '<p>Điểm nổi bật của sản phẩm</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>- Độ ph&acirc;n giải 24.2MP, cảm biến CMOS APS-C</p>\r\n\r\n<p>- Bộ xử l&yacute; h&igrave;nh ảnh Expeed 4</p>\r\n\r\n<p>- Dải ISO 100 - 12800 (mở rộng 25600)</p>\r\n\r\n<p>- 39 điểm lấy n&eacute;t tự động</p>\r\n\r\n<p>- M&agrave;n h&igrave;nh LCD 3&quot;2</p>\r\n\r\n<p>- Chụp li&ecirc;n tiếp 5 h&igrave;nh/gi&acirc;y</p>\r\n', '<h2><strong>M&aacute;y Ảnh Nikon D5300</strong></h2>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>GIỚI THIỆU</strong></p>\r\n\r\n<p><strong>-&nbsp;<a href=\"http://zshop.vn/may-anh-nikon-dslr/\">M&aacute;y ảnh DSLR Nikon</a>&nbsp;D5300</strong>&nbsp;c&oacute; định dạng cảm biến DX 24,2 megapixel v&agrave; bộ xử l&yacute; ảnh EXPEED 4 để cho ra h&igrave;nh ảnh c&oacute; độ ph&acirc;n giải caov&agrave; video full HD với độ nhạy đ&aacute;ng ch&uacute; &yacute;, &aacute;nh s&aacute;ng ti&ecirc;u chuẩn ISO 12800 v&agrave; tốc độ chụp li&ecirc;n tiếp 5 fps nhanh. Thiết kế cảm biến quang học, bỏ qua c&aacute;c bộ lọc th&ocirc;ng thấp truyền thống để đạt được độ sắc n&eacute;t v&agrave; độ ph&acirc;n giải tối đa của cả h&igrave;nh ảnh v&agrave; video. M&aacute;y ảnh<strong>Nikon D5300</strong>&nbsp;c&oacute; cả một hệ thống tự động lấy n&eacute;t linh hoạt 39 điểm, với ch&iacute;n bộ cảm biến cross-type, v&agrave; một bộ cảm biến RGB 2.016 điểm ảnh cho đo tiếp x&uacute;c ch&iacute;nh x&aacute;c trong điều kiện &aacute;nh s&aacute;ng. Ngo&agrave;i ra, &nbsp;quay phim 1080p 1920 x được hỗ trợ, trong nhiều tốc độ khung h&igrave;nh l&ecirc;n đến 60 fps, với khả năng tập trung tự động linh hoạt.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>- M&aacute;y ảnh&nbsp;<strong>Nikon D5300&nbsp;</strong>Nhỏ gọn, nhưng thiết kế bền bỉ, th&acirc;n m&aacute;y kết hợp cả một k&iacute;nh ngắm quang học pentamirror c&ugrave;ng với một m&agrave;n h&igrave;nh LCD lớn 3,2 &quot;1037 k-dot đa g&oacute;c, c&oacute; thiết kế t&iacute;nh năng &nbsp;xoay 180&deg; để hỗ trợ tốt hơn khi sử dụng ở nhiều g&oacute;c độ cao v&agrave; thấp. Ngo&agrave;i ra, Nikon c&ograve;n t&iacute;ch hợp v&agrave;o m&aacute;y ảnh Nikon D5300 cả Wi-Fi v&agrave; GPS, cho ph&eacute;p khả năng chia sẻ h&igrave;nh ảnh ti&ecirc;n tiến cũng như định vị, gắn thẻ ảnh. Khi kết hợp với một thiết bị di động Android hay iOS, c&oacute; thể xem từ xa v&agrave; kiểm so&aacute;t ảnh chụp.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>- M&aacute;y ảnh&nbsp;<strong>Nikon D5300</strong>&nbsp;cho giao diện của h&igrave;nh ảnh trong m&aacute;y ảnh tăng cường s&aacute;ng tạo, một loạt c&aacute;c chế độ kiểm so&aacute;t h&igrave;nh ảnh, chế độ Scene, v&agrave; đặc biệt chế độ hiệu ứng c&oacute; sẵn cũng như một loạt c&aacute;c h&igrave;nh ảnh trong m&aacute;y ảnh c&oacute; khả năng chỉnh sửa để điều chỉnh c&aacute;c thiết lập trước khi chia sẻ. Chế độ D-Lighting cũng c&oacute; thể được sử dụng để tối ưu h&oacute;a kết quả tiếp x&uacute;c v&agrave; c&aacute;c chi tiết cho một phạm vi mở rộng của &acirc;m thanh r&otilde; r&agrave;ng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Nikon D5300\" src=\"http://zshop.vn/images/detailed/31/D5300_BK_18_140_LCD_3_verge_super_wide.jpg\" style=\"height:323px; margin:0px; width:501px\" /></p>\r\n\r\n<p>Nikon D5300</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>T&Iacute;NH NĂNG NỔI BẬT</strong></p>\r\n\r\n<p><strong>Nikon D5300 Với bộ cảm biến DX-Format 24.2MP v&agrave; bộ xử l&yacute; EXPEED 4</strong></p>\r\n\r\n<p>Cảm biến trong m&aacute;y ảnh Nikon D5300 định dạng CMOS 24,2 megapixel v&agrave; bộ vi xử l&yacute; EXPEED 4 kết hợp với nhau để sản xuất h&igrave;nh ảnh chất lượng cao với chi tiết đ&aacute;ng ch&uacute; &yacute;, phạm vi hoạt động, độ ch&iacute;nh x&aacute;c m&agrave;u sắc, v&agrave; độ nhạy &aacute;nh s&aacute;ng theo ti&ecirc;u chuẩn ISO 12800, mở rộng tới ISO 25600. Ngo&agrave;i ra,&nbsp;<strong>Nikon D5300&nbsp;</strong>thiết kế của cảm biến bỏ qua một bộ lọc th&ocirc;ng thấp quang học để c&oacute; được độ sắc n&eacute;t v&agrave; chi tiết nhất của c&aacute;c đối tượng. 4 bộ xử l&yacute; EXPEED cũng cho ph&eacute;p một sự nhanh ch&oacute;ng của tốc độ to&agrave;n bộ hệ thống m&aacute;y ảnh, bao gồm hỗ trợ cho bộ phim HD full 1080p v&agrave; tốc độ chụp li&ecirc;n tiếp 5 fps.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Cảm biến Multi-CAM 4800DX lấy n&eacute;t tự động</strong></p>\r\n\r\n<p>- Chất lượng h&igrave;nh ảnh cao l&agrave; nhờ m&aacute;y ảnh Nikon D5300 c&oacute; một hệ thống AF sử dụng 39 điểm AF ri&ecirc;ng biệt, trong đ&oacute; c&oacute; ch&iacute;n bộ cảm biến cross-type, để nhanh ch&oacute;ng c&oacute; được tập trung trong một loạt c&aacute;c điều kiện &aacute;nh s&aacute;ng, từ -1 đến +19 EV. Một chế độ 3D-theo d&otilde;i duy nhất sử dụng c&aacute;c bộ cảm biến RGB 2.016 điểm ảnh để duy tr&igrave; tập trung mạnh v&agrave;o chuyển động. M&aacute;y ảnh&nbsp;<strong>Nikon D5300</strong>&nbsp;để ph&ugrave; hợp hơn với chủ đề cụ thể khi l&agrave;m việc trong AF-A hoặc chế độ lấy n&eacute;t AF-C, chế độ lấy n&eacute;t động cho ph&eacute;p bạn chọn từ 9, 21, hoặc c&aacute;c mảng 39 điểm để tăng cường theo d&otilde;i đối tượng, ngay cả khi sau một thời gian ngắn chủ đề rời khỏi khung h&igrave;nh. Single-point AF, theo d&otilde;i 3D, v&agrave; v&ugrave;ng chế độ tự động AF cũng c&oacute; sẵn.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>- Khi l&agrave;m việc trong chế độ live view, một hệ thống AF tương phản được sử dụng để c&oacute; được tập trung một c&aacute;ch ch&iacute;nh x&aacute;c v&agrave; c&oacute; thể hoạt động li&ecirc;n tục, chế độ servo to&agrave;n thời gian để l&agrave;m việc tốt hơn với c&aacute;c đối tượng di chuyển. Ngo&agrave;i ra, Nikon D5300c&oacute; bốn chế độ AF-khu vực đều c&oacute; thể khởi động trong chế độ live view: ưu ti&ecirc;n khu&ocirc;n mặt AF, diện rộng AF, Normal AF v&ugrave;ng, v&agrave; đối tượng theo d&otilde;i AF.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Wi-Fi v&agrave; kết nối GPS</strong></p>\r\n\r\n<p>- Hỗ trợ v&ocirc; số chia sẻ, c&aacute;c t&iacute;nh năng t&iacute;ch hợp Wi-Fi v&agrave; c&aacute;c module GPS để cho ph&eacute;p m&aacute;y ảnh&nbsp;<strong>Nikon D5300</strong>&nbsp;đồng bộ ngay h&igrave;nh ảnh cũng như chức năng định vị. Khi ứng dụng tiện &iacute;ch kh&ocirc;ng d&acirc;y của Nikon đ&atilde; được c&agrave;i đặt tr&ecirc;n thiết bị Android hoặc điện thoại di động iOS, bạn c&oacute; thể sử dụng thiết bị để duyệt c&aacute;c nội dung của thẻ nhớ, chọn h&igrave;nh ảnh, v&agrave; li&ecirc;n tục chuyển c&aacute;c tập tin giữa c&aacute;c thiết bị để chia sẻ trực tuyến ngay lập tức. Ngo&agrave;i ra, c&aacute;c thiết bị di động từ xa c&oacute; thể k&iacute;ch hoạt m&agrave;n trập v&agrave; hiển thị h&igrave;nh ảnh từ m&aacute;y ảnh cho ph&eacute;p l&agrave;m việc từ xa.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>- C&aacute;c chức năng GPS t&iacute;ch hợp c&oacute; thể được sử dụng để g&aacute;n dữ liệu về vị tr&iacute; địa cho tất cả c&aacute;c tập tin h&igrave;nh ảnh cho c&aacute;c mục đ&iacute;ch gắn thẻ địa l&yacute; v&agrave; cho vẽ h&igrave;nh ảnh bản đồ. Điểm tham quan cũng c&oacute; thể được t&igrave;m thấy trong m&aacute;y ảnh Nikon D5300 &nbsp;bằng c&aacute;ch sử dụng th&ocirc;ng tin n&agrave;y để hỗ trợ hơn nữa trong hiệu quả gắn thẻ dựa tr&ecirc;n địa điểm.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Thiết kế body của m&aacute;y ảnh Nikon D5300</strong></p>\r\n\r\n<p>Ngay cả trong khi duy tr&igrave; một tổng thể h&igrave;nh thức nhỏ gọn, m&aacute;y ảnh&nbsp;<strong>Nikon D5300</strong>&nbsp;t&iacute;ch hợp một m&agrave;n h&igrave;nh LCD lớn 3,2 &quot;1037 k-dot đa g&oacute;c, c&oacute; t&iacute;nh năng thiết kế xoay 180 độ để ph&ugrave; hợp với tr&igrave;nh l&agrave;m việc từ cao, thấp, v&agrave; c&aacute;c g&oacute;c ph&iacute;a trước. Ngo&agrave;i ra, một k&iacute;nh ngắm quang học pentamirror cũng được t&iacute;ch hợp v&agrave;o c&aacute;c thiết kế cho xem ch&iacute;nh x&aacute;c v&agrave; gi&aacute;m s&aacute;t thời gian thực của c&aacute;c đối tượng.Một đ&egrave;n flash trong cửa sổ pop-up cũng c&oacute; sẵn để chiếu s&aacute;ng bổ sung khi l&agrave;m việc trong điều kiện &aacute;nh s&aacute;ng yếu v&agrave; hỗ trợ hệ thống đo s&aacute;ng i-TTL &nbsp;của Nikon.&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Hệ thống Nhận diện cảnh v&agrave; đo phơi s&aacute;ng</strong></p>\r\n\r\n<p>Hệ thống Nhận diện cảnh th&ocirc;ng minh trong m&aacute;y ảnh Nikon D5300 &nbsp;với 3D Color Matrix Metering II , sử dụng một bộ cảm biến RGB 2.016 điểm ảnh để đ&aacute;nh gi&aacute; v&agrave; ph&acirc;n t&iacute;ch tất cả c&aacute;c kh&iacute;a cạnh trong một cảnh, bao gồm độ s&aacute;ng, độ tương phản, khoảng c&aacute;ch chủ đề, v&agrave; m&agrave;u sắc cảnh, nhanh ch&oacute;ng x&aacute;c định một tiếp x&uacute;c ch&iacute;nh x&aacute;c v&agrave; thiết lập c&acirc;n bằng trắng để l&agrave;m cho chất lượng tốt nhất, m&aacute;y ảnh<strong>Nikon D5300</strong>&nbsp;đảm bảo t&iacute;nh thống nhất từ h&igrave;nh ảnh đến h&igrave;nh ảnh li&ecirc;n quan đến tiếp x&uacute;c, c&acirc;n bằng trắng, c&agrave;i đặt đ&egrave;n flash i-TTL, v&agrave; phải theo d&otilde;i hiệu suất AF.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Quay phim Full HD Movie</strong></p>\r\n\r\n<p>- Quay phim Full HD 1920 x 1080p trong m&aacute;y ảnh Nikon D5300 được hỗ trợ trong 60, 30, 25, v&agrave; 24 fps tỷ lệ khung h&igrave;nh, cũng như HD 720p v&agrave; SD 480p trong nhiều tốc độ khung h&igrave;nh, trong c&aacute;c định dạng MPEG-4 AVC/H.264. Trong qu&aacute; tr&igrave;nh quay,&nbsp;<strong>Nikon D5300</strong>&nbsp;tự động lấy n&eacute;t to&agrave;n thời gian c&oacute; thể được sử dụng, với khả năng nhận diện khu&ocirc;n mặt v&agrave; đối tượng theo d&otilde;i, đảm bảo cho độ sắc n&eacute;t với nhiều loại đối tượng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>- Một micro stereo c&oacute; thể được sử dụng để ghi lại &acirc;m thanh chất lượng cao, c&aacute;ch kh&aacute;c, một đầu v&agrave;o microphone được t&iacute;ch hợp v&agrave;o thiết kế để hỗ trợ việc sử dụng một micro b&ecirc;n ngo&agrave;i để kiểm so&aacute;t tăng cường.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>C&aacute;c t&iacute;nh năng kh&aacute;c của m&aacute;y ảnh Nikon D5300</strong></p>\r\n\r\n<p>- Chế độ kiểm so&aacute;t h&igrave;nh ảnh<strong>&nbsp;Nikon D5300&nbsp;</strong>c&oacute; thể được truy cập trực tiếp th&ocirc;ng qua một n&uacute;t chuy&ecirc;n dụng v&agrave; cho ph&eacute;p bạn thiết lập h&igrave;nh được x&aacute;c định trước trong m&aacute;y ảnh ,v&agrave; bao gồm c&aacute;c cảnh quan, đơn sắc, m&agrave;u trung t&iacute;nh, Ch&acirc;n dung, Standard, Vivid, v&agrave; c&aacute;c thiết lập người d&ugrave;ng t&ugrave;y chỉnh.C&aacute;c tập tin RAW ( NEF ) c&oacute; thể được ghi lại như n&eacute;n kh&ocirc;ng bị mất hoặc n&eacute;n ở độ s&acirc;u 14-bit .</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>- Được x&acirc;y dựng trong hoạt động HDR để mở rộng phạm vi &acirc;m v&agrave; tiếp x&uacute;c của h&igrave;nh ảnh bằng c&aacute;ch ghi lại hai tiếp x&uacute;c ri&ecirc;ng biệt v&agrave; pha trộn ch&uacute;ng lại với nhau để được nổi bật hơn.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>- Active D -Lighting chức năng trong m&aacute;y ảnh Nikon D5300 gi&uacute;p l&agrave;m nổi bật h&igrave;nh ảnh trong điều kiện tương phản, chẳng hạn như &aacute;nh s&aacute;ng backlit. Bằng c&aacute;ch sử dụng sức mạnh của bộ xử l&yacute; EXPEED 4, h&igrave;nh ảnh được kiểm so&aacute;t nhiều hơn sẽ được sản xuất với gi&aacute; trị độ s&aacute;ng gần hơn với c&aacute;ch thức ch&uacute;ng được tự nhi&ecirc;n nh&igrave;n thấy. Chế độ n&agrave;y l&agrave; ph&ugrave; hợp để l&agrave;m việc với c&aacute;c đối tượng di chuyển .</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>- Auto Scene Selector l&agrave; một chế độ tự động th&ocirc;ng minh của m&aacute;y ảnh&nbsp;<strong>Nikon D5300</strong>&nbsp;x&aacute;c định chủ đề của bạn v&agrave; kịch bản chụp , chọn một chế độ cảnh th&iacute;ch hợp để &aacute;p dụng , v&agrave; điều chỉnh c&aacute;c thiết lập tiếp x&uacute;c để l&agrave;m cho tốt nhất.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>- Chế độ cảnh của m&aacute;y ảnh Nikon D5300 : M&agrave;u sắc m&ugrave;a thu , B&atilde;i biển / Tuyết , Blossom , Candlelight , trẻ em , Close-up , trời chạng vạng / b&igrave;nh minh , thực phẩm , Cảnh, Đ&ecirc;m Phong cảnh, Ch&acirc;n dung ban đ&ecirc;m , Tiệc / trong nh&agrave; , Ch&acirc;n dung mang theo vật nu&ocirc;i , Ch&acirc;n dung , Thể thao, v&agrave; ho&agrave;ng h&ocirc;n .</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>- Hiệu ứng đặc biệt: tầm nh&igrave;n ban đ&ecirc;m, m&agrave;u ph&aacute;c thảo, m&aacute;y ảnh đồ chơi , hiệu ứng thu nhỏ, m&agrave;u sắc chọn lọc , Silhouette , quan trọng cao, trọng thấp, v&agrave; HDR bức tranh.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>- C&aacute;c t&iacute;nh năng kh&aacute;c của m&aacute;y&nbsp;<strong>Nikon D5300</strong>&nbsp;: C&acirc;n bằng m&agrave;u , M&agrave;u cương , m&agrave;u Sketch, D-Lighting , Edit Movie , hiệu ứng bộ lọc , Fisheye, Image Overlay , Miniature Effect, đơn sắc , NEF (RAW) chế biến , quan niệm kiểm so&aacute;t, chỉnh sửa nhanh , đỏ Sửa mắt , k&iacute;ch cỡ, Selective Color , Side -by-Side So s&aacute;nh , thẳng , v&agrave; Trim.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>- Hỗ trợ ng&ocirc;n ngữ của m&aacute;y Nikon D5300 &nbsp;: tiếng Ả Rập, Bengali , Bồ Đ&agrave;o Nha, Trung Quốc ( giản thể v&agrave; truyền thống), S&eacute;c, Đan Mạch , H&agrave; Lan, Anh , Phần Lan, Ph&aacute;p , Đức, Hy Lạp , Tiếng Hin-ddi , Hungary , Indonesia , &Yacute;, Nhật Bản , H&agrave;n Quốc , Na Uy, Ba Tư, Ba Lan , Bồ Đ&agrave;o Nha, Rumani, Nga , T&acirc;y Ban Nha, Thụy Điển , v&agrave; Tamil .</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Nikon D5300\" src=\"http://zshop.vn/images/detailed/31/D5300_BK_18_140_frt34l_verge_super_wide.jpg\" style=\"height:462px; margin:0px; width:500px\" /></p>\r\n\r\n<p>Nikon D5300</p>\r\n', '1', '21', '6', '2015-01-22');
INSERT INTO `products` VALUES ('15', 'Nikon D5300 (Body only) (Chính Hãng)', '2', '13124200', '<p>Điểm nổi bật của sản phẩm</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>- Độ ph&acirc;n giải 24.2MP, cảm biến CMOS APS-C</p>\r\n\r\n<p>- Bộ xử l&yacute; h&igrave;nh ảnh Expeed 4</p>\r\n\r\n<p>- Dải ISO 100 - 12800 (mở rộng 25600)</p>\r\n\r\n<p>- 39 điểm lấy n&eacute;t tự động</p>\r\n\r\n<p>- M&agrave;n h&igrave;nh LCD 3&quot;2</p>\r\n\r\n<p>- Chụp li&ecirc;n tiếp 5 h&igrave;nh/gi&acirc;y</p>\r\n', '<h2>M&aacute;y Ảnh Nikon D5300</h2>\r\n\r\n<h2>&nbsp;</h2>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>GIỚI THIỆU</strong></p>\r\n\r\n<p><strong><a href=\"http://zshop.vn/may-anh-nikon-dslr/\">M&aacute;y ảnh</a>&nbsp;DSLR Nikon D5300</strong>&nbsp;c&oacute; định dạng cảm biến DX 24,2 megapixel v&agrave; bộ xử l&yacute; ảnh EXPEED 4 để cho ra h&igrave;nh ảnh c&oacute; độ ph&acirc;n giải caov&agrave; video full HD với độ nhạy đ&aacute;ng ch&uacute; &yacute;, &aacute;nh s&aacute;ng ti&ecirc;u chuẩn ISO 12800 v&agrave; tốc độ chụp li&ecirc;n tiếp 5 fps nhanh. Thiết kế cảm biến quang học, bỏ qua c&aacute;c bộ lọc th&ocirc;ng thấp truyền thống để đạt được độ sắc n&eacute;t v&agrave; độ ph&acirc;n giải tối đa của cả h&igrave;nh ảnh v&agrave; video. M&aacute;y ảnh&nbsp;<strong>Nikon D5300</strong>&nbsp;c&oacute; cả một hệ thống tự động lấy n&eacute;t linh hoạt 39 điểm, với ch&iacute;n bộ cảm biến cross-type, v&agrave; một bộ cảm biến RGB 2.016 điểm ảnh cho đo tiếp x&uacute;c ch&iacute;nh x&aacute;c trong điều kiện &aacute;nh s&aacute;ng. Ngo&agrave;i ra, &nbsp;quay phim 1080p 1920 x được hỗ trợ, trong nhiều tốc độ khung h&igrave;nh l&ecirc;n đến 60 fps, với khả năng tập trung tự động linh hoạt.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>M&aacute;y ảnh&nbsp;<strong>Nikon D5300&nbsp;</strong>Nhỏ gọn, nhưng thiết kế bền bỉ, th&acirc;n m&aacute;y kết hợp cả một k&iacute;nh ngắm quang học pentamirror c&ugrave;ng với một m&agrave;n h&igrave;nh LCD lớn 3,2 &quot;1037 k-dot đa g&oacute;c, c&oacute; thiết kế t&iacute;nh năng &nbsp;xoay 180&deg; để hỗ trợ tốt hơn khi sử dụng ở nhiều g&oacute;c độ cao v&agrave; thấp. Ngo&agrave;i ra, Nikon c&ograve;n t&iacute;ch hợp v&agrave;o m&aacute;y ảnh Nikon D5300 cả Wi-Fi v&agrave; GPS, cho ph&eacute;p khả năng chia sẻ h&igrave;nh ảnh ti&ecirc;n tiến cũng như định vị, gắn thẻ ảnh. Khi kết hợp với một thiết bị di động Android hay iOS, c&oacute; thể xem từ xa v&agrave; kiểm so&aacute;t ảnh chụp.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>M&aacute;y ảnh&nbsp;<strong>Nikon D5300</strong>&nbsp;cho giao diện của h&igrave;nh ảnh trong m&aacute;y ảnh tăng cường s&aacute;ng tạo, một loạt c&aacute;c chế độ kiểm so&aacute;t h&igrave;nh ảnh, chế độ Scene, v&agrave; đặc biệt chế độ hiệu ứng c&oacute; sẵn cũng như một loạt c&aacute;c h&igrave;nh ảnh trong m&aacute;y ảnh c&oacute; khả năng chỉnh sửa để điều chỉnh c&aacute;c thiết lập trước khi chia sẻ. Chế độ D-Lighting cũng c&oacute; thể được sử dụng để tối ưu h&oacute;a kết quả tiếp x&uacute;c v&agrave; c&aacute;c chi tiết cho một phạm vi mở rộng của &acirc;m thanh r&otilde; r&agrave;ng</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Nikon D5300\" src=\"http://zshop.vn/images/detailed/31/D5300_BK_18_140_LCD_3_verge_super_wide.jpg\" style=\"height:323px; margin:0px; width:500px\" /></p>\r\n\r\n<p>Nikon D5300</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>T&Iacute;NH NĂNG NỔI BẬT</strong></p>\r\n\r\n<p><strong>Bộ cảm biến DX-Format 24.2MP v&agrave; bộ xử l&yacute; EXPEED 4</strong></p>\r\n\r\n<p>Cảm biến trong m&aacute;y ảnh Nikon D5300 định dạng CMOS 24,2 megapixel v&agrave; bộ vi xử l&yacute; EXPEED 4 kết hợp với nhau để sản xuất h&igrave;nh ảnh chất lượng cao với chi tiết đ&aacute;ng ch&uacute; &yacute;, phạm vi hoạt động, độ ch&iacute;nh x&aacute;c m&agrave;u sắc, v&agrave; độ nhạy &aacute;nh s&aacute;ng theo ti&ecirc;u chuẩn ISO 12800, mở rộng tới ISO 25600. Ngo&agrave;i ra,&nbsp;<a href=\"http://zshop.vn/may-anh-nikon-dslr/\"><strong>Nikon D5300&nbsp;</strong></a>thiết kế của cảm biến bỏ qua một bộ lọc th&ocirc;ng thấp quang học để c&oacute; được độ sắc n&eacute;t v&agrave; chi tiết nhất của c&aacute;c đối tượng. 4 bộ xử l&yacute; EXPEED cũng cho ph&eacute;p một sự nhanh ch&oacute;ng của tốc độ to&agrave;n bộ hệ thống m&aacute;y ảnh, bao gồm hỗ trợ cho bộ phim HD full 1080p v&agrave; tốc độ chụp li&ecirc;n tiếp 5 fps.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Cảm biến Multi-CAM 4800DX lấy n&eacute;t tự động</strong></p>\r\n\r\n<p>Chất lượng h&igrave;nh ảnh cao l&agrave; nhờ m&aacute;y ảnh Nikon D5300 c&oacute; một hệ thống AF sử dụng 39 điểm AF ri&ecirc;ng biệt, trong đ&oacute; c&oacute; ch&iacute;n bộ cảm biến cross-type, để nhanh ch&oacute;ng c&oacute; được tập trung trong một loạt c&aacute;c điều kiện &aacute;nh s&aacute;ng, từ -1 đến +19 EV. Một chế độ 3D-theo d&otilde;i duy nhất sử dụng c&aacute;c bộ cảm biến RGB 2.016 điểm ảnh để duy tr&igrave; tập trung mạnh v&agrave;o chuyển động. M&aacute;y ảnh&nbsp;<strong>Nikon D5300</strong>&nbsp;để ph&ugrave; hợp hơn với chủ đề cụ thể khi l&agrave;m việc trong AF-A hoặc chế độ lấy n&eacute;t AF-C, chế độ lấy n&eacute;t động cho ph&eacute;p bạn chọn từ 9, 21, hoặc c&aacute;c mảng 39 điểm để tăng cường theo d&otilde;i đối tượng, ngay cả khi sau một thời gian ngắn chủ đề rời khỏi khung h&igrave;nh. Single-point AF, theo d&otilde;i 3D, v&agrave; v&ugrave;ng chế độ tự động AF cũng c&oacute; sẵn.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Khi l&agrave;m việc trong chế độ live view, một hệ thống AF tương phản được sử dụng để c&oacute; được tập trung một c&aacute;ch ch&iacute;nh x&aacute;c v&agrave; c&oacute; thể hoạt động li&ecirc;n tục, chế độ servo to&agrave;n thời gian để l&agrave;m việc tốt hơn với c&aacute;c đối tượng di chuyển. Ngo&agrave;i ra, Nikon D5300c&oacute; bốn chế độ AF-khu vực đều c&oacute; thể khởi động trong chế độ live view: ưu ti&ecirc;n khu&ocirc;n mặt AF, diện rộng AF, Normal AF v&ugrave;ng, v&agrave; đối tượng theo d&otilde;i AF.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Wi-Fi v&agrave; kết nối GPS</strong></p>\r\n\r\n<p>Hỗ trợ v&ocirc; số chia sẻ, c&aacute;c t&iacute;nh năng t&iacute;ch hợp Wi-Fi v&agrave; c&aacute;c module GPS để cho ph&eacute;p m&aacute;y ảnh&nbsp;<strong>Nikon D5300</strong>&nbsp;đồng bộ ngay h&igrave;nh ảnh cũng như chức năng định vị. Khi ứng dụng tiện &iacute;ch kh&ocirc;ng d&acirc;y của Nikon đ&atilde; được c&agrave;i đặt tr&ecirc;n thiết bị Android hoặc điện thoại di động iOS, bạn c&oacute; thể sử dụng thiết bị để duyệt c&aacute;c nội dung của thẻ nhớ, chọn h&igrave;nh ảnh, v&agrave; li&ecirc;n tục chuyển c&aacute;c tập tin giữa c&aacute;c thiết bị để chia sẻ trực tuyến ngay lập tức. Ngo&agrave;i ra, c&aacute;c thiết bị di động từ xa c&oacute; thể k&iacute;ch hoạt m&agrave;n trập v&agrave; hiển thị h&igrave;nh ảnh từ m&aacute;y ảnh cho ph&eacute;p l&agrave;m việc từ xa.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>C&aacute;c chức năng GPS t&iacute;ch hợp c&oacute; thể được sử dụng để g&aacute;n dữ liệu về vị tr&iacute; địa cho tất cả c&aacute;c tập tin h&igrave;nh ảnh cho c&aacute;c mục đ&iacute;ch gắn thẻ địa l&yacute; v&agrave; cho vẽ h&igrave;nh ảnh bản đồ. Điểm tham quan cũng c&oacute; thể được t&igrave;m thấy trong m&aacute;y ảnh Nikon D5300 &nbsp;bằng c&aacute;ch sử dụng th&ocirc;ng tin n&agrave;y để hỗ trợ hơn nữa trong hiệu quả gắn thẻ dựa tr&ecirc;n địa điểm.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Thiết kế body</strong></p>\r\n\r\n<p>Ngay cả trong khi duy tr&igrave; một tổng thể h&igrave;nh thức nhỏ gọn, m&aacute;y ảnh&nbsp;<strong>Nikon D5300</strong>&nbsp;t&iacute;ch hợp một m&agrave;n h&igrave;nh LCD lớn 3,2 &quot;1037 k-dot đa g&oacute;c, c&oacute; t&iacute;nh năng thiết kế xoay 180 độ để ph&ugrave; hợp với tr&igrave;nh l&agrave;m việc từ cao, thấp, v&agrave; c&aacute;c g&oacute;c ph&iacute;a trước. Ngo&agrave;i ra, một k&iacute;nh ngắm quang học pentamirror cũng được t&iacute;ch hợp v&agrave;o c&aacute;c thiết kế cho xem ch&iacute;nh x&aacute;c v&agrave; gi&aacute;m s&aacute;t thời gian thực của c&aacute;c đối tượng.Một đ&egrave;n flash trong cửa sổ pop-up cũng c&oacute; sẵn để chiếu s&aacute;ng bổ sung khi l&agrave;m việc trong điều kiện &aacute;nh s&aacute;ng yếu v&agrave; hỗ trợ hệ thống đo s&aacute;ng i-TTL &nbsp;của Nikon.&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Hệ thống Nhận diện cảnh v&agrave; đo phơi s&aacute;ng</strong></p>\r\n\r\n<p>Hệ thống Nhận diện cảnh th&ocirc;ng minh trong m&aacute;y ảnh Nikon D5300 &nbsp;với 3D Color Matrix Metering II , sử dụng một bộ cảm biến RGB 2.016 điểm ảnh để đ&aacute;nh gi&aacute; v&agrave; ph&acirc;n t&iacute;ch tất cả c&aacute;c kh&iacute;a cạnh trong một cảnh, bao gồm độ s&aacute;ng, độ tương phản, khoảng c&aacute;ch chủ đề, v&agrave; m&agrave;u sắc cảnh, nhanh ch&oacute;ng x&aacute;c định một tiếp x&uacute;c ch&iacute;nh x&aacute;c v&agrave; thiết lập c&acirc;n bằng trắng để l&agrave;m cho chất lượng tốt nhất, m&aacute;y ảnh&nbsp;<strong>Nikon D5300</strong>&nbsp;đảm bảo t&iacute;nh thống nhất từ h&igrave;nh ảnh đến h&igrave;nh ảnh li&ecirc;n quan đến tiếp x&uacute;c, c&acirc;n bằng trắng, c&agrave;i đặt đ&egrave;n flash i-TTL, v&agrave; phải theo d&otilde;i hiệu suất AF.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Quay phim Full HD Movie</strong></p>\r\n\r\n<p>Quay phim Full HD 1920 x 1080p trong m&aacute;y ảnh Nikon D5300 được hỗ trợ trong 60, 30, 25, v&agrave; 24 fps tỷ lệ khung h&igrave;nh, cũng như HD 720p v&agrave; SD 480p trong nhiều tốc độ khung h&igrave;nh, trong c&aacute;c định dạng MPEG-4 AVC/H.264. Trong qu&aacute; tr&igrave;nh quay,&nbsp;<strong>Nikon D5300</strong>&nbsp;tự động lấy n&eacute;t to&agrave;n thời gian c&oacute; thể được sử dụng, với khả năng nhận diện khu&ocirc;n mặt v&agrave; đối tượng theo d&otilde;i, đảm bảo cho độ sắc n&eacute;t với nhiều loại đối tượng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Một micro stereo c&oacute; thể được sử dụng để ghi lại &acirc;m thanh chất lượng cao, c&aacute;ch kh&aacute;c, một đầu v&agrave;o microphone được t&iacute;ch hợp v&agrave;o thiết kế để hỗ trợ việc sử dụng một micro b&ecirc;n ngo&agrave;i để kiểm so&aacute;t tăng cường.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>C&aacute;c t&iacute;nh năng kh&aacute;c của m&aacute;y ảnh</strong></p>\r\n\r\n<p>- Chế độ kiểm so&aacute;t h&igrave;nh ảnh<strong>&nbsp;Nikon D5300&nbsp;</strong>c&oacute; thể được truy cập trực tiếp th&ocirc;ng qua một n&uacute;t chuy&ecirc;n dụng v&agrave; cho ph&eacute;p bạn thiết lập h&igrave;nh được x&aacute;c định trước trong m&aacute;y ảnh ,v&agrave; bao gồm c&aacute;c cảnh quan, đơn sắc, m&agrave;u trung t&iacute;nh, Ch&acirc;n dung, Standard, Vivid, v&agrave; c&aacute;c thiết lập người d&ugrave;ng t&ugrave;y chỉnh.C&aacute;c tập tin RAW ( NEF ) c&oacute; thể được ghi lại như n&eacute;n kh&ocirc;ng bị mất hoặc n&eacute;n ở độ s&acirc;u 14-bit .</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>- Được x&acirc;y dựng trong hoạt động HDR để mở rộng phạm vi &acirc;m v&agrave; tiếp x&uacute;c của h&igrave;nh ảnh bằng c&aacute;ch ghi lại hai tiếp x&uacute;c ri&ecirc;ng biệt v&agrave; pha trộn ch&uacute;ng lại với nhau để được nổi bật hơn.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>- Active D -Lighting chức năng trong m&aacute;y ảnh Nikon D5300 gi&uacute;p l&agrave;m nổi bật h&igrave;nh ảnh trong điều kiện tương phản, chẳng hạn như &aacute;nh s&aacute;ng backlit. Bằng c&aacute;ch sử dụng sức mạnh của bộ xử l&yacute; EXPEED 4, h&igrave;nh ảnh được kiểm so&aacute;t nhiều hơn sẽ được sản xuất với gi&aacute; trị độ s&aacute;ng gần hơn với c&aacute;ch thức ch&uacute;ng được tự nhi&ecirc;n nh&igrave;n thấy. Chế độ n&agrave;y l&agrave; ph&ugrave; hợp để l&agrave;m việc với c&aacute;c đối tượng di chuyển .</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>- Auto Scene Selector l&agrave; một chế độ tự động th&ocirc;ng minh của m&aacute;y ảnh&nbsp;<strong>Nikon D5300</strong>&nbsp;x&aacute;c định chủ đề của bạn v&agrave; kịch bản chụp , chọn một chế độ cảnh th&iacute;ch hợp để &aacute;p dụng , v&agrave; điều chỉnh c&aacute;c thiết lập tiếp x&uacute;c để l&agrave;m cho tốt nhất.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>- Chế độ cảnh của m&aacute;y ảnh Nikon D5300 : M&agrave;u sắc m&ugrave;a thu , B&atilde;i biển / Tuyết , Blossom , Candlelight , trẻ em , Close-up , trời chạng vạng / b&igrave;nh minh , thực phẩm , Cảnh, Đ&ecirc;m Phong cảnh, Ch&acirc;n dung ban đ&ecirc;m , Tiệc / trong nh&agrave; , Ch&acirc;n dung mang theo vật nu&ocirc;i , Ch&acirc;n dung , Thể thao, v&agrave; ho&agrave;ng h&ocirc;n .</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>- Hiệu ứng đặc biệt: tầm nh&igrave;n ban đ&ecirc;m, m&agrave;u ph&aacute;c thảo, m&aacute;y ảnh đồ chơi , hiệu ứng thu nhỏ, m&agrave;u sắc chọn lọc , Silhouette , quan trọng cao, trọng thấp, v&agrave; HDR bức tranh.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>- C&aacute;c t&iacute;nh năng kh&aacute;c của m&aacute;y&nbsp;<strong>Nikon D5300</strong>&nbsp;: C&acirc;n bằng m&agrave;u , M&agrave;u cương , m&agrave;u Sketch, D-Lighting , Edit Movie , hiệu ứng bộ lọc , Fisheye, Image Overlay , Miniature Effect, đơn sắc , NEF (RAW) chế biến , quan niệm kiểm so&aacute;t, chỉnh sửa nhanh , đỏ Sửa mắt , k&iacute;ch cỡ, Selective Color , Side -by-Side So s&aacute;nh , thẳng , v&agrave; Trim.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>- Hỗ trợ ng&ocirc;n ngữ của m&aacute;y Nikon D5300 &nbsp;: tiếng Ả Rập, Bengali , Bồ Đ&agrave;o Nha, Trung Quốc ( giản thể v&agrave; truyền thống), S&eacute;c, Đan Mạch , H&agrave; Lan, Anh , Phần Lan, Ph&aacute;p , Đức, Hy Lạp , Tiếng Hin-ddi , Hungary , Indonesia , &Yacute;, Nhật Bản , H&agrave;n Quốc , Na Uy, Ba Tư, Ba Lan , Bồ Đ&agrave;o Nha, Rumani, Nga , T&acirc;y Ban Nha, Thụy Điển , v&agrave; Tamil .</p>\r\n', '1', '12', '6', '2015-01-26');
INSERT INTO `products` VALUES ('16', 'Nikon D7000 + kit 18-105 VR (Chính Hãng)', '2', '18420300', '<p>Điểm nổi bật của sản phẩm</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>- Cảm biến ảnh DX CMOS 16.2 Megapixels</p>\r\n\r\n<p>- Bộ xử l&yacute; ảnh EXPEED II, ISO cao nhất tới 25,600</p>\r\n\r\n<p>- Hệ thống lấy n&eacute;t tự động 3D-tracking AF 36 điểm</p>\r\n\r\n<p>- T&iacute;ch hợp hai hệ thống tự động l&agrave;m sạch cảm biến</p>\r\n\r\n<p>- M&agrave;n h&igrave;nh 3&rdquo; Super-Density độ ph&acirc;n giải tới 921,000</p>\r\n', '<h2><strong>Nikon D7000 + kit 18-105 VR</strong></h2>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>GIỚI THIỆU</strong></p>\r\n\r\n<p>Đạt giải thưởng danh gi&aacute; &ldquo;Best DSLR Advanced&rdquo; của Hiệp hội nhiếp ảnh b&aacute;o ch&iacute; TIPA năm 2011 v&agrave; giải &ldquo;Best Prodcuct 2011-2012&rdquo; của Hiệp hội &acirc;m thanh v&agrave; h&igrave;nh ảnh ch&acirc;u &Acirc;u EISA,&nbsp;<strong><a href=\"http://zshop.vn/may-anh-nikon-dslr/\">m&aacute;y ảnh</a>&nbsp;Nikon D7000</strong>&nbsp;được đ&aacute;nh gi&aacute; l&agrave; chiếc m&aacute;y ảnh ống k&iacute;nh rời được mong đợi nhất m&ugrave;a 2011-2012. Tiếp tục khẳng định danh tiếng v&agrave; chất lượng Nikon tr&ecirc;n to&agrave;n cầu,&nbsp;Nikon D7000&nbsp;đ&atilde; l&agrave;m cho người d&ugrave;ng ho&agrave;n to&agrave;n h&agrave;i l&ograve;ng khi c&oacute; những cải tiến lớn so với đ&agrave;n anh D90 v&agrave; được xem l&agrave; gần giống với d&ograve;ng m&aacute;y cao cấp D300s. Cảm biến k&iacute;ch thước lớn CMOS 16.2MP DX format kết hợp c&ugrave;ng vi xử l&yacute; Expeed 2 cho khả năng nhạy s&aacute;ng cao, khử nhiễu, c&ocirc; lập m&agrave;u giả v&agrave; hỗ trợ chụp ảnh RAW chất lượng 12-bit v&agrave; 14-bit. Hệ thống lấy n&eacute;t tự động 39 điểm ho&agrave;n to&agrave;n mới với 9 điểm cross-type cho khả năng lấy n&eacute;t cực nhanh v&agrave; ch&iacute;nh x&aacute;c ngay cả khi chủ thể ảnh di chuyển hay khi bạn quay phim. Thiết kế mạnh mẽ, bền bỉ v&agrave; th&acirc;n thiện với người d&ugrave;ng c&ugrave;ng khả năng tương th&iacute;ch với nhiều loại ống k&iacute;nh khiến&nbsp;<strong>Nikon D7000</strong>&nbsp;trở th&agrave;nh ứng cử vi&ecirc;n s&aacute;ng gi&aacute; cho sự lựa chọn của bạn.</p>\r\n\r\n<p><img alt=\"\" src=\"http://zshop.vn/images/b/Update/up2/Nikon%20D7000%20%2B%20kit%2018-105%20VR%202.jpg\" style=\"height:500px; margin:0px; width:500px\" /></p>\r\n\r\n<p>Nikon D7000 + kit 18-105 VR</p>\r\n\r\n<p><br />\r\nMặc d&ugrave; c&oacute; thiết kế kh&aacute;c, nhưng tổng thể&nbsp;<strong>Nikon D7000</strong>&nbsp;vẫn tương tự D90 nhưng chắc chắn hơn với to&agrave;n bộ vỏ m&aacute;y được l&agrave;m bằng hợp kim ma-nh&ecirc; đảm bảo độ cứng v&agrave; nhẹ.&nbsp;Nikon D7000 sở hữu cảm biến CMOS 16,2 triệu điểm ảnh, cao thứ nh&igrave; trong c&aacute;c d&ograve;ng DSLR của Nikon (sau D3X 24 Meagapixel), bộ xử l&yacute; h&igrave;nh ảnh Expeed 2 mới nhanh hơn cho tốc độ li&ecirc;n tiếp 6 khung h&igrave;nh mỗi gi&acirc;y, khung ngắm quang 100%, m&agrave;n LCD 3 inch độ ph&acirc;n giải 921.000 điểm ảnh v&agrave; khả năng quay phim Full HD 1080p24.<br />\r\n&nbsp;</p>\r\n\r\n<p>Hệ thống lấy n&eacute;t v&agrave; đo s&aacute;ng của&nbsp;<strong>Nikon D7000</strong>&nbsp;cũng được ph&aacute;t triển mới so với bản D90 với 39 điểm lấy n&eacute;t, 9 điểm cross-type c&ugrave;ng với cảm biến đo s&aacute;ng RGB 2016 điểm cho ph&eacute;p phi&ecirc;n bản n&agrave;y c&oacute; khả năng b&aacute;m n&eacute;t 3D (theo cả m&agrave;u sắc của đối tượng). Một số cải tiến kh&aacute;c bao gồm kết hợp n&uacute;t quay phim v&agrave; chế độ LiveView l&agrave;m một (như tr&ecirc;n bản D3100 trước đ&acirc;y), quay phim Full HD v&agrave; t&iacute;nh năng duy tr&igrave; lấy n&eacute;t cả trong chế độ Live View v&agrave; quay phim nhờ cơ chế tự động to&agrave;n phần AF-F.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>T&Iacute;NH NĂNG NỔI BẬT</strong><br />\r\n&nbsp;</p>\r\n\r\n<p><strong>Cảm biến k&iacute;ch thước lớn</strong></p>\r\n\r\n<p>Nikon D7000 sử dụng cảm biến CMOS 16.2MP DX format kết hợp c&ugrave;ng vi xử l&yacute; Expeed 2 cho khả năng nhạy s&aacute;ng cao, tăng tốc độ xử l&yacute;, khử nhiễu v&agrave; c&ocirc; lập c&aacute;c m&agrave;u giả được ph&aacute;t hiện từ đ&oacute; cho h&igrave;nh ảnh c&oacute; độ tương phản v&agrave; trung thực cao với m&agrave;u sắc rực rỡ v&agrave; sống động; đồng thời hỗ trợ chụp ảnh RAW chất lượng 12-bit v&agrave; 14-bit. Ngo&agrave;i ra m&aacute;y c&ograve;n hỗ trợ ISO 100-6400, mở rộng l&ecirc;n đến 25,600 v&agrave; tăng tốc độ m&agrave;n trập tối đa l&ecirc;n đến 1/8000s gi&uacute;p bạn nắm bắt trọn vẹn mọi khoảnh khắc để c&oacute; được h&igrave;nh đẹp ngay cả trong điều kiện thiếu s&aacute;ng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Hệ thống lấy n&eacute;t tự động 39 điểm ho&agrave;n to&agrave;n mới&nbsp;</strong></p>\r\n\r\n<p>Nikon D7000&nbsp;sử dụng hệ thống lấy n&eacute;t tự động 39 điểm ho&agrave;n to&agrave;n mới với 9 điểm cross-type đem đến khả năng lấy n&eacute;t cực nhanh v&agrave; ch&iacute;nh x&aacute;c cao ngay cả trong trường hợp chủ thể ảnh đang di chuyển, giảm tối đa hiện tượng nh&ograve;e mờ, cho tốc độ chụp nhanh l&ecirc;n đến 6 fps v&agrave; chụp li&ecirc;n tục 100 tấm. Cảm biến đo s&aacute;ng RGB cho ph&eacute;p m&aacute;y lấy dữ liệu từ 2,016 pixels, b&ugrave; trừ s&aacute;ng v&agrave; m&agrave;u sắc nhằm tối ưu h&oacute;a khả năng nhận diện khung cảnh v&agrave; cho khả năng b&aacute;m n&eacute;t 3D ho&agrave;n hảo hơn.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Quay phim Full HD</strong></p>\r\n\r\n<p><strong>Nikon D7000</strong>&nbsp;được trang bị khả năng quay phim Full HD chuẩn &acirc;m thanh stereo với tốc độ 24 khung h&igrave;nh / gi&acirc;y c&ugrave;ng khả năng lấy n&eacute;t to&agrave;n thời gian khi quay cho bạn những đoạn video đạt chuẩn tối ưu. Chế độ Flicker Reduction được t&iacute;ch hợp với hai tần số nh&aacute;y l&agrave; 50Hz v&agrave; 60Hz để kh&ocirc;ng tr&ugrave;ng với tần số của đ&egrave;n Neon, do đ&oacute; hiện tượng bị nh&aacute;y khi quay dưới &aacute;nh đ&egrave;n Neon hầu như được loại bỏ.</p>\r\n\r\n<p><img alt=\"\" src=\"http://zshop.vn/images/b/Update/up2/Nikon%20D7000%20%2B%20kit%2018-105%20VR.jpg\" style=\"height:340px; margin:0px; width:340px\" /></p>\r\n\r\n<p>Nikon D7000 + kit 18-105 VR</p>\r\n\r\n<p><strong>Th&acirc;n thiện với người sử dụng</strong></p>\r\n\r\n<p>Ngo&agrave;i v&ograve;ng tr&ograve;n điều chỉnh chế độ chụp, Nikon D7000 c&ograve;n hỗ trợ một v&ograve;ng tr&ograve;n chọn kiểu nằm ngay ph&iacute;a dưới để bạn chọn kiểu chụp như S (Single Frame: Chụp 1 tấm); CL (Continuous low speed: Chụp li&ecirc;n tục chậm); CH (Continuous high speed: chụp li&ecirc;n tục nhanh); Q (quiet shutter-release: Chụp y&ecirc;n lặng); Remote (d&ugrave;ng điều khiển để chụp); MUP (Mirror Up)&hellip;Ngo&agrave;i ra b&aacute;nh xe chế độ c&ograve;n c&oacute; th&ecirc;m c&agrave;i đặt U1, U2 để bạn lưu sẵn v&agrave;i chế độ chụp hay thiết lập c&aacute;c mức ISO kh&aacute;c nhau để bạn kh&ocirc;ng cần c&agrave;i đặt lại nhiều lần khi di chuyển hay khi thay ống k&iacute;nh. M&aacute;y cũng hỗ trợ đến hai khe cắm thẻ nhớ để bạn thoải m&aacute;i hơn khi t&aacute;c nghiệp m&agrave; kh&ocirc;ng lo đầy thẻ.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Thiết kế mạnh mẽ</strong></p>\r\n\r\n<p>So với D90,&nbsp;<strong>Nikon D7000</strong>&nbsp;c&oacute; thiết kế lớn hơn nhưng chắc chắn hơn với phần vỏ được l&agrave;m to&agrave;n bộ từ hợp kim Magie, đảm bảo độ cứng tối đa nhưng vẫn đủ nhẹ để người d&ugrave;ng cảm thấy thoải m&aacute;i khi cầm tr&ecirc;n tay. K&iacute;nh ngắm quang học pentaprism hiển thị 100% khung h&igrave;nh để bạn nh&igrave;n thấy trước được tất cả những g&igrave; sẽ xuất hiện trong ảnh của m&igrave;nh. Pin EN-EL15 được ph&aacute;t triển với chấu tiếp x&uacute;c an to&agrave;n với dung lượng l&ecirc;n đến 1900mAh, cho thời gian chụp li&ecirc;n tục l&ecirc;n đến 850 tấm ảnh.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Tương th&iacute;ch đa dạng ống k&iacute;nh hơn</strong></p>\r\n\r\n<p><strong>Nikon D7000</strong>&nbsp;được cho l&agrave; kh&aacute; dễ t&iacute;nh khi c&oacute; thể tương th&iacute;ch với c&aacute;c ống k&iacute;nh AF, AF-D. Ngo&agrave;i ra nhờ một lẫy tiếp x&uacute;c Ai ở chấu tiếp x&uacute;c, Nikon D7000 c&oacute; thể nhận v&agrave; điều chỉnh độ mở tới 9 ống k&iacute;nh Ai. Điều n&agrave;y cho ph&eacute;p gần như tất cả c&aacute;c ống Ai đều c&oacute; thể sử dụng tr&ecirc;n D7000 ở c&aacute;c chế độ Manual hoặc ưu ti&ecirc;n độ mở (trừ chế độ tự động lấy n&eacute;t). Đ&acirc;y được coi l&agrave; một điểm s&aacute;ng gi&aacute; mới của Nikon D7000 d&agrave;nh cho những tay m&aacute;y chuy&ecirc;n nghiệp c&oacute; nhiều ống k&iacute;nh.</p>\r\n', '1', '12', '6', '2015-01-09');
INSERT INTO `products` VALUES ('17', 'Nikon D5100 + AF-S 18-55mm VR (Chính Hãng)', '2', '9494578', '<p>Điểm nổi bật của sản phẩm</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>- Cảm biến ảnh Nikon DX CMOS 16.2 Megapixels</p>\r\n\r\n<p>- Bộ xử l&yacute; ảnh EXPEED II, ISO cao nhất tới 25,600</p>\r\n\r\n<p>- Hệ thống lấy n&eacute;t tự động 3D-tracking AF 11 điểm</p>\r\n\r\n<p>- Quay phim Full-HD 1080p với hiệu ứng Cinematic&nbsp;</p>\r\n\r\n<p>- M&agrave;n h&igrave;nh xoay 3&rdquo; độ ph&acirc;n giải 921,000 điểm ảnh</p>\r\n', '<h2><strong>Nikon D5100</strong></h2>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>GIỚI THIỆU</strong></p>\r\n\r\n<p><strong>-&nbsp;<a href=\"http://zshop.vn/may-anh-nikon-dslr/\">M&aacute;y ảnh</a>&nbsp;Nikon&nbsp;D5100</strong>&nbsp;Nổi bật với m&agrave;n h&igrave;nh đa g&oacute;c ngắm, lật ngang linh hoạt kiểu mới, chế độ quay phim full-HD c&ugrave;ng với menu trực quan th&acirc;n thiện dễ sử dụng với người d&ugrave;ng.&nbsp;Cảm biến CMOS độ ph&acirc;n giải cao với 16.2 triệu điểm ảnh hiệu quả, định dạng DX c&ugrave;ng với c&ocirc;ng nghệ xử l&yacute; h&igrave;nh ảnh của Nikon cung cấp chất lượng h&igrave;nh ảnh sắc n&eacute;t, sống động v&agrave; trang bị chức năng ảnh Phơi S&aacute;ng Cao (HDR) đời mới</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Nikon D5100 được trang bị c&ugrave;ng một cảm biến ảnh CMOS, định dạng DX v&agrave; c&ocirc;ng nghệ xử l&yacute; h&igrave;nh ảnh EXPEED 2 giống với m&aacute;y ảnh SLR đời cao D7000 được c&ocirc;ng bố gần đ&acirc;y. Những t&iacute;nh năng n&agrave;y cho ph&eacute;p chụp ảnh độ ph&acirc;n giải cao với c&aacute;c dải m&agrave;u sắc mượt m&agrave;, sống động. Được c&agrave;i đặt độ nhạy s&aacute;ng ti&ecirc;u chuẩn ISO 100-6400 v&agrave; c&oacute; thể tăng th&ecirc;m đạt khả năng Hi 2 (tương đương ISO 25600), m&aacute;y ảnh được cải tiến với nhiều chức năng hỗ trợ chụp h&igrave;nh đẹp dưới &aacute;nh s&aacute;ng yếu, như v&agrave;o buổi tối hoặc trong nh&agrave;, v&agrave; bắt chụp h&igrave;nh c&aacute;c đối tượng di chuyển nhanh tốt hơn.&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>T&iacute;nh năng Phơi S&aacute;ng Cao (<strong>HDR</strong>) k&iacute;ch hoạt sẽ chụp 2 bức h&igrave;nh với thời gian phơi s&aacute;ng kh&aacute;c nhau qua thao t&aacute;c với n&uacute;t bấm chụp rồi tự động kết hợp th&agrave;nh một h&igrave;nh ảnh đơn c&ugrave;ng với dải nhạy s&aacute;ng kết hợp rộng. Kết quả l&agrave; h&igrave;nh ảnh được t&aacute;i tạo ch&acirc;n thực như bằng mắt thường, ngay cả với những hoạt cảnh chụp ph&iacute;a sau, hiển thị đầy đủ c&aacute;c chi tiết trong b&oacute;ng tối hoặc nổi bật.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Nikon D5100 + AF-S 18-55mm VR\" src=\"http://zshop.vn/images/dslrnikon/2.jpg\" style=\"height:333px; margin:0px; width:499px\" /></p>\r\n\r\n<p><em>H&igrave;nh ảnh minh họa cho t&iacute;nh năng HDR</em></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>T&Iacute;NH NĂNG NỔI BẬT</strong></p>\r\n\r\n<p><strong>M&agrave;n h&igrave;nh 3-in., 921.000 điểm đa g&oacute;c ngắm, tăng cường khả năng hiển thị</strong></p>\r\n\r\n<p>Khả năng hiển thị ở&nbsp;<a href=\"http://zshop.vn/may-anh-nikon-dslr/\"><strong>Nikon D5100</strong></a>&nbsp;được tăng cường với m&agrave;n h&igrave;nh rộng 3 inch, độ ph&acirc;n giải cao 921.000 điểm v&agrave; hỗ trợ đa g&oacute;c ngắm. Ngo&agrave;i ra m&agrave;u sắc được t&aacute;i tạo trung thực bằng m&agrave;n h&igrave;nh tương đương với c&aacute;c dải gam m&agrave;u sRGB.&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Th&ocirc;ng qua một bản lề cho ph&eacute;p m&agrave;n h&igrave;nh đa g&oacute;c ngắm mở rộng linh hoạt m&agrave; kh&ocirc;ng bị ảnh hưởng tới việc lắp th&ecirc;m ch&acirc;n m&aacute;y. M&agrave;n h&igrave;nh đa g&oacute;c mở 0-180 độ sang b&ecirc;n cạnh, xoay chuyển 90 độ theo chiều kim đồng hồ v&agrave; l&ecirc;n đến 180 độ ngược chiều kim khi n&oacute; được mở rộng. Điều n&agrave;y cho ph&eacute;p chụp h&igrave;nh từ nhiều g&oacute;c độ, từ thấp l&ecirc;n cao, ngay cả việc tự chụp ch&acirc;n dung sẽ hết sức dễ d&agrave;ng.&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Nikon D5100 + AF-S 18-55mm VR\" src=\"http://zshop.vn/images/dslrnikon/3.jpg\" style=\"height:375px; margin:0px; width:500px\" /></p>\r\n\r\n<p><em>M&agrave;n h&igrave;nh đa g&oacute;c ngắm linh hoạt</em></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Chức năng D-Movie cho ph&eacute;p quay v&agrave; chỉnh sửa phim Full-HD</strong></p>\r\n\r\n<p><a href=\"http://zshop.vn/may-anh-nikon-dslr/\"><strong>Nikon&nbsp;D5100</strong></a>&nbsp;trang bị chức năng D-Movie hỗ trợ quay phim độ n&eacute;t cao, chất lượng video cao cấp full HD (1920 x 1080p, 30p).&nbsp;<strong>Nikon D5100</strong>&nbsp;hỗ trợ lấy n&eacute;t tự động AF trong suốt qu&aacute; tr&igrave;nh quay phim. Khi chế độ lấy n&eacute;t tập trung được thiết lập to&agrave;n thời gian full AF-F v&agrave; c&aacute;c chế độ lấy n&eacute;t theo khu vực m&aacute;y ảnh sẽ tự động duy tr&igrave; chế độ lấy n&eacute;t tập trung v&agrave;o một chủ dề duy tr&igrave; qua khung h&igrave;nh. M&aacute;y ảnh cũng cung cấp c&aacute;c chức năng chỉnh sửa phim t&iacute;ch hợp sẵn như cho ph&eacute;p người dung lưu lại một khung h&igrave;nh dưới dạng bức ảnh JPEG hay x&oacute;a đoạn phim kh&ocirc;ng cần thiết mở đầu đầu hoặc kết th&uacute;c phim. Hơn nữa,&nbsp;<strong>Nikon&nbsp;D5100</strong>&nbsp;t&iacute;ch hợp khả năng ghi &acirc;m stereo &acirc;m thanh nổi thong qua cổng ri&ecirc;ng Stereo Microphone ME-1, v&agrave; chế độ gạt xoay t&ugrave;y chọn chế độ ngắm chụp trực tiếp Live-View một c&aacute;ch dễ d&agrave;ng, linh hoạt.&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Nikon D5100 + AF-S 18-55mm VR\" src=\"http://zshop.vn/images/dslrnikon/4.jpg\" style=\"height:378px; margin:0px; width:500px\" /></p>\r\n\r\n<p>Quay gạt t&ugrave;y chọn chế độ Live-View (Lv)<br />\r\n&nbsp;</p>\r\n\r\n<p>T&ugrave;y chọn Stereo Microphone ME-1 gi&uacute;p thu lại &acirc;m thanh nổi stereo trung thực, trong khi cũng gi&uacute;p loại bỏ tiếng ồn do ống k&iacute;nh g&acirc;y ra trong qu&aacute; tr&igrave;nh tự động lấy n&eacute;t.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Nikon D5100 + AF-S 18-55mm VR\" src=\"http://zshop.vn/images/dslrnikon/5.jpg\" style=\"height:422px; margin:0px; width:500px\" /></p>\r\n\r\n<p>Nikon D5100 + AF-S 18-55mm VR</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Lấy n&eacute;t tự động nhanh hơn c&aacute;c đời trước</strong></p>\r\n\r\n<p><strong>Nikon D5100</strong>&nbsp;sử dụng hệ thống lấy n&eacute;t tự động 3D-tracking AF 11, do đ&oacute; c&oacute; khả năng lấy n&eacute;t tự động cực nhanh v&agrave; ch&iacute;nh x&aacute;c cao, ngay cả trong trường hợp chủ thể ảnh đang di chuyển, giảm thiểu tối đa hiện tượng nh&ograve;e mờ v&agrave; cho tốc độ chụp ảnh nhanh ở độ ph&acirc;n giải cao l&ecirc;n đến 4 khung h&igrave;nh/gi&acirc;y. Hỗ trợ đ&egrave;n flash lấy n&eacute;t, Nikon D5100 dễ d&agrave;ng lấy n&eacute;t trong điều kiện thiếu s&aacute;ng, hạn chế hiện tượng đốm ảnh, cho bạn những bức ảnh đẹp ho&agrave;n hảo, mượt m&agrave;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>M&aacute;y ảnh SLR đầu ti&ecirc;n của Nikon cung cấp chế độ Hiệu ứng Đặc biệt</strong></p>\r\n\r\n<p><strong>Nikon D5100</strong>&nbsp;cung cấp c&aacute;c hiệu ứng đặc biệt được lựa chọn v&agrave; &aacute;p dụng cho c&aacute;c h&igrave;nh ảnh tĩnh v&agrave; quay phim. Hiệu ứng đặc biết được hiển thị th&ocirc;ng qua ống k&iacute;nh ngắm l&ecirc;n m&agrave;n h&igrave;nh tước khi bắt đầu chụp hoặc quay phim, cho ph&eacute;p người d&ugrave;ng dễ d&agrave;ng x&aacute;c nhận kết quả mong muốn trong thời gian thực. Người d&ugrave;ng c&oacute; thể dễ d&agrave;ng tận hưởng c&aacute;c hiệu ứng đặc biệt &aacute;p dụng cho phim muốn quay. Chế độ n&agrave;y hỗ trợ n&acirc;ng cao sự s&aacute;ng tạo cho người d&ugrave;ng qua đ&oacute; thể hiện từng phong c&aacute;ch c&aacute; nh&acirc;n ri&ecirc;ng.&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>M&aacute;y ảnh&nbsp;Nikon&nbsp;D5100&nbsp;cung cấp 7 chế độ hiệu ứng đặc biệt, bao gồm c&aacute;c lựa chọn t&ugrave;y chỉnh m&agrave;u sắc.</strong></p>\r\n\r\n<p>C&aacute;c hiệu ứng đặc biệt ưa th&iacute;ch</p>\r\n\r\n<p>Lựa chọn M&agrave;u sắc</p>\r\n\r\n<p>Tất cả m&agrave;u sắc kh&aacute;c với những m&agrave;u được người d&ugrave;ng chọn sẽ được chuyển thể th&agrave;nh đen v&agrave; trắng. Tối đa 3 m&agrave;u sắc c&oacute; thể được lựa chọn c&ugrave;ng thời điểm.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Nikon D5100 + AF-S 18-55mm VR\" src=\"http://zshop.vn/images/dslrnikon/6.jpg\" style=\"height:328px; margin:0px; width:500px\" /></p>\r\n\r\n<p>H&igrave;nh ảnh minh họa Lựa chọn M&agrave;u sắc</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Ph&aacute;c thảo M&agrave;u sắc</strong></p>\r\n\r\n<p>Người d&ugrave;ng c&oacute; thể sử dụng m&agrave;n h&igrave;nh điều chỉnh c&aacute;c th&ocirc;ng số hiển thị về m&agrave;u sắc cho ph&aacute;c thảo để c&oacute; thể h&igrave;nh dung kết quả t&ugrave;y chỉnh trước khi &aacute;p dụng l&ecirc;n ảnh.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Hiệu ứng thu nhỏ</strong></p>\r\n\r\n<p>Một hiệu ứng Diorama &aacute;p dụng l&agrave;m cho c&aacute;c đối tượng như bị thu nhỏ lại. Người d&ugrave;ng c&oacute; thể lựa chọn hướng (ngang/dọc) v&agrave; chiều rộng (hẹp/b&igrave;nh thường/rộng) của khu vực sẽ được tập trung.</p>\r\n\r\n<p><br />\r\n<strong>Hiển thị đ&ecirc;m</strong></p>\r\n\r\n<p>- Khi t&ugrave;y chọn n&agrave;y được k&iacute;ch hoạt, độ nhạy s&aacute;ng rất cao sẽ được &aacute;p dụng (tương đương l&ecirc;n tới ISO 102400). Điều n&agrave;y cho ph&eacute;p chụp ảnh trong điều kiện đ&ecirc;m tối, trong khi vẫn hiển thị chi tiết, r&otilde; n&eacute;t đổi tượng v&agrave; c&aacute;c th&agrave;nh phần xung quanh tr&ecirc;n m&agrave;n h&igrave;nh m&aacute;y ảnh.&nbsp;</p>\r\n\r\n<p>- C&aacute;c T&iacute;nh năng v&agrave; Chức năng nổi bật bổ sung kh&aacute;c</p>\r\n\r\n<p>- Chụp li&ecirc;n tiếp tốc độ cao 4 h&igrave;nh/gi&acirc;y v&agrave; hoạt động m&agrave;n trập bền bỉ v&agrave; rất ch&iacute;nh x&aacute;c đạt chu kỳ thử nghiệm 100.000 lần.</p>\r\n\r\n<p>- Trang bị Chế độ M&agrave;n trập y&ecirc;n lặng cho một số t&igrave;nh huống cần thiết. Điểu khiển kh&ocirc;ng d&acirc;y m&agrave;n trập từ 2 ph&iacute;a trước v&agrave; sau của m&aacute;y ảnh (hỗ trợ ML-L3).</p>\r\n\r\n<p>- Trang bị 16 Chế độ Cảnh chụp t&iacute;ch hợp sẵn cho ph&eacute;p người dụng dễ d&agrave;ng chụp ảnh đẹp một c&aacute;ch nhanh ch&oacute;ng, tối ưu nhất.</p>\r\n\r\n<p>- Khả năng tương th&iacute;ch HDMI-CEC sẵn c&oacute; hỗ trợ điều chỉnh xem lại h&igrave;nh ảnh v&agrave; đoạn phim th&ocirc;ng qua bộ điều khiển từ xa của Ti Vi khi m&aacute;y ảnh kết nối với Ti Vi độ n&eacute;t cao qua cổng HDMI</p>\r\n', '1', '12', '8', '2015-01-09');
INSERT INTO `products` VALUES ('18', 'Nikon D750 (body) (Chính hãng)', '2', '39405938', '<p>Điểm nổi bật của sản phẩm</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>- 2 khe cắm SD</p>\r\n\r\n<p>- M&agrave;n h&igrave;nh RGB 3.2-inch, 1.2 triệu điểm ảnh hỗ trợ lật xoay nghi&ecirc;ng</p>\r\n\r\n<p>- Vỏ thiết kế chống nước, bụi bặm</p>\r\n\r\n<p>- Mới, hệ thống gương mới toanh, cơ cấu m&agrave;n trập cũng mới&nbsp;</p>\r\n\r\n<p>- 51 điểm lấy n&eacute;t</p>\r\n\r\n<p>- Hệ thống đo s&aacute;ng RGB sensor với 91,000 pixels&nbsp;</p>\r\n\r\n<p>- Tốc độ chụp 6.5 frames/s</p>\r\n\r\n<p>- 24.3-megapixel FX-format CMOS sensor</p>\r\n\r\n<p>- Bộ xử l&yacute; ảnh mới nhất EXPEED 4&nbsp;</p>\r\n\r\n<p>- Dải ISO 100-12,800 (mở rộng 50-51200)</p>\r\n', '<h2><strong>Nikon D750 (body)</strong></h2>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>GIỚI THIỆU</strong></p>\r\n\r\n<p><strong>-&nbsp;<a href=\"http://zshop.vn/may-anh-nikon-dslr/\">M&aacute;y ảnh DSLR Nikon</a>&nbsp;D750</strong>&nbsp;của Nikon l&agrave; một m&aacute;y ảnh định dạng FX rất ph&ugrave; hợp với h&igrave;nh ảnh v&agrave; video ghi &acirc;m. Với một cảm biến CMOS 24.3MP, c&ugrave;ng với bộ xử l&yacute; ảnh EXPEED 4, m&aacute;y ảnh n&agrave;y c&oacute; khả năng sản xuất h&igrave;nh ảnh độ ph&acirc;n giải cao với tỷ lệ hao hụt mịn m&agrave;u sắc, tiếng ồn thấp, v&agrave; nhạy cảm với một ti&ecirc;u chuẩn ISO 51200 c&oacute; thể mở rộng, với tốc độ li&ecirc;n tục l&ecirc;n đến 6,5 fps . Về quay video, full HD 1080p / 60 được hỗ trợ, c&ugrave;ng với khả năng quay video kh&ocirc;ng n&eacute;n để một m&aacute;y ghi &acirc;m b&ecirc;n ngo&agrave;i t&ugrave;y chọn. L&agrave;m việc từ g&oacute;c độ cao v&agrave; thấp c&oacute; thể do c&aacute;c 3,2 &quot;m&agrave;n h&igrave;nh LCD nghi&ecirc;ng 1,229k chấm hoặc để chụp ảnh từ xa,&nbsp;<em>Nikon D750</em>&nbsp;cũng c&oacute; t&iacute;nh năng t&iacute;ch hợp kết nối Wi-Fi. Được thiết kế cho c&aacute;c h&igrave;nh ảnh nh&agrave; sản xuất hiện đại, DSLR n&agrave;y đ&atilde; sẵn s&agrave;ng được hưởng lợi vẫn c&ograve;n nhiếp ảnh gia v&agrave; quay phim cũng như với t&iacute;nh linh hoạt v&agrave; hiệu quả để ph&ugrave; hợp với bất kỳ t&igrave;nh huống l&agrave;m việc.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>- Mang lại lợi &iacute;ch cho khả năng chụp ảnh của m&aacute;y ảnh<strong>&nbsp;Nikon D750</strong>, một 51-point AF hệ thống đều apt, với 15 điểm cross-type, c&oacute; sẵn v&agrave; c&oacute; thể được cấu h&igrave;nh để sử dụng năm điểm AF như l&agrave; một điểm tập trung duy nhất trong c&aacute;c thiết lập Group Diện t&iacute;ch AF cho cao tượng đầu ti&ecirc;n c&ocirc;ng nhận. Hệ thống 3D Color Matrix Metering III, c&ugrave;ng với bộ cảm biến RGB 91.000 pixel, cũng c&oacute; lợi cho khả năng tập trung cũng như cung cấp tiếp x&uacute;c đo ch&iacute;nh x&aacute;c để ph&ugrave; hợp với một loạt c&aacute;c đối tượng v&agrave; điều kiện &aacute;nh s&aacute;ng. Hơn nữa, c&oacute; lợi cho c&aacute;c ứng dụng s&aacute;ng tạo,&nbsp;<em>Nikon D750</em>&nbsp;hỗ trợ tạo ra trong m&aacute;y ảnh của bộ phim thời gian tr&ocirc;i đi với tiếp x&uacute;c tự động l&agrave;m mịn, v&agrave; một loạt c&aacute;c hồ sơ Picture Control c&oacute; thể được &aacute;p dụng để tinh chỉnh m&agrave;u sắc v&agrave; xử l&yacute; &acirc;m của h&igrave;nh ảnh.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Nikon D750 (body)\" src=\"http://zshop.vn/images/A/thaiqt/Camera/Nikon/Nikon%20D750/Nikon-D750-body.jpg\" style=\"height:500px; margin:0px; width:500px\" /></p>\r\n\r\n<p>Nikon D750 (body) (Ch&iacute;nh h&atilde;ng)</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>T&Iacute;NH NĂNG NỔI BẬT</strong></p>\r\n\r\n<p><strong>Định dạng FX với cảm biến CMOS v&agrave; xử l&yacute; h&igrave;nh ảnh EXPEED 4 24.3MP&nbsp;</strong></p>\r\n\r\n<p>C&aacute;c bộ cảm biến CMOS 24.3MP định dạng FX hoạt động phối hợp chặt chẽ với bộ xử l&yacute; h&igrave;nh ảnh EXPEED 4 để sản xuất chất lượng h&igrave;nh ảnh phong ph&uacute; nhấn mạnh bởi tiếng ồn thấp v&agrave; độ nhạy cảm đ&aacute;ng ch&uacute; &yacute; đến một ti&ecirc;u chuẩn ISO 12800 bản địa, đ&oacute; l&agrave; tiếp tục mở rộng đến ISO 51200. Nikon D750 c&oacute; cảm biến v&agrave; bộ xử l&yacute; kết hợp th&ecirc;m l&agrave;m việc c&ugrave;ng nhau để nhận ra một sự kh&aacute;c biệt của tốc độ tr&ecirc;n to&agrave;n hệ thống camera, trong đ&oacute; c&oacute; một chức năng với tốc độ chụp li&ecirc;n tục đầy đủ độ ph&acirc;n giải 6,5 fps v&agrave; 1080p / 60 quay video full HD. C&aacute;c cảm biến cũng c&oacute; thể ghi lại h&igrave;nh ảnh ở định dạng DX v&agrave; sẽ tự động chuyển sang v&agrave; hỗ trợ việc sử dụng c&aacute;c ống k&iacute;nh DX-chuy&ecirc;n dụng khi đ&iacute;nh k&egrave;m.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Full HD Video Recording</strong></p>\r\n\r\n<p>Sức mạnh của&nbsp;<strong>D750</strong>&nbsp;với bộ xử l&yacute; EXPEED 4 vượt ra ngo&agrave;i vẫn chụp ảnh thực hiện để mang lại lợi &iacute;ch quay video full HD 1080p ở nhiều tốc độ khung h&igrave;nh l&ecirc;n đến 60 fps. Trong qu&aacute; tr&igrave;nh ghi, đầy đủ điều khiển bằng tay c&oacute; thể qua tốc độ m&agrave;n trập, khẩu độ, v&agrave; c&aacute;c thiết lập ISO, c&ugrave;ng với khả năng sử dụng điều chỉnh ISO tự động trong chế độ bằng tay. Ngựa vằn sọc nổi bật c&oacute; thể được &aacute;p dụng để hỗ trợ trong việc kiểm so&aacute;t tiếp x&uacute;c qu&aacute; mức trong qu&aacute; tr&igrave;nh ghi, cũng vậy, c&ugrave;ng với trong m&aacute;y ảnh hồ sơ Picture Control, đường cong giai điệu t&ugrave;y chỉnh, v&agrave; một hồ sơ căn hộ được hưởng lợi c&aacute;c ứng dụng ph&acirc;n loại m&agrave;u sắc. Ngo&agrave;i ra&nbsp;<em>m&aacute;y ảnh Nikon D750</em>&nbsp;c&oacute; chế độ đa diện t&iacute;ch cho ph&eacute;p bạn thu hẹp lĩnh vực c&oacute; hiệu quả của bạn xem trong qu&aacute; tr&igrave;nh ghi, trong FX v&agrave; DX c&agrave;i đặt, cung cấp quan điểm linh hoạt hơn trong qu&aacute; tr&igrave;nh chụp.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Nikon D750 (body) (Chính hãng)\" src=\"http://zshop.vn/images/A/thaiqt/Camera/Nikon/Nikon%20D750/may-anh-Nikon-D750.jpg\" style=\"height:500px; margin:0px; width:500px\" /></p>\r\n\r\n<p>Nikon D750 (body) (Ch&iacute;nh h&atilde;ng)</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2><strong>M&aacute;y ảnh Nikon D750 DSLR mặt tr&ecirc;n</strong></h2>\r\n\r\n<p>- Ghi v&agrave;o một m&aacute;y ghi &acirc;m b&ecirc;n ngo&agrave;i t&ugrave;y chọn c&oacute; thể để đạt được n&eacute;n 4: 2: 2 8-bit đầu ra th&ocirc;ng qua cổng HDMI, v&agrave; khi ghi b&ecirc;n ngo&agrave;i, sử dụng m&agrave;n h&igrave;nh LCD của m&aacute;y ảnh Nikon D750 để theo d&otilde;i xem trực tiếp l&agrave; c&oacute; thể. Kiểm so&aacute;t quyền lực khẩu độ được hỗ trợ trong qu&aacute; tr&igrave;nh ghi b&ecirc;n ngo&agrave;i, qu&aacute;, cũng như khi ghi v&agrave;o thẻ nhớ trong. Đồng thời ghi &acirc;m cho cả thẻ nhớ v&agrave; một thiết bị ghi &acirc;m b&ecirc;n ngo&agrave;i l&agrave; c&oacute; thể cho sao lưu tức thời v&agrave; sao ch&eacute;p c&aacute;c tập tin, v&agrave; 2MP 16: 9 vẫn c&ograve;n h&igrave;nh ảnh cũng c&oacute; thể được ghi đồng thời trong qu&aacute; tr&igrave;nh ghi video. Trong m&aacute;y ảnh&nbsp;<em>Nikon D750</em>&nbsp;trang tr&iacute; video c&oacute; thể l&agrave; tốt, c&ugrave;ng với khả năng chọn điểm đến tiết kiệm của bộ phim.</p>\r\n\r\n<p>- Đối với ghi &acirc;m, một micro stereo b&ecirc;n trong m&aacute;y ảnh c&oacute; thể được sử dụng hoặc, c&aacute;ch kh&aacute;c, một đầu v&agrave;o c&oacute; sẵn cho việc sử dụng một micro stereo b&ecirc;n ngo&agrave;i t&ugrave;y chọn. Mức độ &acirc;m thanh c&oacute; thể được điều chỉnh qua 20 cấp độ trước v&agrave; trong qu&aacute; tr&igrave;nh ghi, c&ugrave;ng với khả năng theo d&otilde;i &acirc;m thanh th&ocirc;ng qua đầu ra tai nghe.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>51 điểm chi tiết Multi-CAM 3500FX AF Sensor</strong></p>\r\n\r\n<p>- Hỗ trợ khả năng chụp ảnh đặc biệt của&nbsp;<em>Nikon D750</em>&nbsp;l&agrave; một hệ thống tự động lấy n&eacute;t apt m&agrave; sử dụng l&ecirc;n đến 51 điểm kh&aacute;c biệt, với c&aacute;c t&ugrave;y chọn c&oacute; sẵn để sử dụng 9, 21, hoặc tất cả 51 điểm t&ugrave;y thuộc v&agrave;o t&igrave;nh h&igrave;nh. 15 điểm cross-type cũng được t&iacute;ch hợp trong mảng n&agrave;y để cung cấp độ ch&iacute;nh x&aacute;c lớn hơn cho c&aacute;c đối tượng trực thuộc Trung ương nằm. Theo d&otilde;i 3D, một phần của th&ocirc;ng minh Scene Recognition System, c&ograve;n c&oacute; t&aacute;c dụng lợi &iacute;ch duy tr&igrave; tập trung v&agrave;o đối tượng chuyển động cho độ ch&iacute;nh x&aacute;c cao khi chụp ở tốc độ chụp li&ecirc;n tiếp cao.</p>\r\n\r\n<p>- Để nhanh hơn ban đầu tập trung v&agrave; c&ocirc;ng nhận đối tượng, năm bộ cảm biến AF c&oacute; thể được sử dụng c&ugrave;ng nhau như l&agrave; một điểm tập trung duy nhất với c&aacute;c thiết lập Group Diện t&iacute;ch AF. Hơn nữa, Single-point AF, Dynamic AF v&ugrave;ng (với 9, 21, hoặc 51 điểm lựa chọn), chế độ 3D theo d&otilde;i (sử dụng tất cả 51 điểm), v&agrave; AF v&ugrave;ng tự động cũng c&oacute; sẵn.</p>\r\n\r\n<p>- Khi l&agrave;m việc theo quan điểm sống, cho cả hai vẫn hay quay phim, một hệ thống AF tương phản dựa tr&ecirc;n được sử dụng để c&oacute; được tập trung ch&iacute;nh x&aacute;c v&agrave; c&oacute; thể hoạt động ở li&ecirc;n tục, chế độ servo to&agrave;n thời gian để tốt hơn ph&ugrave; hợp với l&agrave;m việc với c&aacute;c đối tượng di chuyển. Bốn chế độ kh&aacute;c nhau AF-khu vực c&oacute; sẵn khi sử dụng độ tương phản động ph&aacute;t hiện phương ph&aacute;p tập trung: ưu ti&ecirc;n khu&ocirc;n mặt AF, diện rộng AF, Normal AF v&ugrave;ng v&agrave; đề-theo d&otilde;i AF.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Scene Recognition System v&agrave; đo phơi s&aacute;ng</strong></p>\r\n\r\n<p>Hệ thống nhận diện khung cảnh th&ocirc;ng minh với 3D Color Matrix Metering III sử dụng một bộ cảm biến RGB 91.000 điểm ảnh để đ&aacute;nh gi&aacute; v&agrave; ph&acirc;n t&iacute;ch tất cả c&aacute;c kh&iacute;a cạnh trong một cảnh, bao gồm độ s&aacute;ng, độ tương phản, khoảng c&aacute;ch chủ đề, v&agrave; m&agrave;u sắc cảnh, nhanh ch&oacute;ng x&aacute;c định một tiếp x&uacute;c ch&iacute;nh x&aacute;c v&agrave; c&acirc;n bằng trắng thiết lập để l&agrave;m tốt nhất những cảnh trong tầm tay. C&aacute;c th&ocirc;ng tin thu thập cũng được kiểm tra đối chiếu h&igrave;nh ảnh tr&ecirc;n m&aacute;y bay để đảm bảo thống nhất từ h&igrave;nh ảnh cho h&igrave;nh ảnh li&ecirc;n quan đến tiếp x&uacute;c, c&acirc;n bằng trắng, c&agrave;i đặt đ&egrave;n flash i-TTL, v&agrave; c&oacute; thể theo d&otilde;i hiệu suất-AF. Ngo&agrave;i ra, mang lại lợi &iacute;ch l&agrave;m việc trong điều kiện &aacute;nh s&aacute;ng kh&oacute; khăn, một thiết lập đo s&aacute;ng điểm nhấn trọng c&oacute; thể được sử dụng để duy tr&igrave; sinh sản nổi bật t&iacute;n hữu.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Thiết kế</strong></p>\r\n\r\n<p>- Một m&agrave;n h&igrave;nh LCD 3.2 &quot;1,229k-dot lớn được đưa v&agrave;o thiết kế kiểu d&aacute;ng đẹp của Nikon D750 , v&agrave; c&oacute; thiết kế nghi&ecirc;ng để mang lại lợi &iacute;ch l&agrave;m việc từ g&oacute;c độ cao v&agrave; thấp. Ngo&agrave;i ra, m&agrave;n h&igrave;nh t&iacute;nh năng một mảng m&agrave;u RGBW để tăng cường khả năng hiển thị trong điều kiện s&aacute;ng, v&agrave; c&oacute; thể c&oacute; m&agrave;u sắc t&ugrave;y chỉnh sự c&acirc;n để ph&ugrave; hợp với m&agrave;n h&igrave;nh b&ecirc;n ngo&agrave;i bổ sung.</p>\r\n\r\n<p>- Một k&iacute;nh ngắm quang học lăng k&iacute;nh năm mặt s&aacute;ng cung cấp bảo hiểm 100% khung h&igrave;nh v&agrave; kết hợp một hệ thống hiển thị EL hữu cơ để hiển thị th&ocirc;ng tin r&otilde; r&agrave;ng chụp li&ecirc;n tục điều chỉnh c&aacute;c thiết lập tr&ecirc;n&nbsp;<em>m&aacute;y ảnh Nikon D750</em>&nbsp;m&agrave; kh&ocirc;ng loại bỏ mắt của bạn từ viewfinder.A được x&acirc;y dựng trong cửa sổ pop-up flash c&oacute; thể được sử dụng để cung cấp th&ecirc;m chiếu s&aacute;ng cảnh v&agrave; cũng c&oacute; thể được sử dụng như một chỉ huy đ&egrave;n flash kh&ocirc;ng d&acirc;y giao tiếp với Nikon Speedlights CLS-tương th&iacute;ch kh&aacute;c.</p>\r\n\r\n<p>- Khe cắm thẻ nhớ SD k&eacute;p c&oacute; sẵn để mở rộng khả năng lưu file bằng c&aacute;ch cho ph&eacute;p hoặc ghi tr&agrave;n hoặc trong m&aacute;y ảnh tập tin nh&acirc;n bản. Ngo&agrave;i ra Nikon D750 c&ograve;n hổ trợ m&aacute;y ghi &acirc;m b&ecirc;n ngo&agrave;i HDMI kết nối được hỗ trợ t&ugrave;y chọn để ghi lại kh&ocirc;ng n&eacute;n video v&agrave; thời gian tự tr&ocirc;i đi.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Mặt trên Nikon D750 (body)\" src=\"http://zshop.vn/images/A/thaiqt/Camera/Nikon/Nikon%20D750/zshop-may-anh-Nikon-D750.jpg\" style=\"height:500px; margin:0px; width:500px\" /></p>\r\n\r\n<p>Nikon D750 (body) (Ch&iacute;nh h&atilde;ng)</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2><strong>Nikon D750 hổ trợ Wi-Fi kết nối tốt</strong></h2>\r\n\r\n<p>- Hỗ trợ tuyệt vời của việc chia sẻ v&agrave; gắn thẻ khả năng, c&aacute;c t&iacute;nh năng tr&ecirc;n&nbsp;<em>Nikon D750</em>&nbsp;t&iacute;ch hợp kết nối Wi-Fi để cho ph&eacute;p chuyển liền mạch của h&igrave;nh ảnh cũng như khả năng chụp ảnh từ xa. Một khi ứng dụng Nikon kh&ocirc;ng d&acirc;y di động tiện &iacute;ch đ&atilde; được c&agrave;i đặt tr&ecirc;n một thiết bị di động Android hoặc iOS, bạn c&oacute; thể sử dụng thiết bị để duyệt c&aacute;c nội dung của thẻ nhớ, chọn h&igrave;nh ảnh, v&agrave; li&ecirc;n tục chuyển c&aacute;c tập tin giữa c&aacute;c thiết bị để chia sẻ ngay lập tức trực tuyến. Ngo&agrave;i ra, c&aacute;c thiết bị di động từ xa c&oacute; thể k&iacute;ch hoạt m&agrave;n trập v&agrave; hiển thị một h&igrave;nh ảnh trực tiếp từ m&aacute;y ảnh xem để cho ph&eacute;p l&agrave;m việc từ xa.</p>\r\n\r\n<p>- Đối với hiệu suất kh&ocirc;ng d&acirc;y nhanh hơn,&nbsp;<em><strong>Nikon D750</strong></em>&nbsp;cũng tương th&iacute;ch với c&aacute;c t&ugrave;y chọn kh&ocirc;ng d&acirc;y WT-5A ph&aacute;t v&agrave; UT-1 đơn vị truyền th&ocirc;ng, m&agrave; c&oacute; thể truyền tải tập tin h&igrave;nh ảnh th&ocirc;ng qua FTP.</p>\r\n\r\n<p>- Kết nối video v&agrave; chụp ảnh khả năng, trong m&aacute;y ảnh chụp thời gian tr&ocirc;i đi l&agrave; c&oacute; thể sử dụng c&aacute;c khoản c&aacute;ch t&iacute;ch hợp để ghi &acirc;m l&ecirc;n đến 9999 độ phơi s&aacute;ng li&ecirc;n tiếp trong khoảng thời gian kh&aacute;c nhau. L&agrave;m mịn tiếp x&uacute;c cũng c&oacute; thể được &aacute;p dụng cho nh&oacute;m c&aacute;c h&igrave;nh ảnh li&ecirc;n tục ghi để sản xuất một thời gian tr&ocirc;i đi tự nhi&ecirc;n xuất hiện trực tiếp từ&nbsp;<em>m&aacute;y ảnh Nikon D750</em>&nbsp;.</p>\r\n\r\n<p>- H&igrave;nh ảnh chuy&ecirc;n dụng Shooting v&agrave; phim chụp viện trợ menu một c&aacute;ch dễ d&agrave;ng truy cập v&agrave;o c&aacute;c thiết lập chuy&ecirc;n dụng v&agrave; điều khiển t&ugrave;y thuộc v&agrave;o m&ocirc;i trường bạn đang l&agrave;m việc</p>\r\n\r\n<p>- Hiệu ứng đặc biệt chế độ c&oacute; thể được &aacute;p dụng cho cả hai vẫn c&ograve;n v&agrave; c&aacute;c tập tin video cho trong m&aacute;y ảnh n&acirc;ng cao s&aacute;ng tạo.</p>\r\n\r\n<p>- Kiểm so&aacute;t độ nhạy ISO tự động cho ph&eacute;p bạn chọn tốc độ m&agrave;n trập tối thiểu m&agrave; m&aacute;y ảnh Nikon D750 bị rung c&oacute; thể được điều khiển từ v&agrave; sau đ&oacute; sẽ tự động thiết lập gi&aacute; trị ISO để l&agrave;m cho tốt nhất hiện trường. Một mức độ nhạy tối đa cũng c&oacute; thể được thiết lập để duy tr&igrave; mức độ tiếng ồn tổng thể.</p>\r\n\r\n<p>- Kiểm điểm c&acirc;n bằng trắng cho xem chụp trực tiếp l&agrave;m cho thiết lập c&acirc;n bằng trắng dễ d&agrave;ng như chỉ v&agrave;o khu vực trong khung đ&oacute; phải l&agrave; m&agrave;u trắng.</p>\r\n\r\n<p><strong>-&nbsp;</strong>Chế độ Picture Control cho ph&eacute;p bạn thiết lập được x&aacute;c định trước để nh&igrave;n h&igrave;nh ảnh trong m&aacute;y ảnh v&agrave; bao gồm cảnh quan, đơn sắc, m&agrave;u trung t&iacute;nh, Ch&acirc;n dung, Standard, Vivid, phẳng, v&agrave; c&aacute;c thiết lập người d&ugrave;ng t&ugrave;y chỉnh.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Nikon D750 (body) (Chính hãng)\" src=\"http://zshop.vn/images/A/thaiqt/Camera/Nikon/Nikon%20D750/zshop1-may-anh-Nikon-D750.jpg\" style=\"height:500px; margin:0px; width:500px\" /></p>\r\n\r\n<p>Nikon D750 (body) (Ch&iacute;nh h&atilde;ng)</p>\r\n', '1', '12', '4', '2015-02-05');
INSERT INTO `products` VALUES ('19', 'Nikon D3100 + kit 18-55 VR (Chính Hãng)', '2', '9300880', '<p>Điểm nổi bật của sản phẩm</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>- Cảm biến ảnh DX CMOS 14.2 Megapixels</p>\r\n\r\n<p>- Bộ xử l&yacute; ảnh EXPEED II, ISO cao nhất tới 12,800</p>\r\n\r\n<p>- Hệ thống đo s&aacute;ng Active D-Lighting thế hệ mới nhất</p>\r\n\r\n<p>- T&iacute;ch hợp hai hệ thống tự động l&agrave;m sạch cảm biến</p>\r\n\r\n<p>- Ống k&iacute;nh k&egrave;m theo: AF-S 18-55 VR</p>\r\n', '<h2>M&aacute;y ảnh Nikon D3100</h2>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>GIỚI THIỆU</strong></p>\r\n\r\n<p>Được xem l&agrave; một trong những chiếc m&aacute;y ảnh số ống k&iacute;nh rời tốt nhất hiện nay,&nbsp;<a href=\"http://zshop.vn/may-anh-nikon-dslr/\"><strong>m&aacute;y ảnh</strong></a>&nbsp;<strong>Nikon D3100</strong>&nbsp;KIT k&egrave;m ống kit 18-55mm sử dụng những c&ocirc;ng nghệ hiện đại v&agrave; th&acirc;n thiện người d&ugrave;ng. Bộ cảm biến CMOS 14.2MP v&agrave; c&ocirc;ng nghệ xử l&yacute; h&igrave;nh ảnh EXPEED C2, khả năng ngắm trực tiếp tr&ecirc;n m&agrave;n h&igrave;nh Live View v&agrave; quay phim Full HD, m&aacute;y ảnh ống k&iacute;nh rời Nikon D3100 KIT sẽ gi&uacute;p bạn chụp ảnh chuy&ecirc;n nghiệp theo một c&aacute;ch ho&agrave;n to&agrave;n đơn giản. B&ecirc;n cạnh đ&oacute;, thiết kế liền khối v&agrave; gọn nhẹ của chiếc m&aacute;y n&agrave;y c&ograve;n đảm bảo t&iacute;nh linh động v&agrave; dễ d&agrave;ng đồng h&agrave;nh c&ugrave;ng bạn tr&ecirc;n mọi nẻo đường &ldquo;t&aacute;c nghiệp&rdquo;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Nikon D3100 + kit 18-55 VR\" src=\"http://zshop.vn/images/detailed/13/Nikon_D3100_2.jpg\" style=\"height:500px; margin:0px; width:500px\" /></p>\r\n\r\n<p>Nikon D3100 + kit 18-55 VR</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>T&Iacute;NH NĂNG NỔI BẬT</strong></p>\r\n\r\n<p><strong>Trang bị cảm biến ảnh CMOS 14.2 megapixels định dạng DX v&agrave; sở hữu c&ocirc;ng nghệ xử l&yacute; h&igrave;nh ảnh EXPEED 2 mới nhất của Nikon, cho chất lượng h&igrave;nh ảnh với độ n&eacute;t vượt trội&nbsp;</strong></p>\r\n\r\n<p><a href=\"http://zshop.vn/may-anh-nikon-dslr/\"><strong>Nikon D3100</strong></a>&nbsp;trang bị cảm biến ảnh CMOS 14.2 megapixels (D3000: cảm biến CCD, 10.2 megapixels), v&agrave; c&ocirc;ng nghệ xử l&yacute; h&igrave;nh ảnh EXPEED 2 mới nhất của Nikon cho chất lượng h&igrave;nh ảnh vượt trội. Hỗ trợ dải nhạy s&aacute;ng ISO cao 100-3200 (ở D3000: ISO 100-1600) v&agrave; c&oacute; thể mở rộng tối đa ở mức Hi 2 (tương đương với ISO 12800), điều n&agrave;y gi&uacute;p m&aacute;y ảnh c&oacute; khả năng chụp h&igrave;nh tốt trong c&aacute;c điều kiện thiếu s&aacute;ng (như cảnh đ&ecirc;m, trong nh&agrave;), hay khi chụp h&igrave;nh thể thao với c&aacute;c đội tượng di chuyển nhanh li&ecirc;n tục.&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Expeed 2 l&agrave; c&ocirc;ng nghệ xử l&yacute; h&igrave;nh ảnh mới nhất ph&aacute;t triển độc quyền bởi Nikon, gi&uacute;p tối ưu hiệu suất hoạt động của bộ cảm biến h&igrave;nh ảnh CMOS, qua đ&oacute; giảm độ nhiễu tối đa v&agrave; t&aacute;i tạo m&agrave;u sắc sống động, trung thực hơn cho h&igrave;nh ảnh v&agrave; đoạn phim.&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Nikon D3100 + kit 18-55 VR\" src=\"http://zshop.vn/images/detailed/13/Nikon_D3100_3.jpg\" style=\"height:500px; margin:0px; width:500px\" /></p>\r\n\r\n<p>Nikon D3100 + kit 18-55 VR</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Chế độ hướng dẫn đổi mới, gi&uacute;p chụp h&igrave;nh đẹp dễ d&agrave;ng&nbsp;</strong></p>\r\n\r\n<p><strong>Nikon D3100</strong>&nbsp;hiển thị c&aacute;c h&igrave;nh ảnh trợ gi&uacute;p người sử dụng h&igrave;nh dung trước kết quả chụp ảnh tương ứng với c&aacute;c c&agrave;i đặt ở m&aacute;y, do vậy họ c&oacute; thể t&ugrave;y chỉnh th&ocirc;ng số, hiệu ứng để chụp c&aacute;c bức ảnh ph&ugrave; hợp với &yacute; định c&aacute; nh&acirc;n. V&iacute; dụ, khi lựa chọn &ldquo;Soften Backgrounds&rdquo; ( hiệu ứng x&oacute;a ph&ocirc;ng) v&agrave; một gi&aacute; trị khẩu độ được x&aacute;c định, một h&igrave;nh ảnh hiển thị gi&uacute;p người sử dụng h&igrave;nh dung trước kết quả thực tế tương ứng với c&aacute;c th&ocirc;ng số đang được chọn, qua đ&oacute; gi&uacute;p chụp h&igrave;nh đẹp, nhanh v&agrave; đ&uacute;ng &yacute;. &nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Chức năng D-Movie hỗ trợ quay phim full HD với chế độ lấy n&eacute;t to&agrave;n thời gian AF-F&nbsp;</strong></p>\r\n\r\n<p><strong>Nikon D3100</strong>&nbsp;trang bị chức năng D-Movie, c&oacute; khả năng quay video chuẩn Full HD 1080p tốc độ 24 khung h&igrave;nh/gi&acirc;y, HD 720p ở tốc độ 30/25/24 khung h&igrave;nh/gi&acirc;y v&agrave; 424p tốc độ 24 khung h&igrave;nh/gi&acirc;y định dạng AVCHD. Khi quay video, m&aacute;y ảnh hỗ trợ chế độ lấy n&eacute;t to&agrave;n thời gian AF-F, tự động ph&aacute;t hiện độ tương phản, lấy n&eacute;t, ph&aacute;t hiện khu&ocirc;n mặt v&agrave; theo d&otilde;i đối tượng li&ecirc;n tục.&nbsp;<br />\r\n&nbsp;</p>\r\n\r\n<p><strong>Tự động lựa chọn cảnh chụp&nbsp;</strong></p>\r\n\r\n<p>M&aacute;y ảnh D3100 được trang bị chức năng tự động lựa chọn cảnh chụp th&ocirc;ng minh. Khi t&iacute;nh năng live-view được k&iacute;ch hoạt trong chế độ chụp tự động hoặc tự động (tắt đ&egrave;n flash), m&aacute;y ảnh sẽ tự động ph&acirc;n t&iacute;ch cảnh v&agrave; lựa chọn chế độ chụp th&iacute;ch hợp từ Ch&acirc;n dung, cho đến Phong cảnh, Cận cảnh hay Ch&acirc;n dung buổi tối * . Chức năng n&agrave;y cho ph&eacute;p người d&ugrave;ng dễ d&agrave;ng c&oacute; được những kết quả h&igrave;nh ảnh chất lượng, đ&uacute;ng với &yacute; muốn khi c&aacute;c thiếp lập được t&iacute;nh to&aacute;n tự động tối ưu nhất với bối cảnh chụp.&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>C&aacute;c chức năng nổi bật kh&aacute;c</strong></p>\r\n\r\n<p>- Lựa chọn giữa 3 chế độ chụp: &quot;Sử dụng k&iacute;nh ngắm,&quot; &quot;Ngắm chụp trực tiếp Live-View,&quot; &quot;Chế độ quay phim&quot;&nbsp;</p>\r\n\r\n<p>- C&aacute;c chức năng chụp cao cấp được c&agrave;i đặt trong Chế độ Hướng dẫn&nbsp;</p>\r\n\r\n<p>- Chế độ ngắm chụp trực tiếp Live view tiện lợi&nbsp;</p>\r\n\r\n<p>- Hỗ trợ chụp v&agrave; quay phim qua chế độ Live-view.&nbsp;</p>\r\n\r\n<p>-&nbsp;Chức năng Tự động lựa chọn cảnh chụp sẽ kh&ocirc;ng hoạt động trong chế độ lấy n&eacute;t bằng tay.&nbsp;- Chế độ nhận diện khu&ocirc;n mặt với dữ liệu l&ecirc;n tới 35 định dạng (trong khi ở D5000: l&agrave; 5 định dạng)&nbsp;</p>\r\n\r\n<p>- Hoạt động cải thiện với c&aacute;c n&uacute;t điều chỉnh tiện lợi giữa c&aacute;c chế độ. Thiết kế Grip cao su thuận tiện.&nbsp;</p>\r\n\r\n<p>- Bộ điều khiển tăng cường (CEC), hỗ trợ khả năng điều khiển từ xa, t&ugrave;y chỉnh xem lại ảnh, đoạn phim khi m&aacute;y ảnh được kết nối với TV.&nbsp;</p>\r\n\r\n<p>&nbsp;- Trang bị thuật to&aacute;n kết xuất cải tiến, cải thiện chất lượng tối đa định dạng NEF (RAW).&nbsp;</p>\r\n\r\n<p>- Phần mềm duyệt ảnh ViewNX 2 mới nhất được đi k&egrave;m với D3100</p>\r\n', '1', '32', '8', '2015-01-12');
INSERT INTO `products` VALUES ('20', 'Sony Alpha A7 Mark II / Body (Chính hãng)', '3', '35990059', '<p>Điểm nổi bật của sản phẩm</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>- Cảm biến CMOS Exmor 24.3MP Full Khung h&igrave;nh&nbsp;</p>\r\n\r\n<p>- Bộ xử l&yacute; h&igrave;nh ảnh BIONZ X</p>\r\n\r\n<p>- Khả năng tương th&iacute;ch trực tiếp với Ống k&iacute;nh E-mount&nbsp;</p>\r\n\r\n<p>- LCD 3.0 &quot;tiltable TFT với 1,228 triệu điểm ảnh</p>\r\n\r\n<p>- K&iacute;nh ngắm điện tử OLED 2.4m-Dot&nbsp;</p>\r\n\r\n<p>- Quay phim: XAVC S 50 Mbps, AVCHD 2.0 28 Mbps, 1.920 x 1.080 pixel</p>\r\n\r\n<p>- Chụp li&ecirc;n tiếp: 2,5 h&igrave;nh/gi&acirc;y đến 5 h&igrave;nh/gi&acirc;y</p>\r\n\r\n<p>- T&iacute;ch hợp Wi-Fi v&agrave; c&ocirc;ng nghệ NFC</p>\r\n\r\n<p>- Đa giao diện v&agrave; Flash gắn trong</p>\r\n\r\n<p>- Giao diện truy cập trực tiếp</p>\r\n', '<h2>Sony Alpha A7 Mark II</h2>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>GIỚI THIỆU</strong></p>\r\n\r\n<p><strong><a href=\"http://zshop.vn/may-anh-kts-sony/\">M&aacute;y ảnh</a>&nbsp;Sony a7 mark II</strong>&nbsp;l&agrave; m&aacute;y ảnh kỹ thuật số kh&ocirc;ng gương lật &nbsp;từ Sony t&iacute;ch hợp một bộ cảm biến CMOS Exmor full-frame. E-mount cung cấp khả năng tập trung &aacute;nh s&aacute;ng v&agrave; chi tiết cho h&igrave;nh ảnh với bộ cảm biến full-frame nhỏ gọn, trọng lượng nhẹ v&agrave; cấu tạo kh&ocirc;ng gương lật linh hoạt, ho&aacute;n đổi ống k&iacute;nh cho nhau. Cảm biến 24.3MP của d&ograve;ng m&aacute;y&nbsp;<strong>Sony a7 II</strong>&nbsp;cho ph&eacute;p h&igrave;nh ảnh c&oacute; độ ph&acirc;n giải cao v&agrave; hiệu quả &aacute;nh s&aacute;ng thấp với ti&ecirc;u chuẩn phạm vi độ nhạy ISO 100-25.600. Bộ vi xử l&yacute; h&igrave;nh ảnh mới BIONX X cung cấp xử l&yacute; nhanh v&agrave; tốc độ hoạt động như 5 fps burst shooting v&agrave; front-end LSI (t&iacute;ch hợp quy m&ocirc; lớn) cải thiện xử l&yacute; h&igrave;nh ảnh, kết quả chi tiết hơn, tự nhi&ecirc;n hơn v&agrave; nhạc phong ph&uacute; hơn.&nbsp;<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"http://zshop.vn/images/A/untitled%20folder/untitled%20folder/a72.jpg\" style=\"height:466px; margin:0px; width:700px\" /></p>\r\n\r\n<p>Sony Alpha A7 II<br />\r\n&nbsp;</p>\r\n\r\n<p>M&aacute;y ảnh<strong>&nbsp;Sony a7 mark II được&nbsp;</strong>tăng cường cải tiến&nbsp;hệ thống lấy n&eacute;t lai Hybrid AF gồm 117 điểm phase detection v&agrave; 25 điểm contrast detection) tương tự nhưng c&oacute; hiệu suất được cải thiện khoảng 30%, cho khả năng đ&aacute;p ứng khi bắt n&eacute;t tốt hơn, độ trễ giảm đi, tối ưu khả năng chụp li&ecirc;n tiếp 2,5 fps (hoặc 5 fps ở chế độ hoạt động cao). Khả năng bắt n&eacute;t theo chủ thể chuyển động được gia tăng 1,5 lần, cải thiện hiệu suất khi lấy n&eacute;t.<br />\r\n&nbsp;</p>\r\n\r\n<p>Với A7 II, hệ thống chống rung 5 trục cao đ&atilde; được đưa v&agrave;o A7 II, n&oacute; tương tự như hệ thống trong m&aacute;y ảnh Olympus OM-D E-M1. Tất nhi&ecirc;n với thiết kế vừa chống rung tr&ecirc;n ống k&iacute;nh, vừa chống rung tr&ecirc;n th&acirc;n m&aacute;y, Sony sẽ tự hiệu chỉnh để ph&ugrave; hợp cho qu&aacute; tr&igrave;nh sử dụng. Mặc định th&igrave; hệ thống chống rung tr&ecirc;n th&acirc;n m&aacute;y A7 II sẽ ph&ugrave; hợp với mọi ống k&iacute;nh ng&agrave;m E-mount (kh&ocirc;ng OSS) v&agrave; A-mount khi gắn qua adapter LA-EA3/LA-EA4. Ống k&iacute;nh từ c&aacute;c h&atilde;ng thứ 3 hay qua ng&agrave;m MF cũng sẽ được hỗ trợ lu&ocirc;n, rất l&agrave; kinh tế.<br />\r\n&nbsp;</p>\r\n\r\n<p>M&aacute;y ảnh<strong>&nbsp;Sony a7 II&nbsp;</strong>&nbsp;được cải tiến th&ecirc;m&nbsp;khả năng quay phim khi hỗ trợ chuẩn XAVC S cho chất lượng 50 Mbps, d&ugrave; độ ph&acirc;n giải vẫn ở mức Full HD 1.920 x 1.080 pixel (v&igrave; Sony c&ograve;n c&oacute; A7S với khả năng quay 4K). Ngo&agrave;i ra, chế độ quay phim bổ sung th&ecirc;m t&iacute;nh năng S-Log2 tăng cường dải tương phản động (dynamic range) v&agrave; S-Gamut tăng cường dải m&agrave;u v&agrave; gamma. Ngo&agrave;i những t&iacute;nh ăng được cải tiến th&igrave; Sony A7 II&nbsp;vẫn giữ những t&iacute;nh năng cũ như quay video full HD 1080 với tốc độ khung h&igrave;nh&nbsp;<em>24p/60i/60p hỗ trợ cả codec AVCHD v&agrave; MP4</em>. Video c&oacute; thể được ghi ra m&aacute;y ảnh dưới dạng kh&ocirc;ng n&eacute;n th&ocirc;ng qua kết nối HDMI, v&agrave; cả hai được x&acirc;y dựng trong v&agrave; b&ecirc;n ngo&agrave;i ghi &acirc;m stereo c&oacute; sẵn. HDMI v&agrave; đầu ra kh&ocirc;ng d&acirc;y cho ph&eacute;p xem c&aacute;c h&igrave;nh ảnh tĩnh tr&ecirc;n truyền h&igrave;nh 4K,&nbsp;<em>&nbsp;m&agrave;n h&igrave;nh LCD si&ecirc;u HD. 3.0 &quot; c&oacute; độ ph&acirc;n giải 1.23M-dot</em>&nbsp;<em>v&agrave; c&ocirc;ng nghệ WhiteMagic</em>&nbsp;cho tầm nh&igrave;n tốt hơn trong &aacute;nh s&aacute;ng. K&iacute;nh ngắm điện tử của m&aacute;y&nbsp;<strong>Sony a7 II</strong>&nbsp;cung cấp một độ tương phản cao 2.4m OLED, cho một g&oacute;c nh&igrave;n rộng, v&agrave; t&iacute;nh năng tương tự hệ thống quang học 3, ống k&iacute;nh được sử dụng trong c&aacute;c m&aacute;y ảnh DSLR Sony A99.&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"http://zshop.vn/images/A/untitled%20folder/untitled%20folder/a722.jpg\" style=\"height:392px; margin:0px; width:700px\" /></p>\r\n\r\n<p><strong>Hệ thống chống rung 5 trục cao đ&atilde; được đưa v&agrave;o A7 II</strong><br />\r\n&nbsp;</p>\r\n\r\n<p>T&iacute;ch hợp Wi-Fi v&agrave; NFC cho ph&eacute;p truyền h&igrave;nh ảnh kh&ocirc;ng d&acirc;y dễ d&agrave;ng cũng như điều khiển chụp từ xa từ điện thoại th&ocirc;ng minh v&agrave; m&aacute;y t&iacute;nh bảng. Điều khiển từ xa th&ocirc;ng qua kết nối USB vẫn cho ph&eacute;p kiểm so&aacute;t điều chỉnh h&igrave;nh ảnh v&agrave; bao gồm kiểm so&aacute;t quay video. Giao diện hệ thống Pro của d&ograve;ng m&aacute;y&nbsp;<strong>Sony a7 II</strong>&nbsp;, c&oacute; thể hiển thị tất cả c&aacute;c điều chỉnh m&aacute;y ảnh tr&ecirc;n m&agrave;n h&igrave;nh LCD để thay đổi thiết lập nhanh ch&oacute;ng khi cần thiết.<br />\r\n&nbsp;</p>\r\n\r\n<p>M&aacute;y ảnh<strong>&nbsp;Sony a7 II&nbsp;</strong>c&oacute; nhiều giao diện ti&ecirc;n tiến mở rộng t&ugrave;y chọn tương th&iacute;ch với đ&egrave;n flash, microphone, v&agrave; c&aacute;c phụ kiện kh&aacute;c, v&agrave; như một m&aacute;y ảnh full-frame E-mount, m&aacute;y&nbsp;<strong>Sony a7 II&nbsp;</strong>tương th&iacute;ch với tất cả c&aacute;c cảm biến&nbsp;<em>APS-C k&iacute;ch thước ống k&iacute;nh E-mount hiện trong APS-C</em>. Chế độ c&acirc;y trồng cũng như ống k&iacute;nh G-series E-mount mới full-frame Zeiss. Bộ điều hợp ống k&iacute;nh với một ch&acirc;n m&aacute;y gắn kết cũng c&oacute; sẵn để sử dụng ống k&iacute;nh A-mount tr&ecirc;n m&aacute;y ảnh E-mount v&agrave; một t&ugrave;y chọn r&atilde;nh pin chuy&ecirc;n dụng cho ph&eacute;p chụp mở rộng v&agrave; ổn định.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"http://zshop.vn/images/A/untitled%20folder/untitled%20folder/a723.jpg\" style=\"height:802px; margin:0px; width:700px\" /></p>\r\n\r\n<p>Sony Alpha A7 II</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>T&Iacute;NH NĂNG NỔI BẬT</strong></p>\r\n\r\n<p><strong>M&aacute;y ảnh kỹ thuật số nhỏ gọn kh&ocirc;ng gương lật</strong></p>\r\n\r\n<p>M&aacute;y ảnh<strong>&nbsp;Sony a7</strong>&nbsp;kết hợp khung h&igrave;nh 35,8 x 23,9 v&agrave;o cảm biến, h&igrave;nh thức nhỏ gọn, nhẹ của một m&aacute;y ảnh kh&ocirc;ng gương lật E-mount cung cấp chất lượng h&igrave;nh ảnh tốt, tiện lợi v&agrave; linh hoạt với thiết kế kh&ocirc;ng gương lật.<br />\r\n&nbsp;</p>\r\n\r\n<p><strong>Bộ cảm biến CMOS Exmor 24.3MP&nbsp;</strong></p>\r\n\r\n<p>Với 24,3 megapixel hiệu quả, c&aacute;c bộ cảm biến CMOS Exmor chụp ảnh c&oacute; độ ph&acirc;n giải cao, tiếng ồn thấp với ph&acirc;n cấp &acirc;m phong ph&uacute; v&agrave; nhạy cảm với &aacute;nh s&aacute;ng. Dải ISO tr&ecirc;n m&aacute;y&nbsp;<strong>Sony a7&nbsp;</strong>&nbsp;l&agrave; 100-25.600.</p>\r\n\r\n<p><br />\r\n<strong>Bộ xử l&yacute; h&igrave;nh ảnh BIONZ X</strong></p>\r\n\r\n<p>C&aacute;c BIONZ X xử l&yacute; h&igrave;nh ảnh mới t&aacute;i tạo kết cấu v&agrave; chi tiết trong thời gian thực th&ocirc;ng qua khả năng xử l&yacute; tốc độ cao. C&ugrave;ng với front-end LSI (t&iacute;ch hợp quy m&ocirc; lớn), xử l&yacute; nhanh hơn, n&oacute; cho ph&eacute;p c&aacute;c chi tiết tự nhi&ecirc;n hơn, h&igrave;nh ảnh thực tế hơn, tỷ lệ hao hụt &acirc;m phong ph&uacute; hơn, v&agrave; tiếng ồn thấp hơn cho d&ugrave; bạn chụp h&igrave;nh ảnh tĩnh hoặc quay phim.</p>\r\n\r\n<p><br />\r\n<strong>Tự động lấy n&eacute;t nhanh</strong></p>\r\n\r\n<p>Tăng cường tự động lấy n&eacute;t nhanh kết hợp tốc độ AF, độ tương phản AF ch&iacute;nh x&aacute;c, đ&atilde; được th&uacute;c đẩy th&ocirc;ng qua một thuật to&aacute;n ph&aacute;t hiện đối tượng kh&ocirc;ng gian mới. AF với 117 điểm lấy n&eacute;t nhanh ch&oacute;ng, di chuyển ống k&iacute;nh để mang chủ đề gần th&agrave;nh t&acirc;m điểm, sau đ&oacute; tương phản AF với AF rộng &nbsp;tinh chỉnh v&ugrave;ng phủ s&oacute;ng ch&iacute;nh x&aacute;c.</p>\r\n\r\n<p><br />\r\n<strong>Chụp li&ecirc;n tục 5 fps&nbsp;</strong></p>\r\n\r\n<p>M&aacute;y<strong>&nbsp;Sony a7&nbsp;</strong>chụp&nbsp;nhanh hơn, theo d&otilde;i AF ch&iacute;nh x&aacute;c hơn, c&oacute; thể thực hiện bằng c&aacute;ch lai nhanh AF cho ph&eacute;p bạn chụp ảnh h&agrave;nh động v&agrave; &quot;ho&agrave;n hảo&quot; với chụp li&ecirc;n tục 5 fps ở tốc độ ưu ti&ecirc;n Chụp Li&ecirc;n Tiếp. Alpha a7 cung cấp một hệ thống tập trung Lai cho ph&eacute;p tập trung nhanh hơn v&agrave; tốc độ khung h&igrave;nh cho c&aacute;c nhiếp ảnh gia.</p>\r\n\r\n<p><br />\r\n<strong>M&agrave;n h&igrave;nh LCD 3.0 &quot;nghi&ecirc;ng&nbsp;</strong></p>\r\n\r\n<p>M&agrave;n h&igrave;nh LCD 3.0 &quot; cung cấp độ ph&acirc;n giải 1229 K-dot&nbsp;v&agrave; l&agrave;m cho n&oacute; dễ d&agrave;ng để chụp ảnh từ nhiều g&oacute;c độ thấp hoặc cao, đưa l&ecirc;n 84 &deg; xuống 45 &deg;. C&ocirc;ng nghệ WhiteMagic tăng đ&aacute;ng kể tầm nh&igrave;n trong &aacute;nh s&aacute;ng ban ng&agrave;y. C&aacute;c m&agrave;n h&igrave;nh lớn mang lại chất lượng h&igrave;nh ảnh tĩnh v&agrave; phim trong khi cho ph&eacute;p hoạt động tập trung dễ d&agrave;ng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"http://zshop.vn/images/A/untitled%20folder/untitled%20folder/a734.jpeg\" style=\"height:512px; margin:0px; width:700px\" /></p>\r\n\r\n<p>Sony Alpha A7 II</p>\r\n\r\n<p><br />\r\n<strong>Khả năng tương th&iacute;ch với ống k&iacute;nh E-mount của Sony v&agrave; ống k&iacute;nh Frame ho&agrave;n to&agrave;n mới.</strong></p>\r\n\r\n<p>Duy tr&igrave; h&igrave;nh thức nhỏ gọn, m&aacute;y&nbsp;Sony a7&nbsp;ho&agrave;n to&agrave;n tương th&iacute;ch với hệ thống ống k&iacute;nh APS-C E-mount hiện tại của Sony v&agrave; c&aacute;c d&ograve;ng mới của E-mount, ống k&iacute;nh full-frame từ ống k&iacute;nh Carl Zeiss v&agrave; ống k&iacute;nh G-series h&agrave;ng đầu của Sony.</p>\r\n\r\n<p><img alt=\"\" src=\"http://zshop.vn/images/A/untitled%20folder/untitled%20folder/2644886_tinhte_Sony_Alpha_A7-II_10.jpg\" style=\"height:531px; margin:0px; width:700px\" /></p>\r\n\r\n<h2>Sony Alpha A7 II</h2>\r\n\r\n<h2>&nbsp;</h2>\r\n\r\n<p><strong>K&iacute;nh ngắm điện tử OLED 2.4m-dot &nbsp;&nbsp;</strong></p>\r\n\r\n<p>Với hệ thống quang học 3 ống k&iacute;nh k&iacute;nh ngắm, hiển thị những g&igrave; sẽ xuất hiện trong video của bạn, bao gồm cả c&aacute;c hiệu ứng c&agrave;i đặt m&aacute;y ảnh của bạn. Bạn sẽ tận hưởng tỷ lệ &acirc;m phong ph&uacute; v&agrave; cải thiện độ tương phản. T&iacute;nh năng cao cấp như độ rộng khu&ocirc;n ảnh 100% v&agrave; g&oacute;c nh&igrave;n rộng cho ph&eacute;p th&agrave;nh phần ngang tầm mắt thoải m&aacute;i v&agrave; ổn định.</p>\r\n\r\n<p><br />\r\n<strong>Quay Phim full HD 24p/60i/60p kh&ocirc;ng n&eacute;n với đầu ra HDMI</strong></p>\r\n\r\n<p>M&aacute;y ảnh<strong>&nbsp;Sony a7&nbsp;</strong>hỗ trợ AVCHD m&atilde; h&oacute;a khung trong 60p si&ecirc;u mịn, 60i ti&ecirc;u chuẩn hoặc 24p điện ảnh. MP4 codec cũng c&oacute; sẵn cho c&aacute;c tập tin nhỏ hơn để tải l&ecirc;n c&aacute;c trang web dễ d&agrave;ng hơn. Ngo&agrave;i ra, n&oacute; c&oacute; thể nắm bắt ho&agrave;n to&agrave;n c&aacute;c file video 1080 HD kh&ocirc;ng n&eacute;n, l&agrave;m sạch m&agrave;n h&igrave;nh với c&aacute;c thiết bị ghi &acirc;m b&ecirc;n ngo&agrave;i th&ocirc;ng qua kết nối HDMI trong 60p 60i.</p>\r\n\r\n<p><br />\r\n<strong>T&iacute;ch hợp Wi-Fi v&agrave; c&ocirc;ng nghệ NFC</strong></p>\r\n\r\n<p>Kết nối với điện thoại th&ocirc;ng minh để chia sẻ h&igrave;nh ảnh. Cảm ứng từ xa đ&atilde; được đơn giản h&oacute;a với kiểm so&aacute;t Wi-Fi/NFC. Ngo&agrave;i việc hỗ trợ Wi-Fi để kết nối với điện thoại th&ocirc;ng minh, m&aacute;y&nbsp;<strong>Sony a7 ii</strong>&nbsp;cũng hỗ trợ c&ocirc;ng nghệ NFC (Near Field Communication) cung cấp chuyển thuận tiện h&igrave;nh ảnh điện thoại th&ocirc;ng minh v&agrave; m&aacute;y t&iacute;nh bảng Android.</p>\r\n\r\n<p>Người d&ugrave;ng chỉ cần chạm v&agrave;o thiết bị để kết nối. Hơn nữa, khi sử dụng điều khiển từ xa th&ocirc;ng minh - một t&iacute;nh năng cho ph&eacute;p chụp được điều khiển bởi một điện thoại th&ocirc;ng minh - kết nối với điện thoại th&ocirc;ng minh c&oacute; thể được thiết lập bằng c&aacute;ch chạm v&agrave;o c&aacute;c thiết bị tương th&iacute;ch.</p>\r\n\r\n<p><br />\r\n<strong>Giao diện truy cập trực tiếp</strong></p>\r\n\r\n<p>Navi Pro hiển thị nhanh ch&oacute;ng tất cả c&aacute;c lựa chọn chụp h&igrave;nh lớn tr&ecirc;n m&agrave;n h&igrave;nh LCD, do đ&oacute; bạn c&oacute; thể nhanh ch&oacute;ng x&aacute;c nhận thiết lập v&agrave; điều chỉnh m&agrave; kh&ocirc;ng cần t&igrave;m kiếm th&ocirc;ng qua c&aacute;c menu chuy&ecirc;n dụng. Khi c&oacute; cơ hội chụp ph&aacute;t sinh, bạn sẽ c&oacute; thể đ&aacute;p ứng nhanh ch&oacute;ng.</p>\r\n\r\n<p><br />\r\n<strong>Mắt kiểm so&aacute;t AF mới</strong></p>\r\n\r\n<p>Ngay cả khi chụp một chủ đề quay lưng lại với m&aacute;y ảnh với độ s&acirc;u n&ocirc;ng của trường, khu&ocirc;n mặt sẽ được tập trung mạnh nhờ ph&aacute;t hiện mắt cực kỳ ch&iacute;nh x&aacute;c. Một khung m&agrave;u xanh l&aacute; c&acirc;y xuất hiện tr&ecirc;n mắt ưu ti&ecirc;n tập trung khi đ&atilde; đạt được để x&aacute;c nhận dễ d&agrave;ng. Mắt AF c&oacute; thể được sử dụng khi c&aacute;c chức năng được g&aacute;n cho một n&uacute;t t&ugrave;y biến, cho ph&eacute;p người d&ugrave;ng ngay lập tức k&iacute;ch hoạt n&oacute; phụ thuộc v&agrave;o cảnh.</p>\r\n\r\n<p><br />\r\n<strong>D&acirc;y điều khiển từ xa với kiểm so&aacute;t Video Capture</strong></p>\r\n\r\n<p>Điều khiển camera từ xa cho ph&eacute;p bạn điều khiển m&aacute;y&nbsp;<strong>Sony a7 ii&nbsp;</strong>từ m&aacute;y t&iacute;nh của bạn sử dụng c&aacute;p USB. Điều n&agrave;y đ&atilde; được cập nhật để bao gồm kiểm so&aacute;t quay video.</p>\r\n\r\n<p><br />\r\n<strong>Đa giao diện</strong></p>\r\n\r\n<p>C&aacute;c t&iacute;nh năng của&nbsp;<strong>Sony a7 ii&nbsp;</strong>c&oacute; nhiều giao diện ti&ecirc;n tiến, mở rộng đ&aacute;ng kể khả năng tương th&iacute;ch với c&aacute;c phụ kiện kỹ thuật số Sony như đ&egrave;n flash, micro, đ&egrave;n chiếu s&aacute;ng, gi&aacute;m s&aacute;t v&agrave; do đ&oacute; l&agrave;m tăng khả năng chụp ảnh v&agrave; quay phim.</p>\r\n', '1', '32', '3', '2015-01-04');
INSERT INTO `products` VALUES ('21', 'Sony A9 (chính hãng)	', '3', '3242343', '<p>Điểm nổi bật của sản phẩm</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>- Đang cập nhật th&ocirc;ng tin</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n</ul>\r\n', '<p><strong>Sony A9</strong></p>\r\n\r\n<p>Th&ocirc;ng tin đang cập nhật</p>\r\n\r\n<p><img alt=\"\" src=\"http://zshop.vn/images/A/untitled%20folder/untitled%20folder/a9.jpg\" style=\"height:475px; margin:0px; width:640px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"http://zshop.vn/images/A/untitled%20folder/untitled%20folder/a92.png\" style=\"height:553px; margin:0px; width:680px\" /></p>\r\n\r\n<ul>\r\n</ul>\r\n', '1', '23', '5', '2014-12-30');
INSERT INTO `products` VALUES ('22', 'Sony Alpha A77 Mark II (body) (Chính hãng)', '3', '23800800', '<p>Điểm nổi bật của sản phẩm</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>- Ng&agrave;m: A-mount</p>\r\n\r\n<p>- Cảm biến: CMOS Exmor 24.3 MP</p>\r\n\r\n<p>- Bộ chuyển đổi analog/digital: 14-bit</p>\r\n\r\n<p>- M&agrave;n h&igrave;nh: LCD 3&quot; độ ph&acirc;n giải 1,228,000 điểm ảnh, c&ocirc;ng nghệ WhiteMagic</p>\r\n\r\n<p>- K&iacute;nh ngắm: EVF OLED Tru-Finder 2,359 triệu điểm ảnh</p>\r\n\r\n<p>- Tốc độ m&agrave;n trập: 1/8000 gi&acirc;y</p>\r\n\r\n<p>- ISO: 100 - 25.600, mở rộng 50, hoặc 51.200 trong Multi Frame NR</p>\r\n\r\n<p>- Lấy n&eacute;t: 79 điểm theo pha (Phase-detection), 15 điểm cross-type</p>\r\n\r\n<p>- Quay phim: Full HD 1.920 x 1.080 pixel @ 50p/25p (PAL) 60p/24p (NTSC) AVCHD 2.0</p>\r\n\r\n<ul>\r\n</ul>\r\n', '<h2><strong>Sony A77 Mark II</strong></h2>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>GIỚI THIỆU</strong></p>\r\n\r\n<p>Gần 3 năm sau khi giới thiệu A77, Sony vừa c&ocirc;ng bố phi&ecirc;n bản&nbsp;<strong><a href=\"http://zshop.vn/may-anh-sony-dslr/\">m&aacute;y ảnh Sony</a>&nbsp;A77 II</strong>&nbsp;thế hệ tiếp theo với thiết kế th&acirc;n m&aacute;y được giữ nguy&ecirc;n nhưng được n&acirc;ng cấp về phần cứng rất đ&aacute;ng ch&uacute; &yacute;. Cũng như mẫu mirrorless &alpha;6000, chiếc m&aacute;y n&agrave;y sở hữu cảm biến ảnh Exmor 24.3 MP c&ugrave;ng bộ xử l&yacute; h&igrave;nh ảnh BIONZ X, bộ chuyển t&iacute;n hiệu A/D 14-bit v&agrave; dải ISO hữu dụng l&ecirc;n đến 25.600. Sony cũng tăng cường hệ thống lấy n&eacute;t theo pha mới c&oacute; 79 điểm tuỳ chọn bao gồm 15 điểm cross-type v&agrave; khung lấy n&eacute;t chiếm đến 40%, một diện t&iacute;ch kh&aacute; lớn so với c&aacute;c m&aacute;y DSLR c&ugrave;ng d&ograve;ng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Sony Alpha A77 Mark II\" src=\"http://zshop.vn/images/sony/untitled%20folder/2477988_tinhte_A77_II_LifeStyle_wMen_ExpOct2015_NFC-01.jpg\" style=\"height:333px; margin:0px; width:500px\" /></p>\r\n\r\n<p>Sony Alpha A77 Mark II</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>T&Iacute;NH NĂNG NỔI BẬT</strong></p>\r\n\r\n<p>Sony &alpha;77 II sở hữu bộ cảm biến CMOS Exmor 24.3 MP với cấu tr&uacute;c gapless on-chip lens đ&atilde; c&oacute; mặt trong c&aacute;c mẫu m&aacute;y ảnh cao cấp gần đ&acirc;y như &alpha;7/&alpha;7R v&agrave; &alpha;6000. C&ocirc;ng nghệ n&ecirc;u tr&ecirc;n gi&uacute;p cho tất cả c&aacute;c điểm ảnh n&acirc;ng cao hiệu quả thu thập &aacute;nh s&aacute;ng v&agrave;o cảm biến. Nhờ vậy độ nhạy s&aacute;ng sẽ được cải thiện v&agrave; giảm thiểu độ nhiễu ở mức ISO cao, đồng thời tối ưu được hiệu suất của những mẫu ống k&iacute;nh chất lượng cao. Bộ xử l&yacute; h&igrave;nh ảnh BIONZ X với thuật to&aacute;n t&aacute;i tạo chi tiết (Detail Reproduction Technology), c&ocirc;ng nghệ n&agrave;y hứa hẹn sẽ đem đến những bức ảnh r&otilde; r&agrave;ng hơn, chi tiết tốt hơn. Ngo&agrave;i ra n&oacute; cũng c&oacute; khả năng giảm nhiễu xạ do ống k&iacute;nh g&acirc;y ra. C&ocirc;ng nghệ xử l&yacute; nhiễu theo v&ugrave;ng ảnh ri&ecirc;ng biệt &aacute;p dụng cho Alpha A99 cũng được &aacute;p dụng v&agrave;o trong sản phẩm n&agrave;y.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Sony Alpha A77 Mark II\" src=\"http://zshop.vn/images/sony/untitled%20folder/2477987_tinhte_A77_II_w18135_front.jpg\" style=\"height:333px; margin:0px; width:500px\" /></p>\r\n\r\n<p>Sony Alpha A77 Mark II</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>C&oacute; cơ chế lấy n&eacute;t tương tự như &alpha;77 trước đ&oacute;, Sony cải tiến hệ thống mới tr&ecirc;n &alpha;77 II nhờ việc trang bị đến 79 điểm lấy n&eacute;t theo cơ chế nhận diện theo pha (phase-detection), trong đ&oacute; bao gồm 15 điểm lấy n&eacute;t cross-type. Điểm trung t&acirc;m được tối ưu cho ống k&iacute;nh c&oacute; khẩu độ l&ecirc;n đến F2.8 nhằm tối ưu tốc độ khi lấy n&eacute;t. Hệ thống AF cũng hoạt động tốt trong m&ocirc;i trường &aacute;nh s&aacute;ng yếu, kho&aacute; n&eacute;t đối tượng trong cảnh quay với mức độ chiếu s&aacute;ng thấp dưới mức -2 EV (ISO 100). Ngo&agrave;i những t&iacute;nh năng lấy n&eacute;t vốn c&oacute;, &alpha;77 II cũng hỗ trợ lấy n&eacute;t nhận diện theo mắt như &alpha;7/&alpha;7R hay &alpha;6000, điểm lấy n&eacute;t được nhận diện một c&aacute;ch ch&iacute;nh x&aacute;c, hữu &iacute;ch cho việc chụp ch&acirc;n dung.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Sony Alpha A77 Mark II\" src=\"http://zshop.vn/images/sony/untitled%20folder/2477986_tinhte_A77_II_wSAL85F14Z_right.jpg\" style=\"height:333px; margin:0px; width:500px\" /></p>\r\n\r\n<p>Sony Alpha A77 Mark II</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>M&aacute;y vẫn được thiết kế với bộ khung ma-gi&ecirc;, lớp vỏ polycarbonate chắc chắn bảo vệ b&ecirc;n ngo&agrave;i gi&uacute;p m&aacute;y nhẹ hơn, chịu được độ b&agrave;o m&ograve;n cao khi sử dụng trong thời gian d&agrave;i, đồng thời sử dụng được dưới nhiều thời tiết khắc nghiệt như mưa gi&oacute;, c&aacute;t bụi. &alpha;77 II giữ lại m&agrave;n h&igrave;nh LCD 3&quot; được tối ưu nhờ c&ocirc;ng nghệ hiển thị WhiteMagic, 2 khớp nổi để thay đổi đa dạng g&oacute;c sử dụng m&aacute;y.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Sony Alpha A77 Mark II\" src=\"http://zshop.vn/images/sony/untitled%20folder/2477983_tinhte_A77_II_SAL1650_tilt_lcd_1.jpg\" style=\"height:333px; margin:0px; width:500px\" /></p>\r\n\r\n<p>Sony Alpha A77 Mark II&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>M&aacute;y hỗ trợ quay phim Full HD chất lượng 50p/60p hoặc cinema 24p/25p định dạng AVCHD 2.0, hỗ trợ t&iacute;nh năng Zebra để kiểm so&aacute;t v&ugrave;ng s&aacute;ng, 3 mức lấy n&eacute;t theo chuyển động ph&ugrave; hợp với khả năng sử dụng khi quay phim, thanh trạng th&aacute;i &acirc;m thanh để kiểm so&aacute;t tiếng động được thu từ micro. &alpha;77 II cũng hỗ trợ Wi-Fi v&agrave; NFC để kết nối điện thoại, m&aacute;y t&iacute;nh (Windows 8.1) hoặc m&aacute;y t&iacute;nh bảng (Android/iOS) để chia sẻ h&igrave;nh ảnh, cũng như sử dụng c&aacute;c thiết bị di động để l&agrave;m m&agrave;n h&igrave;nh điều khiển từ xa.</p>\r\n\r\n<ul>\r\n</ul>\r\n', '1', '22', '7', '2015-01-07');
INSERT INTO `products` VALUES ('23', 'Sony Alpha A7 / Body', '3', '26986900', '<p>Điểm nổi bật của sản phẩm</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>- Cảm biến CMOS Exmor 24.3MP Full Khung h&igrave;nh&nbsp;</p>\r\n\r\n<p>- Bộ xử l&yacute; h&igrave;nh ảnh BIONZ X</p>\r\n\r\n<p>- Khả năng tương th&iacute;ch trực tiếp với Ống k&iacute;nh E-mount&nbsp;</p>\r\n\r\n<p>- LCD 3.0 &quot;tiltable TFT với 1,229 K-Dots</p>\r\n\r\n<p>- K&iacute;nh ngắm điện tử OLED 2.4m-Dot&nbsp;</p>\r\n\r\n<p>- Quay video full HD 1080/60p với đầu ra kh&ocirc;ng n&eacute;n</p>\r\n\r\n<p>- Tự động lấy n&eacute;t nhanh, 5 fps Burst Rate</p>\r\n\r\n<p>- T&iacute;ch hợp Wi-Fi v&agrave; c&ocirc;ng nghệ NFC</p>\r\n\r\n<p>- Đa giao diện v&agrave; Flash gắn trong</p>\r\n\r\n<p>- Giao diện truy cập trực tiếp</p>\r\n\r\n<ul>\r\n</ul>\r\n', '<h2>Sony Alpha A7 / Body</h2>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>GIỚI THIỆU</strong></p>\r\n\r\n<p><strong><a href=\"http://zshop.vn/may-anh-kts-sony/\">M&aacute;y ảnh</a>&nbsp;Sony a7</strong>&nbsp;l&agrave; m&aacute;y ảnh kỹ thuật số kh&ocirc;ng gương lật &nbsp;từ Sony t&iacute;ch hợp một bộ cảm biến CMOS Exmor full-frame. E-mount cung cấp khả năng tập trung &aacute;nh s&aacute;ng v&agrave; chi tiết cho h&igrave;nh ảnh với bộ cảm biến full-frame nhỏ gọn, trọng lượng nhẹ v&agrave; cấu tạo kh&ocirc;ng gương lật linh hoạt, ho&aacute;n đổi ống k&iacute;nh cho nhau. Cảm biến 24.3MP của d&ograve;ng m&aacute;y&nbsp;<strong>Sony a7</strong>&nbsp;cho ph&eacute;p h&igrave;nh ảnh c&oacute; độ ph&acirc;n giải cao v&agrave; hiệu quả &aacute;nh s&aacute;ng thấp với ti&ecirc;u chuẩn phạm vi độ nhạy ISO 100-25.600. Bộ vi xử l&yacute; h&igrave;nh ảnh mới BIONX X cung cấp xử l&yacute; nhanh v&agrave; tốc độ hoạt động như 5 fps burst shooting v&agrave; front-end LSI (t&iacute;ch hợp quy m&ocirc; lớn) cải thiện xử l&yacute; h&igrave;nh ảnh, kết quả chi tiết hơn, tự nhi&ecirc;n hơn v&agrave; nhạc phong ph&uacute; hơn.&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Sony Alpha A7 / Body\" src=\"http://zshop.vn/images/detailed/31/Sony_A7.jpg\" style=\"height:500px; margin:0px; width:500px\" /></p>\r\n\r\n<p>Sony Alpha A7 / Body</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>M&aacute;y ảnh<strong>&nbsp;Sony a7&nbsp;</strong>tăng cường hệ thống tự động lấy n&eacute;t lai kết hợp 117 điểm, hệ thống AF với một hệ thống tương phản ph&aacute;t hiện 25 điểm cho tốc độ cao v&agrave; ch&iacute;nh x&aacute;c. Vị tr&iacute; khu vực ngo&agrave;i tập trung đa điểm linh hoạt, trọng t&acirc;m, khu vực tập trung đ&oacute;ng g&oacute;p v&agrave;o chức năng của m&aacute;y ảnh&nbsp;<strong>Sony a7</strong>&nbsp;v&agrave; hỗ trợ lấy n&eacute;t bằng tay c&oacute; thể được tối ưu h&oacute;a cho c&aacute;c khu chụp full-frame hoặc định dạng APS-C. Mắt kiểm so&aacute;t AF mới cung cấp sự ch&iacute;nh x&aacute;c cao, đặt trọng t&acirc;m trực tiếp v&agrave;o mắt của đối tượng, ngay cả khi họ đang quay một phần đi.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>M&aacute;y ảnh<strong>&nbsp;Sony a7&nbsp;</strong>quay video full HD 1080 với tốc độ khung h&igrave;nh&nbsp;<em>24p/60i/60p hỗ trợ cả codec AVCHD v&agrave; MP4</em>. Video c&oacute; thể được ghi ra m&aacute;y ảnh dưới dạng kh&ocirc;ng n&eacute;n th&ocirc;ng qua kết nối HDMI, v&agrave; cả hai được x&acirc;y dựng trong v&agrave; b&ecirc;n ngo&agrave;i ghi &acirc;m stereo c&oacute; sẵn. HDMI v&agrave; đầu ra kh&ocirc;ng d&acirc;y cho ph&eacute;p xem c&aacute;c h&igrave;nh ảnh tĩnh tr&ecirc;n truyền h&igrave;nh 4K,&nbsp;<em>&nbsp;m&agrave;n h&igrave;nh LCD si&ecirc;u HD. 3.0 &quot; c&oacute; độ ph&acirc;n giải 1.23M-dot</em>&nbsp;<em>v&agrave; c&ocirc;ng nghệ WhiteMagic</em>&nbsp;cho tầm nh&igrave;n tốt hơn trong &aacute;nh s&aacute;ng. K&iacute;nh ngắm điện tử của m&aacute;y&nbsp;<strong>Sony a7</strong>&nbsp;cung cấp một độ tương phản cao 2.4m OLED, cho một g&oacute;c nh&igrave;n rộng, v&agrave; t&iacute;nh năng tương tự hệ thống quang học 3, ống k&iacute;nh được sử dụng trong c&aacute;c m&aacute;y ảnh DSLR Sony A99.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Sony Alpha A7 / Body\" src=\"http://zshop.vn/images/detailed/31/Sony_A7_6.jpg\" style=\"height:330px; margin:0px; width:375px\" /></p>\r\n\r\n<p>Sony Alpha A7 / Body</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>T&iacute;ch hợp Wi-Fi v&agrave; NFC cho ph&eacute;p truyền h&igrave;nh ảnh kh&ocirc;ng d&acirc;y dễ d&agrave;ng cũng như điều khiển chụp từ xa từ điện thoại th&ocirc;ng minh v&agrave; m&aacute;y t&iacute;nh bảng. Điều khiển từ xa th&ocirc;ng qua kết nối USB vẫn cho ph&eacute;p kiểm so&aacute;t điều chỉnh h&igrave;nh ảnh v&agrave; bao gồm kiểm so&aacute;t quay video. Giao diện hệ thống Pro của d&ograve;ng m&aacute;y&nbsp;<strong>Sony a7</strong>&nbsp;, c&oacute; thể hiển thị tất cả c&aacute;c điều chỉnh m&aacute;y ảnh tr&ecirc;n m&agrave;n h&igrave;nh LCD để thay đổi thiết lập nhanh ch&oacute;ng khi cần thiết.&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>M&aacute;y ảnh<strong>&nbsp;Sony a7&nbsp;</strong>c&oacute; nhiều giao diện ti&ecirc;n tiến mở rộng t&ugrave;y chọn tương th&iacute;ch với đ&egrave;n flash, microphone, v&agrave; c&aacute;c phụ kiện kh&aacute;c, v&agrave; như một m&aacute;y ảnh full-frame E-mount, m&aacute;y&nbsp;<strong>Sony a7&nbsp;&nbsp;</strong>tương th&iacute;ch với tất cả c&aacute;c cảm biến&nbsp;<em>APS-C k&iacute;ch thước ống k&iacute;nh E-mount hiện trong APS-C</em>. Chế độ c&acirc;y trồng cũng như ống k&iacute;nh G-series E-mount mới full-frame Zeiss. Bộ điều hợp ống k&iacute;nh với một ch&acirc;n m&aacute;y gắn kết cũng c&oacute; sẵn để sử dụng ống k&iacute;nh A-mount tr&ecirc;n m&aacute;y ảnh E-mount v&agrave; một t&ugrave;y chọn r&atilde;nh pin chuy&ecirc;n dụng cho ph&eacute;p chụp mở rộng v&agrave; ổn định.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Sony Alpha A7 / Body\" src=\"http://zshop.vn/images/detailed/31/Sony_A7_1.jpg\" style=\"height:500px; margin:0px; width:500px\" /></p>\r\n\r\n<p>Sony Alpha A7 / Body</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>T&Iacute;NH NĂNG NỔI BẬT</strong></p>\r\n\r\n<p><strong>M&aacute;y ảnh kỹ thuật số nhỏ gọn kh&ocirc;ng gương lật</strong></p>\r\n\r\n<p>M&aacute;y ảnh<strong>&nbsp;Sony a7</strong>&nbsp;kết hợp khung h&igrave;nh 35,8 x 23,9 v&agrave;o cảm biến, h&igrave;nh thức nhỏ gọn, nhẹ của một m&aacute;y ảnh kh&ocirc;ng gương lật E-mount cung cấp chất lượng h&igrave;nh ảnh tốt, tiện lợi v&agrave; linh hoạt với thiết kế kh&ocirc;ng gương lật.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Bộ cảm biến CMOS Exmor 24.3MP&nbsp;</strong></p>\r\n\r\n<p>Với 24,3 megapixel hiệu quả, c&aacute;c bộ cảm biến CMOS Exmor chụp ảnh c&oacute; độ ph&acirc;n giải cao, tiếng ồn thấp với ph&acirc;n cấp &acirc;m phong ph&uacute; v&agrave; nhạy cảm với &aacute;nh s&aacute;ng. Dải ISO tr&ecirc;n m&aacute;y&nbsp;<strong>Sony a7&nbsp;</strong>&nbsp;l&agrave; 100-25.600.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Bộ xử l&yacute; h&igrave;nh ảnh BIONZ X</strong></p>\r\n\r\n<p>C&aacute;c BIONZ X xử l&yacute; h&igrave;nh ảnh mới t&aacute;i tạo kết cấu v&agrave; chi tiết trong thời gian thực th&ocirc;ng qua khả năng xử l&yacute; tốc độ cao. C&ugrave;ng với front-end LSI (t&iacute;ch hợp quy m&ocirc; lớn), xử l&yacute; nhanh hơn, n&oacute; cho ph&eacute;p c&aacute;c chi tiết tự nhi&ecirc;n hơn, h&igrave;nh ảnh thực tế hơn, tỷ lệ hao hụt &acirc;m phong ph&uacute; hơn, v&agrave; tiếng ồn thấp hơn cho d&ugrave; bạn chụp h&igrave;nh ảnh tĩnh hoặc quay phim.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Tự động lấy n&eacute;t nhanh</strong></p>\r\n\r\n<p>Tăng cường tự động lấy n&eacute;t nhanh kết hợp tốc độ AF, độ tương phản AF ch&iacute;nh x&aacute;c, đ&atilde; được th&uacute;c đẩy th&ocirc;ng qua một thuật to&aacute;n ph&aacute;t hiện đối tượng kh&ocirc;ng gian mới. AF với 117 điểm lấy n&eacute;t nhanh ch&oacute;ng, di chuyển ống k&iacute;nh để mang chủ đề gần th&agrave;nh t&acirc;m điểm, sau đ&oacute; tương phản AF với AF rộng &nbsp;tinh chỉnh v&ugrave;ng phủ s&oacute;ng ch&iacute;nh x&aacute;c.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Chụp li&ecirc;n tục 5 fps&nbsp;</strong></p>\r\n\r\n<p>M&aacute;y<strong>&nbsp;Sony a7&nbsp;</strong>chụp&nbsp;nhanh hơn, theo d&otilde;i AF ch&iacute;nh x&aacute;c hơn, c&oacute; thể thực hiện bằng c&aacute;ch lai nhanh AF cho ph&eacute;p bạn chụp ảnh h&agrave;nh động v&agrave; &quot;ho&agrave;n hảo&quot; với chụp li&ecirc;n tục 5 fps ở tốc độ ưu ti&ecirc;n Chụp Li&ecirc;n Tiếp. Alpha a7 cung cấp một hệ thống tập trung Lai cho ph&eacute;p tập trung nhanh hơn v&agrave; tốc độ khung h&igrave;nh cho c&aacute;c nhiếp ảnh gia.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Sony Alpha A7 / Body\" src=\"http://zshop.vn/images/detailed/31/Sony_A7_5.jpg\" style=\"height:500px; margin:0px; width:500px\" /></p>\r\n\r\n<p>Sony Alpha A7 / Body</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Khả năng tương th&iacute;ch với ống k&iacute;nh E-mount của Sony v&agrave; ống k&iacute;nh Frame ho&agrave;n to&agrave;n mới.</strong></p>\r\n\r\n<p>Duy tr&igrave; h&igrave;nh thức nhỏ gọn, m&aacute;y&nbsp;Sony a7&nbsp;ho&agrave;n to&agrave;n tương th&iacute;ch với hệ thống ống k&iacute;nh APS-C E-mount hiện tại của Sony v&agrave; c&aacute;c d&ograve;ng mới của E-mount, ống k&iacute;nh full-frame từ ống k&iacute;nh Carl Zeiss v&agrave; ống k&iacute;nh G-series h&agrave;ng đầu của Sony.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>M&agrave;n h&igrave;nh LCD 3.0 &quot;nghi&ecirc;ng&nbsp;</strong></p>\r\n\r\n<p><em>LCD 3.0 &quot; cung cấp độ ph&acirc;n giải 1229 K-dot&nbsp;</em>v&agrave; l&agrave;m cho n&oacute; dễ d&agrave;ng để chụp ảnh từ nhiều g&oacute;c độ thấp hoặc cao, đưa l&ecirc;n 84 &deg; xuống 45 &deg;. C&ocirc;ng nghệ WhiteMagic tăng đ&aacute;ng kể tầm nh&igrave;n trong &aacute;nh s&aacute;ng ban ng&agrave;y. C&aacute;c m&agrave;n h&igrave;nh lớn mang lại chất lượng h&igrave;nh ảnh tĩnh v&agrave; phim trong khi cho ph&eacute;p hoạt động tập trung dễ d&agrave;ng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Sony Alpha A7 / Body\" src=\"http://zshop.vn/images/detailed/31/Sony_A7_2.jpg\" style=\"height:500px; margin:0px; width:500px\" /></p>\r\n\r\n<p>Sony Alpha A7 / Body</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>K&iacute;nh ngắm điện tử OLED 2.4m-dot &nbsp;&nbsp;</strong></p>\r\n\r\n<p>Với hệ thống quang học 3 ống k&iacute;nh k&iacute;nh ngắm, hiển thị những g&igrave; sẽ xuất hiện trong video của bạn, bao gồm cả c&aacute;c hiệu ứng c&agrave;i đặt m&aacute;y ảnh của bạn. Bạn sẽ tận hưởng tỷ lệ &acirc;m phong ph&uacute; v&agrave; cải thiện độ tương phản. T&iacute;nh năng cao cấp như độ rộng khu&ocirc;n ảnh 100% v&agrave; g&oacute;c nh&igrave;n rộng cho ph&eacute;p th&agrave;nh phần ngang tầm mắt thoải m&aacute;i v&agrave; ổn định.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Quay Phim full HD 24p/60i/60p kh&ocirc;ng n&eacute;n với đầu ra HDMI</strong></p>\r\n\r\n<p>M&aacute;y ảnh<strong>&nbsp;Sony a7&nbsp;</strong>hỗ trợ AVCHD m&atilde; h&oacute;a khung trong 60p si&ecirc;u mịn, 60i ti&ecirc;u chuẩn hoặc 24p điện ảnh. MP4 codec cũng c&oacute; sẵn cho c&aacute;c tập tin nhỏ hơn để tải l&ecirc;n c&aacute;c trang web dễ d&agrave;ng hơn. Ngo&agrave;i ra, n&oacute; c&oacute; thể nắm bắt ho&agrave;n to&agrave;n c&aacute;c file video 1080 HD kh&ocirc;ng n&eacute;n, l&agrave;m sạch m&agrave;n h&igrave;nh với c&aacute;c thiết bị ghi &acirc;m b&ecirc;n ngo&agrave;i th&ocirc;ng qua kết nối HDMI trong 60p 60i.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>T&iacute;ch hợp Wi-Fi v&agrave; c&ocirc;ng nghệ NFC</strong></p>\r\n\r\n<p>Kết nối với điện thoại th&ocirc;ng minh để chia sẻ h&igrave;nh ảnh. Cảm ứng từ xa đ&atilde; được đơn giản h&oacute;a với kiểm so&aacute;t Wi-Fi/NFC. Ngo&agrave;i việc hỗ trợ Wi-Fi để kết nối với điện thoại th&ocirc;ng minh, m&aacute;y&nbsp;<strong>Sony a7</strong>&nbsp;cũng hỗ trợ c&ocirc;ng nghệ NFC (Near Field Communication) cung cấp chuyển thuận tiện h&igrave;nh ảnh điện thoại th&ocirc;ng minh v&agrave; m&aacute;y t&iacute;nh bảng Android.</p>\r\n\r\n<p>Người d&ugrave;ng chỉ cần chạm v&agrave;o thiết bị để kết nối. Hơn nữa, khi sử dụng điều khiển từ xa th&ocirc;ng minh - một t&iacute;nh năng cho ph&eacute;p chụp được điều khiển bởi một điện thoại th&ocirc;ng minh - kết nối với điện thoại th&ocirc;ng minh c&oacute; thể được thiết lập bằng c&aacute;ch chạm v&agrave;o c&aacute;c thiết bị tương th&iacute;ch.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Giao diện truy cập trực tiếp</strong></p>\r\n\r\n<p>Navi Pro hiển thị nhanh ch&oacute;ng tất cả c&aacute;c lựa chọn chụp h&igrave;nh lớn tr&ecirc;n m&agrave;n h&igrave;nh LCD, do đ&oacute; bạn c&oacute; thể nhanh ch&oacute;ng x&aacute;c nhận thiết lập v&agrave; điều chỉnh m&agrave; kh&ocirc;ng cần t&igrave;m kiếm th&ocirc;ng qua c&aacute;c menu chuy&ecirc;n dụng. Khi c&oacute; cơ hội chụp ph&aacute;t sinh, bạn sẽ c&oacute; thể đ&aacute;p ứng nhanh ch&oacute;ng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Mắt kiểm so&aacute;t AF mới</strong></p>\r\n\r\n<p>Ngay cả khi chụp một chủ đề quay lưng lại với m&aacute;y ảnh với độ s&acirc;u n&ocirc;ng của trường, khu&ocirc;n mặt sẽ được tập trung mạnh nhờ ph&aacute;t hiện mắt cực kỳ ch&iacute;nh x&aacute;c. Một khung m&agrave;u xanh l&aacute; c&acirc;y xuất hiện tr&ecirc;n mắt ưu ti&ecirc;n tập trung khi đ&atilde; đạt được để x&aacute;c nhận dễ d&agrave;ng. Mắt AF c&oacute; thể được sử dụng khi c&aacute;c chức năng được g&aacute;n cho một n&uacute;t t&ugrave;y biến, cho ph&eacute;p người d&ugrave;ng ngay lập tức k&iacute;ch hoạt n&oacute; phụ thuộc v&agrave;o cảnh.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Đầu ra RAW 14-bit</strong></p>\r\n\r\n<p>Dữ liệu h&igrave;nh ảnh RAW 14-bit chất lượng rất cao được đưa ra bởi m&aacute;y ảnh&nbsp;<strong>Sony a7&nbsp;</strong>. Dữ liệu n&agrave;y lưu giữ c&aacute;c chi tiết phong ph&uacute; được tạo ra bởi c&aacute;c bộ cảm biến h&igrave;nh ảnh trong 14 bit qu&aacute; tr&igrave;nh chuyển đổi. Khi ph&aacute;t triển với dữ liệu ảnh RAW, những h&igrave;nh ảnh n&agrave;y cung cấp việc chụp ảnh chất lượng đặc biệt cao v&agrave; độ ph&acirc;n cấp cao.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>D&acirc;y điều khiển từ xa với kiểm so&aacute;t Video Capture</strong></p>\r\n\r\n<p>Điều khiển camera từ xa cho ph&eacute;p bạn điều khiển m&aacute;y&nbsp;<strong>Sony a7&nbsp;</strong>từ m&aacute;y t&iacute;nh của bạn sử dụng c&aacute;p USB. Điều n&agrave;y đ&atilde; được cập nhật để bao gồm kiểm so&aacute;t quay video.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Đa giao diện</strong></p>\r\n\r\n<p>C&aacute;c t&iacute;nh năng của&nbsp;<strong>Sony a7&nbsp;</strong>c&oacute; nhiều giao diện ti&ecirc;n tiến, mở rộng đ&aacute;ng kể khả năng tương th&iacute;ch với c&aacute;c phụ kiện kỹ thuật số Sony như đ&egrave;n flash, micro, đ&egrave;n chiếu s&aacute;ng, gi&aacute;m s&aacute;t v&agrave; do đ&oacute; l&agrave;m tăng khả năng chụp ảnh v&agrave; quay phim.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>T&iacute;nh năng ch&iacute;nh của Sony A7/A7R</p>\r\n\r\n<p>- A7: 24.3MP Full Frame Exmor CMOS Sensor / A7R: 36MP kh&ocirc;ng c&oacute; bộ lọc khử răng cưa - Cảm biến thế hệ mới nhất của Sony</p>\r\n\r\n<p>- BIONZ X Image Processor - Bộ xử l&yacute; h&igrave;nh ảnh mới nhất</p>\r\n\r\n<p>- Direct Compatibility with E-mount Lenses - Tương th&iacute;ch với tất cả E-mount lens, c&aacute;c lens trước đ&acirc;y sẽ c&oacute; 2 mode lựa chọn: crop hoặc kh&ocirc;ng crop bị tối g&oacute;c.</p>\r\n\r\n<p>- 3.0&quot; Tiltable TFT LCD with 1,229K-Dots</p>\r\n\r\n<p>- 2.4M-Dot OLED Electronic Viewfinder</p>\r\n\r\n<p>- Full 1080/60p with Uncompressed Output</p>\r\n\r\n<p>- A7: Fast Hybrid Autofocus; 5 fps Burst Rate / A7R kh&ocirc;ng hỗ trợ Fast Hybrid Autofocus, 4 fps</p>\r\n\r\n<p>- Tốc độ chụp: 1/8000s-30s</p>\r\n\r\n<p>- Built-In Wi-Fi and NFC</p>\r\n\r\n<p>- Multi-Interface Shoe and Built-In Flash</p>\r\n\r\n<p>- Direct Access Interface</p>\r\n\r\n<p>- ISO 50-25600&nbsp;</p>\r\n\r\n<p>- Khả năng chống bụi, ẩm tr&ecirc;n body: c&oacute;</p>\r\n\r\n<p>- Dung lượng pin: NP-FW50, chụp khoảng 340 shots</p>\r\n\r\n<p>- Trọng lượng: A7: 474 g / A7R: 465 g</p>\r\n\r\n<p>- Body thiết kế c&oacute; 2 b&aacute;nh xe chỉnh th&ocirc;ng số mặt trước v&agrave; sau. Đồng thời c&oacute; 3 n&uacute;t C1, C2, C3 t&ugrave;y biến d&agrave;nh cho người d&ugrave;ng. C&oacute; v&ograve;ng chỉnh EV b&ugrave; trừ s&aacute;ng</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Gi&aacute; một số lens ra mắt d&agrave;nh cho Sony A7, A7R:</p>\r\n\r\n<p>- Sony Vario-Tessar T* FE 24-70mm f/4 ZA OSS: $1198</p>\r\n\r\n<p>- Sony Sonnar T* FE 35mm f/2.8 ZA: $798</p>\r\n\r\n<p>- Sony Sonnar T* FE 55mm f/1.8 ZA : $998</p>\r\n\r\n<p>- Sony G 70-200 f/4 OSS: chưa r&otilde; gi&aacute;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Gi&aacute; phụ kiện cho Sony A7/A7R:</p>\r\n\r\n<p>- Sony Vertical Battery Grip for Alpha a7 or a7R: $298</p>\r\n\r\n<p>- Sony A-Mount to E-Mount Lens Adapter: kh&ocirc;ng c&oacute; c&ocirc;ng nghệ Translucent Mirror: chỉ AF với lens SSM/SAM: $198</p>\r\n\r\n<p>- Sony A-Mount to E-Mount Lens Adapter with Translucent Mirror Technology: AF với tất cả lens A-mount: $348</p>\r\n\r\n<ul>\r\n</ul>\r\n', '1', '12', '4', '2015-01-22');
INSERT INTO `products` VALUES ('24', 'Sony Alpha A7 Mark II / Body (Chính hãng)', '3', '35990059', '<p>Điểm nổi bật của sản phẩm</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>- Cảm biến CMOS Exmor 24.3MP Full Khung h&igrave;nh&nbsp;</p>\r\n\r\n<p>- Bộ xử l&yacute; h&igrave;nh ảnh BIONZ X</p>\r\n\r\n<p>- Khả năng tương th&iacute;ch trực tiếp với Ống k&iacute;nh E-mount&nbsp;</p>\r\n\r\n<p>- LCD 3.0 &quot;tiltable TFT với 1,228 triệu điểm ảnh</p>\r\n\r\n<p>- K&iacute;nh ngắm điện tử OLED 2.4m-Dot&nbsp;</p>\r\n\r\n<p>- Quay phim: XAVC S 50 Mbps, AVCHD 2.0 28 Mbps, 1.920 x 1.080 pixel</p>\r\n\r\n<p>- Chụp li&ecirc;n tiếp: 2,5 h&igrave;nh/gi&acirc;y đến 5 h&igrave;nh/gi&acirc;y</p>\r\n\r\n<p>- T&iacute;ch hợp Wi-Fi v&agrave; c&ocirc;ng nghệ NFC</p>\r\n\r\n<p>- Đa giao diện v&agrave; Flash gắn trong</p>\r\n\r\n<p>- Giao diện truy cập trực tiếp</p>\r\n', '<p><strong>GIỚI THIỆU</strong></p>\r\n\r\n<p><strong><a href=\"http://zshop.vn/may-anh-kts-sony/\">M&aacute;y ảnh</a>&nbsp;Sony a7 mark II</strong>&nbsp;l&agrave; m&aacute;y ảnh kỹ thuật số kh&ocirc;ng gương lật &nbsp;từ Sony t&iacute;ch hợp một bộ cảm biến CMOS Exmor full-frame. E-mount cung cấp khả năng tập trung &aacute;nh s&aacute;ng v&agrave; chi tiết cho h&igrave;nh ảnh với bộ cảm biến full-frame nhỏ gọn, trọng lượng nhẹ v&agrave; cấu tạo kh&ocirc;ng gương lật linh hoạt, ho&aacute;n đổi ống k&iacute;nh cho nhau. Cảm biến 24.3MP của d&ograve;ng m&aacute;y&nbsp;<strong>Sony a7 II</strong>&nbsp;cho ph&eacute;p h&igrave;nh ảnh c&oacute; độ ph&acirc;n giải cao v&agrave; hiệu quả &aacute;nh s&aacute;ng thấp với ti&ecirc;u chuẩn phạm vi độ nhạy ISO 100-25.600. Bộ vi xử l&yacute; h&igrave;nh ảnh mới BIONX X cung cấp xử l&yacute; nhanh v&agrave; tốc độ hoạt động như 5 fps burst shooting v&agrave; front-end LSI (t&iacute;ch hợp quy m&ocirc; lớn) cải thiện xử l&yacute; h&igrave;nh ảnh, kết quả chi tiết hơn, tự nhi&ecirc;n hơn v&agrave; nhạc phong ph&uacute; hơn.&nbsp;<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"http://zshop.vn/images/A/untitled%20folder/untitled%20folder/a72.jpg\" style=\"height:466px; margin:0px; width:700px\" /></p>\r\n\r\n<p>Sony Alpha A7 II<br />\r\n&nbsp;</p>\r\n\r\n<p>M&aacute;y ảnh<strong>&nbsp;Sony a7 mark II được&nbsp;</strong>tăng cường cải tiến&nbsp;hệ thống lấy n&eacute;t lai Hybrid AF gồm 117 điểm phase detection v&agrave; 25 điểm contrast detection) tương tự nhưng c&oacute; hiệu suất được cải thiện khoảng 30%, cho khả năng đ&aacute;p ứng khi bắt n&eacute;t tốt hơn, độ trễ giảm đi, tối ưu khả năng chụp li&ecirc;n tiếp 2,5 fps (hoặc 5 fps ở chế độ hoạt động cao). Khả năng bắt n&eacute;t theo chủ thể chuyển động được gia tăng 1,5 lần, cải thiện hiệu suất khi lấy n&eacute;t.<br />\r\n&nbsp;</p>\r\n\r\n<p>Với A7 II, hệ thống chống rung 5 trục cao đ&atilde; được đưa v&agrave;o A7 II, n&oacute; tương tự như hệ thống trong m&aacute;y ảnh Olympus OM-D E-M1. Tất nhi&ecirc;n với thiết kế vừa chống rung tr&ecirc;n ống k&iacute;nh, vừa chống rung tr&ecirc;n th&acirc;n m&aacute;y, Sony sẽ tự hiệu chỉnh để ph&ugrave; hợp cho qu&aacute; tr&igrave;nh sử dụng. Mặc định th&igrave; hệ thống chống rung tr&ecirc;n th&acirc;n m&aacute;y A7 II sẽ ph&ugrave; hợp với mọi ống k&iacute;nh ng&agrave;m E-mount (kh&ocirc;ng OSS) v&agrave; A-mount khi gắn qua adapter LA-EA3/LA-EA4. Ống k&iacute;nh từ c&aacute;c h&atilde;ng thứ 3 hay qua ng&agrave;m MF cũng sẽ được hỗ trợ lu&ocirc;n, rất l&agrave; kinh tế.<br />\r\n&nbsp;</p>\r\n\r\n<p>M&aacute;y ảnh<strong>&nbsp;Sony a7 II&nbsp;</strong>&nbsp;được cải tiến th&ecirc;m&nbsp;khả năng quay phim khi hỗ trợ chuẩn XAVC S cho chất lượng 50 Mbps, d&ugrave; độ ph&acirc;n giải vẫn ở mức Full HD 1.920 x 1.080 pixel (v&igrave; Sony c&ograve;n c&oacute; A7S với khả năng quay 4K). Ngo&agrave;i ra, chế độ quay phim bổ sung th&ecirc;m t&iacute;nh năng S-Log2 tăng cường dải tương phản động (dynamic range) v&agrave; S-Gamut tăng cường dải m&agrave;u v&agrave; gamma. Ngo&agrave;i những t&iacute;nh ăng được cải tiến th&igrave; Sony A7 II&nbsp;vẫn giữ những t&iacute;nh năng cũ như quay video full HD 1080 với tốc độ khung h&igrave;nh&nbsp;<em>24p/60i/60p hỗ trợ cả codec AVCHD v&agrave; MP4</em>. Video c&oacute; thể được ghi ra m&aacute;y ảnh dưới dạng kh&ocirc;ng n&eacute;n th&ocirc;ng qua kết nối HDMI, v&agrave; cả hai được x&acirc;y dựng trong v&agrave; b&ecirc;n ngo&agrave;i ghi &acirc;m stereo c&oacute; sẵn. HDMI v&agrave; đầu ra kh&ocirc;ng d&acirc;y cho ph&eacute;p xem c&aacute;c h&igrave;nh ảnh tĩnh tr&ecirc;n truyền h&igrave;nh 4K,&nbsp;<em>&nbsp;m&agrave;n h&igrave;nh LCD si&ecirc;u HD. 3.0 &quot; c&oacute; độ ph&acirc;n giải 1.23M-dot</em>&nbsp;<em>v&agrave; c&ocirc;ng nghệ WhiteMagic</em>&nbsp;cho tầm nh&igrave;n tốt hơn trong &aacute;nh s&aacute;ng. K&iacute;nh ngắm điện tử của m&aacute;y&nbsp;<strong>Sony a7 II</strong>&nbsp;cung cấp một độ tương phản cao 2.4m OLED, cho một g&oacute;c nh&igrave;n rộng, v&agrave; t&iacute;nh năng tương tự hệ thống quang học 3, ống k&iacute;nh được sử dụng trong c&aacute;c m&aacute;y ảnh DSLR Sony A99.&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"http://zshop.vn/images/A/untitled%20folder/untitled%20folder/a722.jpg\" style=\"height:392px; margin:0px; width:700px\" /></p>\r\n\r\n<p><strong>Hệ thống chống rung 5 trục cao đ&atilde; được đưa v&agrave;o A7 II</strong><br />\r\n&nbsp;</p>\r\n\r\n<p>T&iacute;ch hợp Wi-Fi v&agrave; NFC cho ph&eacute;p truyền h&igrave;nh ảnh kh&ocirc;ng d&acirc;y dễ d&agrave;ng cũng như điều khiển chụp từ xa từ điện thoại th&ocirc;ng minh v&agrave; m&aacute;y t&iacute;nh bảng. Điều khiển từ xa th&ocirc;ng qua kết nối USB vẫn cho ph&eacute;p kiểm so&aacute;t điều chỉnh h&igrave;nh ảnh v&agrave; bao gồm kiểm so&aacute;t quay video. Giao diện hệ thống Pro của d&ograve;ng m&aacute;y&nbsp;<strong>Sony a7 II</strong>&nbsp;, c&oacute; thể hiển thị tất cả c&aacute;c điều chỉnh m&aacute;y ảnh tr&ecirc;n m&agrave;n h&igrave;nh LCD để thay đổi thiết lập nhanh ch&oacute;ng khi cần thiết.<br />\r\n&nbsp;</p>\r\n\r\n<p>M&aacute;y ảnh<strong>&nbsp;Sony a7 II&nbsp;</strong>c&oacute; nhiều giao diện ti&ecirc;n tiến mở rộng t&ugrave;y chọn tương th&iacute;ch với đ&egrave;n flash, microphone, v&agrave; c&aacute;c phụ kiện kh&aacute;c, v&agrave; như một m&aacute;y ảnh full-frame E-mount, m&aacute;y&nbsp;<strong>Sony a7 II&nbsp;</strong>tương th&iacute;ch với tất cả c&aacute;c cảm biến&nbsp;<em>APS-C k&iacute;ch thước ống k&iacute;nh E-mount hiện trong APS-C</em>. Chế độ c&acirc;y trồng cũng như ống k&iacute;nh G-series E-mount mới full-frame Zeiss. Bộ điều hợp ống k&iacute;nh với một ch&acirc;n m&aacute;y gắn kết cũng c&oacute; sẵn để sử dụng ống k&iacute;nh A-mount tr&ecirc;n m&aacute;y ảnh E-mount v&agrave; một t&ugrave;y chọn r&atilde;nh pin chuy&ecirc;n dụng cho ph&eacute;p chụp mở rộng v&agrave; ổn định.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"http://zshop.vn/images/A/untitled%20folder/untitled%20folder/a723.jpg\" style=\"height:802px; margin:0px; width:700px\" /></p>\r\n\r\n<p>Sony Alpha A7 II</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>T&Iacute;NH NĂNG NỔI BẬT</strong></p>\r\n\r\n<p><strong>M&aacute;y ảnh kỹ thuật số nhỏ gọn kh&ocirc;ng gương lật</strong></p>\r\n\r\n<p>M&aacute;y ảnh<strong>&nbsp;Sony a7</strong>&nbsp;kết hợp khung h&igrave;nh 35,8 x 23,9 v&agrave;o cảm biến, h&igrave;nh thức nhỏ gọn, nhẹ của một m&aacute;y ảnh kh&ocirc;ng gương lật E-mount cung cấp chất lượng h&igrave;nh ảnh tốt, tiện lợi v&agrave; linh hoạt với thiết kế kh&ocirc;ng gương lật.<br />\r\n&nbsp;</p>\r\n\r\n<p><strong>Bộ cảm biến CMOS Exmor 24.3MP&nbsp;</strong></p>\r\n\r\n<p>Với 24,3 megapixel hiệu quả, c&aacute;c bộ cảm biến CMOS Exmor chụp ảnh c&oacute; độ ph&acirc;n giải cao, tiếng ồn thấp với ph&acirc;n cấp &acirc;m phong ph&uacute; v&agrave; nhạy cảm với &aacute;nh s&aacute;ng. Dải ISO tr&ecirc;n m&aacute;y&nbsp;<strong>Sony a7&nbsp;</strong>&nbsp;l&agrave; 100-25.600.</p>\r\n\r\n<p><br />\r\n<strong>Bộ xử l&yacute; h&igrave;nh ảnh BIONZ X</strong></p>\r\n\r\n<p>C&aacute;c BIONZ X xử l&yacute; h&igrave;nh ảnh mới t&aacute;i tạo kết cấu v&agrave; chi tiết trong thời gian thực th&ocirc;ng qua khả năng xử l&yacute; tốc độ cao. C&ugrave;ng với front-end LSI (t&iacute;ch hợp quy m&ocirc; lớn), xử l&yacute; nhanh hơn, n&oacute; cho ph&eacute;p c&aacute;c chi tiết tự nhi&ecirc;n hơn, h&igrave;nh ảnh thực tế hơn, tỷ lệ hao hụt &acirc;m phong ph&uacute; hơn, v&agrave; tiếng ồn thấp hơn cho d&ugrave; bạn chụp h&igrave;nh ảnh tĩnh hoặc quay phim.</p>\r\n\r\n<p><br />\r\n<strong>Tự động lấy n&eacute;t nhanh</strong></p>\r\n\r\n<p>Tăng cường tự động lấy n&eacute;t nhanh kết hợp tốc độ AF, độ tương phản AF ch&iacute;nh x&aacute;c, đ&atilde; được th&uacute;c đẩy th&ocirc;ng qua một thuật to&aacute;n ph&aacute;t hiện đối tượng kh&ocirc;ng gian mới. AF với 117 điểm lấy n&eacute;t nhanh ch&oacute;ng, di chuyển ống k&iacute;nh để mang chủ đề gần th&agrave;nh t&acirc;m điểm, sau đ&oacute; tương phản AF với AF rộng &nbsp;tinh chỉnh v&ugrave;ng phủ s&oacute;ng ch&iacute;nh x&aacute;c.</p>\r\n\r\n<p><br />\r\n<strong>Chụp li&ecirc;n tục 5 fps&nbsp;</strong></p>\r\n\r\n<p>M&aacute;y<strong>&nbsp;Sony a7&nbsp;</strong>chụp&nbsp;nhanh hơn, theo d&otilde;i AF ch&iacute;nh x&aacute;c hơn, c&oacute; thể thực hiện bằng c&aacute;ch lai nhanh AF cho ph&eacute;p bạn chụp ảnh h&agrave;nh động v&agrave; &quot;ho&agrave;n hảo&quot; với chụp li&ecirc;n tục 5 fps ở tốc độ ưu ti&ecirc;n Chụp Li&ecirc;n Tiếp. Alpha a7 cung cấp một hệ thống tập trung Lai cho ph&eacute;p tập trung nhanh hơn v&agrave; tốc độ khung h&igrave;nh cho c&aacute;c nhiếp ảnh gia.</p>\r\n\r\n<p><br />\r\n<strong>M&agrave;n h&igrave;nh LCD 3.0 &quot;nghi&ecirc;ng&nbsp;</strong></p>\r\n\r\n<p>M&agrave;n h&igrave;nh LCD 3.0 &quot; cung cấp độ ph&acirc;n giải 1229 K-dot&nbsp;v&agrave; l&agrave;m cho n&oacute; dễ d&agrave;ng để chụp ảnh từ nhiều g&oacute;c độ thấp hoặc cao, đưa l&ecirc;n 84 &deg; xuống 45 &deg;. C&ocirc;ng nghệ WhiteMagic tăng đ&aacute;ng kể tầm nh&igrave;n trong &aacute;nh s&aacute;ng ban ng&agrave;y. C&aacute;c m&agrave;n h&igrave;nh lớn mang lại chất lượng h&igrave;nh ảnh tĩnh v&agrave; phim trong khi cho ph&eacute;p hoạt động tập trung dễ d&agrave;ng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"http://zshop.vn/images/A/untitled%20folder/untitled%20folder/a734.jpeg\" style=\"height:512px; margin:0px; width:700px\" /></p>\r\n\r\n<p>Sony Alpha A7 II</p>\r\n\r\n<p><br />\r\n<strong>Khả năng tương th&iacute;ch với ống k&iacute;nh E-mount của Sony v&agrave; ống k&iacute;nh Frame ho&agrave;n to&agrave;n mới.</strong></p>\r\n\r\n<p>Duy tr&igrave; h&igrave;nh thức nhỏ gọn, m&aacute;y&nbsp;Sony a7&nbsp;ho&agrave;n to&agrave;n tương th&iacute;ch với hệ thống ống k&iacute;nh APS-C E-mount hiện tại của Sony v&agrave; c&aacute;c d&ograve;ng mới của E-mount, ống k&iacute;nh full-frame từ ống k&iacute;nh Carl Zeiss v&agrave; ống k&iacute;nh G-series h&agrave;ng đầu của Sony.</p>\r\n\r\n<p><img alt=\"\" src=\"http://zshop.vn/images/A/untitled%20folder/untitled%20folder/2644886_tinhte_Sony_Alpha_A7-II_10.jpg\" style=\"height:531px; margin:0px; width:700px\" /></p>\r\n\r\n<h2>Sony Alpha A7 II</h2>\r\n\r\n<h2>&nbsp;</h2>\r\n\r\n<p><strong>K&iacute;nh ngắm điện tử OLED 2.4m-dot &nbsp;&nbsp;</strong></p>\r\n\r\n<p>Với hệ thống quang học 3 ống k&iacute;nh k&iacute;nh ngắm, hiển thị những g&igrave; sẽ xuất hiện trong video của bạn, bao gồm cả c&aacute;c hiệu ứng c&agrave;i đặt m&aacute;y ảnh của bạn. Bạn sẽ tận hưởng tỷ lệ &acirc;m phong ph&uacute; v&agrave; cải thiện độ tương phản. T&iacute;nh năng cao cấp như độ rộng khu&ocirc;n ảnh 100% v&agrave; g&oacute;c nh&igrave;n rộng cho ph&eacute;p th&agrave;nh phần ngang tầm mắt thoải m&aacute;i v&agrave; ổn định.</p>\r\n\r\n<p><br />\r\n<strong>Quay Phim full HD 24p/60i/60p kh&ocirc;ng n&eacute;n với đầu ra HDMI</strong></p>\r\n\r\n<p>M&aacute;y ảnh<strong>&nbsp;Sony a7&nbsp;</strong>hỗ trợ AVCHD m&atilde; h&oacute;a khung trong 60p si&ecirc;u mịn, 60i ti&ecirc;u chuẩn hoặc 24p điện ảnh. MP4 codec cũng c&oacute; sẵn cho c&aacute;c tập tin nhỏ hơn để tải l&ecirc;n c&aacute;c trang web dễ d&agrave;ng hơn. Ngo&agrave;i ra, n&oacute; c&oacute; thể nắm bắt ho&agrave;n to&agrave;n c&aacute;c file video 1080 HD kh&ocirc;ng n&eacute;n, l&agrave;m sạch m&agrave;n h&igrave;nh với c&aacute;c thiết bị ghi &acirc;m b&ecirc;n ngo&agrave;i th&ocirc;ng qua kết nối HDMI trong 60p 60i.</p>\r\n\r\n<p><br />\r\n<strong>T&iacute;ch hợp Wi-Fi v&agrave; c&ocirc;ng nghệ NFC</strong></p>\r\n\r\n<p>Kết nối với điện thoại th&ocirc;ng minh để chia sẻ h&igrave;nh ảnh. Cảm ứng từ xa đ&atilde; được đơn giản h&oacute;a với kiểm so&aacute;t Wi-Fi/NFC. Ngo&agrave;i việc hỗ trợ Wi-Fi để kết nối với điện thoại th&ocirc;ng minh, m&aacute;y&nbsp;<strong>Sony a7 ii</strong>&nbsp;cũng hỗ trợ c&ocirc;ng nghệ NFC (Near Field Communication) cung cấp chuyển thuận tiện h&igrave;nh ảnh điện thoại th&ocirc;ng minh v&agrave; m&aacute;y t&iacute;nh bảng Android.</p>\r\n\r\n<p>Người d&ugrave;ng chỉ cần chạm v&agrave;o thiết bị để kết nối. Hơn nữa, khi sử dụng điều khiển từ xa th&ocirc;ng minh - một t&iacute;nh năng cho ph&eacute;p chụp được điều khiển bởi một điện thoại th&ocirc;ng minh - kết nối với điện thoại th&ocirc;ng minh c&oacute; thể được thiết lập bằng c&aacute;ch chạm v&agrave;o c&aacute;c thiết bị tương th&iacute;ch.</p>\r\n\r\n<p><br />\r\n<strong>Giao diện truy cập trực tiếp</strong></p>\r\n\r\n<p>Navi Pro hiển thị nhanh ch&oacute;ng tất cả c&aacute;c lựa chọn chụp h&igrave;nh lớn tr&ecirc;n m&agrave;n h&igrave;nh LCD, do đ&oacute; bạn c&oacute; thể nhanh ch&oacute;ng x&aacute;c nhận thiết lập v&agrave; điều chỉnh m&agrave; kh&ocirc;ng cần t&igrave;m kiếm th&ocirc;ng qua c&aacute;c menu chuy&ecirc;n dụng. Khi c&oacute; cơ hội chụp ph&aacute;t sinh, bạn sẽ c&oacute; thể đ&aacute;p ứng nhanh ch&oacute;ng.</p>\r\n\r\n<p><br />\r\n<strong>Mắt kiểm so&aacute;t AF mới</strong></p>\r\n\r\n<p>Ngay cả khi chụp một chủ đề quay lưng lại với m&aacute;y ảnh với độ s&acirc;u n&ocirc;ng của trường, khu&ocirc;n mặt sẽ được tập trung mạnh nhờ ph&aacute;t hiện mắt cực kỳ ch&iacute;nh x&aacute;c. Một khung m&agrave;u xanh l&aacute; c&acirc;y xuất hiện tr&ecirc;n mắt ưu ti&ecirc;n tập trung khi đ&atilde; đạt được để x&aacute;c nhận dễ d&agrave;ng. Mắt AF c&oacute; thể được sử dụng khi c&aacute;c chức năng được g&aacute;n cho một n&uacute;t t&ugrave;y biến, cho ph&eacute;p người d&ugrave;ng ngay lập tức k&iacute;ch hoạt n&oacute; phụ thuộc v&agrave;o cảnh.</p>\r\n\r\n<p><br />\r\n<strong>D&acirc;y điều khiển từ xa với kiểm so&aacute;t Video Capture</strong></p>\r\n\r\n<p>Điều khiển camera từ xa cho ph&eacute;p bạn điều khiển m&aacute;y&nbsp;<strong>Sony a7 ii&nbsp;</strong>từ m&aacute;y t&iacute;nh của bạn sử dụng c&aacute;p USB. Điều n&agrave;y đ&atilde; được cập nhật để bao gồm kiểm so&aacute;t quay video.</p>\r\n\r\n<p><br />\r\n<strong>Đa giao diện</strong></p>\r\n\r\n<p>C&aacute;c t&iacute;nh năng của&nbsp;<strong>Sony a7 ii&nbsp;</strong>c&oacute; nhiều giao diện ti&ecirc;n tiến, mở rộng đ&aacute;ng kể khả năng tương th&iacute;ch với c&aacute;c phụ kiện kỹ thuật số Sony như đ&egrave;n flash, micro, đ&egrave;n chiếu s&aacute;ng, gi&aacute;m s&aacute;t v&agrave; do đ&oacute; l&agrave;m tăng khả năng chụp ảnh v&agrave; quay phim.</p>\r\n', '2', '12', '3', '2015-02-04');
INSERT INTO `products` VALUES ('25', 'Sony Cyber-shot DSC-H400 (chính hãng)', '3', '6100010', '<p>Điểm nổi bật của sản phẩm</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>- 20.1MP 1 / 2.3 &quot;Super HAD CCD Sensor</p>\r\n\r\n<p>- BIONZ R xử l&yacute; h&igrave;nh ảnh</p>\r\n\r\n<p>- Zoom quang học 63x 24.5-1550mm</p>\r\n\r\n<p>- 3 &quot;Clear Photo LCD 460k-Dot &amp; K&iacute;nh ngắm</p>\r\n\r\n<p>- 720p HD Movie Mode</p>\r\n\r\n<p>- Optical SteadyShot ổn định h&igrave;nh ảnh</p>\r\n\r\n<p>- 360˚ Sweep Panorama</p>\r\n\r\n<p>- Dễ sử dụng với chi tiết đơn giản</p>\r\n', '<p><strong>Giới thiệu</strong></p>\r\n\r\n<p><a href=\"http://zshop.vn/sony-h400.html\">Sony Cyber-shot DSC-H400</a>&nbsp;kỹ thuật số l&agrave; một m&aacute;y ảnh si&ecirc;u zoom v&agrave; dễ sử dụng giống như một point-and-shoot, nhưng c&aacute;i nh&igrave;n v&agrave; cảm nhận của một DSLR. N&oacute; c&oacute; t&iacute;nh năng một 20.1MP 1 / 2.3 &quot;Super HAD CCD Sensor với một BIONZ R xử l&yacute; h&igrave;nh ảnh cho h&igrave;nh ảnh chi tiết đẹp, v&agrave; một ống k&iacute;nh zoom quang học 63x mạnh mẽ với chiều d&agrave;i ti&ecirc;u cự tương đương 35mm 24.5-1550mm Một s&aacute;ng 3.&quot; Clear Photo 460k- dot m&agrave;n h&igrave;nh LCD v&agrave; k&iacute;nh ngắm tầm mắt cung cấp cho bạn hai t&ugrave;y chọn để s&aacute;ng t&aacute;c v&agrave; xem h&igrave;nh ảnh của bạn.</p>\r\n\r\n<p>Video HD chụp 720p ở tốc độ 30fps v&agrave; sử dụng ổn định h&igrave;nh ảnh quang học SteadyShot để b&ugrave; đắp cho m&aacute;y ảnh bị rung để gi&uacute;p loại bỏ mờ, đặc biệt l&agrave; khi bạn đang đi bộ. Điều n&agrave;y đặc biệt hữu &iacute;ch trong &aacute;nh s&aacute;ng thấp v&agrave; khi sử dụng độ d&agrave;i ti&ecirc;u cự d&agrave;i. Đối với chụp ở một g&oacute;c độ rộng hơn, h&atilde;y thử những 360˚ Sweep Panorama, cho ph&eacute;p bạn bấm n&uacute;t chụp v&agrave; qu&eacute;t m&aacute;y ảnh của bạn xung quanh để thu thập trong to&agrave;n bộ cảnh quan của bạn bằng c&aacute;ch tham gia nhiều h&igrave;nh ảnh độ ph&acirc;n giải cao v&agrave; tự động hợp lại ch&uacute;ng v&agrave;o một bức ảnh.</p>\r\n\r\n<p>Chụp ảnh chưa bao giờ được dễ d&agrave;ng hơn.&nbsp;<strong>Sony H400</strong>&nbsp;sử dụng chức năng tự động th&ocirc;ng minh cho ph&eacute;p m&aacute;y ảnh để thực hiện c&aacute;c thiết lập tối ưu dựa tr&ecirc;n sự c&ocirc;ng nhận của cảnh bạn đang chụp, v&agrave; Easy Mode của m&aacute;y ảnh để đơn giản h&oacute;a thực đơn của bạn. Để tr&aacute;nh thiếu những khu&ocirc;n mặt tươi cười đơn giản l&agrave; bật Smile Shutter C&ocirc;ng nghệ, trong đ&oacute; c&oacute; một h&igrave;nh ảnh bất cứ khi n&agrave;o một nụ cười được ph&aacute;t hiện, dựa tr&ecirc;n sở th&iacute;ch của bạn. H&atilde;y tận dụng hiệu ứng h&igrave;nh ảnh cho h&igrave;nh ảnh biểu cảm hơn v&agrave; chế độ Đảng để tối ưu h&oacute;a flash để chiếu s&aacute;ng l&yacute; tưởng trong kh&oacute; khăn để chụp ảnh điều kiện &aacute;nh s&aacute;ng.</p>\r\n\r\n<p><img alt=\"Máy ảnh Sony Cyber-shot DSC-H400\" src=\"http://zshop.vn/images/A/untitled%20folder/untitled%20folder/IMG_369696.jpg\" style=\"height:500px; margin:0px; width:500px\" /></p>\r\n\r\n<p>M&aacute;y ảnh Sony Cyber-shot DSC-H400</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Một số t&iacute;nh năng Sony H400</strong></p>\r\n\r\n<p><strong>Cảm biến v&agrave; xử l&yacute; h&igrave;nh ảnh BIONZ R</strong></p>\r\n\r\n<p>Với 20.1 megapixel 1 / 2.3 &quot;Super HAD CCD v&agrave; xử l&yacute; h&igrave;nh ảnh BIONZ R cung cấp cho chất lượng cao, h&igrave;nh ảnh độ ph&acirc;n giải cao với độ tương phản v&agrave; r&otilde; r&agrave;ng chi tiết đ&aacute;ng kể.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Quang học 63x Ph&oacute;ng 24.5-1550mm 35mm tương đương ti&ecirc;u cự ống k&iacute;nh</strong></p>\r\n\r\n<p>Chuy&ecirc;n nghiệp Sony 4.4-277mm f / 3,4-6,5 ống k&iacute;nh n&agrave;y c&oacute; zoom quang 63x đ&aacute;ng kể v&agrave; c&oacute; độ d&agrave;i ti&ecirc;u cự tương đương 35mm 24.5-1550mm, trong đ&oacute; cung cấp cho bạn với một loạt tele đ&aacute;ng kể.</p>\r\n\r\n<p><img alt=\"Sony H400 với ống kính quan học\" src=\"http://zshop.vn/images/A/untitled%20folder/untitled%20folder/IMG_369695.jpg\" style=\"height:500px; margin:0px; width:500px\" /></p>\r\n\r\n<p>Sony H400 với ống k&iacute;nh quan học</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2><strong>Sony H400 với k&iacute;nh ngắm v&agrave; m&agrave;n h&igrave;nh LCD 460k</strong></h2>\r\n\r\n<p>A 3 &quot;Clear Photo 460k-dot m&agrave;n h&igrave;nh LCD s&aacute;ng cho ph&eacute;p bạn 5 điểm dừng kiểm so&aacute;t độ s&aacute;ng để xem h&igrave;nh ảnh của bạn trong tất cả c&aacute;c loại điều kiện &aacute;nh s&aacute;ng b&ecirc;n ngo&agrave;i như &aacute;nh nắng mặt trời hoặc trong nh&agrave; v&agrave;o ban đ&ecirc;m.&nbsp;<strong><a href=\"http://zshop.vn/sony-h400.html\">Sony H400&nbsp;</a></strong>cũng c&oacute; t&iacute;nh năng một biểu đồ v&agrave; hiển thị lưới khi bạn cần gi&uacute;p đỡ th&ecirc;m trong việc s&aacute;ng t&aacute;c c&aacute;c bức ảnh của bạn. đối với sự ổn định chụp th&ecirc;m, k&iacute;nh ngắm điện tử tr&ecirc;n<em>Sony H400</em>&nbsp;cũng được cung cấp cho c&aacute;c th&agrave;nh phần ngang tầm mắt. N&oacute; trở n&ecirc;n hữu &iacute;ch để xem v&agrave; chụp s&aacute;ng t&aacute;c trong &aacute;nh s&aacute;ng mặt trời khắc nghiệt.</p>\r\n\r\n<p><img alt=\"Màn hình LCD 460k trên sony Cyber-shot DSC-H400\" src=\"http://zshop.vn/images/A/untitled%20folder/untitled%20folder/IMG_369697.jpg\" style=\"height:500px; margin:0px; width:500px\" /></p>\r\n\r\n<p>M&agrave;n h&igrave;nh LCD 460k tr&ecirc;n sony Cyber-shot DSC-H400</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Ổn định quang học SteadyShot với h&igrave;nh ảnh th&ocirc;ng minh&nbsp;</strong></p>\r\n\r\n<p>Ổn định h&igrave;nh ảnh Optical SteadyShot cung cấp bồi thường để giảm hiệu ứng l&agrave;m mờ chuyển động m&aacute;y ảnh khi chụp ở tốc độ m&agrave;n trập chậm hơn. Điều n&agrave;y đặc biệt hiệu quả khi chụp trong điều kiện &aacute;nh s&aacute;ng yếu hoặc độ d&agrave;i ti&ecirc;u cự d&agrave;i hơn.&nbsp;<strong>Sony H400</strong>&nbsp;với chế độ ổn định h&igrave;nh ảnh đăng nhập th&ocirc;ng minh của Sony kết hợp c&acirc;n bằng điện tử v&agrave; quang học &iacute;t mờ trong khi quay video khi đang di chuyển.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2><strong>Chế độ quay phim HD 720p tr&ecirc;n Sony H400&nbsp;</strong></h2>\r\n\r\n<p>1280 x 720 phim độ n&eacute;t cao ở tốc độ 30fps để tạo ra video chất lượng cao với việc sử dụng đầy đủ c&aacute;c zoom quang học trong 29 ph&acirc;n đoạn ph&uacute;t. Sau khi chuyển c&aacute;c đoạn video v&agrave;o m&aacute;y t&iacute;nh của bạn,&nbsp;<em>Sony H400</em>&nbsp;sử dụng internet để ngay lập tức tải ch&uacute;ng l&ecirc;n trang web y&ecirc;u th&iacute;ch phương tiện truyền th&ocirc;ng x&atilde; hội của bạn. 360˚ Sweep Panorama</p>\r\n\r\n<p>Để đạt được một g&oacute;c rộng hơn, bạn chỉ cần nhấn n&uacute;t chụp v&agrave; qu&eacute;t m&aacute;y ảnh xung quanh trong khi m&aacute;y ảnh li&ecirc;n tục chụp h&igrave;nh ở tốc độ cao để cung cấp một h&igrave;nh ảnh gh&eacute;p lại v&agrave; chất lượng cao nh&igrave;n to&agrave;n cảnh. Sử dụng n&oacute; để chụp phong cảnh rộng, hoặc đi v&ograve;ng tr&ograve;n đầy đủ l&ecirc;n đến một bức tranh to&agrave;n cảnh 360˚ cho h&igrave;nh ảnh s&aacute;ng tạo hơn.</p>\r\n\r\n<p><img alt=\"\" src=\"http://zshop.vn/images/A/untitled%20folder/untitled%20folder/IMG_369699.jpg\" style=\"height:500px; margin:0px; width:500px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>C&ocirc;ng nghệ Smile Shutter</strong></p>\r\n\r\n<p>Tự động chụp mỉm cười thời điểm ch&uacute;ng xảy ra. Đơn giản chỉ cần nhấn n&uacute;t Smile Shutter để bật t&iacute;nh năng v&agrave; để phần c&ograve;n lại v&agrave;o m&aacute;y ảnh&nbsp;<em>Sony H400</em>. Bạn c&oacute; thể chỉ ra mức độ nhạy cảm ph&aacute;t hiện nụ cười v&agrave; thậm ch&iacute; chọn người lớn hoặc trẻ em nụ cười ưu ti&ecirc;n. Khi chọn chế độ tự động th&ocirc;ng minh l&agrave;, bạn c&oacute; thể sử dụng chế độ Smile Shutter với Intelligent Scene Recognition để nắm bắt nụ cười với c&aacute;c thiết lập tối ưu h&oacute;a cho cảnh cụ thể như trong điều kiện chụp kh&oacute; khăn với ho&agrave;ng h&ocirc;n v&agrave; đ&egrave;n nền.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Chụp s&aacute;ng tạo với hiệu ứng h&igrave;nh ảnh</strong></p>\r\n\r\n<p>Mang phong cảnh v&agrave; ch&acirc;n dung b&igrave;nh thường với cuộc sống, bằng c&aacute;ch n&acirc;ng cao t&acirc;m trạng v&agrave; tăng cường c&aacute;c thuộc t&iacute;nh nhất định với hiệu ứng h&igrave;nh ảnh. Một loạt c&aacute;c t&ugrave;y chọn s&aacute;ng tạo bao gồm bốn chế độ cho h&igrave;nh ảnh của bạn vẫn c&ograve;n, bốn chế độ cho c&aacute;c bộ phim, v&agrave; th&ecirc;m ba chế độ &aacute;p dụng đối với bức ảnh to&agrave;n cảnh của bạn.</p>\r\n\r\n<p><img alt=\"sony Cyber-shot DSC-H400\" src=\"http://zshop.vn/images/A/untitled%20folder/untitled%20folder/IMG_369700.jpg\" style=\"height:500px; margin:0px; width:500px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Khi chụp trong chế độ Easy menu được đơn giản h&oacute;a bằng c&aacute;ch loại bỏ lớp bổ sung kh&aacute;c nhau v&agrave; l&agrave;m cho kinh nghiệm chụp ảnh của bạn dễ d&agrave;ng hơn v&agrave; th&uacute; vị hơn. Bạn n&ecirc;n muốn lựa chọn chi tiết hơn cho shot của bạn, chỉ cần tắt t&iacute;nh năng n&agrave;y, v&agrave; trở về menu b&igrave;nh thường.</p>\r\n\r\n<p>Tẩy xo&aacute; v&agrave; bảo vệ h&igrave;nh ảnh c&oacute; thể được thực hiện trong c&aacute;c chế độ kh&aacute;c nhau: một h&igrave;nh ảnh, lựa chọn h&igrave;nh ảnh, bởi ng&agrave;y, v&agrave; thư mục.</p>\r\n', '2', '31', '7', '2015-01-19');
INSERT INTO `products` VALUES ('26', 'Sony Cyber-shot DSC-WX220 (Chính Hãng)', '3', '4599800', '<p>Điểm nổi bật của sản phẩm</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>- Cảm biến Exmor R CMOS 18.2MP</p>\r\n\r\n<p>- Ống k&iacute;nh G lens&trade; với zoom quang học 10x</p>\r\n\r\n<p>- Bộ xử l&yacute; h&igrave;nh ảnh Bionz X đạt tốc độ cao v&agrave; t&aacute;i tạo chi tiết hiệu quả</p>\r\n\r\n<p>- Quay phim Full HD (1,920 x 1,080 60i) với ổn định h&igrave;nh ảnh Optical SteadyShot</p>\r\n\r\n<p>- Chế độ Intelligent Active Mode loại bỏ hiện tượng mờ nho&egrave; khi ghi h&igrave;nh</p>\r\n\r\n<p>- T&iacute;ch hợp WiFi/ NFC cho việc chia sẻ h&igrave;nh ảnh trở n&ecirc;n dễ d&agrave;ng hơn</p>\r\n', '<p><strong>GIỚI THIỆU</strong></p>\r\n\r\n<p><strong><a href=\"http://zshop.vn/may-anh-kts-sony/\">M&aacute;y ảnh Sony</a>&nbsp;Cyber-shot DSC-WX220</strong>&nbsp;với k&iacute;ch thước bỏ t&uacute;i trang bị ống k&iacute;nh G lens vượt trội với zoom quang học 10x. Được t&iacute;ch hợp Optical SteadyShot&trade; để mang đến h&igrave;nh ảnh vượt bật khi zoom tối đa. Cảm biến 18.2MP Exmor RCMOS đảm bảo hiệu năng xuất sắc ở nơi thiếu s&aacute;ng, v&agrave; kết nối WiFi/NFC cho ph&eacute;p chia sẻ ảnh được thuận tiện.</p>\r\n\r\n<p><img alt=\"Sony Cyber-shot DSC-WX220\" src=\"http://zshop.vn/images/nikon/untitled%20folder/untitled%20folder/untitled%20folder/2d41a9e22e933afa595e62f49635fb75.jpeg\" style=\"height:358px; margin:0px; width:494px\" /></p>\r\n\r\n<p>Sony Cyber-shot DSC-WX220</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>T&Iacute;NH NĂNG NỔI BẬT</strong></p>\r\n\r\n<p><strong>Chụp ảnh tuyệt đẹp ở cả những nơi tối</strong></p>\r\n\r\n<p>Ghi những bộ phim v&agrave; h&igrave;nh ảnh chi tiết vượt bật đạt tương phản cực cao thậm ch&iacute; l&agrave; ở nơi thiếu s&aacute;ng. Với cảm biến Exmor R&trade; CMOS, giờ đ&acirc;y bạn c&oacute; thể đạt được h&igrave;nh ảnh độ n&eacute;t cao m&agrave; kh&ocirc;ng bị nhiễu trong bất kỳ điều kiện chụp ảnh n&agrave;o.</p>\r\n\r\n<p><strong>Zoom mạnh mẽ trong th&acirc;n m&aacute;y nhỏ gọn</strong></p>\r\n\r\n<p>Tận hưởng sự tiện lợi của việc c&oacute; được những bức ảnh cận cảnh tuyệt đẹp từ c&aacute;c chủ thể ở tận xa. Với k&iacute;ch cỡ đủ mỏng để vừa t&uacute;i &aacute;o<a href=\"http://zshop.vn/sony-cyber-shot-dsc-wx220-chinh-hang.html\">, DSC-WX220</a>&nbsp;cung cấp hiệu suất zoom quang học nổi bật l&ecirc;n đến 10x của ống k&iacute;nh Sony G, mang bạn đối diện với c&aacute;c đối tượng ở xa.</p>\r\n\r\n<p><strong>Chi tiết trung thực đến kinh ngạc</strong></p>\r\n\r\n<p>Bộ xử l&yacute; h&igrave;nh ảnh mới BIONZ X&trade; tốc độ cao t&aacute;i tạo cấu tr&uacute;c v&agrave; chi tiết như mắt nh&igrave;n thấy, th&ocirc;ng qua khả năng xử l&yacute; tốc độ cao.Để mang đến h&igrave;nh ảnh sống động hơn v&agrave; như thật bằng độ ph&acirc;n giải cao.</p>\r\n\r\n<p><img alt=\"Sony Cyber-shot DSC-WX220\" src=\"http://zshop.vn/images/nikon/untitled%20folder/untitled%20folder/untitled%20folder/sony-dsc-wx220-1.jpeg\" style=\"height:270px; margin:0px; width:500px\" /></p>\r\n\r\n<p><strong>Chụp ảnh r&otilde; cả ở những nơi tối</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Quay phim độ ph&acirc;n giải cao kh&ocirc;ng nh&ograve;e</strong></p>\r\n\r\n<p>Ghi những bộ phim HD với DSC-WX220, nhờ v&agrave;o khả năng ghi h&igrave;nh Full HD, giờ đ&acirc;y bạn c&oacute; thể ghi lại những bộ phim độ ph&acirc;n giải cao. C&ugrave;ng với t&iacute;nh năng ổn định h&igrave;nh ảnh quang học Optical SteadyShot, m&aacute;y ảnh được giảm rung tuyệt vời mang lại những thước phim độ n&eacute;t cao cho bạn thưởng thức.</p>\r\n\r\n<p><strong>H&igrave;nh ảnh mượt m&agrave; ngay khi đang di chuyển</strong></p>\r\n\r\n<p>Với chế độ Intelligent Auto, việc chụp nhanh một bức ảnh ho&agrave;n hảo thật dễ d&agrave;ng. Việc kết hợp c&aacute;c c&ocirc;ng nghệ ti&ecirc;n tiến;lấy n&eacute;t tự động Intelligent Auto Focus, nhận diện chuyển động Motion Detectiion v&agrave; nhận diện khung cảnh th&ocirc;ng minh Intelligent Scene Recognition để mang đến c&aacute;c thiết lập tối ưu cho những khung cảnh kh&aacute;c nhau v&igrave; thế bạn c&oacute; thể ghi được những bức ảnh tuyệt đẹp, thậm ch&iacute; trong những m&ocirc;i trường th&aacute;ch thức như thiếu s&aacute;ng hoặc ngược s&aacute;ng.</p>\r\n\r\n<p><img alt=\"Sony Cyber-shot DSC-WX220\" src=\"http://zshop.vn/images/nikon/untitled%20folder/untitled%20folder/untitled%20folder/photo-sample_wx220_01.jpg\" style=\"height:375px; margin:0px; width:500px\" /></p>\r\n\r\n<p><strong>Độ ph&acirc;n giải cao</strong></p>\r\n\r\n<p><strong>Kết nối Một chạm cho chia sẻ tức th&igrave;</strong></p>\r\n\r\n<p>Với hỗ trợ WiFi v&agrave; NFC, bạn c&oacute; thể tận hưởng việc chia sẻ nhanh ch&oacute;ng bằng kết nối Một chạm đến c&aacute;c thiết bị Android.V&igrave; thế việc chuyển tải c&aacute;c h&igrave;nh ảnh của bạn đến điện thoại tương th&iacute;ch Wi-Fi hoặc NFC hoặc thiết bị lưu trữ l&agrave; một việc dễ d&agrave;ng.</p>\r\n\r\n<p><strong>Zoom H&igrave;nh R&otilde; N&eacute;t Cho Phim</strong></p>\r\n\r\n<p>Việc đạt được những đoạn phim sắc n&eacute;t v&agrave; r&otilde; r&agrave;ng giờ đ&acirc;y trở n&ecirc;n dễ d&agrave;ng với Clear Image Zoom. Đạt h&igrave;nh ảnh sắc n&eacute;t hơn 2 lần khi bạn ph&oacute;ng to chủ thể.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Tr&igrave;nh Chiếu Ảnh Tĩnh 4K* Qua Kết Nối HDMI</strong></p>\r\n\r\n<p>Đơn giản chỉ việc kết nối m&aacute;y ảnh của bạn đến TV 4K* th&ocirc;ng qua HDMI v&agrave; l&agrave;m sống lại những kỉ niệm của bạn bằng h&igrave;nh ảnh 4K* chi tiết.</p>\r\n', '2', '12', '3', '2014-12-28');
INSERT INTO `products` VALUES ('27', 'Sony DSC-WX350 (Chính hãng)', '3', '6100010', '<p>Điểm nổi bật của sản phẩm</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>-&nbsp;Cảm biến Exmor R CMOS 18.2MP</p>\r\n\r\n<p>- Zoom quang học 20X của ống k&iacute;nh G</p>\r\n\r\n<p>- Chụp ảnh tuyệt đẹp thậm ch&iacute; ở những g&oacute;c tối</p>\r\n\r\n<p>- Bộ xử l&yacute; h&igrave;nh ảnh mới BIONZ X tốc độ cao</p>\r\n\r\n<p>- Pin cho hiệu suất 470 bức ảnh trong một lần sạc đầy</p>\r\n\r\n<p>- Hỗ trợ Wi-Fi v&agrave; NFC</p>\r\n\r\n<p>- Chế Độ Intelligent Auto</p>\r\n\r\n<p>- Kết nối &nbsp;đến TV 4K th&ocirc;ng qua HDMI</p>\r\n', '<p><strong>GIỚI THIỆU</strong></p>\r\n\r\n<p><strong><a href=\"http://zshop.vn/may-anh-kts-sony/\">M&aacute;y ảnh Sony</a>&nbsp;DSC-WX350 nhỏ nhất thế giới với zoom quang học 20x</strong></p>\r\n\r\n<p>Zoom mạnh mẽ trong th&acirc;n m&aacute;y ảnh mỏng v&agrave; thời trang. Tận hưởng sự tiện lợi của việc c&oacute; được những bức ảnh cận cảnh từ c&aacute;c chủ thể ở tận xa bằng chiếc m&aacute;y ảnh nhỏ v&agrave; nhẹ nhất thế giới. Chiếc m&aacute;y đủ mỏng để cho vừa v&agrave;o t&uacute;i &aacute;o của bạn, DSC-WX350 cung cấp hiệu suất zoom quang học nổi bật l&ecirc;n đến 20x, cho bạn đối diện với c&aacute;c đối tượng ở xa.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Sony DSC-WX350\" src=\"http://zshop.vn/images/detailed/34/20x.jpg\" style=\"height:358px; margin:0px; width:494px\" /></p>\r\n\r\n<p>Sony DSC-WX350</p>\r\n\r\n<p><strong>T&Iacute;NH NĂNG NỔI BẬT</strong></p>\r\n\r\n<p><strong>Chụp ảnh tuyệt đẹp thậm ch&iacute; ở những nơi tối</strong></p>\r\n\r\n<p>Ghi những bộ phim v&agrave; h&igrave;nh ảnh chi tiết vượt bật đạt tương phản cực cao thậm ch&iacute; l&agrave; ở nơi thiếu s&aacute;ng. Với cảm biến Exmor R&trade; CMOS, giờ đ&acirc;y bạn c&oacute; thể đạt được h&igrave;nh ảnh độ n&eacute;t cao m&agrave; kh&ocirc;ng bị nhiễu trong bất kỳ điều kiện chụp ảnh n&agrave;o</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Zoom mạnh mẽ với chi tiết kinh ngạc</strong></p>\r\n\r\n<p>H&atilde;y tận dụng Zoom quang học 20X của ống k&iacute;nh G với ổn định quang học Optical SteadyShot. Tiếp cận h&agrave;nh động v&agrave; ghi những h&igrave;nh ảnh thật sắc n&eacute;t v&agrave; r&otilde; r&agrave;ng như những g&igrave; bạn đ&atilde; từng nh&igrave;n thấy.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Chi tiết trung thực đến kinh ngạc</strong></p>\r\n\r\n<p>Bộ xử l&yacute; h&igrave;nh ảnh mới BIONZ X&trade; tốc độ cao t&aacute;i tạo cấu tr&uacute;c v&agrave; chi tiết như mắt nh&igrave;n thấy, th&ocirc;ng qua khả năng xử l&yacute; tốc độ cao. Để mang đến h&igrave;nh ảnh sống động hơn v&agrave; như thật bằng độ ph&acirc;n giải cao.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Sẵn s&agrave;ng cho từng cơ hội chụp ảnh của bạn</strong></p>\r\n\r\n<p>Hiệu suất pin vượt bật cho khả năng chụp l&ecirc;n đến 470 bức ảnh trong một lần sạc đầy, v&igrave; thế bạn c&oacute; thể tự do chụp ảnh m&agrave; kh&ocirc;ng phải lo lắng..</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Chia sẻ tức th&igrave; với kết nối Một chạm</strong></p>\r\n\r\n<p>Với sự hỗ trợ Wi-Fi v&agrave; NFC, h&atilde;y tận hưởng chia sẻ tức th&igrave; với kết nối Một chạm đến thiết bị Android của bạn tr&ecirc;n từng ng&oacute;n tay.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Chế Độ Intelligent Auto</strong></p>\r\n\r\n<p>Với chế độ Intellignet Auto, chụp nhanh một bức ảnh ho&agrave;n hảo thật dễ d&agrave;ng. Để bắt đầu, n&oacute; x&aacute;c định c&aacute;c thiết lập l&yacute; tưởng nhất cho những cảnh kh&aacute;c nhau. Chế độ Intelligent Auto cũng kết hợp c&aacute;c c&ocirc;ng nghệ ti&ecirc;n tiến như Intelligent Auto Focus, Motion Detection v&agrave; Intelligent Scene Recognition để đảm bảo rằng bạn c&oacute; được những bức ảnh tuyệt đẹp, thậm ch&iacute; trong điều kiện thiếu s&aacute;ng hoặc trường hợp ngược s&aacute;ng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Tự Động Đ&oacute;ng Khung V&agrave; Kh&oacute;a Lấy N&eacute;t Chủ Thể</strong></p>\r\n\r\n<p>Tự động đ&oacute;ng khung chủ thể v&agrave; kh&oacute;a lấy n&eacute;t AF gi&uacute;p chiếc m&aacute;y tạo ra những bức ảnh được c&acirc;n bằng tuyệt đẹp cho d&ugrave; l&agrave; chụp một người, một đối tượng hoặc cả hai. Đơn giản chỉ việc chụp chủ thể của bạn v&agrave; t&iacute;nh năng Object Framing tự động lưu lại h&igrave;nh ảnh cắt (ảnh gốc vẫn được lưu) để gi&uacute;p bạn tạo n&ecirc;n bức ảnh như chuy&ecirc;n nghiệp trong khi với kh&oacute;a lấy n&eacute;t AF, cho ph&eacute;p giữ lấy n&eacute;t suốt trong khi chụp chủ thể chuyển động nhanh th&ecirc;m ch&iacute;nh x&aacute;c v&agrave; lấy n&eacute;t li&ecirc;n tục.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Motion Shot Video</strong></p>\r\n\r\n<p>Motion Shot Video tiện lợi để ph&acirc;n t&iacute;ch chuyển động thể thao, cũng như tạo những hiệu ứng ấn tượng. T&iacute;nh năng th&ocirc;ng minh n&agrave;y ghi chủ thể chuyển động bằng một chuỗi h&igrave;nh ảnh, những h&igrave;nh ảnh n&agrave;y được ph&aacute;t tuần tự v&agrave; chồng l&ecirc;n nhau để hiển thị quĩ đạo chuyển động. Trong l&uacute;c ph&aacute;t, người d&ugrave;ng c&oacute; thể t&ugrave;y chỉnh khoảng thời gian giữa c&aacute;c h&igrave;nh ảnh m&igrave;nh muốn, cũng như chụp lại những ảnh tĩnh trong l&uacute;c dừng ph&aacute;t..</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Tr&igrave;nh Chiếu Ảnh Tĩnh 4K Qua Kết Nối HDMI</strong></p>\r\n\r\n<p>Đơn giản chỉ việc kết nối m&aacute;y ảnh của bạn đến TV 4K th&ocirc;ng qua HDMI v&agrave; l&agrave;m sống lại những kỉ niệm của bạn bằng h&igrave;nh ảnh 4K chi tiết.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Hiệu Ứng H&igrave;nh Ảnh (9 Chế Độ)</strong></p>\r\n\r\n<p>H&atilde;y sử dụng chế độ Hiệu ứng h&igrave;nh ảnh để mở rộng phạm vi s&aacute;ng tạo của bạn v&agrave; chuyển tải những bức ảnh thường ng&agrave;y th&agrave;nh những t&aacute;c phẩm nghệ thuật. Với DSC-WX350, c&oacute; đến 9 chế độ Hiệu ứng h&igrave;nh ảnh c&oacute; thể được ứng dụng cho cả quay phim v&agrave; chụp ảnh.</p>\r\n', '2', '11', '48', '2015-01-09');
INSERT INTO `products` VALUES ('28', 'Sony Cyber-Shot DSC-H300 (chính hãng)', '3', '4217890', '<p>Điểm nổi bật của sản phẩm</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>- Zoom quang học l&ecirc;n đến 35x</p>\r\n\r\n<p>- Cảm biến 20.1 MP Super HAD CCD</p>\r\n\r\n<p>- Th&acirc;n m&aacute;y mang phong c&aacute;ch DSLR với giao diện sử dụng dễ d&agrave;ng</p>\r\n\r\n<p>- Quay phim HD (720p)</p>\r\n\r\n<p>- M&agrave;n h&igrave;nh LCD 3.0-inch Clear Photo</p>\r\n\r\n<p>- C&ocirc;ng nghệ Flash ti&ecirc;n tiến</p>\r\n', '<p><strong>GIỚI THIỆU</strong></p>\r\n\r\n<p><strong><a href=\"http://zshop.vn/may-anh-kts-sony/\">M&aacute;y ảnh Sony</a>&nbsp;Cyber-shot DSC H300</strong>&nbsp;l&agrave; một m&aacute;y ảnh point -and-shoot gồm c&oacute; một MP 1/2.3 20.1 &quot; Super HAD CCD cảm biến h&igrave;nh ảnh cho độ ph&acirc;n giải cao m&agrave; h&igrave;nh ảnh v&agrave; video ở chế độ HD 720p . Cảm biến n&agrave;y c&oacute; ưu điểm bởi &nbsp;một &nbsp;ống k&iacute;nh zoom quang 35x , cho một dải ti&ecirc;u cự 35mm tương đương 25 - 875mm . Đối với chụp trong điều kiện &aacute;nh s&aacute;ng thấp v&agrave; khi l&agrave;m việc ở độ ph&oacute;ng đại zoom lớn hơn, ổn định h&igrave;nh ảnh quang học SteadyShot c&oacute; sẵn để chống lại c&aacute;c t&aacute;c động của m&aacute;y ảnh khi bị lắc .</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Để theo d&otilde;i v&agrave; ph&aacute;t lại c&aacute;c h&igrave;nh ảnh, cũng như điều chỉnh c&aacute;c hệ thống menu , một m&agrave;n h&igrave;nh 3.0 &quot; 461k -dot LCD c&oacute; sẵn v&agrave; cung cấp năm bước s&aacute;ng để hỗ trợ trong khi xem điều kiện s&aacute;ng . Ngo&agrave;i ra c&ograve;n c&oacute; một loạt c&aacute;c hiệu ứng m&aacute;y ảnh để tinh chỉnh bao gồm Picture Effects , Hiệu ứng Thẩm mỹ, nhận diện khu&ocirc;n mặt , 360 &deg; Sweep Panorama , v&agrave; chế độ iAuto . Ngo&agrave;i ra, H300 được trang bị 4 pin AA.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Sony Cyber-Shot DSC-H300\" src=\"http://zshop.vn/images/cuong_sp/Macbook/Pro%20Retina/Sony-Cyber-shot%20DSC-H300-3.jpg\" style=\"height:500px; margin:0px; width:500px\" /></p>\r\n\r\n<p>Sony Cyber Shot DSC H300</p>\r\n\r\n<p><strong>T&Iacute;NH NĂNG NỔI BẬT</strong></p>\r\n\r\n<p><strong>Cảm biến 20.1MP Super HAD CCD Sensor</strong></p>\r\n\r\n<p><strong>M&aacute;y ảnh H300</strong>&nbsp;t&iacute;ch hợp một 1/2.3 20,1 megapixel &quot; Super HAD CCD cảm biến h&igrave;nh ảnh để sản xuất h&igrave;nh ảnh với độ ph&acirc;n giải cao từ c&aacute;c ống k&iacute;nh zoom quang 35x v&agrave; ổn định h&igrave;nh ảnh quang học SteadyShot . Sự kết hợp của c&aacute;c c&ocirc;ng nghệ n&agrave;y l&agrave;m cho ảnh sắc n&eacute;t, chi tiết h&igrave;nh ảnh ph&ugrave; hợp cho cả in ấn v&agrave; chia sẻ trực tuyến . C&aacute;c cảm biến cũng cho ph&eacute;p ghi video HD l&ecirc;n đến 1280 x 720 tại 30 fps .</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>35x Zoom quang học Ống k&iacute;nh v&agrave; Quang ổn định h&igrave;nh ảnh</strong></p>\r\n\r\n<p>Một ống k&iacute;nh zoom quang 35x t&iacute;ch hợp cung cấp một dải ti&ecirc;u cự 35mm tương đương 25 - 875mm , cho g&oacute;c rộng đến quan điểm si&ecirc;u tele cho ph&ugrave; hợp với hầu hết c&aacute;c t&igrave;nh huống chụp . Hỗ trợ ống k&iacute;nh n&agrave;y l&agrave; khả năng tầm xa được t&iacute;ch hợp h&igrave;nh ảnh quang học SteadyShot , gi&uacute;p l&agrave;m giảm bớt t&iacute;nh năng rung tr&ecirc;n m&aacute;y ảnh .</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>M&agrave;n h&igrave;nh LCD 3.0 &quot; v&agrave; chức năng</strong></p>\r\n\r\n<p>Thiết kế của&nbsp;<strong>H300&nbsp;</strong>bắt chước của một chiếc m&aacute;y ảnh DSLR , cung cấp một số chất liệu để giữ cho th&acirc;n m&aacute;y tăng t&iacute;nh ổn định khi l&agrave;m việc với độ d&agrave;i ti&ecirc;u cự d&agrave;i hơn Với Một m&agrave;n h&igrave;nh LCD 461k -dot Clear Photo 3.0 hiện c&oacute; sẵn để theo d&otilde;i v&agrave; xem x&eacute;t h&igrave;nh ảnh v&agrave; c&aacute;c t&iacute;nh năng 5 thiết lập độ s&aacute;ng cho khả năng sử dụng lớn hơn trong điều kiện &aacute;nh s&aacute;ng mặt trời .</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>C&aacute;c t&iacute;nh năng m&aacute;y ảnh kh&aacute;c</strong></p>\r\n\r\n<p>- Hiệu ứng h&igrave;nh ảnh gi&uacute;p th&ecirc;m t&acirc;m trạng tăng l&ecirc;n h&igrave;nh ảnh bằng c&aacute;ch thay đổi sự xuất hiện của họ cho ph&ugrave; hợp với hướng s&aacute;ng tạo của bạn . Hiệu ứng bao gồm Toy Camera, Pop Color, v&agrave; c&aacute;c hiệu ứng m&agrave;u một phần trong số những người kh&aacute;c .</p>\r\n\r\n<p>- Hiệu ứng Effect cho bạn khả năng để tăng cường t&ocirc;ng m&agrave;u da đối tượng , mịn sự xuất hiện của da, loại bỏ nhược điểm v&agrave; tỏa s&aacute;ng , răng trắng hơn , v&agrave; nhiều hơn nữa&nbsp;</p>\r\n\r\n<p>- Chế độ iAuto th&ocirc;ng minh ph&acirc;n t&iacute;ch cảnh bạn đang chụp ảnh v&agrave; tối ưu h&oacute;a c&aacute;c c&agrave;i đặt m&aacute;y ảnh để l&agrave;m cho ảnh tốt nhất</p>\r\n\r\n<p>- Nhận diện khu&ocirc;n mặt được hoạt động để cải thiện bức ch&acirc;n dung bằng c&aacute;ch nhấn mạnh tập trung điểm v&agrave; tiếp x&uacute;c tr&ecirc;n khu&ocirc;n mặt của c&aacute;c đối tượng .</p>\r\n\r\n<p>- 360 &deg; Sweep Panorama cho ph&eacute;p bạn tạo ra h&igrave;nh ảnh to&agrave;n cảnh rộng trong m&aacute;y ảnh , &nbsp;bằng c&aacute;ch đơn giản qu&eacute;t to&agrave;n cảnh tr&ecirc;n diện rộng</p>\r\n\r\n<p>- Phần mềm PlayMemories Home được bao gồm để lấy h&igrave;nh ảnh từ m&aacute;y ảnh v&agrave; sắp xếp ch&uacute;ng.</p>\r\n', '2', '12', '94', '2015-01-05');
INSERT INTO `products` VALUES ('29', 'Sony A5000 + Kit 16-50mm (Chính Hãng)', '3', '9400050', '<p>Điểm nổi bật của sản phẩm</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>- M&aacute;y chỉ nặng c&oacute; 210g</p>\r\n\r\n<p>- Cảm biến ảnh CMOS 20.1 MP</p>\r\n\r\n<p>- kết nối Wi-Fi v&agrave; NFC</p>\r\n\r\n<p>- M&agrave;n h&igrave;nh LCD 3&rdquo;</p>\r\n\r\n<p>- Bộ xử l&yacute; ảnh BIONZ X</p>\r\n', '<p><strong>GIỚI THIỆU</strong></p>\r\n\r\n<p><strong><a href=\"http://zshop.vn/may-anh-kts-sony/\">M&aacute;y ảnh Sony</a>&nbsp;Alpha A5000&nbsp;</strong>gương lật (m&agrave;u đen) l&agrave; một m&aacute;y ảnh linh hoạt v&agrave; nhỏ gọn sở hữu cảm biến ảnh APS-C ph&acirc;n giải 20.1 megapixel c&oacute; k&iacute;ch thước cảm biến Exmor APS HD CMOS v&agrave; bộ xử l&yacute; h&igrave;nh ảnh BIONZ X c&oacute; độ ph&acirc;n giải cao v&agrave; phim HD với chất lượng &aacute;nh s&aacute;ng yếu đ&aacute;ng kể v&agrave; độ nhạy s&aacute;ng ISO hỗ trợ tối đa 16000. Bộ xử l&yacute; h&igrave;nh ảnh g&oacute;p phần rất lớn để giảm thiểu tiếng ồn tổng thể v&agrave; cung cấp một sự đầy đủ của tốc độ trong suốt của m&aacute;y ảnh để ghi l&ecirc;n đến 3,5 fps trong Speed Priority với Chế độ li&ecirc;n tục.</p>\r\n\r\n<p><img alt=\"Sony A5000 + Kit 16-50mm\" src=\"http://zshop.vn/images/cuong_sp/Macbook/May_anh/Sony-A5000-9.jpg\" style=\"height:500px; margin:0px; width:500px\" /></p>\r\n\r\n<p>Sony A5000 + Kit 16-50mm&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Để hỗ trợ theo d&otilde;i trực tiếp, xem lại ảnh, v&agrave; điều hướng menu, một &quot;m&agrave;n h&igrave;nh LCD 3,0 460.8k chấm được t&iacute;ch hợp v&agrave;o&nbsp;<strong>A5000&nbsp;</strong>v&agrave; sử dụng một thiết kế nghi&ecirc;ng 180&deg; để phục vụ tốt hơn kể từ g&oacute;c nh&igrave;n thấp cũng như ở ph&iacute;a trước của m&aacute;y ảnh . c&aacute;c m&agrave;n h&igrave;nh lớn v&agrave; giao diện menu trực quan gi&uacute;p dễ d&agrave;ng truy cập v&agrave;o một loạt c&aacute;c t&iacute;nh năng m&aacute;y ảnh, bao gồm chế độ Auto HDR, tự động đối tượng khung, v&agrave; để kết nối với c&aacute;c thiết bị di động sử dụng khả năng kết nối kh&ocirc;ng d&acirc;y v&agrave; NFC</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Th&acirc;n m&aacute;y Sony với 16-50mm f/3.5-5.6 OSS với zoom bật, cung cấp một dải ti&ecirc;u cự 35mm tương đương 24-75mm. Phạm vi của quan điểm rất ph&ugrave; hợp để chụp h&agrave;ng ng&agrave;y v&agrave; được tăng cường hơn nữa th&ocirc;ng qua sự ổn định h&igrave;nh ảnh quang học SteadyShot để giảm thiểu m&aacute;y ảnh bị rung. C&ocirc;ng nghệ năng lượng zoom cho ph&eacute;p điều khiển vị tr&iacute; zoom v&agrave; bao gồm một phần tử ph&acirc;n t&aacute;n cực thấp c&ugrave;ng bốn yếu tố phi cầu gi&uacute;p giảm thiểu quang sai m&agrave;u sắc trong suốt dải zoom cũng như đ&oacute;ng g&oacute;p v&agrave;o độ sắc n&eacute;t tổng thể cao.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>T&Iacute;NH NĂNG NỔI BẬT</strong></p>\r\n\r\n<p><strong>Cảm biến 20,1 MP Exmor APS HD CMOS v&agrave; BIONZ xử l&yacute; h&igrave;nh ảnh X</strong></p>\r\n\r\n<p>Cảm biến Exmor APS HD CMOS độ ph&acirc;n giải 20,1 MP APS-C v&agrave; bộ xử l&yacute; h&igrave;nh ảnh BIONZ X l&agrave;m việc c&ugrave;ng nhau để tạo ra độ ph&acirc;n giải cao, h&igrave;nh ảnh chi tiết tốt với tiếng ồn tối thiểu v&agrave; chất lượng &aacute;nh s&aacute;ng v&agrave; nhạy cảm với ti&ecirc;u chuẩn ISO 16000. Bộ vi xử l&yacute; cũng g&oacute;p phần cung cấp đầy đủ tốc độ to&agrave;n bộ hệ thống m&aacute;y ảnh, bao gồm 25 điểm tương phản ph&aacute;t hiện hệ thống AF nhanh, tốc độ chụp li&ecirc;n tiếp tr&ecirc;n 3,5 fps, v&agrave; 1080i / p quay video full HD ở nhiều tốc độ khung h&igrave;nh.</p>\r\n\r\n<p><img alt=\"Sony A5000 + Kit 16-50mm\" src=\"http://zshop.vn/images/cuong_sp/Macbook/May_anh/Sony-A5000-10.jpg\" style=\"height:500px; margin:0px; width:500px\" /></p>\r\n\r\n<p>Sony A5000 sắc n&eacute;t hơn với 20.1 MP</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Ghi &acirc;m phim HD</strong></p>\r\n\r\n<p>Quay video HD 1080i / p được hỗ trợ ở cả 60 v&agrave; 24 fps tỷ lệ khung h&igrave;nh để phim c&oacute; độ ph&acirc;n giải cao với một mặt b&oacute;ng, xem điện ảnh. Video được ghi lại bằng c&aacute;ch sử dụng m&atilde; h&oacute;a AVCHD chất lượng cao v&agrave; được x&acirc;y dựng trong hồ sơ micro &acirc;m thanh stereo &acirc;m thanh trong &acirc;m thanh Dolby Digital AC-3 định dạng. Ngo&agrave;i ra, 1440 x 1080p, 1280 x 720p, v&agrave; 640 x 480p định dạng cũng được hỗ trợ trong c&aacute;c định dạng MP4 Internet th&acirc;n thiện.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>X&acirc;y dựng trong Wi-Fi kết nối&nbsp;</strong></p>\r\n\r\n<p>Khả năng kết nối Wi-Fi cho ph&eacute;p&nbsp;<strong>A5000</strong>&nbsp;ngay lập tức chia sẻ h&igrave;nh ảnh với c&aacute;c thiết bị di động để chia sẻ trực tuyến trực tiếp đến mạng x&atilde; hội, qua email, v&agrave; c&aacute;c trang web lưu trữ điện to&aacute;n đ&aacute;m m&acirc;y. NFC (Near Field Communication) cũng được hỗ trợ, cho ph&eacute;p kết nối một chạm giữa m&aacute;y ảnh v&agrave; c&aacute;c thiết bị di động tương th&iacute;ch; thiết lập y&ecirc;u cầu.kh&ocirc;ng phức tạp&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>M&agrave;n h&igrave;nh LCD&nbsp;</strong></p>\r\n\r\n<p>Để theo d&otilde;i xem trực tiếp, ph&aacute;t lại h&igrave;nh ảnh v&agrave; xem x&eacute;t, v&agrave; điều hướng menu, một m&agrave;n h&igrave;nh m&agrave;n h&igrave;nh cảm ứng LCD 3,0&quot; , 460.8k chấm được t&iacute;ch hợp v&agrave;o th&acirc;n m&aacute;y v&agrave; c&oacute; thiết kế nghi&ecirc;ng 180 &deg; để th&uacute;c đẩy sử dụng dễ từ g&oacute;c độ thấp cũng như ở ph&iacute;a trước m&aacute;y ảnh.</p>\r\n\r\n<p><img alt=\"Sony A5000 + Kit 16-50mm\" src=\"http://zshop.vn/images/cuong_sp/Macbook/May_anh/Sony-A5000-11.jpg\" style=\"height:500px; margin:0px; width:500px\" /></p>\r\n\r\n<p>M&agrave;n h&igrave;nh sắc n&eacute;t hơn</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Ống k&iacute;nh&nbsp;</strong><strong>Sony 16-50mm f/3.5-5.6 OSS c&oacute; thể thu, zoom &nbsp;</strong></p>\r\n\r\n<p>Bao gồm 16-50mm f/3.5-5.6 OSS c&oacute; thể thu zoom ống k&iacute;nh cung cấp một dải ti&ecirc;u cự 35mm tương đương 24-75mm, bao gồm g&oacute;c rộng đến quan điểm ch&acirc;n dung d&agrave;i cho ph&ugrave; hợp với l&agrave;m việc trong một loạt c&aacute;c t&igrave;nh huống. N&oacute; c&oacute; t&iacute;nh năng ổn định h&igrave;nh ảnh quang học SteadyShot để gi&uacute;p giảm thiểu sự xuất hiện của m&aacute;y ảnh bị rung l&ecirc;n đến bốn bước tốc độ m&agrave;n trập để hưởng lợi rất nhiều hoạt động trong điều kiện &aacute;nh s&aacute;ng v&agrave; với độ ph&oacute;ng đại zoom lớn hơn. Một yếu tố ph&acirc;n t&aacute;n cực thấp v&agrave; bốn yếu tố phi cầu được t&iacute;ch hợp v&agrave;o việc x&acirc;y dựng quang học gi&uacute;p giảm quang sai m&agrave;u sắc cũng như sự gia tăng r&otilde; r&agrave;ng, độ sắc n&eacute;t v&agrave; độ tương phản v&agrave; sức mạnh zoom cơ chế zoom điện tử theo định hướng mang lại hiệu quả cao hơn để kiểm so&aacute;t vị tr&iacute; của zoom ống k&iacute;nh.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>C&aacute;c t&iacute;nh năng m&aacute;y ảnh kh&aacute;c&nbsp;</strong></p>\r\n\r\n<p>-&nbsp;<strong>A5000</strong>&nbsp;được x&acirc;y dựng trong cửa sổ flash c&oacute; sẵn để cung cấp th&ecirc;m &aacute;nh s&aacute;ng khi l&agrave;m việc trong điều kiện thiếu s&aacute;ng.&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>- Tự động lựa chọn đối tượng khung l&agrave; một chế độ chụp th&ocirc;ng minh c&oacute; thể tự động ph&acirc;n t&iacute;ch cảnh v&agrave; c&aacute;c đối tượng b&ecirc;n trong n&oacute; v&agrave; x&aacute;c định một th&agrave;nh phần mặt thẩm mỹ, l&agrave;m h&agrave;i l&ograve;ng cho bạn. Những h&igrave;nh ảnh tiếp theo sẽ được cắt theo chủ đề, kết quả l&agrave; một th&agrave;nh phần c&acirc;n bằng m&agrave; sử dụng nhận diện khu&ocirc;n mặt v&agrave; theo d&otilde;i tập trung để đảm bảo chất lượng h&igrave;nh ảnh cao v&agrave; độ sắc n&eacute;t.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>- Chế độ h&igrave;nh ảnh hiệu ứng cho ph&eacute;p bạn &aacute;p dụng c&aacute;c thiết lập s&aacute;ng tạo v&agrave; nhấn mạnh kh&iacute;a cạnh n&agrave;o đ&oacute; của h&igrave;nh ảnh c&aacute; nh&acirc;n cho một chất lượng h&igrave;nh ảnh thẩm mỹ hơn phong ph&uacute; hơn. Posterization (Color / B &amp; W) , Pop Color, Retro H&igrave;nh ảnh , một phần m&agrave;u (R / G / B / Y) , High Contrast đơn sắc , Toy Camera, mềm cao ch&iacute;nh , Soft Focus , HDR họa, Rich- Giai điệu đơn sắc , thu nhỏ, m&agrave;u nước, v&agrave; minh họa c&aacute;c chế độ c&oacute; sẵn.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>- C&aacute;c t&iacute;nh năng h&igrave;nh ảnh s&aacute;ng tạo v&agrave; cung cấp truy cập nhanh v&agrave;o Picture Effects cũng như kiểm so&aacute;t d&agrave;nh ri&ecirc;ng để điều chỉnh nền Defocus , độ s&aacute;ng, m&agrave;u sắc, v&agrave; vividness . Ngo&agrave;i ra, c&aacute;c hiệu ứng c&oacute; thể được kết hợp với t&ugrave;y chọn điều chỉnh gần như v&ocirc; hạn</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>- Chế độ tự động cao l&agrave; một thiết lập tự động th&ocirc;ng minh c&oacute; thể nhanh ch&oacute;ng x&aacute;c định c&aacute;c m&ocirc;i trường chụp ảnh hiện tại v&agrave; điều chỉnh c&agrave;i đặt m&aacute;y ảnh rất quan trọng để c&oacute; được kết quả tối ưu. Chế độ n&agrave;y cũng sử dụng Auto HDR v&agrave; cầm tay chế độ Twilight, Để c&oacute; thể sử dụng kỹ thuật gh&eacute;p ảnh để xử l&yacute; tốt hơn c&aacute;c điều kiện &aacute;nh s&aacute;ng kh&oacute; khăn .</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>- Anti-Motion Blur tự động hiệu chỉnh cho chủ thể chuyển động để cung cấp h&igrave;nh ảnh sắc n&eacute;t trong điều kiện &aacute;nh s&aacute;ng yếu. được thực hiện bằng c&aacute;ch ghi lại s&aacute;u khung tuần tự v&agrave; hợp phần của mỗi h&igrave;nh ảnh để đảm bảo độ sắc n&eacute;t tổng thể.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>- Chế độ cầm tay tự động chi tiết từ s&aacute;u h&igrave;nh ảnh tuần tự ghi để đạt được cao hơn độ sắc n&eacute;t h&igrave;nh ảnh tổng thể khi l&agrave;m việc trong điều kiện &aacute;nh s&aacute;ng yếu.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>- Nhận diện khu&ocirc;n mặt / nụ cười c&oacute; thể ph&aacute;t hiện v&agrave; cơ sở tập trung, c&acirc;n bằng trắng, v&agrave; thiết lập tiếp x&uacute;c tr&ecirc;n khu&ocirc;n mặt được c&ocirc;ng nhận. C&ocirc;ng nghệ n&agrave;y cũng cho ph&eacute;p m&agrave;n trập sẽ được ph&aacute;t h&agrave;nh , dựa tr&ecirc;n ba cấp độ nhạy cảm , khi một nụ cười được ph&aacute;t hiện trong khung h&igrave;nh.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>- Thiết lập phong c&aacute;ch s&aacute;ng tạo cung cấp kiểm so&aacute;t như thế n&agrave;o m&aacute;y ảnh xử l&yacute; h&igrave;nh ảnh dựa tr&ecirc;n s&aacute;u phong c&aacute;ch kh&aacute;c nhau được x&aacute;c định trước : Standard, Vivid , Portrait, Landscape, Sunset, v&agrave; B &amp; W. Trong c&aacute;c thiết lập n&agrave;y , độ tương phản , độ b&atilde;o h&ograve;a , độ sắc n&eacute;t v&agrave; cũng c&oacute; thể được điều chỉnh t&ugrave;y thuộc v&agrave;o sở th&iacute;ch c&aacute; nh&acirc;n .</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>- Sony E-mount d&agrave;nh khả năng tương th&iacute;ch với một số lượng rộng lớn của ống k&iacute;nh v&agrave; bộ điều hợp ống k&iacute;nh.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>- Sạc USB thuận tiện cho ph&eacute;p bạn sạc pin lithium-ion c&oacute; trong m&aacute;y ảnh th&ocirc;ng qua kết nối USB hoặc từ một ổ cắm AC hoặc nguồn m&aacute;y t&iacute;nh .</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>- Pin năng lượng Stamina cho ph&eacute;p l&ecirc;n đến khoảng 420 bức ảnh được thực hiện trong một lần sạc . Pin InfoLITHIUM cũng cho ph&eacute;p bạn xem tỷ lệ phần trăm c&ograve;n lại của pin c&ograve;n lại.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>- Phần mềm PlayMemories Home được c&agrave;i đặt sẵn tr&ecirc;n m&aacute;y ảnh để tải về tự động v&agrave;o m&aacute;y t&iacute;nh khi kết nối để cung cấp hỗ trợ để xem, tổ chức , chỉnh sửa , chỉnh sửa , in ấn, v&agrave; chia sẻ h&igrave;nh ảnh .</p>\r\n', '2', '12', '4', '2015-01-10');
INSERT INTO `products` VALUES ('30', 'Canon EF 40mm f/2.8 STM', '3', '3587000', '<p>Điểm nổi bật của sản phẩm</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>- Ti&ecirc;u cự: 40mm, Khẩu độ lớn nhất: F2.8</p>\r\n\r\n<p>- Số l&aacute; khẩu: 7, Đường k&iacute;nh filter: 52mm</p>\r\n\r\n<p>- Lấy n&eacute;t trong bằng Stepper Motor</p>\r\n\r\n<p>- Khoảng lấy n&eacute;t gần nhấn: 30cm</p>\r\n\r\n<p>- C&oacute; lớp tr&aacute;ng &quot;Super spectra coatings&quot; gi&uacute;p giảm hiện tượng b&oacute;ng ma, l&oacute;a</p>\r\n', '<h2>Canon EF 40mm f/2.8 STM</h2>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&Ocirc;́ng k&iacute;nh&nbsp;<a href=\"http://zshop.vn/canon-40-f2.8.html\">Canon EF 40mm f/2.8 STM</a>&nbsp;từ Canon l&agrave; một sự ch&agrave;o mừng của Canon cho d&ograve;ng ống k&iacute;nh EF. Đ&acirc;y l&agrave; ống k&iacute;nh nhẹ nhất (c&ugrave;ng với ống k&iacute;nh 50mm f/1.8 II) trong gia đ&igrave;nh EF.&nbsp;<a href=\"http://zshop.vn/canon-40-f2.8.html\">Canon EF 40mm f/2.8 STM</a>&nbsp;d&agrave;i&nbsp;&iacute;t hơn một inch, do đ&oacute;, n&oacute; sẽ kh&ocirc;ng bao giờ thu h&uacute;t sự ch&uacute; &yacute; kh&ocirc;ng mong muốn cho bạn khi bạn đang chụp ở nơi c&ocirc;ng cộng. Một khẩu độ tối đa f/2.8 tươi s&aacute;ng cho ph&eacute;p bạn chụp với b&acirc;́t kỳ điều kiện &aacute;nh s&aacute;ng n&agrave;o, v&agrave; cấu h&igrave;nh ống k&iacute;nh tinh vi, bao gồm cả một trong những yếu tố phi cầu, đảm bảo chất lượng h&igrave;nh ảnh từ trung t&acirc;m đến c&aacute;c cạnh của khung. Giảm đ&aacute;ng kể b&oacute;ng mờ v&agrave; flare, v&agrave; gi&uacute;p c&acirc;n bằng m&agrave;u sắc tuyệt vời.</p>\r\n\r\n<p><img alt=\"\" src=\"http://zshop.vn/images/product/21/33722_113407734194fea942b7e469.jpg\" style=\"height:500px; margin:0px; width:500px\" /></p>\r\n\r\n<p>Canon EF 40mm f/2.8 STM</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>STM chức năng tự động lấy n&eacute;t y&ecirc;n tĩnh, mịn v&agrave; li&ecirc;n tục trong qu&aacute; tr&igrave;nh hoạt động video. Khẩu độ tr&ograve;n được h&igrave;nh th&agrave;nh bởi 7 l&aacute; m&agrave;ng kết hợp với khẩu độ rộng c&oacute; thể cung cấp cho bạn bokeh đẹp - khu vực nền out-of-tập trung h&igrave;nh ảnh của bạn. Ống k&iacute;nh&nbsp;<a href=\"http://zshop.vn/canon-40-f2.8.html\">Canon EF 40mm f/2.8 STM</a><a href=\"http://zshop.vn/canon-40-f2.8.html\">&nbsp;</a>n&agrave;y sẽ cung cấp cho bạn một c&aacute;i nh&igrave;n tương đương &ocirc;́ng k&iacute;nh 64mm khi được sử dụng tr&ecirc;n một m&aacute;y ảnh với một bộ cảm biến APS-C, v&agrave; c&oacute; thể tập trung gần như l&agrave; 11,81&quot;. Nếu bạn đang t&igrave;m kiếm một ống k&iacute;nh &quot;b&igrave;nh thường&quot;, đ&acirc;y l&agrave; một nơi tốt để bắt đầu.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>ĐẠT T&Iacute;NH NỔI BẬT</strong></p>\r\n\r\n<p>- C&ocirc;ng nghệ STM mới được ph&aacute;t triển cho lens lấy n&eacute;t nhanh v&agrave; y&ecirc;n tĩnh, AF hoạt động li&ecirc;n tục trong khi quay video</p>\r\n\r\n<p>- Ống k&iacute;nh nhẹ v&agrave; gọn gi&uacute;p cho việc cầm m&aacute;y dễ d&agrave;ng</p>\r\n\r\n<p>- Khẩu độ f/2.8 s&aacute;ng để chụp trong mọi điều kiện &aacute;nh s&aacute;ng</p>\r\n\r\n<p>- Chất lượng h&igrave;nh ảnh cao từ trung t&acirc;m đến ngoại vi nhờ v&agrave;o cấu h&igrave;nh ống k&iacute;nh ti&ecirc;n tiến của n&oacute; bao gồm một yếu tố phi cầu</p>\r\n\r\n<p>- Lớp phủ tối ưu để giảm thiểu b&oacute;ng mờ v&agrave; flare trong khi cung cấp c&acirc;n bằng m&agrave;u sắc đặc biệt</p>\r\n\r\n<p>- Khẩu độ (7 l&aacute;) cho h&igrave;nh nền mềm tập trung xinh đẹp</p>\r\n\r\n<p>- Khoản c&aacute;ch lấy n&eacute;t gần nhất ngắn khoảng c&aacute;ch chỉ 11,81 &quot;</p>\r\n\r\n<p>- Đặt tr&ecirc;n một m&aacute;y ảnh với một bộ cảm biến APS-C, ống k&iacute;nh n&agrave;y cung cấp một c&aacute;i nh&igrave;n tương tự như một ống k&iacute;nh 64mm ở định dạng 35mm</p>\r\n', '3', '12', '3', '2015-01-05');
INSERT INTO `products` VALUES ('31', 'Canon EF 50mm f/1.8 II (Chính hãng)', '3', '2300111', '<p>Điểm nổi bật của sản phẩm</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>- Cấu tạo ống k&iacute;nh: 6 thấu k&iacute;nh thuộc 5 nh&oacute;m</p>\r\n\r\n<p>- G&oacute;c nh&igrave;n ngang: 46&deg;</p>\r\n\r\n<p>- Khoảng lấy n&eacute;t nhỏ nhất: 45cm</p>\r\n\r\n<p>- Đường k&iacute;nh Filter: &Oslash;52mm</p>\r\n\r\n<p>- K&iacute;ch thước / Trọng lượng: &Oslash;68.2 x 41.0mm / 130g</p>\r\n', '<p><strong>GIỚI THIỆU</strong></p>\r\n\r\n<p><a href=\"http://zshop.vn/lens-canon/\"><strong>Ống k&iacute;nh Canon</strong></a>&nbsp;n&agrave;y l&agrave; ống k&iacute;nh c&oacute; ti&ecirc;u cự cố định&nbsp;<strong>Canon EF 50mm f1.8 II</strong>&nbsp;(tr&ecirc;n m&aacute;y full frame), c&ograve;n gọi l&agrave; ống fixed hay Prime. Ống k&iacute;nh c&oacute; g&oacute;c chụp chuẩn (như mắt người).&nbsp;Được xem như rẻ tiền nhất hiện nay, nhưng chất lượng h&igrave;nh ảnh của n&oacute; d&aacute;m th&aacute;ch thức với bất kỳ ống k&iacute;nh n&agrave;o. Quang sai m&agrave;u thấp, hiện tượng m&eacute;o h&igrave;nh gần như kh&ocirc;ng c&oacute;. Vấn đề của ống k&iacute;nh n&agrave;y l&agrave; h&igrave;nh d&aacute;ng cửa điều s&aacute;ng h&igrave;nh lục gi&aacute;c l&agrave;m bokeh kh&ocirc;ng đẹp v&agrave; chất liệu bằng nhựa (thay v&igrave; th&eacute;p).&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"http://zshop.vn/images/b/Update/up3/Canon%20EF%2050mm.jpg\" style=\"height:340px; margin:0px; width:340px\" /></p>\r\n\r\n<p>Canon EF 50mm f/1.8 II</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ch&acirc;n dung bao giờ cũng l&agrave; thể loại m&agrave; nhiều nhiếp ảnh gia mới bước ch&acirc;n v&agrave;o con đường &ldquo;nghiện ngập&rdquo; m&aacute;y ảnh DSLR đều ưa th&iacute;ch. Những ống k&iacute;nh th&iacute;ch hợp cho nhu cầu n&agrave;y thường l&agrave; c&aacute;c ổng k&iacute;nh c&oacute; độ mở lớn f/2.8 hoặc thậm ch&iacute; l&agrave; nhỏ hơn.&nbsp;<strong>Canon EF 50mm F/1.8 II</strong>&nbsp;l&agrave; một trong số &iacute;t m&aacute;y ảnh c&oacute; gi&aacute; rẻ bất ngờ c&oacute; thể phục vụ cho nhu cầu n&agrave;y. Với khẩu độ tối đa f/1.8, g&oacute;c nh&igrave;n rộng 46 độ v&agrave; chiều d&agrave;i ti&ecirc;u cự 50mm, ống k&iacute;nh&nbsp;<strong>Canon EF 50mm F/1.8 II</strong>&nbsp;tạo được thế mạnh ở khả năng lấy n&eacute;t cao ở khẩu độ từ f/2.8 đến nhỏ hơn mặc d&ugrave; ở độ mở rộng tối đa f/1.8 độ n&eacute;t c&oacute; phần giảm đ&ocirc;i ch&uacute;t.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>T&Iacute;NH NĂNG NỔI BẬT</strong></p>\r\n\r\n<p><strong>Ống k&iacute;nh ti&ecirc;u cự cố định 50mm</strong></p>\r\n\r\n<p>L&agrave; d&ograve;ng ống k&iacute;nh ti&ecirc;u cự cố định,&nbsp;<strong>Canon EF 50mm F/1.8 II</strong>&nbsp;sở hữu độ d&agrave;i ti&ecirc;u cự 50mm, được xem l&agrave; ống k&iacute;nh ti&ecirc;u chuẩn c&oacute; g&oacute;c nh&igrave;n 46 độ v&agrave; phối cảnh giống mắt người nhất, mang lại h&igrave;nh ảnh với độ ch&iacute;nh x&aacute;c cao, chụp được h&igrave;nh ảnh trung thực.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Độ n&eacute;t cao</strong></p>\r\n\r\n<p>Thế mạnh của ống k&iacute;nh&nbsp;<strong>Canon EF 50mm F/1.8 II</strong>&nbsp;l&agrave; độ n&eacute;t vượt trội. Mặc d&ugrave; ở khẩu độ mở rộng tối đa f/1.8mm độ n&eacute;t c&oacute; phần giảm đ&ocirc;i ch&uacute;t, tuy nhi&ecirc;n từ f/2.8 đến nhỏ hơn, ống ngắm cho h&igrave;nh ảnh độ n&eacute;t cao. Theo nhiều chuy&ecirc;n gia đ&aacute;nh gi&aacute;, ống k&iacute;nh n&agrave;y c&ograve;n sắc n&eacute;t hơn cả ống k&iacute;nh&nbsp;<strong>Canon EF 24-70mm f/2.8 L USM</strong>&nbsp;hạng sang rất đắt tiền của h&atilde;ng Canon.</p>\r\n\r\n<p><img alt=\"\" src=\"http://zshop.vn/images/b/Update/up3/Canon%20EF%2050mm%202.jpg\" style=\"height:500px; margin:0px; width:500px\" /></p>\r\n\r\n<p>Canon EF 50mm f/1.8 II</p>\r\n\r\n<p><strong>Tốc độ nhanh</strong></p>\r\n\r\n<p>Khẩu độ mở rộng tối đa f/1.8 khiến ống k&iacute;nh&nbsp;<strong>Canon EF 50mm F/1.8 II</strong>&nbsp;trở th&agrave;nh một ống k&iacute;nh c&oacute; tốc độ nhanh, khả năng bắt s&aacute;ng tốt th&iacute;ch hợp chụp trong m&ocirc;i trường &aacute;nh s&aacute;ng yếu, đặc biệt khi kh&ocirc;ng muốn hoặc kh&ocirc;ng thể sử dụng đ&egrave;n flash.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Gọn nhẹ</strong></p>\r\n\r\n<p>C&oacute; k&iacute;ch thước 68.2 x 50.5mm, ống k&iacute;nh&nbsp;<strong>Canon EF 50mm F/1.8 II</strong>&nbsp;mặc d&ugrave; c&oacute; g&aacute; lắp bằng nhựa tổng hợp tuy nhi&ecirc;n ống c&oacute; phần nhẹ 130g. B&ecirc;n trong c&aacute;c thấu k&iacute;nh đều bằng k&iacute;nh gồm 6 thấu k&iacute;nh thuộc 5 nh&oacute;m ti&ecirc;u chuẩn.</p>\r\n', '3', '12', '7', '2015-01-19');
INSERT INTO `products` VALUES ('32', 'Canon EF 135mm f/2L USM (Chính hãng)', '3', '23091840', '<p>Điểm nổi bật của sản phẩm</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>- Cấu tạo ống k&iacute;nh : 14 thấu k&iacute;nh thuộc 12 nh&oacute;m</p>\r\n\r\n<p>- G&oacute;c nh&igrave;n ngang : 11&deg;25&#39;</p>\r\n\r\n<p>- Khoảng lấy n&eacute;t nhỏ nhất : 0.48m, Độ ph&oacute;ng đại lớn nhất : 1x</p>\r\n\r\n<p>- Đường k&iacute;nh Filter : &Oslash;72 mm</p>\r\n\r\n<p>- K&iacute;ch thước / Trọng lượng : &Oslash;82.5 &times; 186.6mm / 1,090g</p>\r\n', '<p><strong>GIỚI THIỆU</strong></p>\r\n\r\n<p>Sở hữu khoảng c&aacute;ch c&aacute;ch lấy n&eacute;t gần nhất 90cm c&ugrave;ng khẩu độ f/2.0 v&agrave; ti&ecirc;u cự 135mm,&nbsp;<strong><a href=\"http://zshop.vn/lens-canon/\">m&aacute;y ảnh&nbsp;Canon</a>&nbsp;EF 135mm f/2L USM</strong>&nbsp;l&agrave; chiếc ống k&iacute;nh tele tầm trung l&yacute; tưởng cho chụp ảnh ch&acirc;n dung, d&atilde; ngoại, phong cảnh, thể thao...với hiệu ứng bokeh đẹp mắt. Gọn nhẹ c&ugrave;ng những trang bị cao cấp kh&aacute;c như m&ocirc;-tơ lấy n&eacute;t si&ecirc;u thanh, thấu k&iacute;nh chất lượng cao c&ugrave;ng thiết kế &ldquo;L series&rdquo; cao cấp,&nbsp;<strong>Canon EF 135mm f/2L USM</strong>&nbsp;ch&iacute;nh l&agrave; chiếc ống k&iacute;nh tuyệt vời gi&uacute;p bạn biến những bức ảnh của m&igrave;nh trở th&agrave;nh kiệt t&aacute;c.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Canon EF 135mm f/2L USM\" src=\"http://zshop.vn/images/product/1/IMG_152004.jpg\" style=\"height:400px; margin:0px; width:400px\" /></p>\r\n\r\n<p>Canon EF 135mm f/2L USM</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>T&Iacute;NH NĂNG NỔI BẬT</strong></p>\r\n\r\n<p><strong>Chụp ảnh thật đẹp</strong></p>\r\n\r\n<p>Với ti&ecirc;u cự 135mm v&agrave; khẩu độ f/2.0,&nbsp;<strong>Canon EF 135mm f/2L USM</strong>&nbsp;l&agrave; chiếc ống tele tầm trung k&iacute;nh l&yacute; tưởng cho chụp ảnh đa dụng từ phong cảnh, tiệc cưới, hội nghị cho đến ảnh hoang d&atilde;, thể thao hay thậm ch&iacute; l&agrave; ảnh ch&acirc;n dung, với hiệu ứng bokeh đẹp mắt c&ugrave;ng khả năng t&aacute;i tạo m&agrave;u sắc cực kỳ ch&acirc;n thực v&agrave; sống động. D&ugrave; l&agrave; chụp trong điều kiện thiếu s&aacute;ng hay chụp ở g&oacute;c độ ngược s&aacute;ng, ảnh vẫn hết sức tự nhi&ecirc;n v&agrave; tuyệt vời.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Thấu k&iacute;nh chất lượng cao</strong></p>\r\n\r\n<p>Ống k&iacute;nh&nbsp;<strong>Canon EF 135mm f/2L USM</strong>&nbsp;c&oacute; cấu tạo gồm 10 phần tử thấu k&iacute;nh, gh&eacute;p th&agrave;nh 8 nh&oacute;m; trong đ&oacute; c&oacute; nhiều th&agrave;nh phần thấu k&iacute;nh chất lượng cao, c&oacute; t&aacute;c dụng ức chế quang sai v&agrave; điều chỉnh &aacute;nh s&aacute;ng ngoại vi. Ngo&agrave;i ra, c&aacute;c lớp tr&aacute;ng phủ đặc biệt của Canon c&ograve;n gi&uacute;p kiểm so&aacute;t hiện tượng l&oacute;e s&aacute;ng v&agrave; b&oacute;ng mờ tr&ecirc;n h&igrave;nh ảnh.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Lấy n&eacute;t &ecirc;m &aacute;i với m&ocirc;-tơ si&ecirc;u &acirc;m USM</strong></p>\r\n\r\n<p>C&ocirc;ng nghệ lấy n&eacute;t si&ecirc;u thanh USM cho ph&eacute;p&nbsp;<strong>Canon EF 135mm f/2L USM</strong>&nbsp;lấy n&eacute;t thật nhanh, thật &ecirc;m v&agrave; gần như im lặng ho&agrave;n to&agrave;n. M&ocirc;-men xoắn giữ nhạy b&eacute;n đảm bảo đạt đến mức lấy n&eacute;t ch&iacute;nh x&aacute;c tuyệt đối. Sản phẩm đảm bảo h&igrave;nh ảnh lu&ocirc;n chi tiết ở mọi khẩu độ v&agrave; sắc n&eacute;t n&eacute;t từ trung t&acirc;m đến c&aacute;c cạnh v&agrave; c&aacute;c g&oacute;c. Đ&acirc;y l&agrave; điểm quan trọng gi&uacute;p h&igrave;nh ảnh chụp được lu&ocirc;n đạt được độ ho&agrave;n hảo tối đa.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Thiết kế cao cấp của &ldquo;L&rdquo; series</strong></p>\r\n\r\n<p><strong>Canon EF 135mm f/2L USM</strong>&nbsp;thuộc d&ograve;ng ống k&iacute;nh cao cấp của Canon, đặc trưng với đường viền đỏ chạy quanh th&acirc;n. V&ograve;ng xoay v&agrave; vỏ bọc được l&agrave;m từ vật liệu cao cấp, b&ecirc;n cạnh ng&agrave;m kim loại chất lượng cao. Lớp cao su bọc xung quanh mềm, tạo cảm gi&aacute;c chắc chắn khi cầm tr&ecirc;n tay. C&oacute; k&iacute;ch thước 82.5x112mm v&agrave; trọng lượng 750g, bao gồm khả năng chống bụi v&agrave; nước, thỏa m&atilde;n tối đa nhu cầu chụp trong m&ocirc;i trường khắc nghiệt của c&aacute;c nhiếp ảnh gia chuy&ecirc;n nghiệp.</p>\r\n', '3', '12', '7', '2015-01-26');
INSERT INTO `products` VALUES ('33', 'Leica 24mm F/3.8 Elmar M Aspherical', '4', '51695000', '<p>Điểm nổi bật của sản phẩm</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>- Cấu tạo gồm 8 thấu k&iacute;nh chia l&agrave;m 6 nh&oacute;m</p>\r\n\r\n<p>- Khoảng c&aacute;ch lấy n&eacute;t gần nhất 0.7m</p>\r\n\r\n<p>- Độ d&agrave;i ti&ecirc;u cự 24mm, Ng&agrave;m Leica M</p>\r\n\r\n<p>- Đường k&iacute;nh / Trọng lượng 53mm/260g</p>\r\n\r\n<p>- Khoảng khẩu độ F/3.8 - F/16</p>\r\n', '<p>Ống k&iacute;nh ELMAR-M 24mm/f3.8 ASPH với k&iacute;ch thước tiện dụng của n&oacute; được ph&acirc;n biệt bởi chất lượng h&igrave;nh ảnh rất cao v&agrave; h&igrave;nh ảnh g&oacute;c rất rộng - l&agrave;m cho n&oacute; một ống k&iacute;nh g&oacute;c rộng đặc biệt. N&oacute; ph&ugrave; hợp cho chụp ảnh ở một kh&ocirc;ng gian rất rộng để chụp quang cảnh v&agrave; rất th&iacute;ch&nbsp; hơp để đi du lịch chụp lại những cảnh quang thi&ecirc;n nhi&ecirc;n. Khi sử dụng ống k&iacute;nh bức ảnh sẽ c&oacute; độ s&acirc;u của trường rất lớn m&agrave; n&oacute; thậm ch&iacute; c&oacute; thể t&aacute;i tạo to&agrave;n bộ những cảnh quang xung quanh. Điều đ&oacute; l&agrave;m cho n&oacute; tuyệt vời cho sử dụng kết hợp với chế độ chụp ảnh của Leica M8.</p>\r\n\r\n<p><img alt=\"ong-kinh-leica-24mm-f3.8-12\" src=\"http://zshop.vn/images/A/hai/ong-kinh-leica-24mm-f3.8-1.jpg\" style=\"height:400px; margin:0px; width:418px\" /></p>\r\n\r\n<p>Leica 24mm F/3.8 Elmar M Aspherical</p>\r\n\r\n<p><img alt=\"ong-kinh-leica-24mm-f3.313\" src=\"http://zshop.vn/images/A/hai/ong-kinh-leica-24mm-f3.8-12.jpg\" style=\"height:405px; margin:0px; width:400px\" /></p>\r\n\r\n<p>Leica 24mm F/3.8 Elmar M Aspherical</p>\r\n\r\n<p><img alt=\"ong-kinh-leica-24mm-f3.8-1212\" src=\"http://zshop.vn/images/A/hai/ong-kinh-leica-24mm-f3.8-123.jpg\" style=\"height:400px; margin:0px; width:535px\" /></p>\r\n\r\n<p>Leica 24mm F/3.8 Elmar M Aspherical</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Được sử dụng tr&ecirc;n m&ocirc; h&igrave;nh kỹ thuật số LEICA M8, chiều d&agrave;i ti&ecirc;u cự n&agrave;y rất thực tế cho ph&eacute;p một phổ biến rộng r&atilde;i tất cả c&aacute;c ứng dụng từ ch&acirc;n dung cuộc sống đến chụp ảnh phong cảnh v&agrave; kiến ​​tr&uacute;c.</p>\r\n\r\n<p>LEICA ELMAR-M 24 mm f/3.8 ASPH l&agrave; sự kết hợp l&yacute; tưởng với Leica M8. C&aacute;c khẩu độ ban đầu l&agrave; 3.8 cho h&igrave;nh ảnh tương phản v&agrave; r&otilde; đến từng chi tiết. Thực tế ống k&iacute;nh l&agrave;m tăng th&ecirc;m độ s&acirc;u của m&aacute;y ảnh. Đến tới ti&ecirc;u cự ti&ecirc;u chuẩn th&igrave; độ s&acirc;u của trường sẽ tập trung được một c&aacute;ch ho&agrave;n hảo l&ecirc;n to&agrave;n bộ bức ảnh. Đặc biệt th&iacute;ch hợp cho việc chụp ảnh ph&oacute;ng sự ghi lại từng khoảnh khắc đ&aacute;ng nhớ v&agrave; nhiếp ảnh kiến ​​tr&uacute;c.</p>\r\n', '3', '12', '5', '2015-01-16');
INSERT INTO `products` VALUES ('34', 'Leica SUMMILUX-M 24mm f/1,4 ASPH', '4', '152975000', '<p>Điểm nổi bật của sản phẩm</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ng&agrave;m: Leica M</p>\r\n\r\n<p>Phạm vi độ mở: f/1.4 - f/16</p>\r\n\r\n<p>Số lượng thấu k&iacute;nh v&agrave; nh&oacute;m: 8/10</p>\r\n\r\n<p>G&oacute;c nh&igrave;n: 84</p>\r\n\r\n<p>Phạm vi lấy n&eacute;t: 0.7m đến v&ocirc; cực</p>\r\n\r\n<p>Trọng lượng: 500g</p>\r\n', '<p><a href=\"http://zshop.vn/leica-summilux-m-24mm-f-1-4-asph.html\">Leica SUMMILUX-M 24mm f/1,4 ASPH</a>&nbsp;với k&iacute;ch thước nhỏ gọn, tốc độ cao đ&atilde; cung cấp cho c&aacute;c nhiếp ảnh gia một c&ocirc;ng cụ l&agrave;m việc ho&agrave;n to&agrave;n mới. Ống k&iacute;nh n&agrave;y l&agrave; l&yacute; tưởng với c&aacute;c điều kiện &aacute;nh s&aacute;ng tự nhi&ecirc;n cũng như &aacute;nh s&aacute;ng nh&acirc;n tạo.</p>\r\n\r\n<p>Cũng giống như ống k&iacute;nh 21mm f/1.4 (thế hệ trước của Leica&nbsp;<a href=\"http://zshop.vn/leica-summilux-m-24mm-f-1-4-asph.html\">SUMMILUX-M 24mm f/1,4 ASP</a>H), th&igrave; ống thế hệ mới n&agrave;y cũng c&oacute; 10 thấu k&iacute;nh, được chia l&agrave;m 8 nh&oacute;m &ndash; trong đ&oacute; c&oacute; 2 thấu k&iacute;nh aspherical đ&oacute;ng vai tr&ograve; hết sức quan trọng trong việc loại bỏ c&aacute;c yếu tố &aacute;nh s&aacute;ng đi lệch v&agrave; l&agrave;m giảm sự biến dạng h&igrave;nh ảnh.</p>\r\n\r\n<p><img alt=\"\" src=\"http://zshop.vn/images/Waki/Len/Leica/24_1.4/ASPH.jpg\" style=\"height:331px; margin:0px; width:600px\" /></p>\r\n\r\n<p>Được lắp r&aacute;p thủ c&ocirc;ng bởi đội ngủ nh&acirc;n vi&ecirc;n tay nghề cao,&nbsp;<a href=\"http://zshop.vn/leica-summilux-m-24mm-f-1-4-asph.html\">Leica SUMMILUX-M 24mm f/1,4 ASPH</a>&nbsp;c&oacute; độ tin cậy đ&aacute;nh kinh ngạc ngay cả khi bạn sử dụng trong điều kiện kh&oacute; khăn nhất.</p>\r\n\r\n<p>Bằng việc kết hợp c&aacute;c c&ocirc;ng nghệ kĩ thuật cao, c&aacute;c thuật ngữ to&aacute;n học ch&iacute;nh x&aacute;c, c&aacute;c thấu k&iacute;nh ASPH, Leica đ&atilde; loại bỏ gần như ho&agrave;n to&agrave;n hiện tượng quang sai, viền t&iacute;m, c&aacute;c tia s&aacute;ng ngược, m&eacute;o m&oacute; h&igrave;nh ảnh để cho ra đời c&aacute;c t&aacute;c phẩm nghệ thuật đỉnh cao!</p>\r\n\r\n<p>Sơ đồ MTF ở khẩu 1.4, 2.8 v&agrave; 5.6</p>\r\n', '3', '12', '2', '2015-01-11');
INSERT INTO `products` VALUES ('35', 'Leica SUMMILUX-M 21mm/f1,4 ASPH', '4', '151920000', '<p>&nbsp;</p>\r\n\r\n<p>Điểm nổi bật của sản phẩm</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ng&agrave;m: Leica M</p>\r\n\r\n<p>Phạm vi độ mở: f/1.4 - f/16</p>\r\n\r\n<p>Số lượng thấu k&iacute;nh v&agrave; nh&oacute;m: 8/10</p>\r\n\r\n<p>G&oacute;c nh&igrave;n: 92</p>\r\n\r\n<p>Tỉ lệ: 1:29</p>\r\n\r\n<p>Chiều d&agrave;i: 66mm</p>\r\n\r\n<p>Đường k&iacute;nh: 70mm</p>\r\n\r\n<p>Trọng lượng: 580g</p>\r\n', '<p><a href=\"http://zshop.vn/leica-summilux-m-21mm-f1-4-asph.html\"><strong>Leica 21mm f/1.4&nbsp;Summilux-M Aspherical</strong></a>&nbsp;l&agrave; một ống k&iacute;nh g&oacute;c si&ecirc;u rộng, được thiết kế để sử dụng trong c&aacute;c t&igrave;nh huống chụp ảnh nhanh. Với khoảng ti&ecirc;u cự 21mm, khẩu độ 1.4 v&agrave; được cấu tạo bởi c&aacute;c thấu k&iacute;nh cầu, lens cho ph&eacute;p ghi lại to&agrave;n bộ khung cảnh một c&aacute;c ch&iacute;nh x&aacute;c, hay chụp ảnh close-up với DOF cực mỏng v&agrave; n&eacute;t đến &ldquo;từng sợi l&ocirc;ng tơ&rdquo;.</p>\r\n\r\n<p><img alt=\"\" src=\"http://zshop.vn/images/Waki/Len/Leica/21_1.4/6452764337_abaf101cf2_z.jpg\" style=\"height:399px; margin:0px; width:600px\" /></p>\r\n\r\n<p>Ảnh chụp từ Leica SUMMILUX-M 21mm/f1,4 ASPH</p>\r\n\r\n<p><img alt=\"\" src=\"http://zshop.vn/images/Waki/Len/Leica/21_1.4/WD2.jpg\" style=\"height:404px; margin:0px; width:600px\" /></p>\r\n\r\n<p>Ảnh chụp từ Leica SUMMILUX-M 21mm/f1,4 ASPH</p>\r\n\r\n<p>Ống k&iacute;nh n&agrave;y c&oacute; thể tạo ra c&aacute;c hiệu ứng th&acirc;t ấn tượng từ tiền cảnh, hậu cảnh, v&agrave; to&agrave;n bộ khung cảnh. Việc lấy n&eacute;t, đo khoảng c&aacute;ch sẽ được thực hiện trực tiếp qua viewfinder của m&aacute;y ảnh một c&aacute;ch dễ d&agrave;ng.</p>\r\n\r\n<p><img alt=\"\" src=\"http://zshop.vn/images/Waki/Len/Leica/21_1.4/6379613257_45e88c6a74_z.jpg\" style=\"height:472px; margin:0px; width:600px\" /></p>\r\n\r\n<p>Ảnh chụp từ Leica SUMMILUX-M 21mm/f1,4 ASPH</p>\r\n\r\n<p>Với t&iacute;nh năng m&atilde; h&oacute;a &ldquo;6-bit&rdquo;, len cho ph&eacute;p c&aacute;c m&aacute;y ảnh kỹ thuật số d&ograve;ng M c&oacute; thế tương th&iacute;ch v&agrave; đọc được th&ocirc;ng tin quang học m&agrave; ống k&iacute;nh ghi nhận được.</p>\r\n\r\n<p>- Ống k&iacute;nh g&oacute;c rộng, nhỏ gọn với 2 yếu tố Aspherical mang lại hiệu suất quang học tuyệt vời</p>\r\n\r\n<p><img alt=\"\" src=\"http://zshop.vn/images/Waki/Len/Leica/21_1.4/asph.jpg\" style=\"height:347px; margin:0px; width:600px\" /></p>\r\n\r\n<p>- Sơ đồ MTF ở F1.4 v&agrave; 5.6</p>\r\n', '3', '12', '7', '2015-01-20');
INSERT INTO `products` VALUES ('36', 'Nikon AF-S 18-200mm f/3.5-5.6 G ED VR II (Chính hã', '2', '17220554', '<p>&nbsp;</p>\r\n\r\n<p>Điểm nổi bật của sản phẩm</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>- Cấu tạo ống k&iacute;nh : 16 thấu k&iacute;nh thuộc 12 nh&oacute;m</p>\r\n\r\n<p>- G&oacute;c nh&igrave;n ngang &nbsp; : 76&deg; - 8&deg;, Số l&aacute; m&agrave;n chập &nbsp; &nbsp;: 7</p>\r\n\r\n<p>- Khoảng c&aacute;ch lấy n&eacute;t nhỏ nhất : 0,5m</p>\r\n\r\n<p>- Độ ph&oacute;ng đại lớn nhất :1/4.5x, Đường k&iacute;nh filter : 72mm</p>\r\n\r\n<p>- K&iacute;ch thước / Trọng lượng :77 x 96.5mm/ 560g</p>\r\n', '<p><strong>GIỚI THIỆU</strong></p>\r\n\r\n<p><a href=\"http://zshop.vn/lens-nikon/\"><strong>Ống k&iacute;nh Nikon</strong></a>&nbsp;<strong>AF-S DX NIKKOR 18-200mm f/3.5-5.6G ED VR II</strong>&nbsp;cung cấp dải ti&ecirc;u cự trải rộng 18-200mm để sử dụng trong nhiều t&igrave;nh huống (g&oacute;c ảnh tương đương với g&oacute;c ảnh trong ống k&iacute;nh 27-300mm ở định dạng 35mm; với tỷ lệ thu ph&oacute;ng 11.1x) thuận tiện để chụp được mọi loại ảnh từ ảnh phong cảnh rộng đến ảnh ch&acirc;n dung c&acirc;̣n cảnh. Sự sử dụng th&ecirc;m c&ocirc;ng tắc kh&oacute;a thu ph&oacute;ng sẽ cho ph&eacute;p người chụp ảnh bảo vệ v&agrave;nh ống k&iacute;nh ở độ d&agrave;i tối thiểu l&agrave;m giảm những t&aacute;c động do trọng lực tự nhi&ecirc;n m&agrave; n&oacute; c&oacute; thể sẽ k&eacute;o v&agrave;nh ống k&iacute;nh xuống dưới trong suốt qu&aacute; tr&igrave;nh di chuyển.</p>\r\n\r\n<p><img alt=\"\" src=\"http://zshop.vn/images/b/Update/up3/Nikon%20AF-S%2018-200mm%202.jpg\" style=\"height:500px; margin:0px; width:500px\" /></p>\r\n\r\n<p>Nikon AF-S 18-200mm f/3.5-5.6 G ED VR II</p>\r\n\r\n<p><strong>T&Iacute;NH NĂNG NỔI BẬT</strong></p>\r\n\r\n<p><strong>Ti&ecirc;u cự l&yacute; tưởng 18-200mm</strong></p>\r\n\r\n<p>Ống k&iacute;nh&nbsp;<strong>Nikkor Zoom DX AF-S 18-200mm f/3.5-5.6G IF ED VR</strong>&nbsp;sở hữu một dải ti&ecirc;u cự kh&aacute; l&yacute; tưởng&nbsp;<strong>18-105mm</strong>&nbsp;để c&oacute; thể đ&aacute;p ứng hầu hết mọi nhu cầu chụp ảnh từ cảnh sự kiện trong nh&agrave;, ngo&agrave;i trời, phong cảnh cho đến ảnh ch&acirc;n dung. Khi được lắp v&agrave;o bất kỳ m&aacute;y ảnh số DSLR định dạng DX n&agrave;o của Nikon, g&oacute;c ảnh đều tương đương với g&oacute;c ảnh được tạo bởi ống k&iacute;nh c&oacute; chiều d&agrave;i ti&ecirc;u cự từ 27 đến 300mm tr&ecirc;n m&aacute;y ảnh phim 35mm.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Hiệu ứng bokeh mềm c&ugrave;ng khẩu f/3.5-5.6</strong></p>\r\n\r\n<p>Khẩu độ F3.5-F5.6 kết hợp với cấu tạo gồm đến 7 l&aacute; khẩu gi&uacute;p tạo th&agrave;nh một v&ograve;ng tr&ograve;n l&yacute; tưởng,&nbsp;<strong>Nikkor Zoom DX AF-S 18-200mm f/3.5-5.6G IF ED VR</strong>&nbsp;gi&uacute;p tạo ra được những bức ảnh xuất sắc với hiệu ứng bokeh mềm, nhuyễn v&agrave; mịn m&agrave;ng v&agrave; lung linh hơn, nhất l&agrave; khi bạn chụp ảnh đ&ecirc;m, gi&uacute;p nổi bật chủ thể ch&iacute;nh của bức ảnh.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Nhiều cải tiến mới so với những d&ograve;ng ống k&iacute;nh cũ kh&aacute;c</strong></p>\r\n\r\n<p><strong>Nikkor Zoom DX AF-S 18-200mm f/3.5-5.6G IF ED VR</strong>&nbsp;l&agrave; ống k&iacute;nh&nbsp;<strong>IF (Internal Focus)</strong>&nbsp;n&ecirc;n việc lấy n&eacute;t được thực hiện ngay b&ecirc;n trong m&aacute;y, đầu ống k&iacute;nh ngay vị tr&iacute; v&ograve;ng cao su chỉnh n&eacute;t sẽ kh&ocirc;ng xoay như đời ống AF v&agrave; AF-D trước đ&oacute;, điều n&agrave;y gi&uacute;p người d&ugrave;ng thoải m&aacute;i hơn trong việc cầm ngay đầu ống k&iacute;nh l&agrave;m điểm tựa khi chụp. Ngo&agrave;i ra ống k&iacute;nh cũng lược bớt v&ograve;ng chỉnh khẩu tay, do đ&oacute;&nbsp;<strong>Nikkor Zoom DX AF-S 18-200mm f/3.5-5.6G IF ED VR</strong>&nbsp;sẽ th&iacute;ch hợp nhất khi sử dụng với c&aacute;c m&aacute;y DSLR v&agrave; m&aacute;y phim c&oacute; chế độ tự động lấy n&eacute;t AF đời mới. Chữ G in tr&ecirc;n ống minh họa cho đặc điểm n&agrave;y.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>M&ocirc;-tơ si&ecirc;u thanh gi&uacute;p lấy n&eacute;t si&ecirc;u tốc</strong></p>\r\n\r\n<p>M&ocirc;-tơ si&ecirc;u thanh&nbsp;<strong>Silent Wave Motor&nbsp;</strong>được t&iacute;ch hợp b&ecirc;n trong ống k&iacute;nh&nbsp;<strong>Nikkor Zoom DX AF-S 18-200mm f/3.5-5.6G IF ED VR</strong>&nbsp;đảm bảo lấy n&eacute;t nhanh, ch&iacute;nh x&aacute;c v&agrave; &ecirc;m &aacute;i hơn. Ngo&agrave;i ra, ống k&iacute;nh c&ograve;n cho ph&eacute;p người d&ugrave;ng dễ d&agrave;ng chuyển đổi một c&aacute;ch nhanh ch&oacute;ng từ chế độ AF (lấy n&eacute;t tự động) sang lấy n&eacute;t bằng tay (MF). Điều n&agrave;y c&oacute; nghĩa, chế độ MF c&oacute; thể hoạt động bất cứ l&uacute;c n&agrave;o bạn muốn bằng việc nắm v&ograve;ng cao su v&agrave; xoay thay v&igrave; gạt n&uacute;t MF tr&ecirc;n th&acirc;n ống.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Trang bị chống rung VR</strong></p>\r\n\r\n<p>Hệ thống chống rung&nbsp;<strong>VR (Vibration Reduction)</strong>&nbsp;của&nbsp;<strong>Nikkor Zoom DX AF-S 18-200mm f/3.5-5.6G IF ED VR</strong>&nbsp;gi&uacute;p giảm thiểu hiện tượng nh&ograve;e ảnh do rung tay, đồng thời tăng cơ hội giảm tốc độ m&agrave;n trập th&ecirc;m 3 khẩu, tức l&agrave; 8 lần, cho ph&eacute;p m&aacute;y chụp trong c&aacute;c m&ocirc;i trường &aacute;nh s&aacute;ng yếu như m&acirc;y m&ugrave;, trong nh&agrave; một c&aacute;ch dễ d&agrave;ng hơn. Với hệ thống n&agrave;y, ống k&iacute;nh sẽ tự động ph&aacute;t hiện nhưng rung lắc, t&iacute;nh to&aacute;n v&agrave; b&ugrave; trừ, từ đ&oacute; tự động điều chỉnh sự c&acirc;n bằng m&agrave; kh&ocirc;ng cần chuyển m&aacute;y sang bất cứ một chế độ nhất định n&agrave;o.</p>\r\n\r\n<p><img alt=\"\" src=\"http://zshop.vn/images/b/Update/up3/Nikon%20AF-S%2018-200mm.jpg\" style=\"height:340px; margin:0px; width:340px\" /></p>\r\n\r\n<p>Nikon AF-S 18-200mm f/3.5-5.6 G ED VR II</p>\r\n\r\n<p><strong>Cấu tạo ống k&iacute;nh gi&uacute;p tăng cường độ sắc n&eacute;t của h&igrave;nh ảnh</strong></p>\r\n\r\n<p>Ống k&iacute;nh&nbsp;<strong>Nikkor Zoom DX AF-S 18-200mm f/3.5-5.6G IF ED VR</strong>&nbsp;c&oacute; cấu tạo nhiều từ 16 thấu k&iacute;nh, chia l&agrave;m 12 nh&oacute;m, trong đ&oacute; c&oacute; 2 lớp thấu k&iacute;nh ED v&agrave; 3 lớp thấu k&iacute;nh phi cầu. K&iacute;nh ED c&oacute; độ t&aacute;n xạ thấp, khắc phục hiện tượng l&oacute;e s&aacute;ng v&agrave; b&oacute;ng ma, cho h&igrave;nh ảnh c&oacute; m&agrave;u sắc tự nhi&ecirc;n, sắc n&eacute;t v&agrave; c&oacute; độ tương phản cao ngay cả khi khẩu độ c&oacute; độ mở lớn nhất. Thấu k&iacute;nh phi cầu gi&uacute;p giảm thiểu hiện tượng sắc sai, giảm thiểu t&igrave;nh trạng mất n&eacute;t ở r&igrave;a bức ảnh, từ đ&oacute; hạn chế tối đa sự kh&aacute;c biệt về độ n&eacute;t giữa t&acirc;m v&agrave; r&igrave;a bức ảnh, đem đến cho bạn một tổng thể ảnh đẹp mắt.</p>\r\n', '3', '14', '8', '2015-01-26');
INSERT INTO `products` VALUES ('37', 'Nikon AF-S 35mm f/1.8G ED FX (Chính hãng)', '2', '12799260', '<p>Điểm nổi bật của sản phẩm</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>-Định dạng FX</p>\r\n\r\n<p>-Khoảng mở ống k&iacute;nh: f/1.8-16</p>\r\n\r\n<p>-Silent Wave Motor</p>\r\n\r\n<p>-Sử dụng k&iacute;nh lọc 58mm&nbsp;</p>\r\n', '<p><strong>Nikon 35mm f/1.8G ED</strong>&nbsp;l&agrave; một ống k&iacute;nh định dạng FX được thiết kế cho m&aacute;y ảnh Nikon F-mount. N&oacute; c&oacute; độ mở tối đa f/1.8 cho ph&eacute;p bạn tạo ra sự t&aacute;ch biệt giữa chủ đề của bạn v&agrave; nền cũng như khả năng chụp ảnh chất lượng cao trong điều kiện &aacute;nh s&aacute;ng yếu. Để tập trung, ống k&iacute;nh sử dụng một Silent Wave Motor cho nhanh v&agrave; y&ecirc;n tĩnh lấy n&eacute;t tự động.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Để duy tr&igrave; chất lượng quang học, ống k&iacute;nh&nbsp;<strong>Nikon 35mm f/1.8G ED</strong>&nbsp;n&agrave;y c&oacute; một yếu tố k&iacute;nh ph&acirc;n t&aacute;n cực thấp l&agrave;m tăng độ tương phản v&agrave; độ sắc n&eacute;t trong suốt h&igrave;nh ảnh cũng như một yếu tố phi cầu c&oacute; t&aacute;c dụng khử quang sai cầu v&agrave; biến dạng. Điều n&agrave;y t&iacute;ch hợp Lớp phủ m&agrave; thực tế loại bỏ b&oacute;ng mờ, kết quả l&agrave; một h&igrave;nh ảnh r&otilde; r&agrave;ng tr&ecirc;n to&agrave;n khung. Đối với t&iacute;nh linh hoạt ống k&iacute;nh n&agrave;y c&oacute; một khoảng c&aacute;ch tập trung tối thiểu l&agrave; 9,8 &quot;v&agrave; đi k&egrave;m với một nắp đậy ống k&iacute;nh .</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Ống kính Nikon 35mm f/1.8G ED\" src=\"http://zshop.vn/images/detailed/33/h4_Nikon-35mm-f1.8G-ED.jpg\" style=\"height:402px; margin:0px; width:750px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><em>Ống k&iacute;nh Nikon 35mm f/1.8G ED</em></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Th&ocirc;ng tin chi tiết v&ecirc; ống k&iacute;nh&nbsp;Nikon 35mm f/1.8G ED</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>-Định dạng DX ống k&iacute;nh n&agrave;y c&oacute; độ d&agrave;i ti&ecirc;u cự tương đương 52.5mm</p>\r\n\r\n<p>-G&oacute;c nh&igrave;n l&agrave; 63 &deg; tr&ecirc;n m&aacute;y ảnh định dạng FX v&agrave; 44 &deg; tr&ecirc;n m&aacute;y ảnh định dạng DX</p>\r\n\r\n<p>-Bảy c&aacute;nh m&agrave;ng tr&ograve;n sản xuất ngo&agrave;i khu vực tập trung mịn</p>\r\n\r\n<p>-X&acirc;y dựng quang học c&oacute; tới 11 yếu tố trong 8 nh&oacute;m</p>\r\n\r\n<p>-Sử dụng c&aacute;c bộ lọc 58mm&nbsp;</p>\r\n', '3', '15', '4', '2014-12-31');
INSERT INTO `products` VALUES ('38', 'Nikon AF-S 18-140mm f/3.5-5.6G ED VR (Chính hãng)', '2', '7399770', '<p>Điểm nổi bật của sản phẩm</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>- Tương đương 27-210mm tr&ecirc;n film 35mm</p>\r\n\r\n<p>- Ổn định h&igrave;nh ảnh VR</p>\r\n\r\n<p>- Th&ecirc;m thấu k&iacute;nh Low Dispersion Glass</p>\r\n\r\n<p>- Motor Lấy n&eacute;t si&ecirc;u thanh SWM</p>\r\n\r\n<p>- Focus tối thiểu 1,48 ft&nbsp;</p>\r\n\r\n<p>- V&ograve;ng khẩu 7 l&aacute;, Size k&iacute;nh lộc 67mm</p>\r\n', '<p><strong>GIỚI THIỆU</strong></p>\r\n\r\n<p>Mẫu ống k&iacute;nh mới của Nikon cho khoảng ti&ecirc;u cự tương đương 27-210 mm (quy đổi m&aacute;y phim 35 mm) khi lắp tr&ecirc;n m&aacute;y crop hay zoom quang khoảng 7,8x.<a href=\"http://zshop.vn/lens-nikon/\">&nbsp;<strong>Ống k&iacute;nh</strong>&nbsp;</a><strong><a href=\"http://zshop.vn/lens-nikon/\">Nikon</a>&nbsp;AF-S 18-140mm f/3.5-5.6 ED VR</strong>&nbsp;mm t&iacute;ch hợp hệ thống chống rung quang học VR mới nhất cho ph&eacute;p giảm thiểu rung tới 4 bước (stop) theo ti&ecirc;u chuẩn thử nghiệm CIPA.&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Nikon AF-S 18-140mm f/3.5-5.6G ED VR\" src=\"http://zshop.vn/images/detailed/28/AFS-DX-18-140-1375778645_500x013758568385201e8c660d73.jpg\" style=\"height:375px; margin:0px; width:500px\" /></p>\r\n\r\n<p>Nikon AF-S 18-140mm f/3.5-5.6G ED VR</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>- Nikon AF-S 18-140mm f/3.5-5.6 ED VR</strong>&nbsp;c&oacute; khẩu độ mở tối đa f/3.5-5.6 v&agrave; tối thiểu f/22-38. Thiết bị c&oacute; cấu tạo quang học bao gồm 17 thấu k&iacute;nh chia l&agrave;m 12 nh&oacute;m c&ugrave;ng 7 l&aacute; khẩu. Ống k&iacute;nh c&oacute; thể lấy n&eacute;t ở khoảng c&aacute;ch gần nhất 0,45 m&eacute;t, độ ph&oacute;ng đại tối đa 0,23x.&nbsp;</p>\r\n\r\n<p>- Sản phẩm c&oacute; chiều d&agrave;i 97 mm, đường k&iacute;nh tối đa 78 mm, c&acirc;n nặng 490 gram v&agrave; sử dụng k&iacute;nh lọc (filter) k&iacute;ch thước 67 mm.&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>T&Iacute;NH NĂNG NỔI BẬT</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>C&ocirc;ng nghệ ổn định h&igrave;nh ảnh Nikon VR II</strong></p>\r\n\r\n<p>Từ &aacute;nh s&aacute;ng ban ng&agrave;y cho đến ban đ&ecirc;m, b&ecirc;n trong hay b&ecirc;n ngo&agrave;i t&ograve;a nh&agrave;, d&ugrave; chụp gần hay xa, c&aacute;c bức ảnh s&aacute;ng tạo của bạn vẫn được bảo đảm t&iacute;nh linh hoạt cao. Nhờ v&agrave;o c&ocirc;ng nghệ ổn định h&igrave;nh ảnh&nbsp;<strong>Vibration Reduction II</strong>&nbsp;của Nikon cho ph&eacute;p chụp ảnh l&ecirc;n đến 4 tốc độ m&agrave;n trập cho bạn sự tự tin để tạo ra những bức ảnh tự nhi&ecirc;n ngay cả khi &aacute;nh s&aacute;ng &iacute;t hơn l&yacute; tưởng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Lớp phủ Super Integrated Coating độc quyền</strong></p>\r\n\r\n<p>Để n&acirc;ng cao hiệu suất của ống k&iacute;nh quang học, Nikon đ&atilde; phủ l&ecirc;n c&aacute;c th&agrave;nh phần thấu kh&iacute;nh lớp phủ độc quyền gi&uacute;p giảm hiện tượng b&oacute;ng ma v&agrave; t&aacute;n xạ một c&aacute;ch đ&aacute;ng kể. B&ecirc;n cạnh đ&oacute;, c&ocirc;ng nghệ&nbsp;<strong>Super Integrated Coating</strong>&nbsp;n&agrave;y c&ograve;n c&oacute; khả năng c&acirc;n bằng m&agrave;u sắc, đ&aacute;p ứng c&aacute;c ti&ecirc;u chuẩn chụp ảnh cao hơn so với c&aacute;c d&ograve;ng ống k&iacute;nh th&ocirc;ng thường.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>C&ocirc;ng nghệ lấy n&eacute;t si&ecirc;u thanh Silent Wave Motor (SWM)</strong></p>\r\n\r\n<p>C&ocirc;ng nghệ lấy n&eacute;t si&ecirc;u thanh&nbsp;<strong>Silent Wave Motor</strong>&nbsp;được t&iacute;ch hợp b&ecirc;n trong ống k&iacute;nh&nbsp;<strong>Nikon AF-S 18-140mm f/3.5-5.6 ED VR</strong>&nbsp;đảm bảo khả năng căn n&eacute;t tự động nhanh hơn, ch&iacute;nh x&aacute;c v&agrave; &ecirc;m &aacute;i hơn bao giờ hết.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>C&ocirc;ng nghệ thấu k&iacute;nh phi cầu mới nhất</strong></p>\r\n\r\n<p>C&ocirc;ng nghệ&nbsp;<strong>Aspherical Lens Elements</strong>&nbsp;dựa tr&ecirc;n 3 yếu tố m&agrave;i ch&iacute;nh x&aacute;c, kết hợp k&iacute;nh v&agrave; plastic v&agrave; đ&uacute;c, l&agrave;m cho thấu k&iacute;nh gần như phẳng. C&ocirc;ng nghệ cao cấp n&agrave;y gi&uacute;p Nikon gần như ho&agrave;n thiện khả năng triệt ti&ecirc;u hiện tượng coma, b&oacute;ng mờ, hiện tượng quang sai, cũng như c&aacute;c hiện tượng biến dạng kh&aacute;c của ống k&iacute;nh khi chụp ở khẩu độ rộng hơn.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Hệ thống lấy n&eacute;t th&ocirc;ng minh</strong></p>\r\n\r\n<p>Người d&ugrave;ng c&oacute; thể dễ d&agrave;ng chuyển từ chế độ lấy n&eacute;t tự động&nbsp;<strong>AF (Auto Focus)</strong>&nbsp;sang lấy n&eacute;t bằng tay dễ d&agrave;ng v&agrave; nhanh ch&oacute;ng với n&uacute;t chuyển A-M tr&ecirc;n ống k&iacute;nh. Cơ chế lấy n&eacute;t bằng tay mượt m&agrave; v&agrave; ch&iacute;nh x&aacute;c l&agrave; một trong những t&iacute;nh năng của Nikon được ưa chuộng bởi hầu hết c&aacute;c nh&agrave; nhiếp ảnh chuy&ecirc;n nghiệp.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>C&ocirc;ng nghệ ống k&iacute;nh cao cấp</strong></p>\r\n\r\n<p>K&iacute;nh&nbsp;<strong>ED (Extra-low Dispersion)</strong>&nbsp;c&oacute; độ t&aacute;n xạ cực thấp. L&agrave; một yếu tố quan trọng trong c&ocirc;ng nghệ sản xuất ống k&iacute;nh chụp xa (telephoto) của Nikon. C&ocirc;ng nghệ cho ra đời c&aacute;c loại thấu k&iacute;nh độ sắc n&eacute;t cực cao v&agrave; hiệu chỉnh m&agrave;u sắc trung thực th&ocirc;ng qua việc giảm biến dạng m&agrave;u sắc. C&aacute;c ống k&iacute;nh ED cho độ n&eacute;t v&agrave; tương phản cao ngay cả ở khẩu độ mở lớn nhất.</p>\r\n', '3', '15', '68', '2015-01-31');
INSERT INTO `products` VALUES ('39', 'Nikon AF 80-200mm f/2.8D (Chính hãng)', '2', '20234900', '<p>Điểm nổi bật của sản phẩm</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>- H&atilde;ng sản xuất: Nikon, Tele c&oacute; zoom</p>\r\n\r\n<p>- Cấu tạo ống k&iacute;nh: 6 thấu k&iacute;nh thuộc 6 nh&oacute;m</p>\r\n\r\n<p>- G&oacute;c nh&igrave;n ngang: 28&deg;30&#39;, Số l&aacute; m&agrave;n chập : 9 l&aacute;</p>\r\n\r\n<p>- Lấy n&eacute;t: AF/MF, Khẩu độ f : 2.8</p>\r\n\r\n<p>- Khoảng c&aacute;ch chụp gần nhất: 1.9m, Loại hood : BH-7</p>\r\n\r\n<p>- Đường k&iacute;nh filter :&nbsp;&Oslash;77mm, Trọng lượng: 1400g</p>\r\n', '<p><strong>GIỚI THIỆU</strong></p>\r\n\r\n<p>L&agrave; d&ograve;ng ống k&iacute;nh telezoom được hỗ trợ th&ocirc;ng tin cho hệ thống đo s&aacute;ng 3D Matrix v&agrave; ra đời từ năm 1992, nhưng cho đến nay&nbsp;<strong><a href=\"http://zshop.vn/lens-nikon/\">ống k&iacute;nh&nbsp;Nikkor</a>&nbsp;AF 80-200mm F/2.8D ED</strong>&nbsp;vẫn l&agrave; niềm mơ ước của nhiều nhiếp ảnh gia y&ecirc;u th&iacute;ch chụp cảnh thể thao hay cảnh thi&ecirc;n nhi&ecirc;n hoang d&atilde;. Với khẩu độ l&yacute; tưởng F2.8 c&ugrave;ng dải ti&ecirc;u cự rộng 80-200mm, người d&ugrave;ng sẽ thoải m&aacute;i s&aacute;ng tạo kh&ocirc;ng ngừng c&ugrave;ng những khoảnh khắc tuyệt vời của c&aacute;c lo&agrave;i động vật trong thi&ecirc;n nhi&ecirc;n hay thậm ch&iacute; l&agrave; những pha đi b&oacute;ng đẹp mắt trong những trận cầu đỉnh cao hấp dẫn nhất h&agrave;nh tinh. Với những cải tiến so với những d&ograve;ng trước đ&oacute;,&nbsp;<strong>Nikkor AF 80-200mm F/2.8D ED</strong>&nbsp;c&ograve;n cho ph&eacute;p bạn chuyển đổi giữa lấy n&eacute;t bằng tay v&agrave; lấy n&eacute;t tự động thật dễ d&agrave;ng bằng việc nắm v&ograve;ng cao su v&agrave; xoay. Thấu k&iacute;nh ED chi&ecirc;t suất cao c&ugrave;ng đ&oacute; sẽ tăng cường triệt ti&ecirc;u hiện tượng l&oacute;e s&aacute;ng, b&oacute;ng ma v&agrave; cải thiện r&otilde; rệt lượng s&aacute;ng truyền qua, cho ảnh đẹp ho&agrave;n hảo d&ugrave; l&agrave; r&igrave;a hay trung t&acirc;m.</p>\r\n\r\n<p><strong>T&Iacute;NH NĂNG NỔI BẬT</strong></p>\r\n\r\n<p><strong>Ống k&iacute;nh si&ecirc;u zoom</strong></p>\r\n\r\n<p><strong>Nikkor AF 80-200mm F/2.8D ED</strong>&nbsp;sở hữu dải ti&ecirc;u cự rộng 80-200mm c&ugrave;ng khẩu độ F2.8 l&agrave; một trong những d&ograve;ng ống k&iacute;nh zoom đ&aacute;ng tự h&agrave;o của Nikon. Với ti&ecirc;u cự n&agrave;y, chiếc ống k&iacute;nh c&oacute; thể đ&aacute;p ứng tốt nhu cầu chụp ảnh hoang d&atilde; hay ghi lại những khoảnh khắc tuyệt vời trong những trận cầu đỉnh cao hấp dẫn nhất h&agrave;nh tinh.</p>\r\n\r\n<p><img alt=\"\" src=\"http://zshop.vn/images/b/Update/up/Nikon%20AF%2080-200mm2.jpg\" style=\"height:500px; margin:0px; width:500px\" /></p>\r\n\r\n<p>Nikon AF 80-200mm f/2.8D</p>\r\n\r\n<p><strong>Lấy n&eacute;t tự động thật đơn giản</strong></p>\r\n\r\n<p><strong>Nikkor AF 80-200mm F/2.8D ED</strong>&nbsp;cho ph&eacute;p người d&ugrave;ng dễ d&agrave;ng chuyển đổi một c&aacute;ch nhanh ch&oacute;ng từ chế độ AF (lấy n&eacute;t tự động) sang lấy n&eacute;t bằng tay (MF). Điều n&agrave;y c&oacute; nghĩa, chế độ MF c&oacute; thể hoạt động bất cứ l&uacute;c n&agrave;o bạn muốn bằng việc nắm v&ograve;ng cao su v&agrave; xoay thay v&igrave; gạt n&uacute;t MF tr&ecirc;n th&acirc;n ống.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Thấu k&iacute;nh ED gi&uacute;p tăng cường độ sắc n&eacute;t cho h&igrave;nh ảnh</strong></p>\r\n\r\n<p>B&ecirc;n trong ống k&iacute;nh &nbsp;l&agrave; 3 lớp thấu k&iacute;nh&nbsp;<strong>ED (Extra-low Dispersion)</strong>&nbsp;c&oacute; độ t&aacute;n xạ cực thấp, khắc phục hiện tượng l&oacute;e s&aacute;ng v&agrave; b&oacute;ng ma, cho h&igrave;nh ảnh c&oacute; m&agrave;u sắc tự nhi&ecirc;n, sắc n&eacute;t v&agrave; c&oacute; độ tương phản cao ngay cả khi khẩu độ c&oacute; độ mở lớn nhất. Đ&acirc;y l&agrave; một yếu tố cực kỳ quan trọng trong c&ocirc;ng nghệ sản xuất ống k&iacute;nh chụp xa (tele) của Nikon.</p>\r\n\r\n<p><img alt=\"\" src=\"http://zshop.vn/images/b/Update/up/Nikon%20AF%2080-200mm.jpg\" style=\"height:340px; margin:0px; width:340px\" /></p>\r\n\r\n<p>Nikon AF 80-200mm f/2.8D</p>\r\n\r\n<p><strong>Tự động lấy n&eacute;t D-type</strong></p>\r\n\r\n<p>Thuộc d&ograve;ng m&aacute;y ảnh sử dụng c&ocirc;ng nghệ lấy n&eacute;t tự động D-type của Nikon, ống k&iacute;nh&nbsp;<strong>Nikkor AF 80-200mm F2.8D ED</strong>&nbsp;c&oacute; khả năng chuyển tiếp th&ocirc;ng tin về khoảng c&aacute;ch từ chủ thể đến m&aacute;y ảnh. Điều n&agrave;y tạo thuận lợi cho cơ chế đo ma trận 3D v&agrave; c&acirc;n bằng đ&egrave;n Flash đa cảm biến 3D. T&iacute;nh năng n&agrave;y tương th&iacute;ch với c&aacute;c m&aacute;y ảnh tự động phơi s&aacute;ng như: F6, F5, F100, F90X, F80, F75, F70, F65, F60, F55, F50, PRONEA S, PRONEA 600i, series D2, series D1, D100 v&agrave; D70s/D70. V&agrave; tự động điều khiển đ&egrave;n flash như: F6, F5, F100, F90X, F80, F75, F70, series D2, series D1, D100 v&agrave; D70s/D70.</p>\r\n', '3', '12', '5', '2015-01-31');
INSERT INTO `products` VALUES ('40', 'Nikon 30-110mm CX VR f3.5-5.6 (Chính hãng)', '2', '4118720', '<p>Điểm nổi bật của sản phẩm</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>- Độ d&agrave;i ti&ecirc;u cự: 30 - 110 mm,&nbsp;G&oacute;c nh&igrave;n: 29,4 &deg; - 8,2 &deg;</p>\r\n\r\n<p>- Khẩu độ tối đa: f/3.8,&nbsp;khẩu độ Tối thiểu: f/16</p>\r\n\r\n<p>- Nh&oacute;m / th&agrave;nh phần: 12/18,&nbsp;Số thấu k&iacute;nh: &nbsp;7</p>\r\n\r\n<p>- Tự động lấy n&eacute;t: C&oacute;,&nbsp;Ổn định h&igrave;nh ảnh: C&oacute;</p>\r\n', '<p><strong>GIỚI THIỆU</strong></p>\r\n\r\n<p><strong><a href=\"http://zshop.vn/lens-nikon/\">Ống k&iacute;nh Nikon</a>&nbsp;30-110mm CX VR f/3.8-5.6&nbsp;</strong>&nbsp;Định dạng l&agrave; một ống k&iacute;nh zoom 3.6x được thiết kế để sử dụng với c&aacute;c định dạng Nikon CX . Phạm vi 81-297mm (tương đương ở định dạng 35mm) cung cấp cho bạn một ống k&iacute;nh tele zoom tuyệt vời, l&yacute; tưởng cho chụp ch&acirc;n dung, phong cảnh, thể thao, v&agrave; c&aacute;c mũi ch&iacute;ch ngừa h&agrave;nh động. Hệ thống Chống Rung (VR II) l&agrave;m cho n&oacute; c&oacute; thể chụp với tốc độ m&agrave;n trập thấp hơn nhiều, với c&aacute;c kết quả sắc n&eacute;t hơn, hơn l&agrave; th&ocirc;ng thường c&aacute;c trường hợp. Điều n&agrave;y đặc biệt hữu &iacute;ch trong c&aacute;c t&igrave;nh huống &aacute;nh s&aacute;ng thấp, v&agrave; khi sử dụng c&ograve;n l&agrave; một phần của dải zoom (nơi m&agrave; c&aacute;c chuyển động nhỏ được ph&oacute;ng đại trong một h&igrave;nh ảnh).</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Nikon 30-110mm</strong>&nbsp;Sử dụng k&iacute;nh ph&acirc;n t&aacute;n thấp th&ecirc;m, Super Integrated Coating, v&agrave; giảm độ rung ống k&iacute;nh n&agrave;y mang lại h&igrave;nh ảnh tuyệt vời, vẫn c&ograve;n hoặc video. Tập trung nội bộ l&agrave;m cho một ống k&iacute;nh c&oacute; thể, nhỏ nhẹ hơn với khoảng c&aacute;ch gần hơn tập trung. Sắp tới trong &iacute;t hơn 2,5 &quot;(6.35cm) chiều d&agrave;i, v&agrave; chỉ c&acirc;n nặng 6,2 oz (175,76 g) ống k&iacute;nh n&agrave;y sẽ kh&ocirc;ng bao giờ c&acirc;n nhắc bạn xuống.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Nikon 30-110mm CX VR f3.5-5.6\" src=\"http://zshop.vn/images/DIEPTV/NIKON%2030-110/Nikon-30-100.jpg\" style=\"height:408px; margin:0px; width:500px\" /></p>\r\n\r\n<p>Nikon 30-110mm CX VR f3.5-5.6</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>T&Iacute;NH NĂNG NỔI BẬT</strong></p>\r\n\r\n<p><strong>Ống k&iacute;nh Nhỏ gọn</strong></p>\r\n\r\n<p>Nikon 30-110 L&agrave;m việc với phạm vi bảo hiểm zoom tốt hơn v&agrave; chụp h&igrave;nh ảnh độ cao, chạy &acirc;m giai, từ thể thao h&agrave;nh động bắn từ kh&aacute;n đ&agrave;i, với tỷ lệ hao hụt tinh tế nhất của thổi t&oacute;c trong gi&oacute;. Mở rộng tiếp cận của bạn Nikon 30-110 mm (g&oacute;c ảnh tương đương của một mm 81-297 mm trong 35 định dạng), v&agrave; c&ocirc; lập c&aacute;c đối tượng từ nguồn gốc của họ với độ r&otilde; n&eacute;t. IF (Internal Tập trung) duy tr&igrave; chặt của k&iacute;ch thước, ngay cả khi sử dụng zoom.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Ph&ugrave; hợp với chụp ch&acirc;n dung</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Nikon 30-110 mm c&oacute; vẽ ch&acirc;n dung một bước xa hơn. Bức ch&acirc;n dung tuyệt đẹp với hiệu ứng nền mềm tập trung của bạn. Nhỏ gọn v&agrave; dễ mang theo, nhận ra những biểu hiện tinh tế v&agrave; chi tiết ph&uacute;t. 1 ống k&iacute;nh Nikkor ch&iacute;nh x&aacute;c cung cấp độ sắc n&eacute;t cao v&agrave; m&agrave;u sắc điều chỉnh ngay cả ở khẩu độ rộng nhất. Bởi v&igrave; ch&acirc;n dung thường được th&uacute;c đẩy của thời điểm n&agrave;y, c&oacute; giảm rung (VR II) để giữ h&igrave;nh ảnh bị mờ.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Nikon 30-110mm CX VR f3.5-5.6\" src=\"http://zshop.vn/images/DIEPTV/NIKON%2030-110/Nikon-1-30-110mm-Sample.jpg\" style=\"height:368px; margin:0px; width:550px\" /></p>\r\n\r\n<p>Nikon 30-110mm CX VR f3.5-5.6</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đến gần v&agrave; c&aacute; nh&acirc;n để ti&ecirc;m ch&uacute;t kiểu c&aacute;ch cho nhiếp ảnh thể thao của bạn bằng c&aacute;ch sử dụng ống k&iacute;nh Nikon 30-110 mm. Được trang bị với giảm rung (VR II), ống k&iacute;nh n&agrave;y ph&acirc;n biệt c&aacute;c đối tượng từ nguồn gốc, l&agrave;m cho n&oacute; một sự lựa chọn tuyệt vời cho m&agrave;i giũa về đối tượng v&agrave; tạo ra c&aacute;c h&igrave;nh ảnh trực quan sống động v&agrave; ho&agrave;n hảo để tải l&ecirc;n diễn đ&agrave;n hoặc c&aacute;c trang web truyền th&ocirc;ng x&atilde; hội. Ngay cả ở độ ph&oacute;ng đại mở rộng nhất, h&igrave;nh ảnh được kết xuất tinh thể r&otilde; r&agrave;ng v&agrave; sắc n&eacute;t, l&agrave;m cho Nikon 30-110mm l&agrave; một lựa chọn ho&agrave;n hảo cho c&aacute;c đối tượng chuyển động nhanh từ xa.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>H&igrave;nh ảnh cảnh quan đẹp đến với cuộc sống</strong></p>\r\n\r\n<p>L&agrave;m việc với phạm vi zoom mở rộng bằng c&aacute;ch sử dụng Nikon 30-110mm v&agrave; sản xuất vẻ s&acirc;u rộng thể hiện thế giới của bạn. C&aacute;c đối tượng cần phải được ph&acirc;n lập từ nguồn gốc của họ, chẳng hạn như một chiếc thuyền buồm, dễ d&agrave;ng bằng ống k&iacute;nh n&agrave;y. Được trang bị với giảm rung (VR II) l&ecirc;n đến bốn bước, bạn sẽ nab từng chi tiết m&agrave;u sắc ch&iacute;nh x&aacute;c với ho&agrave;ng h&ocirc;n ấn tượng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Vibration Reduction</strong></p>\r\n\r\n<p>Nikon 30-110mm với VR s&aacute;ng tạo hệ thống giảm thiểu h&igrave;nh ảnh mờ do rung m&aacute;y, v&agrave; cung cấp tương đương với chụp ở tốc độ m&agrave;n trập (t&aacute;m lần) nhanh hơn. N&oacute; cho ph&eacute;p chụp cầm tay l&uacute;c ho&agrave;ng h&ocirc;n, v&agrave;o ban đ&ecirc;m, v&agrave; ngay cả trong nội thất k&eacute;m lit. Ống k&iacute;nh VR hệ thống cũng tự động ph&aacute;t hiện khi c&aacute;c nhiếp ảnh gia chảo - kh&ocirc;ng c&oacute; chế độ đặc biệt được y&ecirc;u cầu.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Nikon 30-110mm CX VR f3.5-5.6\" src=\"http://zshop.vn/images/DIEPTV/NIKON%2030-110/Nikon-30-100-Hong.jpg\" style=\"height:443px; margin:0px; width:550px\" /></p>\r\n\r\n<p>Nikon 30-110mm CX VR f3.5-5.6</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Extra-Low Dispersion Glass</strong></p>\r\n\r\n<p>Nikon ph&aacute;t triển ED (th&ecirc;m thấp Dispersion) k&iacute;nh để cho ph&eacute;p sản xuất ống k&iacute;nh cung cấp độ sắc n&eacute;t cao v&agrave; hiệu chỉnh m&agrave;u sắc bằng c&aacute;ch giảm thiểu quang sai m&agrave;u. K&iacute;nh ED cung cấp tất cả c&aacute;c lợi &iacute;ch, nhưng kh&ocirc;ng ai trong số những nhược điểm của canxi k&iacute;nh fluorite. Với sự đổi mới n&agrave;y, Nikon đ&atilde; ph&aacute;t triển một số loại k&iacute;nh ED ph&ugrave; hợp cho c&aacute;c ống k&iacute;nh kh&aacute;c nhau điển h&igrave;nh l&agrave; Nikon 30-110mm</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Nikon Super Integrated Coating</strong></p>\r\n\r\n<p>Để n&acirc;ng cao hiệu suất của c&aacute;c yếu tố ống k&iacute;nh quang học, Nikon 30-110 sử dụng một lớp phủ ống k&iacute;nh độc quyền đa gi&uacute;p giảm ma v&agrave; b&ugrave;ng l&ecirc;n một cấp độ kh&ocirc;ng đ&aacute;ng kể. Nikon Super Integrated Coating đạt được một số mục ti&ecirc;u, bao gồm cả phản &aacute;nh giảm thiểu trong phạm vi bước s&oacute;ng rộng hơn v&agrave; c&acirc;n bằng m&agrave;u sắc v&agrave; sinh sản cao cấp.</p>\r\n', '3', '13', '5', '2015-01-30');

-- ----------------------------
-- Table structure for `users`
-- ----------------------------
DROP TABLE IF EXISTS `users`;
CREATE TABLE `users` (
  `Id` int(11) NOT NULL AUTO_INCREMENT,
  `Username` varchar(20) COLLATE utf8_unicode_ci NOT NULL,
  `Password` varchar(255) COLLATE utf8_unicode_ci NOT NULL,
  `Name` varchar(50) COLLATE utf8_unicode_ci NOT NULL,
  `Email` varchar(50) COLLATE utf8_unicode_ci NOT NULL,
  `DOB` date DEFAULT NULL,
  `Permission` int(11) NOT NULL,
  PRIMARY KEY (`Id`)
) ENGINE=MyISAM AUTO_INCREMENT=32 DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

